INSERT INTO `shuowen_voice_revel` VALUES (834, '唫', 'jìn', '第二卷', '口部', '口急也。从口，金聲。', '口;金', '金', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (835, '噤', 'jìn', '第二卷', '口部', '口閉也。从口，禁聲。', '口;禁', '禁', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (836, '名', 'mínɡ', '第二卷', '口部', '自命也。从口夕。夕者，冥也。冥不相見，故㠯口自名。', '口;夕', '', 'null', NULL, '口;夕', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (837, '吾', 'wú', '第二卷', '口部', '我自偁也。从口，五聲。', '口;五', '五', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (838, '哲;悊', 'zhé', '第二卷', '口部', '知也。从口，㪿聲。哲或从心。古文哲从三吉。', '口;㪿;心;吉', '㪿', 'null', NULL, '口;心;吉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
