INSERT INTO `shuowen_voice_revel` VALUES (895, '𠱫', 'è', '第二卷', '口部', '語相訶歫也。从口䇂。䇂，惡聲也。讀若櫱。', '口;䇂;櫱', '櫱', 'null', NULL, '口;䇂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (896, '吺', 'dōu', '第二卷', '口部', '讘吺，多言也。从口，殳聲。', '口;殳', '殳', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (897, '呧', 'dǐ', '第二卷', '口部', '苛也。从口，氐聲。', '口;氐', '氐', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (898, '呰', 'zǐ', '第二卷', '口部', '苛也。从口，此聲。', '口;此', '此', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
