INSERT INTO `shuowen_voice_revel` VALUES (900, '唊', 'jiá', '第二卷', '口部', '妄言也。从口，夾聲。讀若莢。', '口;夾;莢', '夾;莢', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (901, '㗐', 'kè', '第二卷', '口部', '多言也。从口，盇聲。讀若甲。', '口;盇;甲', '盇;甲', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (902, '嗙', 'bēnɡ', '第二卷', '口部', '訶聲嗙喻也。从口，旁聲。司馬相如說「淮南 宋 蔡舞嗙喻」也。', '口;旁', '旁', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (903, '噧', 'xiè', '第二卷', '口部', '高气多言也。从口，𧍣省聲。春秋傳曰「噧言」。', '口;𧍣', '𧍣', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (904, '叴', 'qiú', '第二卷', '口部', '高气也。从口，九聲。臨淮有叴猶縣。', '口;九', '九', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (905, '嘮', 'chāo', '第二卷', '口部', '嘮呶，讙也。从口，勞聲。', '口;勞', '勞', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (906, '呶', 'náo', '第二卷', '口部', '讙聲也。从口，奴聲。詩曰「載號載呶」。', '口;奴', '奴', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (907, '叱', 'chì', '第二卷', '口部', '訶也。从口，七聲。', '口;七', '七', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
