INSERT INTO `shuowen_voice_revel` VALUES (913, '吁', 'xū', '第二卷', '口部', '驚也。从口，于聲。', '口;于', '于', 'null', NULL, '口', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (914, '嘵', 'xiāo', '第二卷', '口部', '懼聲也。从口，堯聲。詩曰「予維音之嘵嘵」。', '口;堯', '堯', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (915, '嘖;謮', 'zé', '第二卷', '口部', '大呼也。从口，責聲。嘖或从言。', '口;責;言', '責', 'null', NULL, '口;言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (916, '嗸', 'áo', '第二卷', '口部', '眾口愁也。从口，敖聲。詩曰「哀鳴嗸嗸」。', '口;敖', '敖', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (917, '唸', 'diàn', '第二卷', '口部', '唸吚，呻也。从口，念聲。詩曰「民之方唸吚」。', '口;念', '念', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (918, '吚', 'xī', '第二卷', '口部', '唸吚也。从口，伊省聲。', '口;伊', '伊', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (919, '㘙', 'yán', '第二卷', '口部', '呻也。从口，嚴聲。', '口;嚴', '嚴', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (920, '呻', 'shēn', '第二卷', '口部', '吟也。从口，申聲。', '口;申', '申', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (921, '吟', 'yín', '第二卷', '口部', '呻也。从口，今聲。吟或从音。[或从言。]', '口;今;音;言', '今', 'null', NULL, '口;音;言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (922, '嗞', 'zī', '第二卷', '口部', '嗟也。从口，茲聲。', '口;茲', '茲', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (923, '哤', 'mánɡ', '第二卷', '口部', '哤異之言。从口，尨聲。一曰襍語。讀若尨。', '口;尨', '尨', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (924, '叫', 'jiào', '第二卷', '口部', '嘑也。从口，丩聲。', '口;丩', '丩', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (925, '嘅', 'kài', '第二卷', '口部', '嘆也。从口，旣聲。詩曰「嘅其嘆矣」。', '口;旣', '旣', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (926, '唌', 'xián', '第二卷', '口部', '語唌嘆也。从口，延聲。', '口;延', '延', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (927, '嘆', 'tàn', '第二卷', '口部', '吞歎也。从口，歎省聲。一曰大息也。', '口;歎', '歎', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
