INSERT INTO `shuowen_voice_revel` VALUES (933, '各', 'ɡè', '第二卷', '口部', '異䛐也。从口夂。夂者，有行而止之不相聽意。', '口;夂', '', 'null', NULL, '口;夂', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (934, '否', 'fǒu', '第二卷', '口部', '不也。从口不。', '口;不', '不', 'null', NULL, '口', 'null', 'null', 'null', 'null', '重出应删', 0x31, 'null', 'null', 'null', DEFAULT);
