INSERT INTO `shuowen_voice_revel` VALUES (938, '嗀', 'hù', '第二卷', '口部', '歐皃。从口，㱿聲。春秋傳曰「君將嗀之」。', '口;㱿', '㱿', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (939, '咼', 'kuā', '第二卷', '口部', '口戾也。从口，冎聲。', '口;冎', '冎', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
