INSERT INTO `shuowen_voice_revel` VALUES (941, '嗼', 'mò', '第二卷', '口部', '𠴫嗼也。从口，莫聲。', '口;莫', '莫', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (942, '𠯑', 'ɡuā', '第二卷', '口部', '塞口也。从口，氒省聲。氒，古文厥字。古文从甘。', '口;氒;甘', '氒', 'null', NULL, '口;甘', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (943, '嗾', 'sǒu', '第二卷', '口部', '使犬聲。从口，族聲。春秋傳曰「公嗾夫獒」。', '口;族', '族', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (944, '吠', 'fèi', '第二卷', '口部', '犬鳴。从口犬。', '口;犬', '', '犮;伐;發', '字或作㕹、𠲎。释兽有鼣鼠，释文或作𪖆', '口;犬', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (945, '咆', 'páo', '第二卷', '口部', '嗥也。从口，包聲。', '口;包', '包', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
