INSERT INTO `shuowen_voice_revel` VALUES (947, '喈', 'jiē', '第二卷', '口部', '烏鳴聲也。从口，皆聲。一曰鳳皇鳴聲喈喈。', '口;皆', '皆', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (948, '哮', 'xiāo', '第二卷', '口部', '豕驚聲也。从口，孝聲。', '口;孝', '孝', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
