INSERT INTO `shuowen_voice_revel` VALUES (950, '呝', 'è', '第二卷', '口部', '喔也。从口，戹聲。', '口;戹', '戹', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (951, '咮', 'zhòu', '第二卷', '口部', '鳥口也。从口，朱聲。', '口;朱', '朱', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
