INSERT INTO `shuowen_voice_revel` VALUES (953, '啄', 'zhuó', '第二卷', '口部', '鳥食也。从口，豖聲。', '口;豖', '豖', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (954, '唬', 'xiāo', '第二卷', '口部', '虎聲也。从口虎。讀若暠。', '口;虎;暠', '暠', 'null', NULL, '口;虎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (955, '呦', 'yōu', '第二卷', '口部', '鹿鳴聲也。从口，幼聲。呦或从欠。', '口;幼;欠', '幼', 'null', NULL, '口;欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
