INSERT INTO `shuowen_voice_revel` VALUES (957, '喁', 'yónɡ', '第二卷', '口部', '魚口上見。从口，禺聲。', '口;禺', '禺', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (958, '局', 'jú', '第二卷', '口部', '促也。从口在尺下，復局之。一曰博，所㠯行棊，象形。', '口;尺', '', 'null', NULL, '口;尺', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (959, '㕣', 'yǎn', '第二卷', '口部', '山閒陷泥地。从口。从水，敗皃。讀若「沇州」之沇。九州之渥地也。故㠯沇名焉。古文㕣[歺省，谷]。', '口;水;沇;歺;谷', '沇', 'null', NULL, '口;水;歺;谷', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (960, '哦', 'é', '第二卷', '口部', '吟也。从口，我聲。', '口;我', '我', 'null', NULL, '口', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (961, '嗃', 'hè', '第二卷', '口部', '嗃嗃，嚴酷皃。从口，高聲。', '口;高', '高', 'null', NULL, '口', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
