INSERT INTO `shuowen_voice_revel` VALUES (1032, '𧾣', 'jú', '第二卷', '走部', '走意也。从走，敻聲。讀若繘。', '走;敻;繘', '敻;繘', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1033, '趠', 'chuò', '第二卷', '走部', '遠也。从走，卓聲。', '走;卓', '卓', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1034, '䟑', 'yuè', '第二卷', '走部', '趠䟑也。从走，龠聲。', '走;龠', '龠', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1035, '𧾵', 'jué', '第二卷', '走部', '大步也。从走，矍聲。', '走;矍', '矍', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1036, '𧼪', 'chì', '第二卷', '走部', '超特也。从走，契聲。', '走;契', '契', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1037, '䟇', 'jī', '第二卷', '走部', '走也。从走，幾聲。', '走;幾', '幾', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1038, '𧼗', 'fú', '第二卷', '走部', '走也。从走，岪聲。', '走;岪', '岪', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1039, '𧽻', 'jú', '第二卷', '走部', '狂走也。从走，矞聲。', '走;矞', '矞', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1040, '䟂', 'mán', '第二卷', '走部', '行遲也。从走，曼聲。', '走;曼', '曼', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1041, '趉', 'jué', '第二卷', '走部', '走也。从走，出聲。讀若「無尾之屈」。', '走;出;屈', '出;屈', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1042, '趜', 'jú', '第二卷', '走部', '竆也。从走，匊聲。', '走;匊', '匊', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1043, '趑', 'cī', '第二卷', '走部', '趑趄，行不進也。从走，次聲。', '走;次', '次', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1044, '趄', 'jū', '第二卷', '走部', '趑趄也。从走，且聲。', '走;且', '且', 'null', NULL, '走', 'null', 'null', 'null', 'null', '大徐补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1045, '𧽐', 'qiān', '第二卷', '走部', '蹇行𧽐𧽐也。从走，虔聲。讀若愆。', '走;虔;愆', '虔;愆', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1046, '䟒', 'quán', '第二卷', '走部', '行䟒趢也。从走，雚聲。一曰行曲脊皃。', '走;雚', '雚', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1047, '趢', 'lù', '第二卷', '走部', '䟒趢也。从走，录聲。', '走;录', '录', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1048, '䞭', 'qūn', '第二卷', '走部', '行速䞭䞭也。从走，夋聲。', '走;夋', '夋', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1049, '𧻕', 'cì', '第二卷', '走部', '側行也。从走，朿聲。詩曰「謂地葢厚，不敢不𧻕[蹐]」。', '走;朿;蹐', '朿;蹐', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1050, '䞨', 'kuǐ', '第二卷', '走部', '半步也。从走，圭聲。讀若跬同。', '走;圭;跬', '圭;跬', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1051, '䞾', 'chí', '第二卷', '走部', '䞾騭，輕薄也。从走，虒聲。讀若池。', '走;虒;池', '虒;池', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1052, '䞳', 'bó', '第二卷', '走部', '僵也。从走，咅聲。讀若匐。', '走;咅;匐', '咅;匐', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1053, '𧼳;𧻀;𧻧;䞣', 'chě', '第二卷', '走部', '距也。从走，㡿聲。漢令曰「𧻀張百人」。', '走;㡿', '㡿', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1054, '䟏', 'lì', '第二卷', '走部', '動也。从走，樂聲。讀若春秋傳曰「輔䟏」。', '走;樂', '樂', 'null', NULL, '走', 'null', 'null', 'null', 'null', '△', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1055, '趡', 'cuǐ', '第二卷', '走部', '動也。从走，隹聲。春秋傳曰「盟于趡」。趡，地名。', '走;隹', '隹', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1056, '𧻚', 'yuán', '第二卷', '走部', '𧻚田，易居也。从走，亘聲。', '走;亘', '亘', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1057, '𧽍', 'diān', '第二卷', '走部', '走頓也。从走，眞聲。讀若顚。', '走;眞;顚', '眞;顚', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1058, '𧻹', 'yǒnɡ', '第二卷', '走部', '喪擗𧻹。从走，甬聲。', '走;甬', '甬', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1059, '䟆', 'bì', '第二卷', '走部', '止行也。一曰竈上祭名也。从走，畢聲。', '走;畢', '畢', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1060, '𧽯', 'jiàn', '第二卷', '走部', '進也。从走，斬聲。', '走;斬', '斬', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1061, '趧', 'tí', '第二卷', '走部', '趧婁——四夷之舞，各自有曲。从走，是聲。', '走;是', '是', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1062, '趒', 'tiáo', '第二卷', '走部', '雀行也。从走，兆聲。', '走;兆', '兆', 'null', NULL, '走', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1063, '赶', 'qián', '第二卷', '走部', '舉尾走也。从走，干聲。', '走;干', '干', 'null', NULL, '走', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1064, '止', 'zhǐ', '第二卷', '止部', '下基也。象艸木出有阯，故㠯止爲足。凡止之屬皆从止。', '阯', '阯', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1065, '歱', 'zhǒnɡ', '第二卷', '止部', '跟也。从止，重聲。', '止;重', '重', 'null', NULL, '止', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1066, '𣥺;䟫', 'chēnɡ', '第二卷', '止部', '歫也。从止，尙聲。', '止;尙', '尙', 'null', NULL, '止', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1067, '歭', 'chí', '第二卷', '止部', '䠧也。从止，寺聲。', '止;寺', '寺', 'null', NULL, '止', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1068, '歫', 'jù', '第二卷', '止部', '止也。从止，巨聲。一曰槍也。一曰超歫。', '止;巨', '巨', 'null', NULL, '止', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1069, '歬;前', 'qián', '第二卷', '止部', '不行而進謂之歬。从止在舟上。', '止;舟', '', 'null', NULL, '止;舟', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1070, '歷;歴;𠪱', 'lì', '第二卷', '止部', '過也，傳也。从止，厤聲。', '止;厤', '厤', 'null', NULL, '止', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1071, '𣥹', 'chù', '第二卷', '止部', '至也。从止，叔聲。', '止;叔', '叔', 'null', NULL, '止', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1072, '𣦢', 'bì', '第二卷', '止部', '人不能行也。从止，辟聲。', '止;辟', '辟', 'null', NULL, '止', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1073, '歸', 'ɡuī', '第二卷', '止部', '女嫁也。从止，婦省，𠂤聲。籒文省。', '止;婦;𠂤', '𠂤', 'null', NULL, '止;婦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1074, '疌', 'jié', '第二卷', '止部', '疾也。从又。又，手也。从止。屮聲。', '又;止;屮', '屮', 'null', NULL, '又;止', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1075, '𤴘', 'niè', '第二卷', '止部', '機下足所履者。从止，从又，入聲。', '止;又;入', '入', 'null', NULL, '止;又', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1076, '𣥂', 'tà', '第二卷', '止部', '蹈也。从反止。讀若撻。', '止;撻', '撻', 'null', NULL, '止', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1077, '歰', 'sè', '第二卷', '止部', '不滑也。从四止。', '止', '', 'null', NULL, '止', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1078, '癶', 'bō', '第二卷', '癶部', '足剌癶也。从止𣥂。凡癶之屬皆从癶。讀若撥。', '止;𣥂;撥', '撥', 'null', NULL, '止;𣥂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1079, '登', 'dēnɡ', '第二卷', '癶部', '上車也。从癶豆。象登車形。籒文登从廾。', '癶;豆;廾', '', 'null', NULL, '癶;豆;廾', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1080, '癹', 'bá', '第二卷', '癶部', '㠯足蹋夷艸。从癶，从殳。春秋傳曰「癹夷薀祟之」。', '癶;殳', '癶', 'null', NULL, '殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1081, '步', 'bù', '第二卷', '步部', '行也。从止𣥂相背。凡步之屬皆从步。', '止;𣥂', '', 'null', NULL, '止;𣥂', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1082, '歲;嵗', 'suì', '第二卷', '步部', '木星也。越歷二十八宿，宣徧陰陽，十二月一次。从步，戌聲。律曆書名五星爲五步。', '步;戌', '戌', 'null', NULL, '步', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1083, '此', 'cǐ', '第二卷', '此部', '止也。从止匕。匕，相比次也。凡此之屬皆从此。', '止;匕', '', 'null', NULL, '止;匕', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1084, '啙', 'zǐ', '第二卷', '此部', '窳也。闕。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1085, '㭰', 'zuǐ', '第二卷', '此部', '識也。从此，朿聲。一曰藏也。', '此;朿', '朿', 'null', NULL, '此', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1086, '些', 'suò', '第二卷', '此部', '語辭也。从此，从二。', '此;二', '', 'null', NULL, '此;二', 'null', 'null', 'null', 'null', '新', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1087, '正', 'zhènɡ', '第二卷', '正部', '是也。从一，一㠯止。凡正之屬皆从正。古文正从𠄞。𠄞，古文上字。古文正从一足，足亦止也。', '一;止;𠄞;足', '', 'null', NULL, '一;止;𠄞;足', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1088, '乏', 'fá', '第二卷', '正部', '春秋傳曰「反正爲乏」。', '正', '', 'null', NULL, '正', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1089, '是', 'shì', '第二卷', '是部', '直也。从日正。凡是之屬皆从是。籒文是从古文正。', '日;正', '', 'null', NULL, '日;正', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1090, '韙;愇', 'wěi', '第二卷', '是部', '是也。从是，韋聲。春秋傳曰「犯五不韙」。籒文韙从心。', '是;韋;心', '韋', 'null', NULL, '是;心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1091, '尟', 'xiǎn', '第二卷', '是部', '是少也。是少，俱存也。从是少。賈侍中說。', '是;少', '', 'null', NULL, '是;少', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1092, '辵', 'chuò', '第二卷', '辵部', '乍行乍止也。从彳止。凡辵之屬皆从辵。讀若春秋傳曰「辵[躇]階而走」。', '彳;止;躇', '躇', 'null', NULL, '彳;止', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1093, '迹;蹟', 'jī', '第二卷', '辵部', '步處也。从辵，亦聲。或从足責。籒文迹从朿。', '辵;亦;足;責;朿', '亦;責;朿', 'null', NULL, '辵;足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1094, '𨘇', 'huì', '第二卷', '辵部', '無違也。从辵，舝聲。讀若害。', '辵;舝;害', '舝;害', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1095, '䢦', 'shuài', '第二卷', '辵部', '先道也。从辵，率聲。', '辵;率', '率', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1096, '邁', 'mài', '第二卷', '辵部', '遠行也。从辵，萬聲。邁或从𧍣。', '辵;萬;𧍣', '萬;𧍣', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1097, '巡', 'xún', '第二卷', '辵部', '視行也。从辵，川聲。', '辵;川', '川', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1098, '𨖏', 'jiù', '第二卷', '辵部', '恭謹行也。从辵，𣪘聲。讀若九。', '辵;𣪘;九', '𣪘;九', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1099, '徒', 'tú', '第二卷', '辵部', '步行也。从辵，土聲。', '辵;土', '土', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1100, '邎', 'yóu', '第二卷', '辵部', '行邎徑也。从辵，䌛聲。', '辵;䌛', '䌛', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1101, '𨒌;征', 'zhēnɡ', '第二卷', '辵部', '正行也。从辵，正聲。𨒌或从彳。', '辵;正;彳', '正', 'null', NULL, '辵;彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1102, '隨', 'suí', '第二卷', '辵部', '從行也。从辵，隋聲。', '辵;隋', '隋', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1103, '䢌', 'bó', '第二卷', '辵部', '行皃。从辵，𣎵聲。', '辵;𣎵', '𣎵', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1104, '迋', 'wànɡ', '第二卷', '辵部', '往也。从辵，王聲。春秋傳曰「子無我迋」。', '辵;王', '王', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1105, '逝', 'shì', '第二卷', '辵部', '往也。从辵，折聲。讀若誓。', '辵;折;誓', '折;誓', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1106, '䢐;徂', 'cú', '第二卷', '辵部', '往也。从辵，且聲。䢐，齊語。䢐或从彳。籒文从虘。', '辵;且;彳;虘', '且;虘', 'null', NULL, '辵;彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1107, '述', 'shù', '第二卷', '辵部', '循也。从辵，朮聲。籒文从秫。', '辵;朮;秫', '朮;秫', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1108, '遵', 'zūn', '第二卷', '辵部', '循也。从辵，尊聲。', '辵;尊', '尊', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1109, '適', 'shì', '第二卷', '辵部', '之也。从辵，啻聲。適，宋 魯語。', '辵;啻', '啻', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1110, '過', 'ɡuò', '第二卷', '辵部', '度也。从辵，咼聲。', '辵;咼', '咼', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1111, '遦', 'ɡuàn', '第二卷', '辵部', '習也。从辵，貫聲。', '辵;貫', '貫', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1112, '䢱', 'dú', '第二卷', '辵部', '媟䢱也。从辵，𧷏聲。', '辵;𧷏', '𧷏', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1113, '進', 'jìn', '第二卷', '辵部', '登也。从辵，閵省聲。', '辵;閵', '閵', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1114, '造;艁', 'zào', '第二卷', '辵部', '就也。从辵，告聲。譚長說「造，上士也」。古文造从舟。', '辵;告;舟', '告', 'null', NULL, '辵;舟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1115, '逾', 'yú', '第二卷', '辵部', '𨒋進也。从辵，俞聲。周書曰「無敢昏逾」。', '辵;俞', '俞', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1116, '遝', 'tà', '第二卷', '辵部', '䢔也。从辵，眔聲。', '辵;眔', '眔', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1117, '䢔', 'hé', '第二卷', '辵部', '遝也。从辵，合聲。', '辵;合', '合', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1118, '迮', 'zé', '第二卷', '辵部', '迮迮，起也。从辵，乍聲。', '辵;乍', '乍', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1119, '逪', 'cuò', '第二卷', '辵部', '䢒逪也。从辵，昔聲。', '辵;昔', '昔', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1120, '遄', 'chuán', '第二卷', '辵部', '往來數也。从辵，耑聲。易曰「巳事遄往」。', '辵;耑', '耑', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1121, '速;遬;𧫷', 'sù', '第二卷', '辵部', '疾也。从辵，束聲。籒文从欶。古文从欶，从言。', '辵;束;欶;欶;言', '束;欶;欶', 'null', NULL, '辵;言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1122, '迅', 'xùn', '第二卷', '辵部', '疾也。从辵，卂聲。', '辵;卂', '卂', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1123, '适', 'kuò', '第二卷', '辵部', '疾也。从辵，𠯑聲。讀與括同。', '辵;𠯑;括', '𠯑;括', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1124, '逆', 'nì', '第二卷', '辵部', '迎也。从辵，屰聲。關東曰逆，關西日迎。', '辵;屰', '屰', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1125, '迎', 'yínɡ', '第二卷', '辵部', '逢也。从辵，卬聲。', '辵;卬', '卬', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1126, '䢒', 'jiāo', '第二卷', '辵部', '會也。从辵，交聲。', '辵;交', '交', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1127, '遇', 'yù', '第二卷', '辵部', '逢也。从辵，禺聲。', '辵;禺', '禺', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1128, '遭', 'zāo', '第二卷', '辵部', '遇也。从辵，曹聲。一曰邐行。', '辵;曹', '曹', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1129, '遘', 'ɡòu', '第二卷', '辵部', '遇也。从辵，冓聲。', '辵;冓', '冓', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1130, '逢', 'fénɡ', '第二卷', '辵部', '遇也。从辵，夆聲。', '辵;夆', '夆', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1131, '遻', 'è', '第二卷', '辵部', '相遇驚也。从辵屰。屰亦聲。', '辵;屰', '屰', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1132, '迪', 'dí', '第二卷', '辵部', '道也。从辵，由聲。', '辵;由', '由', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '觉部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1133, '遞', 'dì', '第二卷', '辵部', '更易也。从辵，虒聲。', '辵;虒', '虒', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1134, '通', 'tōnɡ', '第二卷', '辵部', '達也。从辵，甬聲。', '辵;甬', '甬', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1135, '徙', 'xǐ', '第二卷', '辵部', '迻也。从辵，沙省聲。徙徙或从彳。古文徙[尾]。', '辵;沙;彳;尾', '沙', 'null', NULL, '辵;彳;尾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1136, '迻', 'yí', '第二卷', '辵部', '遷徙也。从辵，多聲。', '辵;多', '多', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1137, '遷;𨙙', 'qiān', '第二卷', '辵部', '登也。从辵，䙴聲。古文遷从手西。', '辵;䙴;手;西', '䙴;西', 'null', NULL, '辵;手', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1138, '運', 'yùn', '第二卷', '辵部', '迻徙也。从辵，軍聲。', '辵;軍', '軍', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1139, '遁', 'dùn', '第二卷', '辵部', '遷也。一曰逃也。从辵，盾聲。', '辵;盾', '盾', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1140, '遜', 'xùn', '第二卷', '辵部', '遁也。从辵，孫聲。', '辵;孫', '孫', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1141, '返', 'fǎn', '第二卷', '辵部', '還也。从辵反。反亦聲。商書曰「祖伊返」。春秋傳「返」从彳。', '辵;反;彳', '反', 'null', NULL, '辵;彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1142, '還', 'huán', '第二卷', '辵部', '復也。从辵，瞏聲。', '辵;瞏', '瞏', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1143, '選', 'xuǎn', '第二卷', '辵部', '遣也。从辵巽。巽遣之。巽亦聲。一曰擇也。', '辵;巽', '巽', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1144, '送', 'sònɡ', '第二卷', '辵部', '遣也。从辵，𠈪省。籒文不省。', '辵;𠈪', '', 'null', NULL, '辵;𠈪', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1145, '遣', 'qiǎn', '第二卷', '辵部', '縱也。从辵，𨺫聲。', '辵;𨺫', '𨺫', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1146, '邐', 'lǐ', '第二卷', '辵部', '行邐邐也。从辵，麗聲。', '辵;麗', '麗', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '麗轨', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1147, '逮', 'dài', '第二卷', '辵部', '唐逮，及也。从辵，隶聲。', '辵;隶', '隶', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1148, '遲;遟;𨒈', 'chí', '第二卷', '辵部', '徐行也。从辵，犀聲。詩曰「行道遲遲」。遲或从𡰥。籒文遲从屖。', '辵;犀;𡰥;屖', '犀;屖', 'null', NULL, '辵;𡰥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1149, '邌', 'lí', '第二卷', '辵部', '徐也。从辵，黎聲。', '辵;黎', '黎', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1150, '遰', 'dì', '第二卷', '辵部', '去也。从辵，帶聲。', '辵;帶', '帶', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1151, '𨓯', 'yuān', '第二卷', '辵部', '行皃。从辵，𣶒聲。', '辵;𣶒', '𣶒', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1152, '𨙔', 'zhù', '第二卷', '辵部', '不行也。从辵，䮔聲。讀若住。', '辵;䮔;住', '䮔;住', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1153, '逗', 'dòu', '第二卷', '辵部', '止也。从辵，豆聲。', '辵;豆', '豆', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1154, '𨒅', 'qì', '第二卷', '辵部', '曲行也。从辵，只聲。', '辵;只', '只', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1155, '逶', 'wēi', '第二卷', '辵部', '逶迆，衺去皃。从辵，委聲。', '辵;委', '委', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1156, '迆', 'yǐ', '第二卷', '辵部', '衺行也。从辵，也聲。夏書曰「東迆北會于匯」。', '辵;也', '也', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1157, '遹', 'yù', '第二卷', '辵部', '回辟也。从辵，矞聲。', '辵;矞', '矞', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1158, '避', 'bì', '第二卷', '辵部', '回也。从辵，辟聲。', '辵;辟', '辟', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1159, '違', 'wéi', '第二卷', '辵部', '離也。从辵，韋聲。', '辵;韋', '韋', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1160, '遴;䢯', 'lìn', '第二卷', '辵部', '行難也。从辵，粦聲。易曰「㠯往遴[吝]」。', '辵;粦;吝', '粦;吝', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1161, '逡', 'qūn', '第二卷', '辵部', '復也。从辵，逡聲。', '辵;逡', '逡', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1162, '䢑', 'dǐ', '第二卷', '辵部', '怒不進也。一曰驇也。从辵，氐聲。', '辵;氐', '氐', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1163, '達;𨔶;达', 'dá', '第二卷', '辵部', '行不相遇也。从辵，羍聲。詩曰「挑兮𨔶兮」。𨔶或从大。或曰此迭。', '辵;羍;大;迭', '羍;大;迭', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1164, '逯', 'lù', '第二卷', '辵部', '行謹逯逯也。从辵，录聲。', '辵;录', '录', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1165, '迵', 'dònɡ', '第二卷', '辵部', '迵，迭也。从辵，同聲。', '辵;同', '同', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1166, '迭', 'dié', '第二卷', '辵部', '更迭也。从辵，失聲。一曰达。', '辵;失;达', '失;达', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1167, '迷', 'mí', '第二卷', '辵部', '惑也。从辵，米聲。', '辵;米', '米', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1168, '連', 'lián', '第二卷', '辵部', '負車也。从辵車。會意。', '辵;車', '', 'null', NULL, '辵;車', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1169, '逑', 'qiú', '第二卷', '辵部', '斂聚也。从辵，求聲。虞書曰「旁逑[鳩]孱功」，又曰「怨匹曰逑」。', '辵;求;鳩', '求;鳩', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1170, '䢙', 'bài', '第二卷', '辵部', '𣀩也。从辵，貝聲。周書曰「我興受其䢙」。', '辵;貝', '貝', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1171, '逭', 'huàn', '第二卷', '辵部', '逃也。从辵，官聲。逭或从雚，从兆。', '辵;官;雚;兆', '官;雚', 'null', NULL, '辵;兆', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1172, '遯', 'dùn', '第二卷', '辵部', '逃也。从辵，豚聲。', '辵;豚', '豚', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1173, '逋', 'bū', '第二卷', '辵部', '亡也。从辵，甫聲。籒文逋从捕。', '辵;甫;捕', '甫;捕', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1174, '遺', 'yí', '第二卷', '辵部', '亡也。从辵，貴聲。', '辵;貴', '貴', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1175, '遂', 'suì', '第二卷', '辵部', '亡也。从辵，㒸聲。古文遂[未详]。', '辵;㒸', '㒸', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1176, '逃', 'táo', '第二卷', '辵部', '亡也。从辵，兆聲。', '辵;兆', '兆', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1177, '追', 'zhuī', '第二卷', '辵部', '逐也。从辵，𠂤聲。', '辵;𠂤', '𠂤', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1178, '逐', 'zhú', '第二卷', '辵部', '追也。从辵，豖省聲。', '辵;豖', '豖', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1179, '逎;遒', 'qiú', '第二卷', '辵部', '迫也。从辵，酉聲。逎或从酋。', '辵;酉;酋', '酉;酋', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1180, '近', 'jìn', '第二卷', '辵部', '附也。从辵，斤聲。古文近[止]。', '辵;斤;止', '斤', 'null', NULL, '辵;止', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1181, '邋', 'liè', '第二卷', '辵部', '拹也。从辵，巤聲。', '辵;巤', '巤', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1182, '迫', 'pò', '第二卷', '辵部', '近也。从辵，白聲。', '辵;白', '白', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1183, '𨖹', 'rì', '第二卷', '辵部', '近也。从辵，臸聲。', '辵;臸', '臸', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1184, '邇', 'ěr', '第二卷', '辵部', '近也。从辵，爾聲。古文邇 [尒]。', '辵;爾;尒', '爾;尒', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1185, '遏', 'è', '第二卷', '辵部', '微止也。从辵，曷聲。讀若「桑蟲之蝎」。', '辵;曷;蝎', '曷;蝎', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1186, '遮', 'zhē', '第二卷', '辵部', '遏也。从辵，庶聲。', '辵;庶', '庶', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1187, '䢭', 'yàn', '第二卷', '辵部', '遮䢭也。从辵，羨聲。', '辵;羨', '羨', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1188, '迣', 'zhì', '第二卷', '辵部', '迾也。晉、趙曰迣。从辵，世聲。讀若窴。', '辵;世;窴', '世;窴', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1189, '迾', 'liè', '第二卷', '辵部', '遮也。从辵，列聲。', '辵;列', '列', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1190, '迀', 'ɡān', '第二卷', '辵部', '進也。从辵，干聲。讀若干。', '辵;干', '干', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1191, '𨓲', 'qiān', '第二卷', '辵部', '過也。从辵，侃聲。', '辵;侃', '侃', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1192, '遱', 'lóu', '第二卷', '辵部', '連遱也。从辵，婁聲。', '辵;婁', '婁', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '連遱≈謰謱≈覶縷≈覼縷≈㘓哰', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1193, '䢡', 'zhì', '第二卷', '辵部', '前頓也。从辵，枼聲。賈侍中說「一讀若拾，又若郅」。', '辵;枼;拾;郅', '枼;拾;郅', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1194, '𨔣', 'jiā', '第二卷', '辵部', '𨔣牙，令不得行也。从辵，枷聲。', '辵;枷', '枷', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1195, '𨒋', 'yuè', '第二卷', '辵部', '踰也。从辵，戉聲。易曰「襍而不𨒋」。', '辵;戉', '戉', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1196, '逞', 'chěnɡ', '第二卷', '辵部', '通也。从辵，呈聲。楚謂疾行爲逞。春秋傳曰「何所不逞欲」。', '辵;呈', '呈', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1197, '遼', 'liáo', '第二卷', '辵部', '遠也。从辵，尞聲。', '辵;尞', '尞', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1198, '遠', 'yuǎn', '第二卷', '辵部', '遼也。从辵，袁聲。古文遠[步]。', '辵;袁;步', '袁', 'null', NULL, '辵;步', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1199, '逖;逷', 'tì', '第二卷', '辵部', '遠也。从辵，狄聲。古文逖[易]。', '辵;狄;易', '狄;易', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1200, '迥', 'jiǒnɡ', '第二卷', '辵部', '遠也。从辵，冋聲。', '辵;冋', '冋', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1201, '逴', 'chuò', '第二卷', '辵部', '遠也。从辵，卓聲。一曰蹇也。讀若「掉苕」之掉。', '辵;卓;掉', '卓;掉', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1202, '迂', 'yū', '第二卷', '辵部', '避也。从辵，于聲。', '辵;于', '于', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1203, '𨔥', 'jiān', '第二卷', '辵部', '自進極也。从辵，𦘔聲。', '辵;𦘔', '𦘔', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1204, '邍', 'yuán', '第二卷', '辵部', '高平曰邍。人所登。从辵备录。闕。', '辵;备;录', '', 'null', NULL, '辵;备;录', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1205, '道', 'dào', '第二卷', '辵部', '所行道也。从辵首。一達謂之道。古文道从首寸。', '辵;首;首;寸', '', 'null', NULL, '辵;首;首;寸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1206, '遽', 'jù', '第二卷', '辵部', '傳也。一曰窘也。从辵，豦聲。', '辵;豦', '豦', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1207, '迒', 'hánɡ', '第二卷', '辵部', '獸迹也。从辵，亢聲。迒或从足更。', '辵;亢;足;更', '亢;更', 'null', NULL, '辵;足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1208, '𨑩', 'dì', '第二卷', '辵部', '至也。从辵，弔聲。', '辵;弔', '弔', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1209, '邊;𨘢', 'biān', '第二卷', '辵部', '行垂崖也。从辵，𦤝聲。', '辵;𦤝', '𦤝', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1210, '邂', 'xiè', '第二卷', '辵部', '邂逅，不期而遇也。从辵，解聲。', '辵;解', '解', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1211, '逅', 'hòu', '第二卷', '辵部', '邂逅也。从辵，后聲。', '辵;后', '后', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1212, '遑;徨', 'huánɡ', '第二卷', '辵部', '急也。从辵，皇聲。[徨]或从彳。', '辵;皇;彳', '皇', 'null', NULL, '辵;彳', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1213, '逼', 'bī', '第二卷', '辵部', '近也。从辵，畐聲。', '辵;畐', '畐', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1214, '𨘷;邈;𨗿', 'miǎo', '第二卷', '辵部', '遠也。从辵，䫉聲。', '辵;䫉', '䫉', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1215, '遐', 'xiá', '第二卷', '辵部', '遠也。从辵，叚聲。', '辵;叚', '叚', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1216, '迄', 'qì', '第二卷', '辵部', '至也。从辵，气聲。', '辵;气', '气', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1217, '迸', 'bènɡ', '第二卷', '辵部', '散走也。从辵，并聲。', '辵;并', '并', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1218, '透', 'tòu', '第二卷', '辵部', '跳也，過也。从辵，秀聲。', '辵;秀', '秀', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1219, '邏', 'luó', '第二卷', '辵部', '巡也。从辵，羅聲。', '辵;羅', '羅', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1220, '迢', 'tiáo', '第二卷', '辵部', '迢遰也。从辵，召聲。', '辵;召', '召', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1221, '逍', 'xiāo', '第二卷', '辵部', '逍遥，猶翱翔也。从辵，肖聲。', '辵;肖', '肖', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1222, '遥;遙', 'yáo', '第二卷', '辵部', '逍遥也。又遠也。从辵，䍃聲。', '辵;䍃', '䍃', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1223, '彳', 'chì', '第二卷', '彳部', '小步也。象人脛三屬相連也。凡彳之屬皆从彳。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1224, '德;徳', 'dé', '第二卷', '彳部', '升也。从彳，𢛳[悳]聲。', '彳;悳', '悳', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1225, '徑', 'jìnɡ', '第二卷', '彳部', '步道也。从彳，巠聲。', '彳;巠', '巠', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1226, '復', 'fù', '第二卷', '彳部', '往來也。从彳，复聲。', '彳;复', '复', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1227, '𢔟', 'rǒu', '第二卷', '彳部', '復也。从彳，柔聲。', '彳;柔', '柔', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1228, '徎', 'chěnɡ', '第二卷', '彳部', '徑行也。从彳，呈聲。', '彳;呈', '呈', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1229, '往', 'wǎnɡ', '第二卷', '彳部', '之也。从彳，㞷聲。古文从辵。', '彳;㞷;辵', '㞷', 'null', NULL, '彳;辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1230, '忂', 'qú', '第二卷', '彳部', '行皃。从彳，瞿聲。', '彳;瞿', '瞿', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1231, '彼', 'bǐ', '第二卷', '彳部', '往有所加也。从彳，皮聲。', '彳;皮', '皮', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1232, '徼', 'jiào', '第二卷', '彳部', '循也。从彳，敫聲。', '彳;敫', '敫', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '见纽沃部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1233, '循', 'xún', '第二卷', '彳部', '順行也。从彳，盾聲。', '彳;盾', '盾', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '邪纽文部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1234, '彶', 'jí', '第二卷', '彳部', '急行也。从彳，及聲。', '彳;及', '及', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1235, '𢕬', 'sà', '第二卷', '彳部', '行皃。从彳，歰聲。一曰此與馺同。', '彳;歰;馺', '歰;馺', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1236, '微', 'wēi', '第二卷', '彳部', '隱行也。从彳，𢼸聲。春秋傳曰「白公其徒微之」。', '彳;𢼸', '𢼸', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1237, '徥', 'shì', '第二卷', '彳部', '徥徥行皃也。从彳，是聲。爾雅曰「徥，則也」。', '彳;是', '是', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1238, '徐', 'xú', '第二卷', '彳部', '安行也。从彳，余聲。', '彳;余', '余', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1239, '𢓡', 'yí', '第二卷', '彳部', '行平易也。从彳，夷聲。', '彳;夷', '夷', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1240, '𢖊', 'pīnɡ', '第二卷', '彳部', '使也。从彳，䛣聲。', '彳;䛣', '䛣', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1241, '𢓱', 'fēnɡ', '第二卷', '彳部', '使也。从彳，夆聲。讀若𧒒。', '彳;夆;𧒒', '夆;𧒒', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1242, '㣤', 'jiàn', '第二卷', '彳部', '迹也。从彳，戔聲。', '彳;戔', '戔', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1243, '徬', 'bànɡ', '第二卷', '彳部', '附行也。从彳，旁聲。', '彳;旁', '旁', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1244, '徯;蹊', 'xī', '第二卷', '彳部', '待也。从彳，奚聲。徯或从足。', '彳;奚;足', '奚', 'null', NULL, '彳;足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1245, '待', 'dài', '第二卷', '彳部', '竢也。从彳，寺聲。', '彳;寺', '寺', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1246, '㣙', 'dí', '第二卷', '彳部', '行㣙㣙也。从彳，由聲。', '彳;由', '由', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1247, '徧', 'biàn', '第二卷', '彳部', '帀也。从彳，扁聲。', '彳;扁', '扁', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1248, '徦', 'jiǎ', '第二卷', '彳部', '至也。从彳，叚聲。', '彳;叚', '叚', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1249, '𢓴;𢓇;退;𨓤', 'tuì', '第二卷', '彳部', '卻也。从彳日夊。一曰行遲。𢓴或从內。古文从辵。', '彳;日;夊;內;辵', '內', 'null', NULL, '彳;日;夊;辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1250, '後', 'hòu', '第二卷', '彳部', '遲也。从彳幺夊。幺夊者，後也。古文後从辵。', '彳;幺;夊;辵', '', 'null', NULL, '彳;幺;夊;辵', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1251, '徲;𢕌', 'tí', '第二卷', '彳部', '久也。从彳，犀聲。讀若遲。', '彳;犀;遲', '犀;遲', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1252, '很', 'hěn', '第二卷', '彳部', '不聽從也。一曰行難也。从彳，艮聲。一曰盭也。', '彳;艮', '艮', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1253, '㣫', 'zhǒnɡ', '第二卷', '彳部', '相迹也。从彳，重聲。', '彳;重', '重', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1254, '得', 'dé', '第二卷', '彳部', '行有所䙷也。从彳，䙷聲。古文省彳。', '彳;䙷', '䙷', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1255, '徛', 'jì', '第二卷', '彳部', '舉脛有渡也。从彳，奇聲。', '彳;奇', '奇', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1256, '徇', 'xùn', '第二卷', '彳部', '行示也。从彳，匀聲。司馬法「斬㠯徇」。', '彳;匀', '匀', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1257, '律', 'lǜ', '第二卷', '彳部', '均布也。从彳，聿聲。', '彳;聿', '聿', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1258, '御;馭', 'yù', '第二卷', '彳部', '使馬也。从彳卸。古文御从又馬。', '彳;卸;又;馬', '', 'null', NULL, '彳;卸;又;馬', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1259, '亍', 'chù', '第二卷', '彳部', '步止也。从反彳。讀若畜。', '彳;畜', '畜', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1260, '廴', 'yǐn', '第二卷', '廴部', '長行也。从彳引之。凡廴之屬皆从廴。', '彳', '', 'null', NULL, '彳', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1261, '廷', 'tínɡ', '第二卷', '廴部', '朝中也。从廴，𡈼聲。', '廴;𡈼', '𡈼', 'null', NULL, '廴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1262, '𢌛', 'zhēnɡ', '第二卷', '廴部', '行也。从廴，正聲。', '廴;正', '正', 'null', NULL, '廴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1263, '建', 'jiàn', '第二卷', '廴部', '立朝律也。从聿，从廴。', '聿;廴', '', 'null', NULL, '聿;廴', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1264, '㢟', 'chān', '第二卷', '㢟部', '安步㢟㢟也。从廴止。凡㢟之屬皆从㢟。', '廴;止', '', 'null', NULL, '廴;止', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1265, '延', 'yán', '第二卷', '㢟部', '長行也。从㢟，𠂆聲。', '㢟;𠂆', '𠂆', 'null', NULL, '㢟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1266, '行', 'xínɡ', '第二卷', '行部', '人之步趨也。从彳亍。凡行之屬皆从行。', '彳;亍', '', 'null', NULL, '彳;亍', 'null', 'null', 'null', 'null', '★', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1267, '術', 'shù', '第二卷', '行部', '邑中道也。从行，朮聲。', '行;朮', '朮', 'null', NULL, '行', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1268, '街', 'jiē', '第二卷', '行部', '四通道也。从行，圭聲。', '行;圭', '圭', 'null', NULL, '行', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1269, '衢', 'qú', '第二卷', '行部', '四達謂之衢。从行，瞿聲。', '行;瞿', '瞿', 'null', NULL, '行', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1270, '𧘂', 'chōnɡ', '第二卷', '行部', '通道也。从行，童聲。春秋傳曰「及𧘂㠯擊之」。', '行;童', '童', 'null', NULL, '行', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1271, '衕', 'tónɡ', '第二卷', '行部', '通街也。从行，同聲。', '行;同', '同', 'null', NULL, '行', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1272, '𧗸', 'jiàn', '第二卷', '行部', '迹也。从行，戔聲。', '行;戔', '戔', 'null', NULL, '行', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1273, '衙', 'yú', '第二卷', '行部', '衙衙，行皃。从行，吾聲。又音牙', '行;吾;牙', '吾;牙', 'null', NULL, '行', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1274, '衎', 'kàn', '第二卷', '行部', '行喜皃。从行，干聲。', '行;干', '干', 'null', NULL, '行', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1275, '𧗳;衒', 'xuàn', '第二卷', '行部', '行且賣也。从行言。𧗳或从玄。', '行;言;玄', '玄', 'null', NULL, '行;言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1276, '𧗿', 'shuài', '第二卷', '行部', '將𧗿也。从行，率聲。', '行;率', '率', 'null', NULL, '行', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1277, '衛', 'wèi', '第二卷', '行部', '宿衛也。从韋帀行。行，列也。', '韋;帀;行', '韋', 'null', NULL, '帀;行', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1278, '齒', 'chǐ', '第二卷', '齒部', '口齗骨也。象口齒之形。止聲。凡齒之屬皆从齒。古文齒字。', '止', '止', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1279, '齗', 'yín', '第二卷', '齒部', '齒本肉也。从齒，斤聲。', '齒;斤', '斤', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1280, '齔', 'chèn', '第二卷', '齒部', '毀齒也。男八月生齒，八歲而齔。女七月生齒，七歲而齔。从齒𠤎。', '齒;七', '七', 'null', NULL, '齒;𠤎', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1281, '䶦', 'zé', '第二卷', '齒部', '齒相值也。从齒，責聲。一曰齧也。春秋傳曰「皙䶦」。', '齒;責', '責', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1282, '齜', 'chái', '第二卷', '齒部', '齒相齘也。一曰開口見齒之皃。从齒，此聲。讀若柴。', '齒;此;柴', '此;柴', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1283, '齘', 'xiè', '第二卷', '齒部', '齒相切也。从齒，介聲。', '齒;介', '介', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1284, '齞', 'yǎn', '第二卷', '齒部', '張口見齒也。从齒，只聲。', '齒;只', '只', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1285, '𪙊', 'yàn', '第二卷', '齒部', '齒差也。从齒，兼聲。', '齒;兼', '兼', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1286, '齺', 'zōu', '第二卷', '齒部', '齒拹也。一曰馬口中橜也。从齒，芻聲。一曰齰也。', '齒;芻', '芻', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1287, '齱', 'zōu', '第二卷', '齒部', '齱齵，齒不正也。从齒，取聲。', '齒;取', '取', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1288, '齵', 'óu', '第二卷', '齒部', '齱齵也。从齒，禺聲。', '齒;禺', '禺', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1289, '䶥', 'zhā', '第二卷', '齒部', '䶥齬，齒不相值也。从齒，虘聲。', '齒;虘', '虘', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1290, '齹', 'cī', '第二卷', '齒部', '齒參差。从齒，差聲。', '齒;差', '差', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1291, '齬', 'yǔ', '第二卷', '齒部', '䶥齬也。从齒，吾聲。', '齒;吾', '吾', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1292, '𪘓', 'cuó', '第二卷', '齒部', '齒差跌皃。从齒，佐[左]聲。春秋傳鄭有子𪘓[齹]。', '齒;左;齹', '左;齹', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1293, '齤', 'quán', '第二卷', '齒部', '缺齒也。一曰曲齒。从齒，𢍏聲。讀又若權。', '齒;𢍏;權', '𢍏;權', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1294, '齳', 'yǔn', '第二卷', '齒部', '無齒也。从齒，軍聲。', '齒;軍', '軍', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1295, '齾', 'yà', '第二卷', '齒部', '缺齒也。从齒，獻聲。', '齒;獻', '獻', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1296, '䶙', 'jù', '第二卷', '齒部', '齗腫也。从齒，巨聲。', '齒;巨', '巨', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1297, '齯', 'ní', '第二卷', '齒部', '老人齒。从齒，兒聲。', '齒;兒', '兒', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1298, '齮', 'yǐ', '第二卷', '齒部', '齧也。从齒，奇聲。', '齒;奇', '奇', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1299, '𪗨', 'zhí', '第二卷', '齒部', '齚齒也。从齒，出聲。', '齒;出', '出', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1300, '齰;齚', 'zé', '第二卷', '齒部', '齧也。从齒，昔聲。齰或从乍。', '齒;昔;乍', '昔;乍', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1301, '䶢', 'jiān', '第二卷', '齒部', '齧也。从齒，咸聲。', '齒;咸', '咸', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1302, '齦', 'kěn', '第二卷', '齒部', '齧也。从齒，艮聲。', '齒;艮', '艮', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1303, '𪗙', 'yǎn', '第二卷', '齒部', '齒見皃。从齒，干聲。', '齒;干', '干', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1304, '𪘧', 'zú', '第二卷', '齒部', '𪘧，齚也。从齒，卒聲。', '齒;卒', '卒', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1305, '𪗿', 'là', '第二卷', '齒部', '齒分骨聲。从齒，列聲。讀若剌。', '齒;列;剌', '列;剌', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1306, '齩', 'yǎo', '第二卷', '齒部', '齧骨也。从齒，交聲。', '齒;交', '交', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1307, '𪙑', 'qiè', '第二卷', '齒部', '齒差也。从齒，㞕聲。讀若切。', '齒;㞕;切', '㞕;切', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
