INSERT INTO `shuowen_voice_revel` VALUES (1311, '齕', 'hé', '第二卷', '齒部', '齧也。从齒，气聲。', '齒;气', '气', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1312, '𪚁', 'lián', '第二卷', '齒部', '齒見皃。从齒，聯聲。', '齒;聯', '聯', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1313, '齧', 'niè', '第二卷', '齒部', '噬也。从齒，㓞聲。', '齒;㓞', '㓞', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1314, '齭', 'chǔ', '第二卷', '齒部', '齒傷酢也。从齒，所聲。讀若楚。', '齒;所;楚', '所;楚', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1315, '齨', 'jiù', '第二卷', '齒部', '老人齒如臼也。从齒臼。臼亦聲。一曰馬八歲[齒臼]也。', '齒;臼', '臼', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
