INSERT INTO `shuowen_voice_revel` VALUES (1317, '齸', 'yì', '第二卷', '齒部', '鹿麋粻。从齒，益聲。', '齒;益', '益', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1318, '𪗻', 'zhì', '第二卷', '齒部', '齧堅也。从齒，至聲。', '齒;至', '至', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
