INSERT INTO `shuowen_voice_revel` VALUES (1321, '𪙍', 'bó', '第二卷', '齒部', '噍堅也。从齒，尃聲。', '齒;尃', '尃', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1322, '齡', 'línɡ', '第二卷', '齒部', '年也。从齒，令聲。', '齒;令', '令', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1323, '牙', 'yá', '第二卷', '牙部', '壯齒也。象上下相錯之形。凡牙之屬皆从牙。古文牙 [齒]。', '齒', '', 'null', NULL, '齒', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1324, '𤘌', 'qī', '第二卷', '牙部', '虎牙也。从牙奇。奇亦聲。', '牙;奇', '奇', 'null', NULL, '牙', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1325, '𤘐;齲', 'qǔ', '第二卷', '牙部', '齒蠧也。从牙，禹聲。𤘐或从齒。', '牙;禹;齒', '禹', 'null', NULL, '牙;齒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1326, '足', 'zú', '第二卷', '足部', '人之足也，在體下。从口止。凡足之屬皆从足。', '口;止', '', 'null', NULL, '口;止', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1327, '蹏', 'tí', '第二卷', '足部', '足也。从足，虒聲。', '足;虒', '虒', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1328, '跟', 'ɡēn', '第二卷', '足部', '足歱也。从足，艮聲。跟或从止。', '足;艮;止', '艮', 'null', NULL, '足;止', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1329, '踝', 'huái', '第二卷', '足部', '足踝也。从足，果聲。', '足;果', '果', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1330, '跖', 'zhí', '第二卷', '足部', '足下也。从足，石聲。', '足;石', '石', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1331, '踦', 'qī', '第二卷', '足部', '一足也。从足，奇聲。', '足;奇', '奇', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
