INSERT INTO `shuowen_voice_revel` VALUES (1334, '踧', 'dí', '第二卷', '足部', '行平易也。从足，叔聲。詩曰「踧踧周道」。', '足;叔', '叔', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1335, '躣', 'qú', '第二卷', '足部', '行皃。从足，瞿聲。', '足;瞿', '瞿', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1336, '踖', 'jí', '第二卷', '足部', '長脛行也。从足，昔聲。一曰踧踖。', '足;昔', '昔', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
