INSERT INTO `shuowen_voice_revel` VALUES (1347, '躋', 'jī', '第二卷', '足部', '登也。从足，齊聲。商書曰「予顚躋」。', '足;齊', '齊', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
