INSERT INTO `shuowen_voice_revel` VALUES (1349, '跧', 'zhuān', '第二卷', '足部', '蹴也。从足，全聲。一曰卑也，絭也。', '足;全', '全', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
