INSERT INTO `shuowen_voice_revel` VALUES (1362, '踶', 'dì', '第二卷', '足部', '躗也。从足，是聲。', '足;是', '是', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
