INSERT INTO `shuowen_voice_revel` VALUES (1365, '䟗', 'shì', '第二卷', '足部', '尌也。从足，氏聲。', '足;氏', '氏', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1366, '蹢', 'zhí', '第二卷', '足部', '蹢躅，逗足也。从足，啻聲。或曰蹢躅。賈侍中說，足垢也。', '足;啻', '啻', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
