INSERT INTO `shuowen_voice_revel` VALUES (1368, '踤', 'zú', '第二卷', '足部', '觸也。从足，卒聲。一曰駭也。一曰倉踤。', '足;卒', '卒', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
