INSERT INTO `shuowen_voice_revel` VALUES (1376, '䠛', 'yáo', '第二卷', '足部', '跳也。从足，䍃聲。', '足;䍃', '䍃', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1377, '趿', 'sà', '第二卷', '足部', '進足有所擷取也。从足，及聲。爾雅曰「趿謂之擷」。', '足;及', '及', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1378, '䟺', 'bèi', '第二卷', '足部', '步行獵跋也。从足，貝聲。', '足;貝', '貝', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1379, '躓', 'zhì', '第二卷', '足部', '跲也。从足，質聲。詩曰「載躓[𤴡]其尾」。', '足;質;𤴡', '質;𤴡', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1380, '跲', 'jiá', '第二卷', '足部', '躓也。从足，合聲。', '足;合', '合', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1381, '跇', 'yì', '第二卷', '足部', '述也。从足，世聲。', '足;世', '世', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1382, '蹎', 'diān', '第二卷', '足部', '跋也。从足，眞聲。', '足;眞', '眞', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1383, '跋', 'bá', '第二卷', '足部', '蹎也。从足，犮聲。', '足;犮', '犮', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1384, '蹳', 'bō', '第二卷', '足部', '蹎蹳也。从足，發聲。', '足;發', '發', 'null', NULL, '足', 'null', 'null', 'null', 'null', '小徐本', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1385, '蹐', 'jí', '第二卷', '足部', '小步也。从足，脊聲。詩曰「不敢不蹐」。', '足;脊', '脊', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1386, '跌', 'diē', '第二卷', '足部', '踼也。从足，失聲。一曰越也。', '足;失', '失', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1387, '踼', 'tánɡ', '第二卷', '足部', '跌也。从足，昜聲。一曰槍也。', '足;昜', '昜', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1388, '蹲', 'dūn', '第二卷', '足部', '居也。从足，尊聲。', '足;尊', '尊', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1389, '踞', 'jù', '第二卷', '足部', '蹲也。从足，居聲。', '足;居', '居', 'null', NULL, '足', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1390, '𨃖', 'kuà', '第二卷', '足部', '居也。从足，荂聲。', '足;荂', '荂', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1391, '躩', 'jué', '第二卷', '足部', '足躩如也。从足，矍聲。', '足;矍', '矍', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1392, '踣', 'bó', '第二卷', '足部', '僵也。从足，咅聲。春秋傳曰「晉人踣之」。', '足;咅', '咅', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1393, '跛', 'bǒ', '第二卷', '足部', '行不正也。从足，皮聲。一曰足排之。讀若彼。', '足;皮;彼', '皮;彼', 'null', NULL, '足', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1394, '跂', 'qí', '第二卷', '足部', '足多指也。从足，支聲。', '足;支', '支', 'null', NULL, '足', 'null', 'null', 'null', 'null', '政宇调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1395, '蹇', 'jiǎn', '第二卷', '足部', '㝿也。从足，寒省聲。', '足;寒', '寒', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1396, '蹁', 'pián', '第二卷', '足部', '足不正也。从足，扁聲。一曰拖後足馬。讀若苹。或曰徧。', '足;扁;苹;徧', '扁;苹;徧', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1397, '䟸', 'kuí', '第二卷', '足部', '脛肉也。从足，𢌳聲。一曰曲脛也。讀若逵。', '足;𢌳;逵', '𢌳;逵', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1398, '踒', 'wō', '第二卷', '足部', '足跌也。从足，委聲。', '足;委', '委', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1399, '跣', 'xiǎn', '第二卷', '足部', '足親地也。从足，先聲。', '足;先', '先', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1400, '跔', 'jū', '第二卷', '足部', '天寒足跔也。从足，句聲。', '足;句', '句', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1401, '䠅', 'kǔn', '第二卷', '足部', '瘃足也。从足，囷聲。', '足;囷', '囷', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1402, '距', 'jù', '第二卷', '足部', '雞足距也。从足，巨聲。', '足;巨', '巨', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1403, '躧', 'xǐ', '第二卷', '足部', '舞履也。从足，麗聲。或从革。', '足;麗;革', '麗', 'null', NULL, '足;革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1404, '䠍', 'xiā', '第二卷', '足部', '足所履也。从足，叚聲。', '足;叚', '叚', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1405, '䠊', 'fèi', '第二卷', '足部', '跀也。从足，非聲。', '足;非', '非', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1406, '跀', 'yuè', '第二卷', '足部', '𣃔足也。从足，月聲。跀或从兀。', '足;月;兀', '月;兀', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1407, '趽', 'fànɡ', '第二卷', '足部', '曲脛馬也。从足，方聲。讀與彭同。', '足;方;彭', '方;彭', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1408, '趹', 'jué', '第二卷', '足部', '馬行皃。从足，夬聲。', '足;夬', '夬', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1409, '趼', 'yàn', '第二卷', '足部', '獸足企也。从足，开聲。', '足;开', '开', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1410, '路', 'lù', '第二卷', '足部', '道也。从足，各聲。', '足;各', '各', 'null', NULL, '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1411, '蹸', 'lìn', '第二卷', '足部', '轢也。从足，粦聲。', '足;粦', '粦', '閵', '蹂躙字为后起字。', '足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1412, '蹮', 'xiān', '第二卷', '足部', '蹁蹮，旋行。从足，䙴聲。', '足;䙴', '䙴', 'null', NULL, '足', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1413, '蹭', 'cènɡ', '第二卷', '足部', '蹭蹬，失道也。从足，曾聲。', '足;曾', '曾', 'null', NULL, '足', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1414, '蹬', 'dènɡ', '第二卷', '足部', '蹭蹬也。从足，登聲。', '足;登', '登', 'null', NULL, '足', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1415, '蹉', 'cuō', '第二卷', '足部', '蹉跎，失時也。从足，差聲。', '足;差', '差', 'null', NULL, '足', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1416, '跎', 'tuó', '第二卷', '足部', '蹉跎也。从足，它聲。', '足;它', '它', 'null', NULL, '足', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1417, '蹙;䠞', 'cù', '第二卷', '足部', '迫也。从足，戚聲。', '足;戚', '戚', 'null', NULL, '足', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1418, '踸', 'chěn', '第二卷', '足部', '踸踔，行無常皃。从足，甚聲。', '足;甚', '甚', 'null', NULL, '足', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1420, '跏', 'jiā', '第二卷', '足部', '跏趺，般足也。从足，加聲。', '足;加', '加', 'null', NULL, '足', 'null', 'null', 'null', 'null', '政宇新附', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1421, '趺', 'fū', '第二卷', '足部', '跏趺也。从足，夫聲。', '足;夫', '夫', 'null', NULL, '足', 'null', 'null', 'null', 'null', '政宇新附', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1422, '疋', 'shū', '第二卷', '疋部', '足也。上象腓腸。下从止。弟子職曰「問疋何止」。古文㠯爲詩「大雅」字，亦㠯爲足字。或曰胥字。一曰疋，記也。凡疋之屬皆从疋。', '止;雅;足;胥', '雅;足;胥', 'null', NULL, '止', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1423, '𤴙', 'shū', '第二卷', '疋部', '門戸靑疏䆫也。从疋，疋亦聲。囪象𤴙形。讀若疏。', '疋;囪;疏', '疋;疏', 'null', NULL, '囪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1424, '𤕟', 'shū', '第二卷', '疋部', '通也。从爻疋，疋亦聲。', '爻;疋', '疋', 'null', NULL, '爻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1425, '品', 'pǐn', '第二卷', '品部', '衆庶也。从三口。凡品之屬皆从品。', '口', '', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1426, '喦', 'niè', '第二卷', '品部', '多言也。从品相連。春秋傳曰「次于喦北」。讀與聶同。', '品;聶', '聶', 'null', NULL, '品', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1427, '喿', 'zào', '第二卷', '品部', '鳥羣鳴也。从品，在木上。', '品;木', '', 'null', NULL, '品;木', 'null', 'null', 'null', 'null', '雥🤰', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1428, '龠', 'yuè', '第二卷', '龠部', '樂之竹管。三孔，㠯和衆聲也。从品侖。侖，理也。凡龠之屬皆从龠。', '品;侖', '', 'null', NULL, '品;侖', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1429, '䶴;龡', 'chuī', '第二卷', '龠部', '䶴。音律管壎之樂也。从龠，炊聲。', '龠;炊', '炊', 'null', NULL, '龠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1430, '䶵;篪', 'chí', '第二卷', '龠部', '管樂也。七孔。从龠，虒聲。䶵或从竹。', '龠;虒;竹', '虒', 'null', NULL, '龠;竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1431, '龢', 'hé', '第二卷', '龠部', '調也。从龠，禾聲。讀與和同。', '龠;禾;和', '禾;和', 'null', NULL, '龠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1432, '龤', 'xié', '第二卷', '龠部', '樂龢也。从龠，皆聲。虞書曰「八音克龤」。', '龠;皆', '皆', 'null', NULL, '龠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1433, '冊;册;笧', 'cè', '第二卷', '冊部', '符命也。諸侯進受於王者也。象其札一長一短，中有二編之形。凡冊之屬皆从冊。古文冊从竹。', '竹', '', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1434, '嗣', 'sì', '第二卷', '冊部', '諸侯嗣國也。从冊口，司聲。古文嗣从子。', '冊;口;司;子', '司', 'null', NULL, '冊;口;子', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1435, '扁', 'biǎn', '第二卷', '冊部', '署也。从戸冊。戸冊者，署門戸之文也。', '戸;冊', '', 'null', NULL, '戸;冊', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1436, '㗊', 'jí', '第三卷', '㗊部', '衆口也。从四口。凡㗊之屬皆从㗊。讀若戢。一曰呶。', '戢', '戢', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1437, '嚚', 'yín', '第三卷', '㗊部', '語聲也。从㗊，臣聲。', '㗊;臣', '臣', 'null', NULL, '㗊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1438, '囂;嚻', 'xiāo', '第三卷', '㗊部', '聲也。气出頭上。从㗊頁。頁亦首也。囂或省[吅]。', '㗊;頁;吅', '', 'null', NULL, '㗊;頁;吅', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1439, '嘂', 'jiào', '第三卷', '㗊部', '高聲也。一曰大嘑也。从㗊，丩聲。春秋公羊傳曰「魯昭公嘂[噭]然而哭」。', '㗊;丩;噭', '丩;噭', 'null', NULL, '㗊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1440, '𡅻;𡅽', 'huàn', '第三卷', '㗊部', '嘑也。从㗊，萈聲。讀若讙。', '㗊;萈;讙', '萈;讙', '奐', '𡅻、喚古今字。', '㗊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1441, '器', 'qì', '第三卷', '㗊部', '皿也。[㗊，]象器之口。犬，所㠯守之。', '㗊;犬', '', 'null', NULL, '㗊;犬', 'null', 'null', 'null', 'null', '△', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1442, '干', 'ɡān', '第三卷', '干部', '犯也。从一，从反入。凡干之屬皆从干。', '一;入', '', 'null', NULL, '一;入', 'null', 'null', 'null', 'null', '元部', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1443, '𢆉', 'rěn', '第三卷', '干部', '㨖也。从干。入一爲干，入二爲𢆉。讀若飪。言稍甚也。', '干;飪', '飪', 'null', NULL, '干', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1444, '屰', 'nì', '第三卷', '干部', '不順也。从干下凵。屰之也。', '干;凵', '', 'null', NULL, '干;凵', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1445, '舌', 'shé', '第三卷', '舌部', '在口所㠯言別味者也。从干口。干亦聲。凡舌之屬皆从舌。', '干;口', '干', 'null', NULL, '口', 'null', 'null', 'null', 'null', '政宇调部 | 月部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1446, '𦧥', 'tà', '第三卷', '舌部', '歠也。从舌，沓聲。', '舌;沓', '沓', 'null', NULL, '舌', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1447, '舓', 'shì', '第三卷', '舌部', '㠯舌取食也。从舌，易聲。舓或从也。', '舌;易;也', '易;也', 'null', NULL, '舌', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1448, '𧮫;臄', 'jué', '第三卷', '𧮫部', '口上阿也。从口。上象其理。凡𧮫之屬皆从𧮫。𧮫或如此[卻]。𧮫或从豦肉。', '口;卻;豦;肉', '卻;豦', 'null', NULL, '口;肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1449, '㐁;𠀬', 'tiàn', '第三卷', '𧮫部', '舌皃。从𧮫省。象形。古文㐁。讀若「三年導服」之導。一曰竹上皮。讀若沾。一曰讀若誓。弼字从此。', '𧮫;導;沾;誓;弼', '導;沾;誓;弼', 'null', NULL, '𧮫', 'null', 'null', 'null', 'null', '△ | 侵部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1450, '只', 'zhǐ', '第三卷', '只部', '語巳䛐也。从口。象气下引之形。凡只之屬皆从只。', '口', '', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1451, '𠷓', 'xīnɡ', '第三卷', '只部', '聲也。从只，甹聲。讀若馨。', '只;甹;馨', '甹;馨', 'null', NULL, '只', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1452, '㕯', 'nè', '第三卷', '㕯部', '言之訥也。从口內。凡㕯之屬皆从㕯。', '口;內', '內', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1453, '矞', 'yù', '第三卷', '㕯部', '㠯錐有所穿也。从矛㕯。一曰滿有所岀也。', '矛;㕯', '㕯', 'null', NULL, '矛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1454, '商', 'shānɡ', '第三卷', '㕯部', '從外知內也。从㕯，章省聲。古文商。亦古文商。籒文商[晶省、辛、丙、口]。', '㕯;章;晶;辛;丙;口', '章', 'null', NULL, '㕯;晶;辛;丙;口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1455, '丩', 'jiū', '第三卷', '丩部', '相糾繚也。一曰瓜瓠結丩起。象形。凡丩之屬皆从丩。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '⚙️', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1456, '𦱠', 'jiū', '第三卷', '丩部', '艸之相丩者。从茻丩。丩亦聲。', '茻;丩', '丩', 'null', NULL, '茻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1457, '糾', 'jiū', '第三卷', '丩部', '繩三合也。从糸丩。丩亦聲。', '糸;丩', '丩', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1458, '句', 'ɡōu', '第三卷', '句部', '曲也。从口，丩聲。凡句之屬皆从句。', '口;丩', '丩', 'null', NULL, '口', 'null', 'null', 'null', 'null', '政宇调部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1459, '拘', 'jū', '第三卷', '句部', '止也。从手句。句亦聲。', '手;句', '句', 'null', NULL, '手', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1460, '笱', 'ɡǒu', '第三卷', '句部', '曲竹捕魚笱也。从竹句。句亦聲。', '竹;句', '句', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1461, '鉤', 'ɡōu', '第三卷', '句部', '曲鉤也。从金句。句亦聲。', '金;句', '句', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1462, '古', 'ɡǔ', '第三卷', '古部', '故也。从十口。識前言者也。凡古之屬皆从古。古文古[宀川，余未详]。', '十;口;宀;川', '', 'null', NULL, '十;口;宀;川', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1463, '嘏', 'jiǎ', '第三卷', '古部', '大遠也。从古，叚聲。', '古;叚', '叚', 'null', NULL, '古', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1464, '十', 'shí', '第三卷', '十部', '數之具也。一爲東西，丨爲南北，則四方中央備矣。凡十之屬皆从十。', '一;丨', '', 'null', NULL, '一;丨', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1465, '丈', 'zhànɡ', '第三卷', '十部', '十尺也。从又持十。', '又;十', '', 'null', NULL, '又;十', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1466, '千', 'qiān', '第三卷', '十部', '十百也。从十，人聲。', '十;人', '人', 'null', NULL, '十', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1467, '肸', 'xì', '第三卷', '十部', '肸蠁，布也。从十，䏌聲。', '十;䏌', '䏌', 'null', NULL, '十', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1468, '卙', 'jí', '第三卷', '十部', '卙卙，盛也。从十，甚聲。汝南名蠶盛曰卙。', '十;甚', '甚', 'null', NULL, '十', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1469, '博', 'bó', '第三卷', '十部', '大通也。从十尃。尃，布也，亦聲。', '十;尃', '尃', 'null', NULL, '十', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1470, '㔹', 'lè', '第三卷', '十部', '材十人也。从十力。力亦聲。', '十;力', '力', 'null', NULL, '十', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1471, '廿', 'niàn', '第三卷', '十部', '二十并也。古文省多。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1472, '𠦫', 'jí', '第三卷', '十部', '詞之集也。从十，咠聲。', '十;咠', '咠', 'null', NULL, '十', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1473, '卅', 'sà', '第三卷', '卅部', '三十并也。古文省。凡卅之屬皆从卅。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1474, '世', 'shì', '第三卷', '卅部', '三十年爲一世。从卅而曳長之，[乁]亦取其聲。', '卅;乁', '乁', 'null', NULL, '卅', 'null', 'null', 'null', 'null', '月部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1475, '言', 'yán', '第三卷', '言部', '直言曰言，論難曰語。从口，䇂聲。凡言之屬皆从言。', '口;䇂', '䇂', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1476, '譻', 'yīnɡ', '第三卷', '言部', '聲也。从言，賏聲。', '言;賏', '賏', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1477, '謦', 'qǐnɡ', '第三卷', '言部', '欬也。从言，殸聲。殸，籒文磬字。', '言;殸', '殸', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1478, '語', 'yǔ', '第三卷', '言部', '論也。从言，吾聲。', '言;吾', '吾', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1479, '談', 'tán', '第三卷', '言部', '語也。从言，炎聲。', '言;炎', '炎', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1480, '謂', 'wèi', '第三卷', '言部', '報也。从言，胃聲。', '言;胃', '胃', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1481, '諒', 'liànɡ', '第三卷', '言部', '信也。从言，京聲。', '言;京', '京', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1482, '詵', 'shēn', '第三卷', '言部', '致言也。从言先。先亦聲。詩曰「螽斯羽，詵詵[辛]兮」。', '言;先;辛', '先;辛', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1483, '請', 'qǐnɡ', '第三卷', '言部', '謁也。从言，靑聲。', '言;靑', '靑', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1484, '謁', 'yè', '第三卷', '言部', '白也。从言，曷聲。', '言;曷', '曷', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1485, '許', 'xǔ', '第三卷', '言部', '𦗟言也。从言，午聲。', '言;午', '午', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1486, '諾', 'nuò', '第三卷', '言部', '應也。从言，若聲。', '言;若', '若', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1487, '譍', 'yìnɡ', '第三卷', '言部', '㠯言對也。从言，䧹聲。', '言;䧹', '䧹', 'null', NULL, '言', 'null', 'null', 'null', 'null', '大徐补 | 应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1488, '讎', 'chóu', '第三卷', '言部', '猶應也。从言，雔聲。', '言;雔', '雔', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1489, '諸', 'zhū', '第三卷', '言部', '辯也。从言，者聲。', '言;者', '者', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1490, '詩', 'shī', '第三卷', '言部', '志也。从言，寺聲。古文詩省[之]。', '言;寺;之', '寺;之', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1491, '讖', 'chèn', '第三卷', '言部', '驗也。有徵驗之書，河雒所岀書曰讖。从言，韱聲。', '言;韱', '韱', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1492, '諷', 'fěnɡ', '第三卷', '言部', '誦也。从言，風聲。', '言;風', '風', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1493, '誦', 'sònɡ', '第三卷', '言部', '諷也。从言，甬聲。', '言;甬', '甬', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1494, '讀', 'dú', '第三卷', '言部', '籒書也。从言，𧷏聲。', '言;𧷏', '𧷏', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1495, '𠶷', 'yì', '第三卷', '言部', '快也。从言中。', '言;中', '', 'null', NULL, '言;中', 'null', 'null', 'null', 'null', '政宇应删', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1496, '訓', 'xùn', '第三卷', '言部', '說敎也。从言，川聲。', '言;川', '川', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1497, '誨', 'huì', '第三卷', '言部', '曉敎也。从言，每聲。', '言;每', '每', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1498, '譔', 'zhuàn', '第三卷', '言部', '專敎也。从言，巽聲。', '言;巽', '巽', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1499, '譬', 'pì', '第三卷', '言部', '諭也。从言，辟聲。', '言;辟', '辟', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1500, '謜', 'yuán', '第三卷', '言部', '徐語也。从言，原聲。孟子曰「故謜謜而來」。', '言;原', '原', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1501, '詇', 'yànɡ', '第三卷', '言部', '早知也。从言，央聲。', '言;央', '央', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1502, '諭;喻', 'yù', '第三卷', '言部', '告也。从言，俞聲。', '言;俞', '俞', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1503, '詖', 'bì', '第三卷', '言部', '辨論也。古文㠯爲頗字。从言，皮聲。', '頗;言;皮', '頗;皮', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1504, '諄', 'zhūn', '第三卷', '言部', '告曉之孰也。从言，𦎫聲。讀若庉。', '言;𦎫;庉', '𦎫;庉', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1505, '謘', 'chí', '第三卷', '言部', '語諄謘也。从言，屖聲。讀若「行道遟遟[遲]」。', '言;屖;遲', '屖;遲', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1506, '詻', 'è', '第三卷', '言部', '論訟也。傳曰「詻詻孔子容」。从言，各聲。', '言;各', '各', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1507, '誾', 'yín', '第三卷', '言部', '和說而諍也。从言，門聲。', '言;門', '門', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1508, '謀', 'móu', '第三卷', '言部', '慮難曰謀。从言，某聲。古文謀[母口]。亦古文[謀][母言]。', '言;某;母;口', '某;母', 'null', NULL, '言;口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1509, '謨', 'mó', '第三卷', '言部', '議謀也。从言，莫聲。虞書曰「咎繇謨」。古文謨从口。', '言;莫;口', '莫', 'null', NULL, '言;口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1510, '訪', 'fǎnɡ', '第三卷', '言部', '汎謀曰訪。从言，方聲。', '言;方', '方', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1511, '諏', 'jū', '第三卷', '言部', '聚謀也。从言，取聲。', '言;取', '取', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1512, '論', 'lún', '第三卷', '言部', '議也。从言，侖聲。', '言;侖', '侖', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1513, '議', 'yì', '第三卷', '言部', '語也。一曰謀也。从言，義聲。', '言;義', '義', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1514, '訂', 'dìnɡ', '第三卷', '言部', '平議也。从言，丁聲。', '言;丁', '丁', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1515, '詳', 'xiánɡ', '第三卷', '言部', '審議也。从言，羊聲。', '言;羊', '羊', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1516, '諟', 'shì', '第三卷', '言部', '理也。从言，是聲。', '言;是', '是', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1517, '諦', 'dì', '第三卷', '言部', '審也。从言，帝聲。', '言;帝', '帝', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1518, '識', 'shí', '第三卷', '言部', '常也。一曰知也。从言，戠聲。', '言;戠', '戠', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1519, '訊', 'xùn', '第三卷', '言部', '問也。从言，卂聲。古文訊从卥[西]。', '言;卂;西', '卂;西', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1520, '詧', 'chá', '第三卷', '言部', '言微親察也。从言，祭省聲。', '言;祭', '祭', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1521, '謹', 'jǐn', '第三卷', '言部', '愼也。从言，堇聲。', '言;堇', '堇', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1522, '䚮', 'rénɡ', '第三卷', '言部', '厚也。从言，乃聲。', '言;乃', '乃', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1523, '諶', 'chén', '第三卷', '言部', '誠諦也。从言，甚聲。詩曰「天難諶[忱]斯」。', '言;甚;忱', '甚;忱', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1524, '信;㐰;訫', 'xìn', '第三卷', '言部', '誠也。从言，身省聲。古文信省也[口]。[訫]古文信[心]。', '言;身;口;心', '身', 'null', NULL, '言;口;心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1525, '訦', 'chén', '第三卷', '言部', '燕、代、東齊謂信訦也。从言，冘聲。', '言;冘', '冘', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1526, '誠', 'chénɡ', '第三卷', '言部', '信也。从言，成聲。', '言;成', '成', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1527, '誡', 'jiè', '第三卷', '言部', '敕也。从言，戒聲。', '言;戒', '戒', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1528, '誋', 'jì', '第三卷', '言部', '誡也。从言，忌聲。', '言;忌', '忌', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1529, '誥', 'ɡào', '第三卷', '言部', '告也。从言，告聲。古文誥[肘]。', '言;告;肘', '告;肘', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1530, '詔', 'zhào', '第三卷', '言部', '告也。从言，从召。召亦聲。', '言;召', '召', 'null', NULL, '言', 'null', 'null', 'null', 'null', '大徐补 | 应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1531, '誓', 'shì', '第三卷', '言部', '約束也。从言，㪿聲。', '言;㪿', '㪿', 'null', NULL, '言', 'null', 'null', 'null', 'null', '月部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1532, '譣', 'xiǎn', '第三卷', '言部', '問也。从言，僉聲。周書曰「勿㠯譣人」。', '言;僉', '僉', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1533, '詁', 'ɡǔ', '第三卷', '言部', '訓古言也。从言，古聲。詩曰「詁訓」。', '言;古', '古', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1534, '藹;譪', 'ǎi', '第三卷', '言部', '臣盡力之美。詩曰「譪譪王多吉士」。从言，葛聲。', '言;葛', '葛', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1535, '誎', 'sù', '第三卷', '言部', '餔旋促也。从言，束聲。', '言;束', '束', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1536, '諝', 'xū', '第三卷', '言部', '知也。从言，胥聲。', '言;胥', '胥', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1537, '証', 'zhènɡ', '第三卷', '言部', '諫也。从言，正聲。讀若「正月」。', '言;正', '正', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1538, '諫', 'jiàn', '第三卷', '言部', '証也。从言，柬聲。', '言;柬', '柬', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1539, '諗', 'shěn', '第三卷', '言部', '深諫也。从言，念聲。春秋傳曰「辛伯諗周桓公」。', '言;念', '念', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1540, '課', 'kè', '第三卷', '言部', '試也。从言，果聲。', '言;果', '果', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1541, '試', 'shì', '第三卷', '言部', '用也。从言，式聲。虞書曰「明試㠯功」。', '言;式', '式', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1542, '諴', 'xián', '第三卷', '言部', '和也。从言，咸聲。周書曰「不能諴于小民」。', '言;咸', '咸', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1543, '䚻', 'yóu', '第三卷', '言部', '從也。从言，肉聲。', '言;肉', '肉', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1544, '詮', 'quán', '第三卷', '言部', '具也。从言，全聲。', '言;全', '全', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1545, '訢', 'xīn', '第三卷', '言部', '喜也。从言，斤聲。', '言;斤', '斤', 'null', NULL, '言', 'null', 'null', 'null', 'null', '应删 | 并入重文', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1546, '說;悅', 'shuō', '第三卷', '言部', '說釋也。从言，兌聲。一曰談說。', '言;兌', '兌', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1547, '計', 'jì', '第三卷', '言部', '會也，筭也。从言十。', '言;十', '', 'null', NULL, '言;十', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1548, '諧', 'xié', '第三卷', '言部', '詥也。从言，皆聲。', '言;皆', '皆', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1549, '詥', 'hé', '第三卷', '言部', '諧也。从言，合聲。', '言;合', '合', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1550, '調', 'tiáo', '第三卷', '言部', '龢也。从言，周聲。', '言;周', '周', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1551, '話', 'huà', '第三卷', '言部', '會合善言也。从言，𠯑聲。傳曰「告之話言」。籒文話从言會。', '言;𠯑;會', '𠯑;會', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1552, '諈', 'zhuì', '第三卷', '言部', '諈諉，絫也。从言，垂聲。', '言;垂', '垂', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1553, '諉', 'wěi', '第三卷', '言部', '諈諉也。从言，委聲。', '言;委', '委', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1554, '警', 'jǐnɡ', '第三卷', '言部', '言之戒也。从言敬。敬亦聲。', '言;敬', '敬', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1555, '謐', 'mì', '第三卷', '言部', '靜語也。从言，𥁑聲。一曰無聲也。', '言;𥁑', '𥁑', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1556, '謙', 'qiān', '第三卷', '言部', '敬也。从言，兼聲。', '言;兼', '兼', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1557, '誼', 'yì', '第三卷', '言部', '人所宜也。从言宜。宜亦聲也。', '言;宜', '宜', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1558, '詡', 'xǔ', '第三卷', '言部', '大言也。从言，羽聲。', '言;羽', '羽', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1559, '諓', 'jiàn', '第三卷', '言部', '善言也。从言，戔聲。一曰謔也。', '言;戔', '戔', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1560, '誐', 'é', '第三卷', '言部', '嘉善也。从言，我聲。詩曰「誐㠯謐我」。', '言;我', '我', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1561, '詷', 'tónɡ', '第三卷', '言部', '共也。从言，同聲。周書曰「在后之詗」。一曰譀也。', '言;同', '同', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1562, '設', 'shè', '第三卷', '言部', '施陳也。从言殳。殳，使人也。', '言;殳', '', 'null', NULL, '言;殳', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1563, '護', 'hù', '第三卷', '言部', '救視也。从言，蒦聲。', '言;蒦', '蒦', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1564, '譞', 'xuān', '第三卷', '言部', '譞，慧也。从言，瞏聲。', '言;瞏', '瞏', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1565, '誧', 'bū', '第三卷', '言部', '大也。从言，甫聲。一曰人相助也。讀若逋。', '言;甫;逋', '甫;逋', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1566, '諰', 'xǐ', '第三卷', '言部', '思之意也。从言思。思亦聲。', '言;思', '思', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1567, '託', 'tuō', '第三卷', '言部', '寄也。从言，乇聲。', '言;乇', '乇', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1568, '記', 'jì', '第三卷', '言部', '疋也。从言，己聲。', '言;己', '己', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1569, '譽', 'yù', '第三卷', '言部', '稱也。从言，與聲。', '言;與', '與', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1570, '譒', 'bò', '第三卷', '言部', '𢾭也。从言，番聲。商書曰「王譒告之」。', '言;番', '番', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1571, '謝', 'xiè', '第三卷', '言部', '辤去也。从言，射聲。', '言;射', '射', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1572, '謠', 'yáo', '第三卷', '言部', '獨歌也。从言，䍃聲。', '言;䍃', '䍃', 'null', NULL, '言', 'null', 'null', 'null', 'null', '政宇补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1573, '謳', 'ōu', '第三卷', '言部', '歌也。从言，區聲。', '言;區', '區', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1574, '詠;咏', 'yǒnɡ', '第三卷', '言部', '歌也。从言，永聲。咏詠或从口。', '言;永;口', '永', 'null', NULL, '言;口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1575, '諍', 'zhènɡ', '第三卷', '言部', '止也。从言，爭聲。', '言;爭', '爭', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1576, '𧦝', 'hū', '第三卷', '言部', '召也。从言，乎聲。', '言;乎', '乎', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1577, '謼', 'hū', '第三卷', '言部', '𧦝也。从言，虖聲。', '言;虖', '虖', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1578, '訖', 'qì', '第三卷', '言部', '止也。从言，气聲。', '言;气', '气', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1579, '諺', 'yàn', '第三卷', '言部', '傳言也。从言，彥聲。', '言;彥', '彥', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1580, '訝;迓', 'yà', '第三卷', '言部', '相迎也。周禮曰：諸矦有卿訝也。从言，牙聲。迓訝或從辵。', '言;牙;辵', '牙', 'null', NULL, '言;辵', 'null', 'null', 'null', 'null', '大徐补 | 应删重文', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1581, '詣', 'yì', '第三卷', '言部', '候至也。从言，旨聲。', '言;旨', '旨', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1582, '講', 'jiǎnɡ', '第三卷', '言部', '和解也。从言，冓聲。', '言;冓', '冓', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1583, '謄', 'ténɡ', '第三卷', '言部', '迻書也。从言，朕聲。', '言;朕', '朕', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1584, '訒', 'rèn', '第三卷', '言部', '頓也。从言，刃聲。論語曰「其言也訒」。', '言;刃', '刃', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1585, '訥', 'nè', '第三卷', '言部', '難也。从言內。', '言;內', '內', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1586, '謯', 'jiē', '第三卷', '言部', '謯娽也。从言，虘聲。', '言;虘', '虘', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1587, '𧨍', 'xì', '第三卷', '言部', '待也。从言，伿聲。讀若䭆。', '言;伿;䭆', '伿;䭆', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1588, '譥', 'jiào', '第三卷', '言部', '痛嘑也。从言，敫聲。', '言;敫', '敫', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1589, '譊', 'náo', '第三卷', '言部', '恚嘑也。从言，堯聲。', '言;堯', '堯', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1590, '謍', 'yínɡ', '第三卷', '言部', '小聲也。从言，熒省聲。詩曰「謍謍靑蠅」。', '言;熒', '熒', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1591, '諎;唶', 'zé', '第三卷', '言部', '大聲也。从言，昔聲。讀若笮。諎或从口。', '言;昔;笮;口', '昔;笮', 'null', NULL, '言;口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1592, '諛', 'yú', '第三卷', '言部', '讇也。从言，臾聲。', '言;臾', '臾', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1593, '讇;諂', 'chǎn', '第三卷', '言部', '諛也。从言，閻聲。讇或从臽。', '言;閻;臽', '閻;臽', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1594, '謲', 'càn', '第三卷', '言部', '相怒使也。从言，參聲。', '言;參', '參', 'null', NULL, '言', 'null', 'null', 'null', 'null', '政宇调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1595, '諼', 'xuān', '第三卷', '言部', '詐也。从言，爰聲。', '言;爰', '爰', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1596, '謷', 'áo', '第三卷', '言部', '不省人言也。从言，敖聲。一曰哭不止，悲聲謷謷。', '言;敖', '敖', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1597, '訹', 'xù', '第三卷', '言部', '誘也。从言，朮聲。', '言;朮', '朮', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1598, '詑;訑', 'tuó', '第三卷', '言部', '沇州謂欺曰詑。从言，它聲。', '言;它', '它', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1599, '謾', 'mán', '第三卷', '言部', '欺也。从言，曼聲。', '言;曼', '曼', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1600, '譇', 'zhā', '第三卷', '言部', '譇拏，羞竆也。从言，奢聲。', '言;奢', '奢', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1601, '𧧻', 'zhà', '第三卷', '言部', '慙語也。从言，作聲。', '言;作', '作', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1602, '謰', 'lián', '第三卷', '言部', '謰謱也。从言，連聲。', '言;連', '連', 'null', NULL, '言', 'null', 'null', 'null', 'null', '連遱≈謰謱≈覶縷≈覼縷≈㘓哰', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1603, '謱', 'lóu', '第三卷', '言部', '謰謱也。从言，婁聲。', '言;婁', '婁', 'null', NULL, '言', 'null', 'null', 'null', 'null', '連遱≈謰謱≈覶縷≈覼縷≈㘓哰', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1604, '詒', 'yí', '第三卷', '言部', '相欺詒也。一曰相喜也。从言，台聲。', '言;台', '台', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1605, '譺', 'ài', '第三卷', '言部', '騃也。从言，疑聲。', '言;疑', '疑', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1606, '誑', 'kuánɡ', '第三卷', '言部', '欺也。从言，狂聲。', '言;狂', '狂', 'null', NULL, '言', 'null', 'null', 'null', 'null', '政宇调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1607, '𧬮', 'ɡuà', '第三卷', '言部', '相誤也。从言，𥈀聲。', '言;𥈀', '𥈀', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1608, '訕', 'shàn', '第三卷', '言部', '謗也。从言，山聲。', '言;山', '山', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1609, '譏', 'jī', '第三卷', '言部', '誹也。从言，幾聲。', '言;幾', '幾', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1610, '誣', 'wū', '第三卷', '言部', '加也。从言，巫聲。', '言;巫', '巫', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1611, '誹', 'fěi', '第三卷', '言部', '謗也。从言，非聲。', '言;非', '非', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1612, '謗', 'bànɡ', '第三卷', '言部', '毀也。从言，旁聲。', '言;旁', '旁', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1613, '譸', 'zhōu', '第三卷', '言部', '詶也。从言，壽聲。讀若醻。周書曰「無或譸張爲幻」。', '言;壽;醻', '壽;醻', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1614, '詶', 'chóu', '第三卷', '言部', '詛也。从言，州聲。', '言;州', '州', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1615, '詛', 'zǔ', '第三卷', '言部', '詶也。从言，且聲。', '言;且', '且', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1616, '䛆', 'zhòu', '第三卷', '言部', '詶也。从言，由聲。', '言;由', '由', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1617, '誃', 'chǐ', '第三卷', '言部', '離也。从言，多聲。讀若論語「跢[啓]予之足」。周景王作洛陽 誃臺。', '言;多;啓', '多;啓', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1618, '誖;悖;𢨋', 'bèi', '第三卷', '言部', '亂也。从言，孛聲。誖或从心。籒文誖从二或。', '言;孛;心;或', '孛', 'null', NULL, '言;心;或', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1619, '䜌', 'luán', '第三卷', '言部', '亂也。一曰治也。一曰不絕也。从言絲。古文䜌[𠬪糸]。', '言;絲;𠬪;糸', '', '倫', '後漢書章懷注：古之南䜌，今之倫城。', '言;絲;𠬪;糸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1620, '誤', 'wù', '第三卷', '言部', '謬也。从言，吳聲。', '言;吳', '吳', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1621, '詿', 'ɡuà', '第三卷', '言部', '誤也。从言，圭聲。', '言;圭', '圭', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1622, '誒', 'xī', '第三卷', '言部', '誒誒，可惡之䛐。从言，矣聲。一曰誒，然。春秋傳曰「誒誒[譆]岀岀」。', '言;矣;譆', '矣;譆', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1623, '譆', 'xī', '第三卷', '言部', '哀痛也。从言，喜聲。', '言;喜', '喜', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1624, '詯', 'huì', '第三卷', '言部', '膽滿气也。从言，自聲。讀若反目相睞。', '言;自;睞', '自;睞', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1625, '謧', 'lí', '第三卷', '言部', '謧詍，多言也。从言，离聲。', '言;离', '离', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1626, '詍', 'yì', '第三卷', '言部', '詍詍，多言也。从言，世聲。詩曰「無然詍詍」。', '言;世', '世', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1627, '訾;訿', 'zǐ', '第三卷', '言部', '訾訾，不思稱意也。从言，此聲。詩曰「翕翕訿訿」。', '言;此', '此', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1628, '䛬', 'táo', '第三卷', '言部', '往來言也。一曰小兒未能正言也。一曰祝也。从言，匋聲。䛬或从包。', '言;匋;包', '匋;包', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1629, '𧦦;䛁', 'nán', '第三卷', '言部', '𧦦𧦦，多語也。从言，冉聲。樂浪有𧦦邯縣。', '言;冉', '冉', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1630, '䜚', 'tà', '第三卷', '言部', '䜚誻，語相及也。从言，遝聲。', '言;遝', '遝', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1631, '誻', 'tà', '第三卷', '言部', '䜚誻也。从言，沓聲。', '言;沓', '沓', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1632, '訮', 'yán', '第三卷', '言部', '諍語訮訮也。从言，开聲。', '言;开', '开', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1633, '讗', 'xié', '第三卷', '言部', '疾言皃。一曰相怒也。从言，巂聲。讀若畫。', '言;巂;畫', '巂;畫', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1634, '訇', 'hōnɡ', '第三卷', '言部', '駭言聲。从言，匀省聲。漢中 西城有訇鄉。又讀若玄。籒文不省。', '言;匀;玄', '匀;玄', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1635, '諞', 'piǎn', '第三卷', '言部', '便巧言也。从言，扁聲。周書曰「𢧵𢧵善諞言」。諭語曰「友諞[便]佞」。', '言;扁;便', '扁;便', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1636, '𧭹', 'pín', '第三卷', '言部', '比也。从言，頻聲。', '言;頻', '頻', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1637, '𧥣', 'kòu', '第三卷', '言部', '扣也。如求婦先𧥣發之。从言口。口亦聲。', '言;口', '口', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1638, '誽', 'nì', '第三卷', '言部', '言相誽司也。从言，兒聲。', '言;兒', '兒', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1639, '誂', 'tiǎo', '第三卷', '言部', '相𧦝誘也。从言，兆聲。', '言;兆', '兆', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1640, '譄', 'zēnɡ', '第三卷', '言部', '加也。从言，曾聲。', '言;曾', '曾', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1641, '詄', 'dié', '第三卷', '言部', '忘也。从言，失聲。', '言;失', '失', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1642, '諅', 'jì', '第三卷', '言部', '忌也。从言，其聲。周書曰「上不諅[忌]于凶德」。', '言;其;忌', '其;忌', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1643, '譀', 'hàn', '第三卷', '言部', '誕也。从言，敢聲。俗譀从忘。', '言;敢;忘', '敢', 'null', NULL, '言;忘', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1644, '誇', 'kuā', '第三卷', '言部', '譀也。从言，夸聲。', '言;夸', '夸', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1645, '誕', 'dàn', '第三卷', '言部', '詷也。从言，延聲。籒文誕省正[廴]。', '言;延;廴', '延', 'null', NULL, '言;廴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1646, '䜕', 'mài', '第三卷', '言部', '譀也。从言，萬聲。', '言;萬', '萬', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1647, '謔', 'xuè', '第三卷', '言部', '戲也。从言，虐聲。詩曰「善戲謔兮」。', '言;虐', '虐', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1648, '詪', 'hěn', '第三卷', '言部', '很戾也。从言，艮聲。', '言;艮', '艮', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1649, '訌', 'hònɡ', '第三卷', '言部', '䜋也。从言，工聲。詩曰「蟊賊內訌」。', '言;工', '工', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1650, '䜋', 'huì', '第三卷', '言部', '中止也。从言，貴聲。司馬法曰「師多則民䜋」。䜋，止也。', '言;貴', '貴', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1651, '𧬨', 'huì', '第三卷', '言部', '聲也。从言，歲聲。詩曰「有𧬨[嘒]其聲」。', '言;歲;嘒', '歲;嘒', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1652, '諣', 'huà', '第三卷', '言部', '疾言也。从言，咼聲。', '言;咼', '咼', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1653, '𧮓', 'tuí', '第三卷', '言部', '譟也。从言，魋聲。', '言;魋', '魋', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1654, '譟', 'zào', '第三卷', '言部', '擾也。从言，喿聲。', '言;喿', '喿', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1655, '訆', 'jiào', '第三卷', '言部', '妄言也。从言，丩聲。春秋傳曰「或訆于宋大廟」。', '言;丩', '丩', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1656, '謕', 'tí', '第三卷', '言部', '數諒也。从言，虒聲。', '言;虒', '虒', 'null', NULL, '言', 'null', 'null', 'null', 'null', '政宇补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1657, '諕', 'háo', '第三卷', '言部', '號也。从言虎。', '言;虎', '', 'null', NULL, '言;虎', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1658, '讙', 'huān', '第三卷', '言部', '譁也。从言，雚聲。', '言;雚', '雚', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1659, '譁', 'huā', '第三卷', '言部', '讙也。从言，華聲。', '言;華', '華', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1660, '謣;𧪮', 'yú', '第三卷', '言部', '妄言也。从言，雩聲。謣或从荂。', '言;雩;荂', '雩;荂', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1661, '譌', 'é', '第三卷', '言部', '譌言也。从言，爲聲。詩曰「民之譌[化]言」。', '言;爲;化', '爲;化', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1662, '詿', 'ɡuà', '第三卷', '言部', '誤也。从言，佳省聲。', '言;佳', '佳', 'null', NULL, '言', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1663, '誤', 'wù', '第三卷', '言部', '謬也。从言，吳聲。', '言;吳', '吳', 'null', NULL, '言', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1664, '謬', 'miù', '第三卷', '言部', '狂者之妄言也。从言，翏聲。', '言;翏', '翏', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1665, '𧧢', 'huǎnɡ', '第三卷', '言部', '夢言也。从言，巟聲。', '言;巟', '巟', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1666, '謈', 'bó', '第三卷', '言部', '大嘑自冤也。从言，㬥省聲。', '言;㬥', '㬥', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1667, '訬', 'chāo', '第三卷', '言部', '訬，擾也。一曰訬，獪。从言，少聲。讀若「狡毚」。', '言;少;狡', '少;狡', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1668, '諆', 'qī', '第三卷', '言部', '欺也。从言，其聲。', '言;其', '其', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1669, '譎', 'jué', '第三卷', '言部', '權詐也。益、梁曰謬，欺天下曰譎。从言，矞聲。', '言;矞', '矞', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1670, '詐', 'zhà', '第三卷', '言部', '欺也。从言，乍聲。', '言;乍', '乍', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1671, '訏', 'xū', '第三卷', '言部', '𧪰也。从言，于聲。一曰詭譌。齊、楚謂信曰訏。', '言;于', '于', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1672, '𧪰', 'jiē', '第三卷', '言部', '嗞也。从言，差聲。一曰痛惜。', '言;差', '差', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1673, '讋', 'zhé', '第三卷', '言部', '失气也。一曰言不止也。从言，龖省聲。傅毅讀若慴。籒文讋不省。', '言;龖;慴', '龖;慴', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1674, '謵', 'xí', '第三卷', '言部', '讋也。从言，習聲。', '言;習', '習', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1675, '䛩', 'wù', '第三卷', '言部', '相毀也。从言，亞聲。一曰畏䛩。', '言;亞', '亞', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1676, '䜐', 'huī', '第三卷', '言部', '相毀也。从言，隋聲。', '言;隋', '隋', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1677, '𧮑', 'tà', '第三卷', '言部', '㗐也。从言，闒聲。', '言;闒', '闒', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1678, '詾', 'xiōnɡ', '第三卷', '言部', '訟也。从言，匈聲。或省[凶]。詾或从兇。', '言;匈;凶;兇', '匈;凶;兇', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1679, '訟', 'sònɡ', '第三卷', '言部', '爭也。从言，公聲。一曰歌訟。古文訟[谷]。', '言;公;谷', '公;谷', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1680, '謓', 'chēn', '第三卷', '言部', '恚也。从言，眞聲。賈侍中說「謓，𥬇」。一曰讀若振。', '言;眞;振', '眞;振', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1681, '謚', 'shì', '第三卷', '言部', '笑皃。从言，益聲。', '言;益', '益', 'null', NULL, '言', 'null', 'null', 'null', 'null', '应删 | 政宇调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1682, '謺', 'zhé', '第三卷', '言部', '謺讘也。从言，執聲。', '言;執', '執', 'null', NULL, '言', 'null', 'null', 'null', 'null', '政宇调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1683, '讘', 'zhè', '第三卷', '言部', '多言也。从言，聶聲。河東有狐讘縣。', '言;聶', '聶', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1684, '訶', 'hē', '第三卷', '言部', '大言而怒也。从言，可聲。', '言;可', '可', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1685, '𧧰', 'zhǐ', '第三卷', '言部', '訐也。从言，臣聲。讀若指。', '言;臣;指', '臣;指', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1686, '訐', 'jié', '第三卷', '言部', '面相㡿罪也。从言，干聲。', '言;干', '干', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1687, '訴;愬', 'sù', '第三卷', '言部', '告訐也。从言，㡿聲。論語曰「訴子路於季孫」。訴或从言朔。訴或从朔心。', '言;㡿;言;朔;朔;心', '㡿;朔;朔', 'null', NULL, '言;言;心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1688, '譖', 'zèn', '第三卷', '言部', '愬也。从言，朁聲。', '言;朁', '朁', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1689, '讒', 'chán', '第三卷', '言部', '譖也。从言，毚聲。', '言;毚', '毚', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1690, '譴', 'qiǎn', '第三卷', '言部', '謫問也。从言，遣聲。', '言;遣', '遣', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1691, '謫', 'zhé', '第三卷', '言部', '罰也。从言，啻聲。', '言;啻', '啻', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1692, '諯', 'zhuān', '第三卷', '言部', '數也。一曰相讓也。从言，耑聲。讀若專。', '言;耑;專', '耑;專', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1693, '讓', 'rànɡ', '第三卷', '言部', '相責讓。从言，襄聲。', '言;襄', '襄', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1694, '譙;誚', 'qiào', '第三卷', '言部', '嬈譊也。从言，焦聲。讀若嚼。古文譙从肖。周書曰「亦未敢誚公」。', '言;焦;嚼;肖', '焦;嚼;肖', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1695, '𧧒', 'cì', '第三卷', '言部', '數諫也。从言，朿聲。', '言;朿', '朿', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1696, '誶', 'suì', '第三卷', '言部', '讓也。从言，卒聲。國語曰「誶申胥」。', '言;卒', '卒', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1697, '詰', 'jié', '第三卷', '言部', '問也。从言，吉聲。', '言;吉', '吉', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1698, '𧭅', 'wànɡ', '第三卷', '言部', '責望也。从言，朢聲。', '言;朢', '朢', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1699, '詭', 'ɡuǐ', '第三卷', '言部', '責也。从言，危聲。', '言;危', '危', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1700, '證', 'zhènɡ', '第三卷', '言部', '告也。从言，登聲。', '言;登', '登', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1701, '詘', 'qū', '第三卷', '言部', '詰詘也。一曰屈襞。从言，岀聲。詘或从屈。', '言;岀;屈', '岀;屈', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1702, '𧧁', 'yuǎn', '第三卷', '言部', '尉也。从言，夗聲。', '言;夗', '夗', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1703, '詗', 'xiònɡ', '第三卷', '言部', '知処告言之。从言，冋聲。', '言;冋', '冋', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1704, '讂', 'juàn', '第三卷', '言部', '流言也。从言，敻聲。', '言;敻', '敻', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1705, '詆', 'dǐ', '第三卷', '言部', '訶也。从言，氐聲。', '言;氐', '氐', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1706, '誰', 'shuí', '第三卷', '言部', '誰何也。从言，隹聲。', '言;隹', '隹', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1707, '諽', 'ɡé', '第三卷', '言部', '飭也。从言，革聲。讀若戒。一曰更也。', '言;革;戒', '革;戒', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1708, '讕', 'lán', '第三卷', '言部', '抵讕也。从言，闌聲。', '言;闌', '闌', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1709, '診', 'zhěn', '第三卷', '言部', '視也。从言，㐱聲。', '言;㐱', '㐱', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1710, '𧬜;𧬊', 'xī', '第三卷', '言部', '悲聲也。从言，斯省聲。', '言;斯', '斯', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1711, '訧', 'yóu', '第三卷', '言部', '罪也。从言，尤聲。周書曰「報㠯庶訧」。', '言;尤', '尤', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1712, '誅', 'zhū', '第三卷', '言部', '討也。从言，朱聲。', '言;朱', '朱', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1713, '討', 'tǎo', '第三卷', '言部', '治也。从言寸。', '言;寸', '', 'null', NULL, '言;寸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1714, '諳', 'ān', '第三卷', '言部', '悉也。从言，音聲。', '言;音', '音', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1715, '讄', 'lěi', '第三卷', '言部', '禱也。絫功德㠯求福也。論語云「讄曰『禱尒于上下神祇』」。从言，畾聲。讄或从纍。', '言;畾;纍', '畾;纍', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1716, '謚', 'yì', '第三卷', '言部', '行之迹也。从言，益聲。', '言;益', '益', 'null', NULL, '言', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1717, '誄', 'lěi', '第三卷', '言部', '謚也。从言，耒聲。', '言;耒', '耒', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1718, '諱', 'huì', '第三卷', '言部', '忌也。从言，韋聲。', '言;韋', '韋', 'null', NULL, '言', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1719, '謑;䜁', 'xǐ', '第三卷', '言部', '謑詬，恥也。从言，奚聲。謑或从奊。', '言;奚;奊', '奚;奊', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1720, '詬;訽', 'ɡòu', '第三卷', '言部', '謑詬也。从言，后聲。詬或从句。', '言;后;句', '后;句', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1721, '諜', 'dié', '第三卷', '言部', '軍中反閒也。从言，枼聲。', '言;枼', '枼', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1722, '該', 'ɡāi', '第三卷', '言部', '軍中約也。从言，亥聲。讀若「心中滿該」。', '言;亥;該', '亥;該', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1723, '譯;𧬳', 'yì', '第三卷', '言部', '傳四夷之語者。从言，睪聲。', '言;睪', '睪', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1724, '訄', 'qiú', '第三卷', '言部', '迫也。从言，九聲。讀又若丘。', '言;九;丘', '九;丘', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1725, '譶', 'tà', '第三卷', '言部', '疾言也。从三言。讀若沓。', '沓', '沓', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1726, '詢', 'xún', '第三卷', '言部', '謀也。从言，旬聲。', '言;旬', '旬', 'null', NULL, '言', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1727, '讜', 'dǎnɡ', '第三卷', '言部', '直言也。从言，黨聲。', '言;黨', '黨', 'null', NULL, '言', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1728, '譜', 'pǔ', '第三卷', '言部', '籍録也。从言，普聲。', '言;普', '普', 'null', NULL, '言', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1729, '詎', 'jù', '第三卷', '言部', '詎猶豈也。从言，巨聲。', '言;巨', '巨', 'null', NULL, '言', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1730, '𧩮', 'xiǎo', '第三卷', '言部', '小也，誘也。从言，叜聲。', '言;叜', '叜', 'null', NULL, '言', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1731, '謎', 'mí', '第三卷', '言部', '隱語也。从言迷。迷亦聲。', '言;迷', '迷', 'null', NULL, '言', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1732, '誌', 'zhì', '第三卷', '言部', '記誌也。从言，志聲。', '言;志', '志', 'null', NULL, '言', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1733, '訣', 'jué', '第三卷', '言部', '訣別也。一曰法也。从言，決省聲。', '言;決', '決', 'null', NULL, '言', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1734, '誜', 'shuà', '第三卷', '言部', '狂言也。从言，夋聲。', '言;夋', '夋', 'null', NULL, '言', 'null', 'null', 'null', 'null', '政宇新附', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1735, '誩', 'jìnɡ', '第三卷', '誩部', '競言也。从二言。凡誩之屬皆从誩。讀若競。', '言;競', '競', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1736, '譱;善;𦎍', 'shàn', '第三卷', '誩部', '吉也。从誩羊。此與義、美同意。篆文从言。', '誩;羊;言', '', 'null', NULL, '誩;羊;言', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1737, '競', 'jìnɡ', '第三卷', '誩部', '彊語也。从誩二人。一曰逐也。', '誩;人', '', 'null', NULL, '誩;人', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1738, '讟', 'dú', '第三卷', '誩部', '痛怨也。从誩，𧷏聲。春秋傳曰「民無怨讟」。', '誩;𧷏', '𧷏', 'null', NULL, '誩', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1739, '音', 'yīn', '第三卷', '音部', '聲生於心、有節於外謂之音。宮商角徵羽，聲也。絲竹金石、匏土革木，音也。从言含一。凡音之屬皆从音。古文[俟考]。', '言;一', '', 'null', NULL, '言;一', 'null', 'null', 'null', 'null', '政宇补重文', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1740, '響', 'xiǎnɡ', '第三卷', '音部', '聲也。从音，鄉聲。', '音;鄉', '鄉', 'null', NULL, '音', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1741, '韽', 'ān', '第三卷', '音部', '下徹聲。从音，酓聲。', '音;酓', '酓', 'null', NULL, '音', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1742, '韶', 'sháo', '第三卷', '音部', '虞舜樂也。書曰「簫韶九成，鳳皇來儀」。从音，召聲。', '音;召', '召', 'null', NULL, '音', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1743, '章', 'zhānɡ', '第三卷', '音部', '樂竟爲一章。从音十。十，數之終也。', '音;十', '', 'null', NULL, '音;十', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1744, '竟', 'jìnɡ', '第三卷', '音部', '樂曲盡爲竟。从音儿。', '音;儿', '', 'null', NULL, '音;儿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1745, '韻', 'yùn', '第三卷', '音部', '和也。从音，員聲。', '音;員', '員', 'null', NULL, '音', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1746, '䇂', 'qiān', '第三卷', '䇂部', '辠也。从干𠄞。𠄞，古文上字。凡䇂之屬皆从䇂。讀若愆。張林說。', '干;𠄞;愆', '愆', 'null', NULL, '干;𠄞', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1747, '童', 'tónɡ', '第三卷', '䇂部', '男有辠曰奴。奴曰童，女曰妾。从䇂，重省聲。籒文童。中與竊中同，从廿。廿，㠯爲古文疾字。', '䇂;重;疾', '重', 'null', NULL, '䇂;疾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1748, '妾', 'qiè', '第三卷', '䇂部', '有辠女子給事之得接於君者。从䇂女。春秋傳云「女爲人妾」。妾，不娉也。', '䇂;女', '', 'null', NULL, '䇂;女', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1749, '丵', 'zhuó', '第三卷', '丵部', '叢生艸也。象丵嶽相竝岀也。凡丵之屬皆从丵。讀若浞。', '浞', '浞', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1750, '業', 'yè', '第三卷', '丵部', '大版也。所㠯飾縣鐘𡔷，捷業如鋸齒，㠯白畫之。象其鉏鋙相承也。从丵，从巾。巾象版。詩曰「巨業維樅」。古文業[口大]。', '丵;巾;口;大', '', 'null', NULL, '丵;巾;口;大', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1751, '叢', 'cónɡ', '第三卷', '丵部', '聚也。从丵，取聲。', '丵;取', '取', 'null', NULL, '丵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1752, '𡭊;對', 'duì', '第三卷', '丵部', '應無方也。从丵口，从寸。𡭊或从士。漢文帝㠯爲責對而面言，多非誠對，故去其口㠯从士也。', '丵;口;寸;士', '', 'null', NULL, '丵;口;寸;士', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1753, '菐', 'pú', '第三卷', '菐部', '瀆菐也。从丵，从廾。廾亦聲。凡菐之屬皆从菐。', '丵;廾', '廾', 'null', NULL, '丵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1754, '僕', 'pú', '第三卷', '菐部', '給事者。从人菐。菐亦聲。古文从臣。', '人;菐;臣', '菐', 'null', NULL, '人;臣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1755, '𠔯', 'bān', '第三卷', '菐部', '賦事也。从菐八。八，分之也。八亦聲。讀若頒。一曰讀若非。', '菐;八;頒;非', '八;頒;非', 'null', NULL, '菐', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1756, '廾', 'ɡǒnɡ', '第三卷', '廾部', '竦手也。从𠂇又。楊雄說「廾从㒳手」。凡廾之屬皆从廾。', '𠂇;又;手', '', 'null', NULL, '𠂇;又;手', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1757, '奉', 'fènɡ', '第三卷', '廾部', '承也。从手廾。丰聲。', '手;廾;丰', '丰', 'null', NULL, '手;廾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1758, '丞;㞼', 'chénɡ', '第三卷', '廾部', '翊也。从廾，从卪、从山。[𡴺]山高，奉承之義。', '廾;𡴺', '', 'null', NULL, '廾;𡴺', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1759, '奐', 'huàn', '第三卷', '廾部', '取奐也。一曰大也。从廾，敻省聲。', '廾;敻', '敻', 'null', NULL, '廾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1760, '弇', 'yǎn', '第三卷', '廾部', '葢也。从廾，合聲。古文弇[穴日]。', '廾;合;穴;日', '合', 'null', NULL, '廾;穴;日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1761, '𢍰', 'yì', '第三卷', '廾部', '引繒也。从廾，睪聲。', '廾;睪', '睪', 'null', NULL, '廾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1762, '𢍁', 'qí', '第三卷', '廾部', '舉也。从廾，甾聲。春秋傳曰「晉人或㠯廣隊，楚人𢍁[惎]之」。黃顥說「廣車陷，楚人爲舉之」。杜林㠯爲「麒麟」字。', '廾;甾;惎;麒', '甾;惎;麒', 'null', NULL, '廾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1763, '异', 'yì', '第三卷', '廾部', '舉也。从廾，㠯聲。虞書曰「嶽曰异哉」。', '廾;㠯', '㠯', 'null', NULL, '廾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1764, '弄', 'lònɡ', '第三卷', '廾部', '玩也。从廾玉。', '廾;玉', '', 'null', NULL, '廾;玉', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1765, '𢌻', 'yù', '第三卷', '廾部', '㒳手盛也。从廾，𡴆聲。', '廾;𡴆', '𡴆', 'null', NULL, '廾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1766, '𢍏', 'juàn', '第三卷', '廾部', '摶飯也。从廾，釆聲。釆，古文辨字。讀若「書卷」。', '廾;釆;辨;卷', '釆;辨;卷', 'null', NULL, '廾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1767, '𢌳', 'kuí', '第三卷', '廾部', '持弩拊。从廾，肉聲。', '廾;肉', '肉', 'null', NULL, '廾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1768, '戒', 'jiè', '第三卷', '廾部', '警也。从廾戈。持戈㠯戒不虞。', '廾;戈', '', 'null', NULL, '廾;戈', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1769, '兵', 'bīnɡ', '第三卷', '廾部', '械也。从廾持斤。并力之皃。古文兵从人廾干。籒文兵[一]。', '廾;斤;人;廾;干;一', '', 'null', NULL, '廾;斤;人;廾;干;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1770, '龏', 'ɡōnɡ', '第三卷', '廾部', '愨也。从廾，龍聲。', '廾;龍', '龍', 'null', NULL, '廾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1771, '弈', 'yì', '第三卷', '廾部', '圍棊也。从廾，亦聲。論語曰「不有博弈者乎」。', '廾;亦', '亦', 'null', NULL, '廾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1772, '具', 'jù', '第三卷', '廾部', '共置也。从廾，貝省。古㠯貝爲貨。', '廾;貝', '', 'null', NULL, '廾;貝', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1773, '𡴂;攀', 'pān', '第三卷', '𡴂部', '引也。从反廾。凡𡴂之屬皆从𡴂。𡴂或从手，从樊。', '廾;手;樊', '樊', 'null', NULL, '廾;手', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1774, '樊', 'fán', '第三卷', '𡴂部', '驇不行也。从𡴂棥。棥亦聲。', '𡴂;棥', '棥', 'null', NULL, '𡴂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1775, '奱', 'luán', '第三卷', '𡴂部', '樊也。从𡴂，䜌聲。', '𡴂;䜌', '䜌', 'null', NULL, '𡴂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1776, '共', 'ɡònɡ', '第三卷', '共部', '同也。从廿廾。凡共之屬皆从共。古文共[茻]。', '廿;廾;茻', '', 'null', NULL, '廿;廾;茻', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1777, '龔', 'ɡōnɡ', '第三卷', '共部', '給也。从共，龍聲。', '共;龍', '共;龍', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1778, '異', 'yì', '第三卷', '異部', '分也。从廾畀。畀，予也。凡異之屬皆从異。', '廾;畀', '', 'null', NULL, '廾;畀', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1779, '戴', 'dài', '第三卷', '異部', '分物得增益曰戴。从異，𢦒聲。籒文戴[弋]。', '異;𢦒;弋', '𢦒;弋', 'null', NULL, '異', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1780, '舁', 'yú', '第三卷', '舁部', '共舉也。从𦥑廾。凡舁之屬皆从舁。讀若余。', '𦥑;廾;余', '余', 'null', NULL, '𦥑;廾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1781, '𢍱;𠨧;䙴', 'qiān', '第三卷', '舁部', '升高也。从舁，𦥓聲。𢍱或从卪。古文𢍱。', '舁;𦥓;卪', '𦥓', 'null', NULL, '舁;卪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1782, '與', 'yǔ', '第三卷', '舁部', '黨與也。从舁与。古文與[廾]。', '舁;与;廾', '舁;与', 'null', NULL, '廾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1783, '興', 'xīnɡ', '第三卷', '舁部', '起也。从舁同。同，同力也。', '舁;同', '', 'null', NULL, '舁;同', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1784, '𦥑', 'jú', '第三卷', '𦥑部', '叉手也。从𠀉彐。凡𦥑之屬皆从𦥑。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1785, '要', 'yāo', '第三卷', '𦥑部', '身中也。象人要自𦥑之形。从𦥑。古文要[𦥓女]。', '𦥑;𦥓;女', '', 'null', NULL, '臼;𦥓;女', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1786, '䢅', 'chén', '第三卷', '䢅部', '早昧爽也。从𦥑辰。辰，時也。辰亦聲。丮夕爲𡖊，𦥑辰爲䢅，皆同意。凡䢅之屬皆从䢅。', '𦥑;辰', '辰', 'null', NULL, '臼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1787, '農', 'nónɡ', '第三卷', '䢅部', '耕人也。从䢅，𦥓聲。籒文農从林[辰]。古文農[甘]。亦古文農。', '䢅;𦥓;林;辰;甘', '𦥓', 'null', NULL, '䢅;林;辰;甘', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1788, '爨', 'cuàn', '第三卷', '爨部', '齊謂炊爨。[𦥑]象持甑，冂爲竈口，廾推林內火。凡爨之屬皆从爨。籒文爨省。', '𦥑;冂;廾;林;火', '', 'null', NULL, '臼;冂;廾;林;火', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1789, '𦦧', 'qiónɡ', '第三卷', '爨部', '所㠯枝鬲者。从爨省，鬲省。', '爨;鬲', '', 'null', NULL, '爨;鬲', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1790, '釁', 'xìn', '第三卷', '爨部', '血祭也。象祭𫁘也。从爨省。从酉。酉所㠯祭也。从分。分亦聲。', '爨;酉;分', '分', 'null', NULL, '爨;酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1791, '革', 'ɡé', '第三卷', '革部', '獸皮治去其毛曰革。革，更也。象古文革之形。凡革之屬皆从革。古文革从卅。卅年爲一世而道更也。𦥑聲。', '卅;𦥑', '𦥑', 'null', NULL, '卅', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1792, '鞹', 'kuò', '第三卷', '革部', '革也。論語曰「虎豹之鞹」。从革，郭聲。', '革;郭', '郭', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1793, '靬', 'jiān', '第三卷', '革部', '靬，乾革也。武威有麗靬縣。从革，干聲。', '革;干', '干', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1794, '𩊚', 'luò', '第三卷', '革部', '生革可㠯爲縷束也。从革，各聲。', '革;各', '各', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1795, '鞄', 'páo', '第三卷', '革部', '柔革工也。从革，包聲。讀若朴。周禮曰「柔皮之工——鮑氏」，鮑卽鞄也。', '革;包;朴;鮑', '包;朴;鮑', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1796, '䩵;韗', 'yùn', '第三卷', '革部', '攻皮治𡔷工也。从革，軍聲。讀若運。䩵或从韋。', '革;軍;運;韋', '軍;運', 'null', NULL, '革;韋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1797, '鞣', 'róu', '第三卷', '革部', '耎也。从革柔。柔亦聲。', '革;柔', '柔', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1798, '靼', 'dá', '第三卷', '革部', '柔革也。从革，旦聲。古文靼从亶。', '革;旦;亶', '旦;亶', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1799, '鞼', 'ɡuì', '第三卷', '革部', '革繡也。从革，貴聲。', '革;貴', '貴', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1800, '鞶', 'pán', '第三卷', '革部', '大帶也。易曰「或錫之鞶帶」。男子帶鞶。婦人帶絲。从革，般聲。', '革;般', '般', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1801, '鞏', 'ɡǒnɡ', '第三卷', '革部', '㠯韋束也。易曰「鞏用黃牛之革」。从革，巩聲。', '革;巩', '巩', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1802, '鞔', 'mán', '第三卷', '革部', '履空也。从革，免聲。', '革;免', '免', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1803, '靸', 'sǎ', '第三卷', '革部', '小兒履也。从革，及聲。', '革;及', '及', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1804, '䩕', 'ánɡ', '第三卷', '革部', '䩕角，鞮屬。从革，卬聲。', '革;卬', '卬', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1805, '鞮', 'dī', '第三卷', '革部', '革履也。胡人履連脛，謂之絡鞮。从革，是聲。', '革;是', '是', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1806, '䩡', 'jiá', '第三卷', '革部', '鞮䩡沙也。从革，夾聲。', '革;夾', '夾', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1807, '𩌦', 'xǐ', '第三卷', '革部', '鞮屬。从革，徙聲。', '革;徙', '徙', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1808, '鞵', 'xié', '第三卷', '革部', '生革鞮也。从革，奚聲。', '革;奚', '奚', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1809, '靪', 'dīnɡ', '第三卷', '革部', '補履下也。从革，丁聲。', '革;丁', '丁', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1810, '鞠', 'jū', '第三卷', '革部', '蹋鞠也。从革，匊聲。鞠或从𥱩。', '革;匊;𥱩', '匊;𥱩', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1811, '鞀;鼗', 'táo', '第三卷', '革部', '鞀，遼也。从革，召聲。鞀或从兆聲。鼗鞀或从𡔷兆。籒文鞀从殸召。', '革;召;兆;𡔷;殸', '召;兆', 'null', NULL, '革;𡔷;殸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1812, '𩌑', 'yuān', '第三卷', '革部', '量物之𩌑。一曰抒井𩌑。古㠯革。从革，冤聲。𩌑或从宛。', '革;冤;宛', '冤;宛', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1813, '鞞', 'bǐnɡ', '第三卷', '革部', '刀室也。从革，卑聲。', '革;卑', '卑', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1814, '鞎', 'hén', '第三卷', '革部', '車革前曰鞎。从革，艮聲。', '革;艮', '艮', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1815, '鞃', 'hónɡ', '第三卷', '革部', '車軾中把也。从革，弘聲。詩曰「鞹鞃淺幭」。讀若穹。', '革;弘;穹', '弘;穹', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1816, '鞪', 'mù', '第三卷', '革部', '車軸束也。从革，敄聲。', '革;敄', '敄', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1817, '䩛', 'bì', '第三卷', '革部', '車束也。从革，必聲。', '革;必', '必', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1818, '𩎑;𩎈', 'zuān', '第三卷', '革部', '車衡三束也。曲轅𩎑縛，直轅䡞縛。从革，爨聲。讀若論語「鑽燧」之鑽。𩎑或从革贊。', '革;爨;鑽;贊', '爨;鑽;贊', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1819, '𩊝', 'zhì', '第三卷', '革部', '葢杠系也。从革，旨聲。', '革;旨', '旨', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1820, '鞁', 'bèi', '第三卷', '革部', '車駕具也。从革，皮聲。', '革;皮', '皮', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1821, '鞥', 'ēnɡ', '第三卷', '革部', '轡鞥。从革，弇聲。讀若應。一曰龓頭繞者。', '革;弇;應', '弇;應', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1822, '靶', 'bà', '第三卷', '革部', '轡革也。从革，巴聲。', '革;巴', '巴', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1823, '𩎍', 'xiǎn', '第三卷', '革部', '箸亦鞥也。从革，顯聲。', '革;顯', '顯', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1824, '靳', 'jìn', '第三卷', '革部', '當膺也。从革，斤聲。', '革;斤', '斤', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1825, '䩶', 'chěnɡ', '第三卷', '革部', '驂具也。从革，𧈪聲。讀若「騁蜃」。', '革;𧈪;騁', '𧈪;騁', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1826, '靷', 'yǐn', '第三卷', '革部', '所㠯引軸者也。从革，引聲。籒文靷[昏]。', '革;引;昏', '引;昏', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1827, '䩪', 'ɡuǎn', '第三卷', '革部', '車鞁具也。从革，官聲。', '革;官', '官', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1828, '𩊪', 'dòu', '第三卷', '革部', '車鞁具也。从革，豆聲。', '革;豆', '豆', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1829, '䩒', 'yú', '第三卷', '革部', '䩪內環靼也。从革，于聲。', '革;于', '于', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1830, '𩌏', 'bó', '第三卷', '革部', '車下索也。从革，尃聲。', '革;尃', '尃', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1831, '𩋊', 'è', '第三卷', '革部', '車具也。从革，奄聲。', '革;奄', '奄', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1832, '𩋁', 'zhuó', '第三卷', '革部', '車具也。从革，叕聲。', '革;叕', '叕', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1833, '鞍;鞌', 'ān', '第三卷', '革部', '馬鞁具也。从革，安聲。', '革;安', '安', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1834, '䩸', 'rónɡ', '第三卷', '革部', '鞍毳飾也。从革，茸聲。', '革;茸', '茸', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1835, '䩞', 'tié', '第三卷', '革部', '鞍飾。从革，占聲。', '革;占', '占', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1836, '鞈', 'ɡé', '第三卷', '革部', '防汗也。从革，合聲。', '革;合', '合', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1837, '勒', 'lè', '第三卷', '革部', '馬頭落銜也。从革，力聲。', '革;力', '力', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1838, '鞙', 'xuàn', '第三卷', '革部', '大車縛軛靼。从革，肙聲。', '革;肙', '肙', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1839, '𩋠', 'miǎn', '第三卷', '革部', '勒靼也。从革，面聲。', '革;面', '面', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1840, '靲', 'qín', '第三卷', '革部', '鞮也。从革，今聲。', '革;今', '今', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1841, '鞬', 'jiān', '第三卷', '革部', '所㠯戢弓矢。从革，建聲。', '革;建', '建', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1842, '韇', 'dú', '第三卷', '革部', '弓矢韇也。从革，𧷏聲。', '革;𧷏', '𧷏', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1843, '䪎', 'suī', '第三卷', '革部', '緌也。从革，巂聲。', '革;巂', '巂', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1844, '䩯', 'jí', '第三卷', '革部', '急也。从革，亟聲。', '革;亟', '亟', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1845, '鞭', 'biān', '第三卷', '革部', '毆也。从革，便聲。古文鞭[亼攴]。', '革;便;亼;攴', '便', 'null', NULL, '革;亼;攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1846, '鞅', 'yǎnɡ', '第三卷', '革部', '頸靼也。从革，央聲。', '革;央', '央', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1847, '韄', 'hù', '第三卷', '革部', '佩刀系也。从革，蒦聲。', '革;蒦', '蒦', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1848, '𩉺', 'tuó', '第三卷', '革部', '馬尾𩉺也。从革，它聲。今之般緧。', '革;它', '它', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1849, '䩤', 'xié', '第三卷', '革部', '繫牛脛也。从革，見聲。', '革;見', '見', 'null', NULL, '革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1850, '鞘', 'qiào', '第三卷', '革部', '刀室也。从革，肖聲。', '革;肖', '肖', 'null', NULL, '革', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1851, '韉', 'jiān', '第三卷', '革部', '馬鞁具也。从革，薦聲。', '革;薦', '薦', 'null', NULL, '革', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1852, '鞾', 'xuē', '第三卷', '革部', '鞮屬。从革，華聲。', '革;華', '華', 'null', NULL, '革', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1853, '靮', 'dí', '第三卷', '革部', '馬羈也。从革，勺聲。', '革;勺', '勺', 'null', NULL, '革', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1854, '鬲;㽁', 'lì', '第三卷', '鬲部', '鼎屬也。實五觳，斗二升曰觳。象腹交文三足。凡鬲之屬皆从鬲。鬲或从瓦。漢令鬲从瓦，厤聲。', '瓦;厤', '厤', 'null', NULL, '瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1855, '䰙', 'yǐ', '第三卷', '鬲部', '三足鍑也。一曰滫米器也。从鬲，支聲。', '鬲;支', '支', 'null', NULL, '鬲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1856, '鬹', 'ɡuī', '第三卷', '鬲部', '三足鬴也。有柄喙。讀若嬀。从鬲，規聲。', '嬀;鬲;規', '嬀;規', 'null', NULL, '鬲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1857, '鬷', 'zōnɡ', '第三卷', '鬲部', '鬴屬。从鬲，㚇聲。', '鬲;㚇', '㚇', 'null', NULL, '鬲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1858, '𩰫;𩰬', 'ɡuō', '第三卷', '鬲部', '秦名土鬴曰𩰫。从鬲，㐄聲。讀若過。', '鬲;㐄;過', '㐄;過', 'null', NULL, '鬲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1859, '鬵', 'qín', '第三卷', '鬲部', '大鬴也。一曰鼎大上小下若甑曰鬵。从鬲，兓聲。讀若岑。籒文鬵[䰜]。', '鬲;兓;岑;䰜', '兓;岑', 'null', NULL, '鬲;䰜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1860, '䰝', 'zènɡ', '第三卷', '鬲部', '鬵屬。从鬲，曾聲。', '鬲;曾', '曾', 'null', NULL, '鬲', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1861, '鬴;釜;𨥏;釡', 'fǔ', '第三卷', '鬲部', '鍑屬也。从鬲，甫聲。鬴或从金，父聲。', '鬲;甫;金;父', '甫;父', 'null', NULL, '鬲;金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1862, '鬳', 'yàn', '第三卷', '鬲部', '鬲屬。从鬲，虍聲。', '鬲;虍', '虍', 'null', NULL, '鬲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1863, '融', 'rónɡ', '第三卷', '鬲部', '炊气上出也。从鬲，蟲省聲。籒文融不省。', '鬲;蟲', '蟲', 'null', NULL, '鬲', 'null', 'null', 'null', 'null', '冬部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1864, '𩱴', 'xiāo', '第三卷', '鬲部', '炊气皃。从鬲，嚻聲。', '鬲;嚻', '嚻', 'null', NULL, '鬲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1865, '𩰱', 'shānɡ', '第三卷', '鬲部', '䰞也。从鬲，羊聲。', '鬲;羊', '羊', 'null', NULL, '鬲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1866, '𩰾', 'fèi', '第三卷', '鬲部', '涫也。从鬲，沸聲。', '鬲;沸', '沸', 'null', NULL, '鬲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1867, '䰜', 'lì', '第三卷', '䰜部', '㽁也。古文亦鬲字。象孰飪五味气上出也。凡䰜之屬皆从䰜。', '鬲', '鬲', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1868, '𩱡;餰;䭈;𩜾;飦', 'zhān', '第三卷', '䰜部', '鬻也。从䰜，侃聲。𩱡或从食，衍聲。𩱡或从食，干聲。𩱡或从食，建聲。', '䰜;侃;食;衍;食;干;食;建', '侃;衍;干;建', 'null', NULL, '䰜;食;食;食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1869, '鬻', 'zhōu', '第三卷', '䰜部', '䭈也。从䰜米。', '䰜;米', '', 'null', NULL, '䰜;米', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1870, '𩱍', 'hú', '第三卷', '䰜部', '䭈也。从䰜，古聲。', '䰜;古', '古', 'null', NULL, '䰜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1871, '𩱧;羹;羮;𩱋', 'ɡēnɡ', '第三卷', '䰜部', '五味盉𩱧也。从䰜，从羔。詩曰「亦有和𩱧」。𩱧或省[鬲]。或从美䰜省。小篆从羔，从美。', '䰜;羔;鬲;美;䰜;羔;美', '', 'null', NULL, '䰜;羔;鬲;美;䰜;羔;美', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1872, '𩱫', 'sù', '第三卷', '䰜部', '鼎實，惟葦及蒲。陳畱謂䭈爲𩱫。从䰜，速聲。𩱫或从食束。', '䰜;速;食;束', '速;束', 'null', NULL, '䰜;食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1873, '𩱱;𩱙', 'yù', '第三卷', '䰜部', '鬻也。从䰜，毓聲。𩱱或省从米[𠫓]。', '䰜;毓;米;𠫓', '毓', 'null', NULL, '䰜;米;𠫓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1874, '𩱷;粖', 'miè', '第三卷', '䰜部', '涼州謂鬻爲𩱷。从䰜，𥽘聲。𩱷或省从末[米]。', '䰜;𥽘;末;米', '𥽘;末', 'null', NULL, '䰜;米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1875, '𩱓;餌', 'ěr', '第三卷', '䰜部', '粉餅也。从䰜，耳聲。𩱓或从食耳。', '䰜;耳;食', '耳', 'null', NULL, '䰜;食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1876, '𩱦', 'chǎo', '第三卷', '䰜部', '熬也。从䰜，芻聲。', '䰜;芻', '芻', 'null', NULL, '䰜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1877, '𩱲', 'yuè', '第三卷', '䰜部', '內肉及菜湯中薄出之。从䰜，翟聲。', '䰜;翟', '翟', 'null', NULL, '䰜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1878, '䰞;煮', 'zhǔ', '第三卷', '䰜部', '亯也。从䰜，者聲。䰞或从火。䰞或从水。', '䰜;者;火;水', '者', 'null', NULL, '䰜;火;水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1879, '𩱚', 'bó', '第三卷', '䰜部', '炊鬴𩰾溢也。从䰜，孛聲。', '䰜;孛', '孛', 'null', NULL, '䰜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1880, '爪', 'zhǎo', '第三卷', '爪部', '丮也。覆手曰爪。象形。凡爪之屬皆从爪。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1881, '孚;𤔍;𤓽', 'fú', '第三卷', '爪部', '卵卽孚也。从爪子。一曰信也。古文孚从𣎼。𣎼，古文保。保亦聲。', '爪;子;𣎼', '𣎼', 'null', NULL, '爪;子', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1882, '爲', 'wéi', '第三卷', '爪部', '母猴也。其爲禽好爪。下腹爲母猴形。王育曰「爪象形也」。古文爲。象㒳母猴相對形。', '爪', '', 'null', NULL, '爪', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1883, '𤓯', 'zhǎnɡ', '第三卷', '爪部', '亦丮也。从反爪。闕。', '爪', '', 'null', NULL, '爪', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1884, '丮', 'jí', '第三卷', '丮部', '持也。象手有所丮據也。凡丮之屬皆从丮。讀若𢧢。', '手;𢧢', '𢧢', 'null', NULL, '手', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1885, '埶', 'yì', '第三卷', '丮部', '穜也。从丮坴。丮持穜之。詩曰「我埶黍稷」。', '丮;坴', '', 'null', NULL, '丮;坴', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1886, '孰;熟', 'shú', '第三卷', '丮部', '食餁也。从丮𦎫。易曰「孰餁」。', '丮;𦎫', '', 'null', NULL, '丮;𦎫', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1887, '𩛥', 'zài', '第三卷', '丮部', '設餁也。从丮食，才聲。讀若載。', '丮;食;才;載', '才;載', 'null', NULL, '丮;食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1888, '巩', 'ɡǒnɡ', '第三卷', '丮部', '袌也。从丮，工聲。巩或加手。', '丮;工;手', '工', 'null', NULL, '丮;手', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1889, '谻', 'jué', '第三卷', '丮部', '相踦谻也。从丮，𧮫聲。', '丮;𧮫', '𧮫', 'null', NULL, '丮', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1890, '𢦚', 'huà', '第三卷', '丮部', '擊踝也。从丮戈。讀若踝。', '丮;戈;踝', '戈;踝', 'null', NULL, '丮', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1891, '𠨭', 'jú', '第三卷', '丮部', '亦持也。从反丮。闕。', '丮', '', 'null', NULL, '丮', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1892, '鬥', 'dòu', '第三卷', '鬥部', '㒳士相對，兵杖在後，象鬥之形。凡鬥之屬皆从鬥。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1893, '鬭;鬬', 'dòu', '第三卷', '鬥部', '遇也。从鬥，斲聲。', '鬥;斲', '斲', 'null', NULL, '鬥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1894, '鬨', 'hònɡ', '第三卷', '鬥部', '鬥也。从鬥，共聲。孟子曰「鄒與魯鬨」。', '鬥;共', '共', 'null', NULL, '鬥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1895, '䰘', 'liú', '第三卷', '鬥部', '經繆殺也。从鬥，翏聲。', '鬥;翏', '翏', 'null', NULL, '鬥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1896, '鬮', 'jiū', '第三卷', '鬥部', '鬥取也。从鬥，龜聲。讀若「三合繩糾」。', '鬥;龜;糾', '龜;糾', 'null', NULL, '鬥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1897, '𩰞', 'nǐ', '第三卷', '鬥部', '智少力劣也。从鬥，爾聲。', '鬥;爾', '爾', 'null', NULL, '鬥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1898, '𩰗;𩰝;𦆯', 'pīn', '第三卷', '鬥部', '𩰝𩰟，鬥連結𦆯紛相牽也。从鬥，賓省聲。讀若𦆯。', '鬥;賓;𦆯', '賓;𦆯', 'null', NULL, '鬥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1899, '𩰟', 'fēn', '第三卷', '鬥部', '𩰝𩰟也。从鬥，燹聲。讀若紛。', '鬥;燹;紛', '燹;紛', 'null', NULL, '鬥', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1900, '鬩', 'xì', '第三卷', '鬥部', '恆訟也。詩曰「兄弟鬩于牆」。从鬥兒。兒，善訟者也。', '鬥;兒', '兒', 'null', NULL, '鬥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1901, '𩰎', 'xuàn', '第三卷', '鬥部', '試力士錘也。从鬥，从戈。或从戰省。讀若縣。', '鬥;戈;戰;縣', '戰;縣', 'null', NULL, '鬥;戈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1902, '鬧', 'nào', '第三卷', '鬥部', '不靜也。从市鬥。', '市;鬥', '', 'null', NULL, '市;鬥', 'null', 'null', 'null', 'null', '新', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1903, '又', 'yòu', '第三卷', '又部', '手也。象形。三指者，手之列多略不過三也。凡又之屬皆从又。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1904, '右', 'yòu', '第三卷', '又部', '助也。从又口。', '又;口', '又', 'null', NULL, '口', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1905, '厷;厶;肱', 'ɡōnɡ', '第三卷', '又部', '臂上也。从又。从古文厷。古文厷。象形。左或从肉。', '又;肉', '', 'null', NULL, '又;肉', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1906, '叉', 'chā', '第三卷', '又部', '手指相錯也。从又一。象叉之形。', '又;一', '', 'null', NULL, '又;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1907, '㕚', 'zhǎo', '第三卷', '又部', '手足甲也。从又。象㕚形。', '又', '', 'null', NULL, '又', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1908, '父', 'fù', '第三卷', '又部', '巨也。家長率敎者。从又舉杖。', '又', '', 'null', NULL, '又', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1909, '叜;傁;叟', 'sǒu', '第三卷', '又部', '老也。从又灾。籒文从寸。叜或从人。', '又;灾;寸;人', '', 'null', NULL, '又;灾;寸;人', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1910, '燮', 'xiè', '第三卷', '又部', '和也。从言又，炎聲。讀若溼。籒文燮从𢆉。', '言;又;炎;溼;𢆉', '炎;溼;𢆉', 'null', NULL, '言;又', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1911, '曼;漫', 'màn', '第三卷', '又部', '引也。从又，冒聲。', '又;冒', '冒', 'null', NULL, '又', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1912, '𠭙', 'shēn', '第三卷', '又部', '伸也。从又，𢑚聲。𢑚，古文申。', '又;𢑚', '𢑚', 'null', NULL, '又', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1913, '夬', 'ɡuài', '第三卷', '又部', '分決也。从又。象決形。', '又', '', 'null', NULL, '又', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1914, '尹', 'yǐn', '第三卷', '又部', '治也。从又丿。握事者也。古文尹[廾，㣇省声]。', '又;丿;廾;㣇', '㣇', 'null', NULL, '又;丿;廾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1915, '𠭯', 'zhā', '第三卷', '又部', '叉卑也。从又，虘聲。', '又;虘', '虘', 'null', NULL, '又', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1916, '𠭰', 'lí', '第三卷', '又部', '引也。从又，𠩺聲。', '又;𠩺', '𠩺', 'null', NULL, '又', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1917, '㕞', 'shuā', '第三卷', '又部', '飾也。从又持巾在尸下。', '又;巾;尸', '', 'null', NULL, '又;巾;尸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1918, '及;乁;𢎜', 'jí', '第三卷', '又部', '逮也。从又人。古文及。秦刻石及如此。亦古文及。亦古文及[辵干隶][ 臿、疌]。', '又;人;辵', '', 'null', NULL, '又;人;辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1919, '秉', 'bǐnɡ', '第三卷', '又部', '禾束也。从又持禾。', '又;禾', '', 'null', NULL, '又;禾', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1920, '反', 'fǎn', '第三卷', '又部', '覆也。从又。厂，反形。[及]。古文。', '又;厂;及', '厂', 'null', NULL, '又;及', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1921, '𠬝', 'fú', '第三卷', '又部', '治也。从又卪。卪，事之節。', '又;卪', '', 'null', NULL, '又;卪', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1922, '𠬢', 'tāo', '第三卷', '又部', '滑也。詩云「𠬢[挑]兮𨔶兮」。从又屮。一曰取也。', '挑;又;屮', '挑', 'null', NULL, '又;屮', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1923, '𠭥', 'zhuì', '第三卷', '又部', '楚人謂卜問吉凶曰𠭥。从又持祟。讀若贅。', '又;祟;贅', '祟;贅', 'null', NULL, '又', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1924, '叔', 'shū', '第三卷', '又部', '拾也。从又，尗聲。汝南名收芋爲叔。叔或从寸。', '又;尗;寸', '尗', 'null', NULL, '又;寸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1925, '𠬛', 'mò', '第三卷', '又部', '入水有所取也。从又在囘下。囘，古文回。回，淵水也。讀若沬。', '又;囘;沬', '沬', 'null', NULL, '又;囘', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1926, '取', 'qǔ', '第三卷', '又部', '捕取也。从又耳。周禮「獲者取左耳」。司馬法曰「載獻聝」。聝者，耳也。', '又;耳', '', 'null', NULL, '又;耳', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1927, '彗;篲;𥱵', 'huì', '第三卷', '又部', '埽竹也。从又持甡。彗或从竹。古文彗从竹習。', '又;甡;竹;竹;習', '習', 'null', NULL, '又;甡;竹;竹', 'null', 'null', 'null', 'null', '月部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1928, '叚', 'jiǎ', '第三卷', '又部', '借也。闕。[又𡰥]古文叚[宀彳攴]。譚長說叚如此[耑省]。', '又;𡰥;宀;彳;攴;耑', '', 'null', NULL, '又;𡰥;宀;彳;攴;耑', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1929, '度', 'dù', '第三卷', '又部', '法制也。从又，庶省聲。', '又;庶', '庶', 'null', NULL, '又', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1930, '友', 'yǒu', '第三卷', '又部', '同志爲友。从二又相交。古文友[二寸]。亦古文友[羽酉]。', '又;寸;羽;酉', '酉', 'null', NULL, '又;寸;羽', 'null', 'null', 'null', 'null', '政宇调序 | 之部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1931, '𠂇', 'zuǒ', '第三卷', '𠂇部', '左手也。象形。凡𠂇之屬皆从𠂇。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1932, '卑', 'bēi', '第三卷', '𠂇部', '賤也。執事者。从𠂇甲。', '𠂇;甲', '', 'null', NULL, '𠂇;甲', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1933, '史', 'shǐ', '第三卷', '史部', '記事者也。从又持中。中，正也。凡史之屬皆从史。', '又;中', '', 'null', NULL, '又;中', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1934, '事', 'shì', '第三卷', '史部', '職也。从史，之省聲。古文事[之不省]。', '史;之', '之', 'null', NULL, '史', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1935, '支', 'zhī', '第三卷', '支部', '去竹之枝也。从又持半竹。凡支之屬皆从支。古文支。', '又;竹', '', 'null', NULL, '又;竹', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1936, '攲', 'jī', '第三卷', '支部', '持去也。从支，奇聲。', '支;奇', '奇', 'null', NULL, '支', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1937, '𦘒', 'niè', '第三卷', '𦘒部', '手之疌巧也。从又持巾。凡𦘒之屬皆从𦘒。', '又;巾', '', 'null', NULL, '又;巾', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1938, '𢑩;肄', 'yì', '第三卷', '𦘒部', '習也。从𦘒，㣇聲。籒文𢑩。篆文𢑩[矢隶]。', '𦘒;㣇;矢;隶', '㣇;矢', 'null', NULL, '𦘒;隶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1939, '肅', 'sù', '第三卷', '𦘒部', '持事振敬也。从𦘒在𣶒上。戰戰兢兢也。古文肅从心卪。', '𦘒;𣶒;心;卪', '', 'null', NULL, '𦘒;𣶒;心;卪', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1940, '聿', 'yù', '第三卷', '聿部', '所㠯書也。楚謂之聿，吳謂之不律，燕謂之弗。从𦘒一。凡聿之屬皆从聿。', '𦘒;一', '', 'null', NULL, '𦘒;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1941, '筆', 'bǐ', '第三卷', '聿部', '秦謂之筆。从聿竹。', '聿;竹', '聿', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1942, '𦘔', 'jīn', '第三卷', '聿部', '聿飾也。从聿，从彡。俗語㠯書好爲𦘔。讀若津也。', '聿;彡;津', '津', 'null', NULL, '聿;彡', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1943, '書', 'shū', '第三卷', '聿部', '箸也。从聿，者聲。', '聿;者', '者', 'null', NULL, '聿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1944, '畫', 'huà', '第三卷', '畫部', '介也。从聿。象田四介。聿所㠯畫之。凡畫之屬皆从畫。古文畫。亦古文畫[刀]。', '田;聿;刀', '', 'null', NULL, '田;聿;刀', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1945, '晝', 'zhòu', '第三卷', '畫部', '日之出入，與夜爲介。从畫省，从日。籒文晝。', '畫;日', '', 'null', NULL, '畫;日', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1946, '隶', 'dài', '第三卷', '隶部', '及也。从又，尾省。又，持尾者，从後及之也。凡隶之屬皆从隶。', '又;尾', '', 'null', NULL, '又;尾', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1947, '𨽿', 'dài', '第三卷', '隶部', '及也。从隶，枲聲。詩曰「𨽿天之未陰雨」。', '隶;枲', '枲', 'null', NULL, '隶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1948, '隸;隷', 'lì', '第三卷', '隶部', '附箸也。从隶，柰聲。篆文隸，从古文之體[米𠭥]。', '隶;柰;米;𠭥', '柰;𠭥', 'null', NULL, '隶;米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1949, '臤', 'qiān', '第三卷', '臤部', '堅也。从又，臣聲。凡臤之屬皆从臤。讀若「鏗鎗」。古文㠯爲賢字。', '又;臣;鏗;賢', '臣;鏗;賢', 'null', NULL, '又', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1950, '緊', 'jǐn', '第三卷', '臤部', '纏絲急也。从臤，絲省。', '臤;絲', '臤', 'null', NULL, '臤;絲', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1951, '堅', 'jiān', '第三卷', '臤部', '土剛也。从臤土。', '臤;土', '臤', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1952, '豎', 'shù', '第三卷', '臤部', '堅立也。从臤，豆聲。籒文豎从殳。', '臤;豆;殳', '豆', 'null', NULL, '臤;殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1953, '臣', 'chén', '第三卷', '臣部', '牽也。事君者。象屈服之形。凡臣之屬皆从臣。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1954, '臦', 'ɡuànɡ', '第三卷', '臣部', '乖也。从二臣相違。讀若誑。', '臣;誑', '誑', 'null', NULL, '臣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1955, '臧', 'zānɡ', '第三卷', '臣部', '善也。从臣，戕聲。籒文[上]。', '臣;戕;上', '戕', 'null', NULL, '臣;上', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1956, '𠘧', 'shū', '第三卷', '𠘧部', '鳥之短羽飛𠘧𠘧也。象形。凡𠘧之屬皆从𠘧。讀若殊。', '殊', '殊', 'null', NULL, '', 'null', 'null', 'null', 'null', '⚙️ | 政宇调部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1957, '𠘱', 'zhěn', '第三卷', '𠘧部', '新生羽而飛。从𠘧，从彡。', '𠘧;彡', '', 'null', NULL, '𠘧;彡', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1958, '鳧', 'fú', '第三卷', '𠘧部', '舒鳧，鶩也。从𠘧鳥。𠘧亦聲。', '𠘧;鳥', '𠘧', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1959, '殳', 'shū', '第三卷', '殳部', '㠯杖殊人也。周禮「殳㠯積竹。八觚。長丈二尺，建於兵車，旅賁㠯先驅」。从又，𠘧聲。凡殳之屬皆从殳。', '又;𠘧', '𠘧', 'null', NULL, '又', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1960, '祋', 'duì', '第三卷', '殳部', '殳也。从殳，示聲。或說，城郭市里，高懸羊皮，有不當入而欲入者，暫下㠯驚牛馬，曰祋。詩曰「何戈與祋」。', '殳;示', '示', 'null', NULL, '殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1961, '杸', 'shū', '第三卷', '殳部', '軍中士所持殳也。从木殳。司馬法曰「執羽从杸」。', '木;殳', '殳', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1962, '毄;𣪠', 'jī', '第三卷', '殳部', '相擊中也。如車相轚，故从殳軎也。', '殳;軎', '', 'null', NULL, '殳;軎', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1963, '㱿', 'què', '第三卷', '殳部', '从上擊下也。从殳，𡉉聲。一曰素也。', '殳;𡉉', '𡉉', 'null', NULL, '殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1964, '㱽', 'zhěn', '第三卷', '殳部', '下擊上也。从殳，冘聲。', '殳;冘', '冘', 'null', NULL, '殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1965, '𣪌', 'tóu', '第三卷', '殳部', '繇擊也。从殳，豆聲。古文投如此。', '殳;豆;投', '豆;投', 'null', NULL, '殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1966, '𣪐', 'chóu', '第三卷', '殳部', '縣物𣪐擊也。从殳，𠃬聲。', '殳;𠃬', '𠃬', 'null', NULL, '殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1967, '𧰵', 'dú', '第三卷', '殳部', '椎擊物也。从殳，豖聲。', '殳;豖', '豖', 'null', NULL, '殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1968, '毆', 'ōu', '第三卷', '殳部', '捶𣪠物也。从殳，區聲。', '殳;區', '區', 'null', NULL, '殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1969, '毃', 'qiāo', '第三卷', '殳部', '擊頭也。从殳，高聲。', '殳;高', '高', 'null', NULL, '殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1970, '殿', 'diàn', '第三卷', '殳部', '擊聲也。从殳，𡱂聲。', '殳;𡱂', '𡱂', 'null', NULL, '殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1971, '殹', 'yì', '第三卷', '殳部', '擊中聲也。从殳，医聲。', '殳;医', '医', 'null', NULL, '殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1972, '段', 'duàn', '第三卷', '殳部', '椎物也。从殳，耑省聲。', '殳;耑', '耑', 'null', NULL, '殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1973, '𣪯', 'tónɡ', '第三卷', '殳部', '擊空聲也。从殳，宮聲。', '殳;宮', '宮', 'null', NULL, '殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1974, '殽', 'xiáo', '第三卷', '殳部', '相襍錯也。从殳，肴聲。', '殳;肴', '肴', 'null', NULL, '殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1975, '毅', 'yì', '第三卷', '殳部', '妄怒也。一曰毅，有決也。从殳，𧱏聲。', '殳;𧱏', '𧱏', 'null', NULL, '殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1976, '𣪘;㲃', 'jiù', '第三卷', '殳部', '揉屈也。从殳𠧢。𠧢，古叀字。廏字从此。', '殳;叀;廏', '廏', 'null', NULL, '殳;叀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1977, '役', 'yì', '第三卷', '殳部', '戍也。从殳彳。古文役从人[余未详]。', '殳;彳;人', '', 'null', NULL, '殳;彳;人', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1978, '㱾', 'ɡāi', '第三卷', '殳部', '㱾攺，大剛卯也。㠯逐精鬽。从殳，亥聲。', '殳;亥', '亥', 'null', NULL, '殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1979, '殺;杀', 'shā', '第三卷', '殺部', '戮也。从殳，杀聲。凡殺之屬皆从殺。古文殺。古文殺[介八攴，余未详]。古文殺[手刀攴]。古文殺。籒文殺。', '殳;介;丯;八;攴;手;刀;攴', '', 'null', NULL, '殳;介;丯;八;攴;手;刀;攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1980, '弑', 'shì', '第三卷', '殺部', '臣殺君也。易曰「臣弑其君」。从殺省，式聲。', '殺;式', '式', 'null', NULL, '殺', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1981, '寸', 'cùn', '第三卷', '寸部', '十分也。人手卻一寸動𧖴謂之寸口。从又一。凡寸之屬皆从寸。', '又;一', '', 'null', NULL, '又;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (1982, '寺', 'sì', '第三卷', '寸部', '廷也。有法度者也。从寸，之聲。', '寸;之', '之', 'null', NULL, '寸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
