INSERT INTO `shuowen_voice_revel` VALUES (2048, '收', 'shōu', '第三卷', '攴部', '捕也。从攴，丩聲。', '攴;丩', '丩', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2049, '鼔', 'ɡǔ zhǔ', '第三卷', '攴部', '擊𡔷也。从攴壴。壴亦聲。讀若屬。', '攴;壴;屬', '壴;屬', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2050, '攷', 'kǎo', '第三卷', '攴部', '敂也。从攴，丂聲。', '攴;丂', '丂', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2051, '敂', 'kòu', '第三卷', '攴部', '擊也。从攴，句聲。讀若扣。', '攴;句;扣', '句;扣', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2052, '攻', 'ɡōnɡ', '第三卷', '攴部', '擊也。从攴，工聲。', '攴;工', '工', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2053, '敲;㪣', 'qiāo', '第三卷', '攴部', '橫擿也。从攴，高聲。', '攴;高', '高', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2054, '𢽴;𢽚', 'zhuó', '第三卷', '攴部', '擊也。从攴，豖聲。', '攴;豖', '豖', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2055, '𢼟', 'wǎnɡ', '第三卷', '攴部', '放也。从攴，㞷聲。', '攴;㞷', '㞷', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2056, '𠩺', 'xī', '第三卷', '攴部', '坼也。从攴，从厂。厂之性坼。果孰有味亦坼，故从未。', '攴;厂', '', 'null', NULL, '攴;厂', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2057, '斀;豖;𣀈', 'zhuó', '第三卷', '攴部', '去陰之㓝也。从攴，蜀聲。周書曰「刖劓𣀈黥」。古文从豕。一，椓其勢。', '攴;蜀;豕;一;椓', '蜀;椓', 'null', NULL, '攴;豕;一', 'null', 'null', 'null', 'null', '政宇补重文', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2058, '𢾞', 'mǐn', '第三卷', '攴部', '冒也。从攴，昏聲。周書曰「𢾞不畏死」。', '攴;昏', '昏', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2059, '敔', 'yǔ', '第三卷', '攴部', '禁也。一曰樂器椌楬也，形如木虎。从攴，吾聲。', '攴;吾', '吾', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2060, '㪙', 'kě', '第三卷', '攴部', '研治也。从攴，果聲。舜女弟名㪙首。', '攴;果', '果', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2061, '鈙', 'qín', '第三卷', '攴部', '持止也。从攴，金聲。讀若琴。', '攴;金;琴', '金;琴', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2062, '𢿟', 'chóu', '第三卷', '攴部', '棄也。从攴，𠷎聲。周書㠯爲討。詩曰「無我𢿟兮」。', '攴;𠷎;討', '𠷎;討', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2063, '畋', 'tián', '第三卷', '攴部', '平田也。从攴田。周書曰「畋尒田」。', '攴;田', '田', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2064, '攺', 'yǐ', '第三卷', '攴部', '㱾攺，大剛卯㠯逐鬼鬽也。从攴，巳聲。讀若巳。', '攴;巳', '巳', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2065, '敘', 'xù', '第三卷', '攴部', '次弟也。从攴，余聲。', '攴;余', '余', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2066, '㪏', 'bǐ', '第三卷', '攴部', '㪏㪒，毀也。从攴，卑聲。', '攴;卑', '卑', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2067, '㪒', 'ní', '第三卷', '攴部', '㪏㪒也。从攴，兒聲。', '攴;兒', '兒', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2068, '牧', 'mù', '第三卷', '攴部', '養牛人也。从攴牛。詩曰「牧人乃夢」。', '攴;牛', '', 'null', NULL, '攴;牛', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2069, '敇', 'cè', '第三卷', '攴部', '擊馬也。从攴，朿聲。', '攴;朿', '朿', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2070, '𣀔', 'chuàn', '第三卷', '攴部', '小舂也。从攴，算聲。', '攴;算', '算', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2071, '𢿲', 'qiāo', '第三卷', '攴部', '䚫田也。从攴，堯聲。', '攴;堯', '堯', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2072, '敎;教;𤕝', 'jiào', '第三卷', '敎部', '上所施，下所效也。从攴𡥈。凡敎之屬皆从敎。古文敎[言]。亦古文敎[爻]。', '攴;𡥈;言;爻', '𡥈;爻', 'null', NULL, '攴;言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2073, '斆;學', 'xué', '第三卷', '敎部', '覺也。从敎冂。冂，尙矇也。𦥑聲。篆文斆省[𡥈]。', '敎;冂;𦥑;𡥈;學', '𦥑;學', 'null', NULL, '敎;冂;𡥈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2074, '卜', 'bǔ', '第三卷', '卜部', '灼剝龜也。象灸龜之形。一曰象龜兆之縱衡也。凡卜之屬皆从卜。古文卜。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2075, '卦', 'ɡuà', '第三卷', '卜部', '所㠯簭也。从卜，圭聲。', '卜;圭', '圭', 'null', NULL, '卜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2076, '卟', 'jī', '第三卷', '卜部', '卜㠯問疑也。从口卜。讀與稽同。', '口;卜;稽', '稽', 'null', NULL, '口;卜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2077, '貞', 'zhēn', '第三卷', '卜部', '卜問也。从卜貝。貝，㠯爲𡠗。一曰鼎省聲。京房所說。', '卜;貝;鼎', '鼎', 'null', NULL, '卜;貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2078, '𠧩', 'huì', '第三卷', '卜部', '易卦之上體也。商書曰「曰貞曰𠧩」。从卜，每聲。', '卜;每', '每', 'null', NULL, '卜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2079, '占', 'zhān', '第三卷', '卜部', '視兆問也。从卜口。', '卜;口', '', 'null', NULL, '卜;口', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2080, '𠧙', 'shào', '第三卷', '卜部', '卜問也。从卜，召聲。', '卜;召', '召', 'null', NULL, '卜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2081, '𠧞;兆', 'zhào', '第三卷', '卜部', '灼龜坼也。从卜兆。象形。古文𠧞省。', '卜', '', 'null', NULL, '卜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2082, '用', 'yònɡ', '第三卷', '用部', '可施行也。从卜中，衛宏說。凡用之屬皆从用。古文用。', '卜;中', '', 'null', NULL, '卜;中', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2083, '甫', 'fǔ', '第三卷', '用部', '男子美偁也。从用父。父亦聲。', '用;父', '父', 'null', NULL, '用', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2084, '庸', 'yōnɡ', '第三卷', '用部', '用也。从用庚。庚，更事也。易曰「先庚三日」。', '用;庚', '', 'null', NULL, '用;庚', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2085, '𤰈', 'bèi', '第三卷', '用部', '具也。从用，茍省。', '用;茍', '茍', 'null', NULL, '用', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2086, '甯', 'nìnɡ', '第三卷', '用部', '所願也。从用，寧省聲。', '用;寧', '寧', 'null', NULL, '用', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2087, '爻', 'yáo', '第三卷', '爻部', '交也。象易六爻頭交也。凡爻之屬皆从爻。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2088, '棥', 'fán', '第三卷', '爻部', '藩也。从爻林。詩曰「營營靑蠅，止于棥」。', '爻;林', '', 'null', NULL, '爻;林', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2089, '㸚', 'lǐ', '第三卷', '㸚部', '二爻也。凡㸚之屬皆从㸚。', '爻', '', 'null', NULL, '爻', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2090, '爾', 'ěr', '第三卷', '㸚部', '麗爾，猶靡麗也。从冂㸚。㸚，其孔㸚㸚。从尒聲。此與爽同意。', '冂;㸚;尒', '尒', 'null', NULL, '冂;㸚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2091, '爽', 'shuǎnɡ', '第三卷', '㸚部', '明也。从㸚大。篆文爽。', '㸚;大', '', 'null', NULL, '㸚;大', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2092, '𡕥', 'xuè', '第四卷', '𡕥部', '舉目使人也。从攴目。凡𡕥之屬皆从𡕥。讀若𩖶。', '攴;目;𩖶', '𩖶', 'null', NULL, '攴;目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2093, '敻;夐', 'xuàn', '第四卷', '𡕥部', '營求也。从𡕥，人在穴。商書曰「高宗夢得說，使百工營求，得之傅巖」。巖，穴也。', '𡕥;人;穴', '𡕥', 'null', NULL, '人;穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2094, '䦩', 'wén', '第四卷', '𡕥部', '氐目視也。从𡕥，門聲。弘農 湖縣有䦩鄉。汝南 西平有䦩亭。', '𡕥;門', '門', 'null', NULL, '𡕥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2095, '𡙋', 'quán', '第四卷', '𡕥部', '大視也。从大𡕥。讀若齤。', '大;𡕥;齤', '齤', 'null', NULL, '大;𡕥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2096, '目', 'mù', '第四卷', '目部', '人眼也。象形。重童子也。凡目之屬皆从目。古文目。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2097, '眼', 'yǎn', '第四卷', '目部', '目也。从目，艮聲。', '目;艮', '艮', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2098, '𥌡', 'biǎn', '第四卷', '目部', '兒初生瞥者。从目，瞏聲。讀若「告之謂調[懽]」。', '目;瞏;懽', '瞏;懽', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2099, '眩', 'xuàn', '第四卷', '目部', '目無常主也。从目，玄聲。', '目;玄', '玄', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2100, '眥;眦', 'zì', '第四卷', '目部', '目匡也。从目，此聲。', '目;此', '此', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2101, '䀹', 'jié', '第四卷', '目部', '目旁毛也。从目，夾聲。', '目;夾', '夾', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2102, '𥌭', 'xuàn', '第四卷', '目部', '盧童子也。从目，縣聲。', '目;縣', '縣', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2103, '瞦', 'xī', '第四卷', '目部', '目童子精瞦也。从目，喜聲。讀若爾雅「禧福」。', '目;喜;禧', '喜;禧', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2104, '𥌗', 'mián', '第四卷', '目部', '目旁薄緻宀宀也。从目，𦤝聲。', '目;𦤝', '𦤝', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2105, '𥇖', 'fēi', '第四卷', '目部', '大目也。从目，非聲。', '目;非', '非', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2106, '䁂', 'xiàn', '第四卷', '目部', '大目也。从目，臤聲。', '目;臤', '臤', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2107, '睅;睆', 'hàn', '第四卷', '目部', '大目也。从目，旱聲。睅或从完。', '目;旱;完', '旱;完', 'null', NULL, '目', 'null', 'null', 'null', 'null', '大徐补 | 应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2108, '䁔', 'xuān', '第四卷', '目部', '大目也。从目，爰聲。', '目;爰', '爰', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2109, '瞞', 'mán', '第四卷', '目部', '平目也。从目，㒼聲。', '目;㒼', '㒼', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2110, '睴', 'ɡùn', '第四卷', '目部', '大目出也。从目，軍聲。', '目;軍', '軍', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2111, '矕', 'mǎn', '第四卷', '目部', '目矕矕也。从目，䜌聲。', '目;䜌', '䜌', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2112, '睔', 'ɡùn', '第四卷', '目部', '目大也。从目，侖聲。春秋傳有鄭伯 睔。', '目;侖', '侖', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2113, '盼', 'pàn', '第四卷', '目部', '[目]白黑分也。詩曰「美目盼兮」。从目，分聲。', '目;分', '分', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2114, '盰', 'ɡàn', '第四卷', '目部', '張目也。从目，干聲。', '目;干', '干', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2115, '眅', 'pān', '第四卷', '目部', '目多白也。从目，反聲。春秋傳曰「鄭 游眅字子朙」。', '目;反', '反', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2116, '睍', 'xiàn', '第四卷', '目部', '目出皃也。从目，見聲。', '目;見', '見', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2117, '矔', 'ɡuàn', '第四卷', '目部', '目多精也。从目，雚聲。益州謂瞋目曰矔。', '目;雚', '雚', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2118, '瞵', 'lín', '第四卷', '目部', '目精也。从目，粦聲。', '目;粦', '粦', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2119, '窅', 'yǎo', '第四卷', '目部', '深目皃。从穴中目。', '穴;目', '', 'null', NULL, '穴;目', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2120, '眊', 'mào', '第四卷', '目部', '目少精也。从目，毛聲。虞書耄[𦽡]字从此。', '目;毛;𦽡', '毛;𦽡', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2121, '矘', 'tǎnɡ', '第四卷', '目部', '目無精直視也。从目，黨聲。', '目;黨', '黨', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2122, '睒', 'shǎn', '第四卷', '目部', '暫視皃。从目，炎聲。讀若「白葢謂之苫」相似。', '目;炎;苫', '炎;苫', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2123, '眮', 'dònɡ', '第四卷', '目部', '吳、楚謂瞋目顧視曰眮。从目，同聲。', '目;同', '同', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2124, '䀣', 'bì', '第四卷', '目部', '直視也。从目，必聲。讀若詩云「泌彼泉水」。', '目;必;泌', '必;泌', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2125, '瞴', 'móu', '第四卷', '目部', '瞴婁，微視也。从目，無聲。', '目;無', '無', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2126, '䀘;𥅝', 'xié', '第四卷', '目部', '蔽人視也。从目，开聲。讀若「攜手」。一曰直視也。䀘，目或在下。', '目;开;攜', '开;攜', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2127, '睌', 'mǎn', '第四卷', '目部', '睌䁂，目視皃。从目，免聲。', '目;免', '免', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2128, '眂', 'shì', '第四卷', '目部', '視皃也。从目，氏聲。', '目;氏', '氏', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2129, '睨', 'nì', '第四卷', '目部', '衺視也。从目，兒聲。', '目;兒', '兒', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2130, '𥈆', 'mào', '第四卷', '目部', '氐目視也。从目，冒聲。周書曰「武王惟𥈆」。', '目;冒', '冒', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2131, '眓', 'huò', '第四卷', '目部', '視高皃。从目，戉聲。讀若詩曰「施罟濊濊」。', '目;戉;濊', '戉;濊', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2132, '眈', 'dān', '第四卷', '目部', '視近而志遠。从目，冘聲。易曰「虎視眈眈」。', '目;冘', '冘', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2133, '𢌨;𥆊', 'yàn', '第四卷', '目部', '相顧視而行也。从目，从㢟。㢟亦聲。', '目;㢟', '㢟', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2134, '盱', 'xū', '第四卷', '目部', '張目也。从目，于聲。一曰朝鮮謂盧童子曰盱。', '目;于', '于', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2135, '瞏;睘', 'qiónɡ', '第四卷', '目部', '目驚視也。从目，袁聲。詩曰「獨行瞏瞏」。', '目;袁', '袁', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2136, '䁴', 'zhǎn', '第四卷', '目部', '視而不止也。从目，亶聲。', '目;亶', '亶', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2137, '䀛', 'mèi', '第四卷', '目部', '目冥遠視也。从目，勿聲。一曰久視也。一曰旦朙也。', '目;勿', '勿', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2138, '眕', 'zhěn', '第四卷', '目部', '目有所恨而止也。从目，㐱聲。', '目;㐱', '㐱', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2139, '瞟', 'piǎo', '第四卷', '目部', '𥉻也。从目，票聲。', '目;票', '票', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2140, '𥉻', 'qì', '第四卷', '目部', '察也。从目，祭聲。', '目;祭', '祭', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2141, '睹;覩', 'dǔ', '第四卷', '目部', '見也。从目，者聲。古文从見。', '目;者;見', '者', 'null', NULL, '目;見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2142, '眔', 'dà', '第四卷', '目部', '目相及也。从目，隶省。讀若與隶同也。', '目;隶', '隶', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2143, '睽', 'kuí', '第四卷', '目部', '目不相聽也。从目，癸聲。', '目;癸', '癸', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2144, '眜', 'mò', '第四卷', '目部', '目不朙也。从目，末聲。', '目;末', '末', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2145, '𥈼', 'pán', '第四卷', '目部', '轉目視也。从目，般聲。', '目;般', '般', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2146, '𥌊', 'pàn', '第四卷', '目部', '小兒白眼視也。从目，辡聲。', '目;辡', '辡', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2147, '眽', 'mò', '第四卷', '目部', '目財視也。从目，𠂢聲。', '目;𠂢', '𠂢', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2148, '𥉈', 'tì', '第四卷', '目部', '失意視也。从目，條聲。', '目;條', '條', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2149, '𥇜', 'zhùn', '第四卷', '目部', '謹鈍目也。从目，𦎫聲。', '目;𦎫', '𦎫', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2150, '瞤', 'rún', '第四卷', '目部', '目動也。从目，閏聲。', '目;閏', '閏', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2151, '矉', 'pín', '第四卷', '目部', '恨張目也。从目，賓聲。詩曰「國步斯矉[頻]」。', '目;賓;頻', '賓;頻', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2152, '眢', 'yuān', '第四卷', '目部', '目無朙也。从目，夗聲。讀若委。', '目;夗;委', '夗;委', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2153, '睢', 'huī', '第四卷', '目部', '仰目也。从目，隹聲。', '目;隹', '隹', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2154, '䀏;眴', 'xuàn', '第四卷', '目部', '目搖也。从目，匀省聲。䀏或从目旬。', '目;匀;旬', '匀;旬', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2155, '矆', 'huò', '第四卷', '目部', '大視皃。从目，蒦聲。', '目;蒦', '蒦', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2156, '睦;𡍬', 'mù', '第四卷', '目部', '目順也。从目，坴聲。一曰敬和也。古文睦[𡴆]。', '目;坴;𡴆', '坴;𡴆', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2157, '瞻', 'zhān', '第四卷', '目部', '臨視也。从目，詹聲。', '目;詹', '詹', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2158, '瞀', 'mào', '第四卷', '目部', '氐目謹視也。从目，敄聲。', '目;敄', '敄', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2159, '䁲', 'mái', '第四卷', '目部', '小視也。从目，買聲。', '目;買', '買', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2160, '𥌈', 'jiān', '第四卷', '目部', '視也。从目，監聲。', '目;監', '監', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2161, '䁈', 'qì', '第四卷', '目部', '省視也。从目，啟省聲。', '目;啟', '啟', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2162, '相', 'xiānɡ', '第四卷', '目部', '省視也。从目木。易曰「地可觀者，莫可觀於木」。詩曰「相鼠有皮」。', '目;木', '', 'null', NULL, '目;木', 'null', 'null', 'null', 'null', '△', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2163, '瞋', 'chēn', '第四卷', '目部', '張目也。从目，眞聲。祕書瞋从戌。', '目;眞;戌', '眞;戌', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2164, '瞗', 'diāo', '第四卷', '目部', '目孰視也。从目，鳥聲。讀若雕。', '目;鳥;雕', '鳥;雕', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2165, '睗', 'shì', '第四卷', '目部', '目疾視也。从目，易聲。', '目;易', '易', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2166, '睊', 'juàn', '第四卷', '目部', '視皃也。从目，肙聲。', '目;肙', '肙', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2167, '𥈾', 'yuè', '第四卷', '目部', '目𥥍皃也。从目窅。讀若易曰「勿卹」之卹。', '目;窅;卹', '卹', 'null', NULL, '目;窅', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2168, '睼', 'tiàn', '第四卷', '目部', '迎視也。从目，是聲。讀若「珥瑱」之瑱。', '目;是;瑱', '是;瑱', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2169, '䁙', 'yǎn', '第四卷', '目部', '目相戲也。从目，晏聲。詩曰「䁙[燕]婉之求」。', '目;晏;燕', '晏;燕', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2170, '䁊', 'wò', '第四卷', '目部', '短𥥍目皃也。从目，𥄗聲。', '目;𥄗', '𥄗', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2171, '眷', 'juàn', '第四卷', '目部', '顧也。从目，𢍏聲。詩曰「乃眷西顧」。', '目;𢍏', '𢍏', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2172, '督', 'dū', '第四卷', '目部', '察視也。从目，叔聲。一曰目痛也。', '目;叔', '叔', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2173, '看', 'kān', '第四卷', '目部', '睎也。从手下目。看或从倝。', '手;目;倝', '倝', 'null', NULL, '手;目', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2174, '睎', 'xī', '第四卷', '目部', '望也。从目，𢁫聲。海岱之閒謂眄曰睎。', '目;𢁫', '𢁫', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2175, '瞫', 'shěn', '第四卷', '目部', '深視也。一曰下視也。又竊見也。从目，覃聲。', '目;覃', '覃', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2176, '眝', 'zhù', '第四卷', '目部', '長眙也。从目，宁聲。一曰張眼。', '目;宁', '宁', 'null', NULL, '目', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2177, '眙', 'chì', '第四卷', '目部', '直視也。从目，台聲。', '目;台', '台', 'null', NULL, '目', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2178, '睇', 'dì', '第四卷', '目部', '小衺視也。从目，弟聲。南楚謂「眄睇」。', '目;弟', '弟', 'null', NULL, '目', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2179, '䀩', 'luò', '第四卷', '目部', '眄也。从目，各聲。', '目;各', '各', 'null', NULL, '目', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2180, '盻', 'xì', '第四卷', '目部', '恨視也。从目，兮聲。', '目;兮', '兮', 'null', NULL, '目', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2181, '睡', 'shuì', '第四卷', '目部', '坐寐也。从目垂。', '目;垂', '垂', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2182, '瞑;眠', 'mínɡ', '第四卷', '目部', '翕目也。从目冥。', '目;冥', '冥', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2183, '眚', 'shěnɡ', '第四卷', '目部', '目病生翳也。从目，生聲。', '目;生', '生', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2184, '瞥', 'piē', '第四卷', '目部', '過目也。又目翳也。从目，敝聲。一曰財見也。', '目;敝', '敝', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2185, '眵', 'chī', '第四卷', '目部', '目傷眥也。从目，多聲。一曰𥋚兜也。', '目;多', '多', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2186, '𥋚', 'miè', '第四卷', '目部', '𥋚兜，目眵也。从目，蔑省聲。', '目;蔑', '蔑', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2187, '䀗', 'jué', '第四卷', '目部', '睊也。从目，夬聲。', '目;夬', '夬', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2188, '䀶', 'liànɡ', '第四卷', '目部', '目病也。从目，良聲。', '目;良', '良', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2189, '眛', 'mèi', '第四卷', '目部', '目不朙也。从目，未聲。', '目;未', '未', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2190, '瞯;瞷', 'xián', '第四卷', '目部', '戴目也。从目，閒聲。江淮之閒謂眄曰瞯。', '目;閒', '閒', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2191, '眯', 'mǐ', '第四卷', '目部', '艸入目中也。从目，米聲。', '目;米', '米', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2192, '眺', 'tiào', '第四卷', '目部', '目不正也。从目，兆聲。', '目;兆', '兆', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2193, '睞', 'lài', '第四卷', '目部', '目童子不正也。从目，來聲。', '目;來', '來', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2194, '睩', 'lù', '第四卷', '目部', '目睞謹也。从目，录聲。讀若鹿。', '目;录;鹿', '录;鹿', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2195, '䀺;𠌪', 'chōu', '第四卷', '目部', '眣也。从目，攸聲。𠌪或从丩。', '目;攸;丩', '攸;丩', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2196, '眣', 'dié', '第四卷', '目部', '目不從正也。从目，失聲。', '目;失', '失', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2197, '矇', 'ménɡ', '第四卷', '目部', '童蒙也。从目，蒙聲。一曰不明也。', '目;蒙', '蒙', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2198, '眇', 'miǎo', '第四卷', '目部', '小目也。从目少。', '目;少', '少', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2199, '眄', 'miǎn', '第四卷', '目部', '目徧合也。从目，丏聲。一曰衺視也。秦語。', '目;丏', '丏', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2200, '盲', 'mánɡ', '第四卷', '目部', '目無牟子也。从目，亡聲。', '目;亡', '亡', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2201, '䁍', 'qià', '第四卷', '目部', '目陷也。从目，咸聲。', '目;咸', '咸', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2202, '瞽', 'ɡǔ', '第四卷', '目部', '目但有朕也。从目，𡔷聲。', '目;𡔷', '𡔷', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2203, '𥈟;瞍', 'sǒu', '第四卷', '目部', '無目也。从目，叜聲。', '目;叜', '叜', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2204, '䁝', 'yínɡ', '第四卷', '目部', '䁝惑也。从目，熒省聲。', '目;熒', '熒', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2205, '睉', 'cuó', '第四卷', '目部', '小目也。从目，𡋲聲。', '目;𡋲', '𡋲', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2206, '𥄗', 'wò', '第四卷', '目部', '捾目也。从目㕚。', '目;㕚', '', 'null', NULL, '目;㕚', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2207, '瞚', 'shùn', '第四卷', '目部', '開𨵵目數搖也。从目，寅聲。', '目;寅', '寅', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2208, '𥄱', 'fèi', '第四卷', '目部', '目不朙也。从目，弗聲。', '目;弗', '弗', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2209, '瞼', 'jiǎn', '第四卷', '目部', '目上下瞼也。从目，僉聲。', '目;僉', '僉', 'null', NULL, '目', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2210, '眨', 'zhǎ', '第四卷', '目部', '動目也。从目，乏聲。', '目;乏', '乏', 'null', NULL, '目', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2211, '眭', 'huī', '第四卷', '目部', '深目也。从目，圭聲。', '目;圭', '圭', 'null', NULL, '目', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2212, '眹', 'zhèn', '第四卷', '目部', '目精也。从目，灷聲。', '目;灷', '灷', 'null', NULL, '目', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2213, '眸', 'móu', '第四卷', '目部', '目童子也。从目，牟聲。', '目;牟', '牟', 'null', NULL, '目', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2214, '睚', 'yá', '第四卷', '目部', '目際也。从目厓。', '目;厓', '厓', 'null', NULL, '目', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2215, '䀠', 'jù', '第四卷', '䀠部', '𠂇又視也。从二目。凡䀠之屬皆从䀠。讀若拘。又若良士瞿瞿。', '目;拘;瞿', '拘;瞿', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2216, '𥆞', 'juàn', '第四卷', '䀠部', '目圍也。从䀠𠂆。讀若「書卷」之卷。古文㠯爲靦字。', '䀠;𠂆;卷;靦', '卷;靦', 'null', NULL, '䀠;𠂆', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2217, '𥇛;𥈀', 'jū', '第四卷', '䀠部', '目衺也。从䀠，从大。大，人也。', '䀠;大', '', 'null', NULL, '䀠;大', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2218, '眉', 'méǐ', '第四卷', '眉部', '目上毛也。从目。象眉之形。上象頟理也。凡眉之屬皆从眉。', '目', '', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2219, '省', 'xǐnɡ', '第四卷', '眉部', '視也。从眉省，从屮。古文省从少目。', '眉;屮;少;目', '', 'null', NULL, '眉;屮;少;目', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2220, '盾', 'dùn', '第四卷', '盾部', '瞂也。所㠯扞身蔽目。从目。象形。凡盾之屬皆从盾。', '目', '', 'null', NULL, '目', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2221, '瞂', 'fá', '第四卷', '盾部', '盾也。从盾，犮聲。', '盾;犮', '犮', 'null', NULL, '盾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2222, '𡐠', 'kuī', '第四卷', '盾部', '盾握也。从盾，圭聲。', '盾;圭', '圭', 'null', NULL, '盾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2223, '自', 'zì', '第四卷', '自部', '鼻也。象鼻形。凡自之屬皆从自。古文自。', '鼻', '鼻', 'null', '皇下曰：自读若鼻。', '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2224, '𦤝', 'mián', '第四卷', '自部', '宀宀不見也。闕。', '自', '', 'null', NULL, '自', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2225, '𪞶', 'zì', '第四卷', '𪞶部', '此亦自字也。省自者，䛐言之气，从鼻出，與口相助。凡𪞶之屬皆从𪞶。', '自;鼻', '自;鼻', 'null', NULL, '自', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2226, '皆', 'jiē', '第四卷', '𪞶部', '俱䛐也。从比，从𪞶。', '比;𪞶', '', 'null', NULL, '比;𪞶', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2227, '魯', 'lǔ', '第四卷', '𪞶部', '鈍䛐也。从𪞶，魚聲。論語曰「參也魯」。', '𪞶;魚', '魚', 'null', NULL, '𪞶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2228, '者', 'zhě', '第四卷', '𪞶部', '別事䛐也。从𪞶，𣥐聲。𣥐，古文旅。', '𪞶;𣥐', '𣥐', 'null', NULL, '𪞶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2229, '𤾊', 'chóu', '第四卷', '𪞶部', '䛐也。从𪞶，𢏚聲。𢏚與𠷎同。虞書曰「帝曰𤾊咨」。', '𪞶;𠷎', '𠷎', 'null', NULL, '𪞶', 'null', 'null', 'null', 'null', '经 | 隶古定', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2230, '𥏼;智;𣉻', 'zhì', '第四卷', '𪞶部', '識䛐也。从𪞶亏知。古文𥏼[𦣞]。', '𪞶;亏;知;𦣞', '知', 'null', NULL, '𪞶;亏;𦣞', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2231, '百', 'bǎi', '第四卷', '𪞶部', '十十也。从一𪞶。數。十十爲一百。百，白也。十百爲一貫。貫，章也。古文百。', '一;𪞶', '', 'null', NULL, '一;𪞶', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2232, '鼻', 'bí', '第四卷', '鼻部', '所㠯引气自畀也。从自畀。凡鼻之屬皆从鼻。', '自;畀', '畀', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2233, '齅', 'xiù', '第四卷', '鼻部', '㠯鼻就臭也。从鼻臭。臭亦聲。讀若「嘼牲」之嘼。', '鼻;臭;嘼', '臭;嘼', 'null', NULL, '鼻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2234, '鼾', 'hān', '第四卷', '鼻部', '臥息也。从鼻，干聲。讀若汗。', '鼻;干;汗', '干;汗', 'null', NULL, '鼻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2235, '鼽', 'qiú', '第四卷', '鼻部', '病寒鼻窒也。从鼻，九聲。', '鼻;九', '九', 'null', NULL, '鼻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2236, '齂', 'xiè', '第四卷', '鼻部', '臥息也。从鼻，隶聲。讀若虺。', '鼻;隶;虺', '隶;虺', 'null', NULL, '鼻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2237, '皕', 'bì', '第四卷', '皕部', '二百也。凡皕之屬皆从皕。讀若逼。', '百;逼', '逼', 'null', NULL, '百', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2238, '奭', 'shì', '第四卷', '皕部', '盛也。从大，从皕。皕亦聲。此燕召公名，讀若郝，史篇名醜。古文奭。', '大;皕;郝', '皕;郝', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2239, '習', 'xí', '第四卷', '習部', '數飛也。从羽，𪞶聲。凡習之屬皆从習。', '羽;𪞶', '𪞶', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '辑部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2240, '翫', 'wàn', '第四卷', '習部', '習猒也。从習，元聲。春秋傳曰「翫歲而愒日」。', '習;元', '元', 'null', NULL, '習', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2241, '羽', 'yǔ', '第四卷', '羽部', '鳥長毛也。象形。凡羽之屬皆从羽。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2242, '翨', 'chì', '第四卷', '羽部', '鳥之彊羽猛者。从羽，是聲。', '羽;是', '是', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2243, '翰', 'hàn', '第四卷', '羽部', '天雞也。赤羽。从羽，倝聲。逸周書曰「文翰若翬雉，一名鷐風。周成王時蜀人獻之」。', '羽;倝', '倝', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2244, '翟', 'dí', '第四卷', '羽部', '山雉也。尾長。从羽，从隹。', '羽;隹', '', 'null', NULL, '羽;隹', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2245, '翡', 'fěi', '第四卷', '羽部', '赤羽雀也。出鬱林。从羽，非聲。', '羽;非', '非', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2246, '翠', 'cuì', '第四卷', '羽部', '靑羽雀也。出鬱林。从羽，卒聲。', '羽;卒', '卒', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2247, '翦', 'jiǎn', '第四卷', '羽部', '羽生也。一曰𠂕羽。从羽，歬聲。', '羽;歬', '歬', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2248, '翁', 'wēnɡ', '第四卷', '羽部', '頸毛也。从羽，公聲。', '羽;公', '公', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2249, '翄;翅;𦐊', 'chì', '第四卷', '羽部', '翼也。从羽，支聲。翄或从羽氏。', '羽;支;氏', '支;氏', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2250, '𦑜', 'ɡé', '第四卷', '羽部', '翅也。从羽，革聲。', '羽;革', '革', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2251, '翹', 'qiáo', '第四卷', '羽部', '尾長毛也。从羽，堯聲。', '羽;堯', '堯', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2252, '翭;𦑚', 'hóu', '第四卷', '羽部', '羽本也。从羽，矦聲。', '羽;矦', '矦', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2253, '翮', 'hé', '第四卷', '羽部', '羽莖也。从羽，鬲聲。', '羽;鬲', '鬲', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2254, '𦐛', 'qú', '第四卷', '羽部', '羽曲也。从羽，句聲。', '羽;句', '句', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2255, '𦐧;羿', 'yì', '第四卷', '羽部', '羽之𦐧風。亦古諸矦也。一曰射師。从羽开。', '羽;开', '开', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2256, '翥', 'zhù', '第四卷', '羽部', '飛舉也。从羽，者聲。', '羽;者', '者', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2257, '翕', 'xī', '第四卷', '羽部', '起也。从羽，合聲。', '羽;合', '合', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2258, '翾', 'xuān', '第四卷', '羽部', '小飛也。从羽，瞏聲。', '羽;瞏', '瞏', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2259, '翬;𦑩', 'huī', '第四卷', '羽部', '大飛也。从羽，軍聲。一曰伊、雒而南，雉五采皆備曰翬。詩曰「有翬斯飛」。', '羽;軍', '軍', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2260, '翏', 'liù', '第四卷', '羽部', '高飛也。从羽㐱。', '羽;㐱', '', 'null', NULL, '羽;㐱', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2261, '翩', 'piān', '第四卷', '羽部', '疾飛也。从羽，扁聲。', '羽;扁', '扁', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2262, '翜', 'shà', '第四卷', '羽部', '捷也。飛之疾也。从羽，夾聲。讀若歰、濇。一曰俠也。', '羽;夾;歰;濇', '夾;歰;濇', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2263, '翊;翋', 'yì', '第四卷', '羽部', '飛皃。从羽，立聲。', '羽;立', '立', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2264, '𦐇', 'tà', '第四卷', '羽部', '飛盛皃。从羽冃。', '羽;冃', '', 'null', NULL, '羽;冃', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2265, '𦐉', 'chī', '第四卷', '羽部', '羽盛皃也。从之，羽聲。', '之;羽', '羽', 'null', NULL, '之', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2266, '翱;翺', 'áo', '第四卷', '羽部', '翱，翔也。从羽，皋聲。', '羽;皋', '皋', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2267, '翔', 'xiánɡ', '第四卷', '羽部', '回飛也。从羽，羊聲。', '羽;羊', '羊', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2268, '翽', 'huì', '第四卷', '羽部', '羽聲也。从羽，歲聲。詩曰「鳳皇于飛，翽翽其羽」。', '羽;歲', '歲', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2269, '翯', 'xué', '第四卷', '羽部', '鳥白肥澤皃。从羽，高聲。詩曰「白鳥翯翯[鶴]」。', '羽;高;鶴', '高;鶴', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2270, '䍿', 'huánɡ', '第四卷', '羽部', '樂舞。㠯羽翿自翳其首，㠯祀星辰也。从羽，王聲。讀若皇。', '羽;王;皇', '王;皇', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '经', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2271, '翇', 'fú', '第四卷', '羽部', '樂舞。執全羽㠯祀社稷也。从羽，犮聲。讀若紱。', '羽;犮;紱', '犮;紱', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2272, '翿;𦒛', 'dào', '第四卷', '羽部', '翳也。所㠯舞也。从羽，𣪐聲。詩曰「左執翿」。', '羽;𣪐', '𣪐', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2273, '翳', 'yì', '第四卷', '羽部', '華葢也。从羽，殹聲。', '羽;殹', '殹', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2274, '翣', 'shà', '第四卷', '羽部', '棺羽飾也。天子八。諸矦六。大夫四。士二。下𠂹，从羽。妾聲。', '羽;妾', '妾', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2275, '翻;飜', 'fān', '第四卷', '羽部', '飛也。从羽，番聲。[飜]或从飛。', '羽;番;飛', '番', 'null', NULL, '羽;飛', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2276, '翎', 'línɡ', '第四卷', '羽部', '羽也。从羽，令聲。', '羽;令', '令', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2277, '𦏺', 'hónɡ', '第四卷', '羽部', '飛聲。从羽，工聲。', '羽;工', '工', 'null', NULL, '羽', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2278, '隹', 'zhuī', '第四卷', '隹部', '鳥之短尾總名也。象形。凡隹之屬皆从隹。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2279, '雅', 'yǎ', '第四卷', '隹部', '楚烏也。一名鸒。一名卑居。秦謂之雅。从隹，牙聲。', '隹;牙', '牙', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2280, '隻', 'zhī', '第四卷', '隹部', '鳥一枚也。从又持隹。持一隹曰隻，持二隹曰雙。', '又;隹', '', 'null', NULL, '又;隹', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2281, '雒', 'luò', '第四卷', '隹部', '忌欺也。从隹，各聲。', '隹;各', '各', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2282, '閵', 'lìn', '第四卷', '隹部', '今閵，佀鴝鵒而黃。从隹，𨳌省聲。籒文不省。', '隹;𨳌', '𨳌', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2283, '巂;嶲;雟', 'ɡuī', '第四卷', '隹部', '巂周，燕也。从隹。屮，象其冠也。㕯聲。一曰蜀王 望帝婬其相妻，慙亡去，爲子巂鳥，故蜀人聞子巂鳴，皆起曰「是望帝也」。', '隹;屮;㕯', '㕯', 'null', NULL, '隹;屮', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2284, '𨾔', 'fānɡ', '第四卷', '隹部', '𨾔鳥也。从隹，方聲。讀若方。', '隹;方', '方', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2285, '雀', 'què', '第四卷', '隹部', '依人小鳥也。从小隹。讀與爵同。', '小;隹;爵', '爵', 'null', NULL, '小;隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2286, '猚', 'yá', '第四卷', '隹部', '猚鳥也。从隹，犬聲。睢陽有猚水。', '隹;犬', '犬', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2287, '雗', 'hàn', '第四卷', '隹部', '雗鷽也。从隹，倝聲。', '隹;倝', '倝', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2288, '雉', 'zhì', '第四卷', '隹部', '有十四穜。盧諸雉。鷮雉。卜雉。鷩雉。秩秩海雉。翟山雉。雗雉。卓雉。伊雒而南曰翬。江淮而南曰搖。南方曰𢏚[𠷎]。東方曰甾。北方曰稀。西方曰蹲。从隹，矢聲。古文雉从弟。', '隹;矢;弟', '矢;弟', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2289, '雊', 'ɡòu', '第四卷', '隹部', '雄雉鳴也。雷始動。雉乃鳴而句其頸。从隹句。句亦聲。', '隹;句', '句', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2290, '雞;鷄', 'jī', '第四卷', '隹部', '知時畜也。从隹，奚聲。籒文雞从鳥。', '隹;奚;鳥', '奚', 'null', NULL, '隹;鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2291, '雛', 'chú', '第四卷', '隹部', '雞子也。从隹，芻聲。籒文雛从鳥。', '隹;芻;鳥', '芻', 'null', NULL, '隹;鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2292, '雡;𩁙', 'liù', '第四卷', '隹部', '鳥大雛也。从隹，翏聲。一曰雉之莫子爲雡。', '隹;翏', '翏', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2293, '離', 'lí', '第四卷', '隹部', '離黃，倉庚也。鳴則蠶生。从隹，离聲。', '隹;离', '离', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '歌部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2294, '雕', 'diāo', '第四卷', '隹部', '𪆃也。从隹，周聲。籒文雕从鳥。', '隹;周;鳥', '周', 'null', NULL, '隹;鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2295, '䧹;鷹', 'yīnɡ', '第四卷', '隹部', '䧹鳥也。从隹，从人，瘖省聲。籒文䧹从鳥。', '隹;人;瘖;鳥', '瘖', 'null', NULL, '隹;人;鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2296, '𨾦;鴟', 'chī', '第四卷', '隹部', '𨿠也。从隹，氐聲。籒文鴟从鳥。', '隹;氐;鳥', '氐', 'null', NULL, '隹;鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2297, '𨿠', 'shuì', '第四卷', '隹部', '鴟也。从隹，垂聲。', '隹;垂', '垂', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2298, '雃', 'qiān', '第四卷', '隹部', '石鳥。一名雝渠。一曰精列。从隹，开聲。春秋傳秦有士鳽。', '隹;开', '开', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2299, '雝;雍', 'yōnɡ', '第四卷', '隹部', '雝渠也。从隹，邕聲。', '隹;邕', '邕', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2300, '雂', 'qián', '第四卷', '隹部', '雂鳥也。从隹，今聲。春秋傳有公子若雂。', '隹;今', '今', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2301, '雁', 'yàn', '第四卷', '隹部', '雁鳥也。从隹，从人，厂聲。', '隹;人;厂', '厂', 'null', NULL, '隹;人', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2302, '𩁟', 'lí', '第四卷', '隹部', '𩁟黃也。从隹，黎聲。一曰楚雀也，其色黎黑而黃。', '隹;黎', '黎', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2303, '雐', 'hū', '第四卷', '隹部', '雐鳥也。从隹，虍聲。', '隹;虍', '虍', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2304, '𨾯;鴑', 'rú', '第四卷', '隹部', '牟毋也。从隹，奴聲。𨾯或从鳥。', '隹;奴;鳥', '奴', 'null', NULL, '隹;鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2305, '雇', 'hù', '第四卷', '隹部', '九雇——農桑𠊱鳥，扈民不婬者也。从隹，戸聲。春雇，鳻盾；夏雇竊玄；秋雇竊藍；冬雇竊黃；棘雇竊丹；行雇唶唶；宵雇嘖嘖；桑雇竊脂；老雇，鴳也。雇或从雩。籒文雇从鳥。', '隹;戸;雩;鳥', '戸;雩', 'null', NULL, '隹;鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2306, '𨿡;鶉', 'chún', '第四卷', '隹部', '䨄屬也。从隹，𦎫聲。', '隹;𦎫', '𦎫', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2307, '䨄', 'ān', '第四卷', '隹部', '𨿡屬也。从隹，酓聲。籒文䨄从鳥。', '隹;酓;鳥', '酓', 'null', NULL, '隹;鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2308, '䧴', 'zhī', '第四卷', '隹部', '䧴鳥也。从隹，支聲。一曰䧴度。', '隹;支', '支', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2309, '𨾊', 'hónɡ', '第四卷', '隹部', '鳥肥大𨾊𨾊然也。从隹，工聲。𨾊或从鳥。', '隹;工;鳥', '工', 'null', NULL, '隹;鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2310, '𩀼', 'sàn', '第四卷', '隹部', '繳𩀼也。从隹，𢽳聲。一曰飛𢽳也。', '隹;𢽳', '𢽳', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2311, '隿', 'yì', '第四卷', '隹部', '繳射飛鳥也。从隹，弋聲。', '隹;弋', '弋', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2312, '雄', 'xiónɡ', '第四卷', '隹部', '鳥父也。从隹，厷聲。', '隹;厷', '厷', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2313, '雌', 'cī', '第四卷', '隹部', '鳥母也。从隹，此聲。', '隹;此', '此', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2314, '䍜', 'zhào', '第四卷', '隹部', '覆鳥令不得飛走也。从网隹。讀若到。', '网;隹;到', '到', 'null', NULL, '网;隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2315, '雋', 'juàn', '第四卷', '隹部', '鳥肥也。从弓隹。弓，所㠯射隹。長沙有下雋縣。', '弓;隹', '', 'null', NULL, '弓;隹', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2316, '𩁌', 'wéi', '第四卷', '隹部', '飛也。从隹，隓聲。', '隹;隓', '隓', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2317, '魋', 'tuí', '第四卷', '隹部', '如小熊。赤毛而黃。从隹，鬼聲。', '隹;鬼', '鬼', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '大徐补 | 调部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2318, '奞', 'suī', '第四卷', '奞部', '鳥張毛羽自奮奞也。从大隹。凡奞之屬皆从奞。讀若睢。', '大;隹;睢', '睢', 'null', NULL, '大;隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2319, '奪', 'duó', '第四卷', '奞部', '手持隹失之也。从又奞。', '又;奞', '', 'null', NULL, '又;奞', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2320, '奮', 'fèn', '第四卷', '奞部', '翬也。从奞在田上。詩曰「不能奮飛」。', '奞;田', '', 'null', NULL, '奞;田', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2321, '雈', 'huán', '第四卷', '雈部', '𨾦屬。从隹，从𦫳。有毛角，所鳴其民有旤。凡雈之屬皆从雈。讀若和。', '隹;𦫳;和', '和', 'null', NULL, '隹;𦫳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2322, '蒦;彠', 'huò', '第四卷', '雈部', '規蒦，商也。从又持萑。一曰視遽皃。一曰蒦，度也。蒦或从尋。尋亦度也。楚辭曰「求矩彠之所同」。', '又;萑;尋', '', 'null', NULL, '又;萑;尋', 'null', 'null', 'null', 'null', '矍≌蒦', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2323, '雚', 'ɡuàn', '第四卷', '雈部', '水爵也。从雈，吅聲。詩曰「雚鳴于垤」。', '雈;吅', '吅', 'null', NULL, '雈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2324, '舊;鵂', 'jiù', '第四卷', '雈部', '𨾦舊，舊畱也。从雈，臼聲。舊或从鳥，休聲。', '雈;臼;鳥;休', '臼;休', 'null', NULL, '雈;鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2325, '𦫳', 'ɡuǎ', '第四卷', '𦫳部', '羊角也。象形。凡𦫳之屬皆从𦫳。讀若乖。', '乖', '乖', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2326, '乖', 'ɡuāi', '第四卷', '𦫳部', '戾也。从𦫳𠔁。', '𦫳;𠔁', '', 'null', NULL, '𦫳;𠔁', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2327, '芇', 'mián', '第四卷', '𦫳部', '相當也。闕。讀若宀。', '𦫳冂', '宀', 'null', NULL, '𦫳冂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2328, '𥄕', 'mò', '第四卷', '𥄕部', '目不正也。从𦫳目。凡𥄕之屬皆从𥄕。讀若末。', '𦫳;目;末', '末', 'null', NULL, '𦫳;目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2329, '瞢', 'ménɡ', '第四卷', '𥄕部', '目不明也。从𥄕，从䀏。䀏，目數搖也。', '𥄕;䀏', '', 'null', NULL, '𥄕;䀏', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2330, '𤊾', 'miè', '第四卷', '𥄕部', '火不明也。从𥄕，从火，𥄕亦聲。周書曰「布重𤊾席」。𤊾席，纖蒻席也。讀與蔑同。', '𥄕;火;蔑', '𥄕;蔑', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2331, '蔑', 'miè', '第四卷', '𥄕部', '勞目無精也。从𥄕，从戍。人勞則蔑然也。', '𥄕;戍', '𥄕', 'null', NULL, '戍', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2332, '羊', 'yánɡ', '第四卷', '羊部', '祥也。从𦫳，象四足尾之形。孔子曰「牛羊之字，㠯形舉也」。凡羊之屬皆从羊。', '𦫳', '', 'null', NULL, '𦫳', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2333, '羋;芈', 'mǐ', '第四卷', '羊部', '羊鳴也。从羊。象气上出。與牟同意。', '羊', '', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2334, '羔;羙', 'ɡāo', '第四卷', '羊部', '羊子也。从羊，照省聲。', '羊;照', '照', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2335, '羜', 'zhù', '第四卷', '羊部', '五月生羔也。从羊，宁聲。讀若煮。', '羊;宁;煮', '宁;煮', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2336, '𦎦', 'yù', '第四卷', '羊部', '六月生羔也。从羊，敄聲。讀若霚。', '羊;敄;霚', '敄;霚', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2337, '羍', 'dá', '第四卷', '羊部', '小羊也。从羊，大聲。讀若𨔶同。羍或省[人]。', '羊;大;𨔶;人', '大;𨔶', 'null', NULL, '羊;人', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2338, '䍮', 'zhào', '第四卷', '羊部', '羊未卒歲也。从羊，兆聲。或曰羠羊百斤𠂇又爲䍮。讀若春秋「盟于洮」。', '羊;兆;洮', '兆;洮', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2339, '羝', 'dī', '第四卷', '羊部', '牡羊也。从羊，氐聲。', '羊;氐', '氐', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2340, '羒', 'fén', '第四卷', '羊部', '牡羊也。从羊，分聲。', '羊;分', '分', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2341, '牂', 'zānɡ', '第四卷', '羊部', '牝羊也。从羊，爿聲。', '羊;爿', '爿', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2342, '羭', 'yú', '第四卷', '羊部', '夏羊。牝曰羭。从羊，俞聲。', '羊;俞', '俞', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2343, '羖', 'ɡǔ', '第四卷', '羊部', '夏羊。牡曰羖。从羊，殳聲。', '羊;殳', '殳', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2344, '羯', 'jié', '第四卷', '羊部', '羊羖犗也。从羊，曷聲。', '羊;曷', '曷', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2345, '羠', 'yí', '第四卷', '羊部', '騬羊也。从羊，夷聲。', '羊;夷', '夷', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2346, '羳', 'fán', '第四卷', '羊部', '黃腹羊也。从羊，番聲。', '羊;番', '番', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2347, '羥', 'qiān', '第四卷', '羊部', '羊名也。从羊，巠聲。', '羊;巠', '巠', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2348, '𦎷', 'jìn', '第四卷', '羊部', '羊名。从羊，𡘺聲。汝南 平輿有𦎷亭。讀若晉。', '羊;𡘺;晉', '𡘺;晉', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2349, '羸;𦏞', 'léi', '第四卷', '羊部', '𤸃也。从羊，𦟀聲。', '羊;𦟀', '𦟀', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2350, '䍴', 'wèi', '第四卷', '羊部', '羊相䍴𦎸也。从羊，委聲。', '羊;委', '委', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2351, '𦎸', 'zì', '第四卷', '羊部', '羊相䍴𦎸也。从羊，責聲。', '羊;責', '責', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2352, '羣', 'qún', '第四卷', '羊部', '輩也。从羊，君聲。', '羊;君', '君', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2353, '𦎣', 'yān', '第四卷', '羊部', '羣羊相積也。一曰黑羊也。从羊，垔聲。', '羊;垔', '垔', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2354, '𦍧', 'cī', '第四卷', '羊部', '羊名。蹏皮可㠯割桼。从羊，此聲。', '羊;此', '此', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2355, '美', 'měi', '第四卷', '羊部', '甘也。从羊大。羊在六畜，主給膳也。美與善同意。', '羊;大', '', 'null', NULL, '羊;大', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2356, '羌', 'qiānɡ', '第四卷', '羊部', '西戎。羊穜也。从羊儿。羊亦聲。南方蠻、閩从虫。北方狄从犬。東方貉从豸。西方羌从羊。此六穜也。西南僰人、焦僥从人。葢在坤地，頗有順理之性。唯東夷从大。大，人也。夷俗仁，仁者壽，有君子不死之國。孔子曰「道不行，欲之九夷，乘桴浮於海」，有㠯也。古文羌如此。', '羊;儿', '羊', 'null', NULL, '儿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2357, '羑', 'yǒu', '第四卷', '羊部', '進善也。从羊，久聲。文王拘羑里，在蕩陰。', '羊;久', '久', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2358, '羴;羶', 'shān', '第四卷', '羴部', '羊臭也。从三羊。凡羴之屬皆从羴。羴或从亶。', '羊;亶', '亶', 'null', NULL, '羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2359, '羼', 'chàn', '第四卷', '羴部', '羊相廁也。从羴在尸下。尸，屋也。一曰相出前也。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2360, '瞿', 'jù', '第四卷', '瞿部', '䧹隼之視也。从隹䀠。䀠亦聲。凡瞿之屬皆从瞿。讀若「章句」之句。又音衢。', '隹;䀠;句;衢', '䀠;句;衢', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2361, '矍', 'jué', '第四卷', '瞿部', '隹欲逸走也。从又持之，瞿瞿也。讀若詩云「穬彼淮夷」之穬。一曰視遽皃。', '又;瞿;穬', '穬', 'null', NULL, '又;瞿', 'null', 'null', 'null', 'null', '矍≌蒦', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2362, '雔', 'chóu', '第四卷', '雔部', '雙鳥也。从二隹。凡雔之屬皆从雔。讀若醻。', '隹;醻', '醻', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2363, '靃;霍', 'huò', '第四卷', '雔部', '飛聲也。从雨雔。雨而雔飛者，其聲靃然。', '雨;雔', '', 'null', NULL, '雨;雔', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2364, '雙', 'shuānɡ', '第四卷', '雔部', '隹二枚也。从雔，又持之。', '雔;又', '', 'null', NULL, '雔;又', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2365, '雥', 'zá', '第四卷', '雥部', '羣鳥也。从三隹。凡雥之屬皆从雥。', '隹', '', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '🤰喿', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2366, '䨊', 'yuān', '第四卷', '雥部', '鳥羣也。从雥，𣶒聲。', '雥;𣶒', '𣶒', 'null', NULL, '雥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2367, '雧;集', 'jí', '第四卷', '雥部', '羣鳥在木上也。从雥木。雧或省。', '雥;木;集', '雥;集', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2368, '鳥', 'niǎo', '第四卷', '鳥部', '長尾禽總名也。象形。鳥之足佀匕。从匕。凡鳥之屬皆从鳥。', '匕', '', 'null', NULL, '匕', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2369, '鳳;朋;鵬', 'fènɡ', '第四卷', '鳥部', '神鳥也。天老曰「鳳之像也，麐前鹿後，蛇頸魚尾，龍文龜背，燕頷雞喙，五色備舉」。出於東方君子之國，翺翔四海之外，過崐崘，飲砥柱，濯羽弱水，莫宿風穴。見則天下大安寧。从鳥，凡聲。古文鳳。象形。鳳飛，羣鳥從㠯萬數，故㠯爲「朋黨」字。亦古文鳳。', '鳥;凡;朋', '凡;朋', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2370, '鸞', 'luán', '第四卷', '鳥部', '赤神靈之精也。赤色五采，雞形，鳴中五音，頌聲作則至。从鳥，䜌聲。周成王時氐、羌獻鸞鳥。', '鳥;䜌', '䜌', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2371, '鸑', 'yuè', '第四卷', '鳥部', '鸑鷟——鳳屬，神鳥也。从鳥，獄聲。春秋國語曰「周之興也，鸑鷟鳴於岐山」。江中有鸑鷟，佀鳧而大，赤目。', '鳥;獄', '獄', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2372, '鷟', 'zhuó', '第四卷', '鳥部', '鸑鷟也。从鳥，族聲。', '鳥;族', '族', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2373, '鷫;𪆭;𪂸', 'sù', '第四卷', '鳥部', '鷫鷞也。从鳥，肅聲。五方神鳥也——東方發明，南方焦明，西方鷫鷞，北方豳昌，中央鳳皇。司馬相如說从叜聲。', '鳥;肅;叜', '肅;叜', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2374, '鷞', 'shuānɡ', '第四卷', '鳥部', '鷫鷞也。从鳥，爽聲。', '鳥;爽', '爽', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
