INSERT INTO `shuowen_voice_revel` VALUES (2439, '䳁;䳊', 'bó', '第四卷', '鳥部', '䳁鳥也。从鳥，犮聲。讀若撥。', '鳥;犮;撥', '犮;撥', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2440, '鷛;𪅟', 'yónɡ', '第四卷', '鳥部', '𪅟鳥也。从鳥，庸聲。', '鳥;庸', '庸', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2441, '鶃;鶂;鷊;𪁌', 'yì', '第四卷', '鳥部', '鶃鳥也。从鳥，兒聲。春秋傳曰「六鶃退飛」。鶃或从鬲。司馬相如鶃从赤。', '鳥;兒;鬲;赤', '兒;鬲;赤', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2442, '鴺;鵜', 'tí', '第四卷', '鳥部', '鴺胡，污澤也。从鳥，夷聲。鴺或从弟。', '鳥;夷;弟', '夷;弟', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2443, '鴗', 'lì', '第四卷', '鳥部', '天狗也。从鳥，立聲。', '鳥;立', '立', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2444, '鶬;𩀞', 'cānɡ', '第四卷', '鳥部', '麋鴰也。从鳥，倉聲。鶬或从隹。', '鳥;倉;隹', '倉', 'null', NULL, '鳥;隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2445, '鴰;𪁝', 'ɡuā', '第四卷', '鳥部', '麋鴰也。从鳥，𠯑聲。', '鳥;𠯑', '𠯑', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2446, '𪁉;鵁', 'jiāo', '第四卷', '鳥部', '𪁉𪂴也。从鳥，交聲。一曰𪁉𪈒也。', '鳥;交', '交', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2447, '𪂴;鶄', 'jīnɡ', '第四卷', '鳥部', '𪁉𪂴也。从鳥，靑聲。', '鳥;靑', '靑', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2448, '鳽', 'jiān', '第四卷', '鳥部', '𪁉𪂴也。从鳥，开聲。', '鳥;开', '开', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2449, '𪇳;𪈁', 'zhēn', '第四卷', '鳥部', '𪇳鴜也。从鳥，箴聲。', '鳥;箴', '箴', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2450, '鴜;䳄', 'cí', '第四卷', '鳥部', '𪇳鴜也。从鳥，此聲。', '鳥;此', '此', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2451, '𪆃;𪆝;鷻', 'tuán', '第四卷', '鳥部', '雕也。从鳥，敦聲。詩曰「匪𪆃匪𪀝」。', '鳥;敦', '敦', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2452, '𪀝;鶚', 'yuān', '第四卷', '鳥部', '鷙鳥也。从鳥，屰聲。', '鳥;屰', '屰', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2453, '鷳;鷼', 'xián', '第四卷', '鳥部', '鴟也。从鳥，閒聲。', '鳥;閒', '閒', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2454, '鷂', 'yào', '第四卷', '鳥部', '鷙鳥也。从鳥，䍃聲。', '鳥;䍃', '䍃', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2455, '鷢;𪆙', 'jué', '第四卷', '鳥部', '白鷢，王雎也。从鳥，厥聲。', '鳥;厥', '厥', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2456, '鴡;雎', 'jū', '第四卷', '鳥部', '王鴡也。从鳥，且聲。', '鳥;且', '且', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2457, '𪈩;鸛', 'huān', '第四卷', '鳥部', '𪈩專，畐蹂。如䧿，短尾。射之，銜矢射人。从鳥，雚聲。', '鳥;雚', '雚', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2458, '鸇;𪇮', 'zhān', '第四卷', '鳥部', '鷐風也。从鳥，亶聲。籒文鸇从廛。', '鳥;亶;廛', '亶;廛', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2459, '鷐', 'chén', '第四卷', '鳥部', '鷐風也。从鳥，晨聲。', '鳥;晨', '晨', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2460, '鷙', 'zhì', '第四卷', '鳥部', '擊殺鳥也。从鳥，从執。', '鳥;執', '執', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2461, '鴪;鴥', 'yù', '第四卷', '鳥部', '鸇飛皃。从鳥，穴聲。詩曰「鴪彼鷐風」。', '鳥;穴', '穴', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2462, '鶯', 'yīnɡ', '第四卷', '鳥部', '鳥有文章皃。从鳥，熒省聲。詩曰「有鶯其羽」。', '鳥;熒', '熒', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2463, '鴝', 'qú', '第四卷', '鳥部', '鴝鵒也。从鳥，句聲。', '鳥;句', '句', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2464, '鵒;𩀑', 'yù', '第四卷', '鳥部', '鴝鵒也。从鳥，谷聲。古者鴝鵒不踰泲。鵒或从隹臾。', '鳥;谷;隹;臾', '谷;臾', 'null', NULL, '鳥;隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2465, '鷩', 'biē', '第四卷', '鳥部', '赤雉也。从鳥，敝聲。周禮曰「孤服鷩冕」。', '鳥;敝', '敝', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2466, '鵔;鵕', 'jùn', '第四卷', '鳥部', '鵔鸃，鷩也。从鳥，夋聲。', '鳥;夋', '夋', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2467, '鸃;䴊', 'yí', '第四卷', '鳥部', '鵔鸃也。从鳥，義聲。秦 漢之初，侍中冠鵔鸃。', '鳥;義', '義', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2468, '𪄱', 'dí', '第四卷', '鳥部', '雉屬。戇鳥也。从鳥，啻聲。', '鳥;啻', '啻', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2469, '鶡', 'hé', '第四卷', '鳥部', '佀雉。出上黨。从鳥，曷聲。', '鳥;曷', '曷', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2470, '䲸;𩾴', 'jiè', '第四卷', '鳥部', '䲸雀也。佀鶡而靑，出羌中。从鳥，介聲。', '鳥;介', '介', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2471, '鸚;𪈤', 'yīnɡ', '第四卷', '鳥部', '鸚䳇，能言鳥也。从鳥，嬰聲。', '鳥;嬰', '嬰', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2472, '䳇', 'wǔ', '第四卷', '鳥部', '鸚䳇也。从鳥，母聲。', '鳥;母', '母', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2473, '鷮', 'jiāo', '第四卷', '鳥部', '長尾雉。走且鳴。乘輿尾爲防釳，箸馬頭上。从鳥，喬聲。', '鳥;喬', '喬', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2474, '鷕;𪄼', 'yǎo', '第四卷', '鳥部', '雌雉鳴也。从鳥，唯聲。詩曰「有鷕雉鳴」。', '鳥;唯', '唯', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2475, '䴎;鸓;𤴒;𪉀', 'lěi', '第四卷', '鳥部', '鼠形，飛走且乳之鳥也。从鳥，畾聲。籒文䴎[靁]。', '鳥;畾;靁', '畾;靁', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2476, '鶾', 'hàn', '第四卷', '鳥部', '雞肥翰音者也。从鳥，倝聲。魯郊㠯丹雞祝曰「㠯斯翰音赤羽，去魯矦之咎」。', '鳥;倝', '倝', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2477, '鴳', 'yàn', '第四卷', '鳥部', '雇也。从鳥，安聲。', '鳥;安', '安', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2478, '鴆', 'zhèn', '第四卷', '鳥部', '毒鳥也。从鳥，冘聲。一曰運日。', '鳥;冘', '冘', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2479, '鷇;𣫠;𪃟', 'kòu', '第四卷', '鳥部', '鳥子生哺者。从鳥，㱿聲。', '鳥;㱿', '㱿', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2480, '鳴', 'mínɡ', '第四卷', '鳥部', '鳥聲也。从鳥口。', '鳥;口', '', 'null', NULL, '鳥;口', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2481, '鶱', 'xiān', '第四卷', '鳥部', '飛皃也。从鳥，寒省聲。', '鳥;寒', '寒', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2482, '𩿈;鳻', 'fēn', '第四卷', '鳥部', '鳥聚皃也。一曰飛皃也。从鳥，分聲。', '鳥;分', '分', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2483, '鷓', 'zhè', '第四卷', '鳥部', '鷓鴣，鳥名。从鳥，庶聲。', '鳥;庶', '庶', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2484, '鴣', 'ɡū', '第四卷', '鳥部', '鷓鴣也。从鳥，古聲。', '鳥;古', '古', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2485, '鴨', 'yā', '第四卷', '鳥部', '鶩也。从鳥，甲聲。', '鳥;甲', '甲', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2486, '𪀦;𪀸', 'shì', '第四卷', '鳥部', '谿𪀦，水鳥。从鳥，式聲。', '鳥;式', '式', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2487, '烏;於', 'wū', '第四卷', '烏部', '孝鳥也。象形。孔子曰「烏，亏呼也」。取其助气，故㠯爲「烏呼」。凡烏之屬皆从烏。古文烏象形。象古文烏省。', '鳥', '', 'null', NULL, '鳥', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2488, '舄;舃;𩁆;䧿;鵲', 'què', '第四卷', '烏部', '䧿也。象形。篆文舄从隹昔。', '隹;昔', '昔', 'null', NULL, '隹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2489, '焉', 'yān', '第四卷', '烏部', '焉鳥，黃色，出於江 淮。象形。凡字，「朋」者羽蟲之長，「烏」者日中之禽，「舄」者知大歲之所在，「燕」者請子之𠊱，作巢避戊己。所貴者，故皆象形，「焉」亦是也。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2490, '𠦒', 'bān', '第四卷', '𠦒部', '箕屬，所㠯推糞之器也。象形。凡𠦒之屬皆从𠦒。官溥說。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2491, '畢', 'bì', '第四卷', '𠦒部', '田网也。从田，从𠦒。象形。或曰田聲。', '田;𠦒;田', '田', 'null', NULL, '田;𠦒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2492, '糞', 'fèn', '第四卷', '𠦒部', '棄除也。从廾推𠦒糞釆也。官浦說，佀米而非米者，矢字。', '廾;𠦒;釆', '釆', 'null', NULL, '廾;𠦒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2493, '棄', 'qì', '第四卷', '𠦒部', '捐也。从廾推𠦒棄也。从㐬。㐬，逆子也。古文棄。籒文棄。', '廾;𠦒;㐬', '', 'null', NULL, '廾;𠦒;㐬', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2494, '冓', 'ɡòu', '第四卷', '冓部', '交積材也。象對交之形。凡冓之屬皆从冓。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2495, '再', 'zài', '第四卷', '冓部', '一舉而二也。从一，冓省。', '一;冓', '', 'null', NULL, '一;冓', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2496, '爯', 'chēnɡ', '第四卷', '冓部', '并舉也。从爪，冓省。', '爪;冓', '', 'null', NULL, '爪;冓', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2497, '幺', 'yāo', '第四卷', '幺部', '小也。象子初生之形。凡幺之屬皆从幺。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2498, '幼', 'yòu', '第四卷', '幺部', '少也。从幺力。', '幺;力', '幺', 'null', NULL, '力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2499, '麽', 'mǒ', '第四卷', '幺部', '細也。从幺，麻聲。', '幺;麻', '麻', 'null', NULL, '幺', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2500, '𢆶', 'yōu', '第四卷', '𢆶部', '微也。从二幺。凡𢆶之屬皆从𢆶。', '幺', '幺', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2501, '幽', 'yōu', '第四卷', '𢆶部', '隱也。从山𢆶。𢆶亦聲。', '山;𢆶', '𢆶', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2502, '幾', 'jī', '第四卷', '𢆶部', '微也。殆也。从𢆶，从戍。戍，兵守也。𢆶而兵守者，危也。', '𢆶;戍', '', 'null', NULL, '𢆶;戍', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2503, '叀;𠧢', 'zhuān', '第四卷', '叀部', '小謹也。从幺省，从屮。屮，財見也。田象謹形。屮亦聲。凡叀之屬皆从叀。古文叀。亦古文叀。', '幺;屮;田', '屮', 'null', NULL, '幺;田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2504, '惠', 'huì', '第四卷', '叀部', '仁也。从心叀。古文惠从卉。', '心;叀;卉', '卉', 'null', NULL, '心;叀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2505, '疐;𤴡', 'zhì', '第四卷', '叀部', '礙不行也。从叀，引而止之也。叀者，如叀馬之鼻。从冂。此與牽同意。詩曰「載𤴡其尾」。', '叀;止;冂', '', 'null', NULL, '叀;止;冂', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2506, '玄', 'xuán', '第四卷', '玄部', '幽遠也。象幽而[入]覆之也。黑而有赤色者爲玄。凡玄之屬皆从玄。古文。', '入', '', 'null', NULL, '入', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2507, '玆', 'zī', '第四卷', '玄部', '黑也。从二玄。春秋傳曰「何故使吾水玆」。', '玄', '', 'null', NULL, '玄', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2508, '玈', 'lú', '第四卷', '玄部', '黑色也。从玄，旅省聲。', '玄;旅', '旅', 'null', NULL, '玄', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2509, '予', 'yǔ', '第四卷', '予部', '推予也。象相予之形。凡予之屬皆从予。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2510, '舒', 'shū', '第四卷', '予部', '伸也。从予，舍聲。一曰舒，緩也。', '予;舍', '舍', 'null', NULL, '予', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2511, '幻', 'huàn', '第四卷', '予部', '相詐惑也。从反予。周書曰「無或譸張爲幻」。', '予', '', 'null', NULL, '予', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2512, '放', 'fànɡ', '第四卷', '放部', '逐也。从攴，方聲。凡放之屬皆从放。', '攴;方', '方', 'null', NULL, '攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2513, '敖;𢾕', 'áo', '第四卷', '放部', '出遊也。从出放。', '出;放', '', 'null', NULL, '出;放', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2514, '敫', 'yuè', '第四卷', '放部', '光景流皃。从白放。讀若龠。', '白;放;龠', '龠', 'null', NULL, '白;放', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2515, '𠬪', 'biào', '第四卷', '𠬪部', '物落也。上下相付也。从爪又。凡𠬪之屬皆从𠬪。讀若詩「摽有梅」。', '爪;又;摽', '摽', 'null', NULL, '爪;又', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2516, '爰', 'yuán', '第四卷', '𠬪部', '引也。从𠬪，从亏。籒文㠯爲「車轅」字。', '𠬪;亏;轅', '轅', 'null', NULL, '𠬪;亏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2517, '𤔔', 'luàn', '第四卷', '𠬪部', '治也。幺子相亂[𫡃]，𠬪治之也。讀若亂同。一曰理也。古文𤔔[未详]。', '幺;𫡃;𠬪;亂', '亂', 'null', NULL, '幺;𫡃;𠬪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2518, '受', 'shòu', '第四卷', '𠬪部', '相付也。从𠬪，舟省聲。', '𠬪;舟', '舟', 'null', NULL, '𠬪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2519, '𤓴', 'liè', '第四卷', '𠬪部', '撮也。从𠬪，己聲。', '𠬪;己', '', 'null', NULL, '𠬪;己', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2520, '爭', 'zhēnɡ', '第四卷', '𠬪部', '引也。从𠬪𠂆。', '𠬪;𠂆', '', 'null', NULL, '𠬪;𠂆', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2521, '𤔌', 'yǐn', '第四卷', '𠬪部', '有所依也。从𠬪工。讀與隱同。', '𠬪;工;隱', '隱', 'null', NULL, '𠬪;工', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2522, '寽', 'lǜ', '第四卷', '𠬪部', '五指寽也。从𠬪，一聲。讀若律。', '𠬪;一;律', '一;律', 'null', NULL, '𠬪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2523, '𠭖;敢', 'ɡǎn', '第四卷', '𠬪部', '進取也。从𠬪，古聲。籒文𠭖[爪冃攴]。古文𠭖。', '𠬪;古;爪;冃;殳', '古', 'null', NULL, '𠬪;爪;冃;殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2524, '𣦼', 'cán', '第四卷', '𣦼部', '殘穿也。从又𣦵。𣦵亦聲。凡𣦼之屬皆从𣦼。讀若殘。', '又;𣦵;殘', '𣦵;殘', 'null', NULL, '又', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2525, '㕡', 'hè', '第四卷', '𣦼部', '溝也。从𣦼，从谷。讀若郝。㕡或从土。', '𣦻;谷;郝;土', '郝', 'null', NULL, '𣦻;谷;土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2526, '㕢', 'ɡài', '第四卷', '𣦼部', '𥥍堅意也。从𣦼，从貝。貝，堅實也。讀若概。', '𣦼;貝;概', '概', 'null', NULL, '𣦼;貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2527, '𠭘', 'jǐnɡ', '第四卷', '𣦼部', '阬也。从𣦼井。井亦聲。', '𣦼;井', '井', 'null', NULL, '𣦼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2528, '叡;睿', 'ruì', '第四卷', '𣦼部', '𥥍明也。从𣦼，从目，从谷省。古文叡[𣦵]。籒文叡从土。', '𣦼;目;谷;𣦵;土', '', 'null', NULL, '𣦼;目;谷;𣦵;土', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2529, '𣦵;歺', 'è', '第四卷', '𣦵部', '列骨之殘也。从半冎。凡𣦵之屬皆从𣦵。讀若「櫱岸[牙]」之櫱[糱]。古文𣦵。', '冎;糱', '糱', 'null', NULL, '冎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2530, '𣨙', 'wěi', '第四卷', '𣦵部', '病也。从𣦵，委聲。', '𣦵;委', '委', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2531, '殙;㱪', 'hūn', '第四卷', '𣦵部', '瞀也。从𣦵，昏聲。', '𣦵;昏', '昏', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2532, '殰', 'dú', '第四卷', '𣦵部', '胎敗也。从𣦵，𧷏聲。', '𣦵;𧷏', '𧷏', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2533, '歾;歿;殁', 'mò', '第四卷', '𣦵部', '終也。从𣦵，勿聲。歾或从𠬛。', '𣦵;勿;𠬛', '勿;𠬛', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2534, '𣨛', 'zú', '第四卷', '𣦵部', '大夫死曰𣨛。从𣦵，卒聲。', '𣦵;卒', '卒', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2535, '殊', 'shū', '第四卷', '𣦵部', '死也。从𣦵，朱聲。一曰𣃔也。漢令曰「蠻夷長有罪當殊之」。', '𣦵;朱', '朱', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2536, '殟', 'wēn', '第四卷', '𣦵部', '𣉱無知也。从𣦵，𥁕聲。', '𣦵;𥁕', '𥁕', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2537, '殤', 'shānɡ', '第四卷', '𣦵部', '不成人也。人年十九至十六死爲「長殤」，十五至十二死爲「中殤」，十一至八嵗死爲「下殤」。从𣦵，傷省聲。', '𣦵;傷', '傷', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2538, '殂', 'cú', '第四卷', '𣦵部', '往死也。从𣦵，且聲。虞書曰「勛乃殂」。古文殂从𣦵，从作。', '𣦵;且;作', '且;作', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2539, '殛', 'jí', '第四卷', '𣦵部', '殊也。从𣦵，从亟聲。虞書曰「殛鯀于羽山」。', '𣦵;亟', '亟', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2540, '殪', 'yì', '第四卷', '𣦵部', '死也。从𣦵，壹聲。古文殪从死。', '𣦵;壹;死', '壹', 'null', NULL, '𣦵;死', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2541, '㱳', 'mò', '第四卷', '𣦵部', '死𡧯㱳也。从𣦵，莫聲。', '𣦵;莫', '莫', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2542, '殯;𣩵', 'bìn', '第四卷', '𣦵部', '死在棺，將遷葬，柩。賔遇之。从𣦵賔。賔亦聲。夏后𣩵於胙階，殷人𣩵於兩楹之閒，周人𣩵於賔階。', '𣦵;賔', '賔', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2543, '殔', 'yì', '第四卷', '𣦵部', '瘞也。从𣦵，隶聲。', '𣦵;隶', '隶', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2544, '殣', 'jìn', '第四卷', '𣦵部', '道中死人，人所覆也。从𣦵，堇聲。詩曰「行有死人，尙或殣之」。', '𣦵;堇', '堇', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2545, '殠', 'chòu', '第四卷', '𣦵部', '腐气也。从𣦵，臭聲。', '𣦵;臭', '臭', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2546, '殨', 'kuì', '第四卷', '𣦵部', '爛也。从𣦵，貴聲。', '𣦵;貴', '貴', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2547, '㱙;朽', 'xiǔ', '第四卷', '𣦵部', '腐也。从𣦵，丂聲。㱙或从木。', '𣦵;丂;木', '丂', 'null', NULL, '𣦵;木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2548, '殆', 'dài', '第四卷', '𣦵部', '危也。从𣦵，台聲。', '𣦵;台', '台', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2549, '殃', 'yānɡ', '第四卷', '𣦵部', '凶也。从𣦵，央聲。', '𣦵;央', '央', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2550, '殘', 'cán', '第四卷', '𣦵部', '賊也。从𣦵，戔聲。', '𣦵;戔', '戔', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2551, '殄', 'tiǎn', '第四卷', '𣦵部', '盡也。从𣦵，㐱聲。古文殄如此[眞省声]。', '𣦵;㐱;眞', '㐱;眞', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2552, '殲', 'jiān', '第四卷', '𣦵部', '微盡也。从𣦵，韱聲。春秋傳曰「齊人殲于遂」。', '𣦵;韱', '韱', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2553, '殫', 'dān', '第四卷', '𣦵部', '極盡也。从𣦵，單聲。', '𣦵;單', '單', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2554, '殬', 'dù', '第四卷', '𣦵部', '敗也。从𣦵，睪聲。商書曰「彝倫攸殬」。', '𣦵;睪', '睪', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2555, '㱻', 'luò', '第四卷', '𣦵部', '畜産疫病也。从𣦵，从羸聲。', '𣦵;羸', '羸', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2556, '㱯', 'ái', '第四卷', '𣦵部', '殺羊出其胎也。从𣦵，豈聲。', '𣦵;豈', '豈', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2557, '㱚;𣧻', 'cán', '第四卷', '𣦵部', '禽獸所食餘也。从𣦵，从月。', '𣦵;月', '', 'null', NULL, '𣦵;月', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2558, '殖', 'zhí', '第四卷', '𣦵部', '脂膏久殖也。从𣦵，直聲。', '𣦵;直', '直', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2559, '㱠', 'kū', '第四卷', '𣦵部', '枯也。从𣦵，古聲。', '𣦵;古', '古', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2560, '㱦', 'qī', '第四卷', '𣦵部', '棄也。从𣦵，奇聲。俗語謂死曰「大㱦」。', '𣦵;奇', '奇', 'null', NULL, '𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2561, '死', 'sǐ', '第四卷', '死部', '澌也，人所離也。从𣦵人。凡死之屬皆从死。古文死如此。', '𣦵;人', '', 'null', NULL, '𣦵;人', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2562, '薨', 'hōnɡ', '第四卷', '死部', '公矦𣨛也。从死，瞢省聲。', '死;瞢', '瞢', 'null', NULL, '死', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2563, '薧', 'hāo', '第四卷', '死部', '死人里也。从死，蒿省聲。', '死;蒿', '蒿', 'null', NULL, '死', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2564, '𣣌', 'zì', '第四卷', '死部', '戰。見血曰「傷」。亂或爲「惛」。死而復生爲「𣣌」。从死，次聲。', '死;次', '次', 'null', NULL, '死', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2565, '冎', 'ɡuǎ', '第四卷', '冎部', '剔人肉置其骨也。象形。頭隆骨也。凡冎之屬皆从冎。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2566, '別;𠛰;别', 'bié', '第四卷', '冎部', '分解也。从冎，从刀。', '冎;刀', '', 'null', NULL, '冎;刀', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2567, '㗗', 'bēi', '第四卷', '冎部', '別也。从冎，卑聲。讀若罷。', '冎;卑;罷', '卑;罷', 'null', NULL, '冎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2568, '骨', 'ɡǔ', '第四卷', '骨部', '肉之覈也。从冎有肉。凡骨之屬皆从骨。', '冎;肉', '', 'null', NULL, '冎;肉', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2569, '髑', 'dú', '第四卷', '骨部', '髑髏，頂骨也。从骨，蜀聲。', '骨;蜀', '蜀', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2570, '髏', 'lóu', '第四卷', '骨部', '髑髏也。从骨，婁聲。', '骨;婁', '婁', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2571, '髆', 'bó', '第四卷', '骨部', '肩甲也。从骨，尃聲。', '骨;尃', '尃', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2572, '髃', 'ǒu', '第四卷', '骨部', '肩前也。从骨，禺聲。', '骨;禺', '禺', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2573, '骿', 'pián', '第四卷', '骨部', '骿脅，并榦也。从骨，并聲。晉文公骿脅。', '骨;并', '并', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2574, '髀', 'bǐ', '第四卷', '骨部', '股外也。从骨，卑聲。古文髀[足]。', '骨;卑;足', '卑', 'null', NULL, '骨;足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2575, '髁', 'kē', '第四卷', '骨部', '髀骨也。从骨，果聲。', '骨;果', '果', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2576, '𩪗;𩪜', 'jué', '第四卷', '骨部', '𡱂骨也。从骨，厥聲。', '骨;厥', '厥', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2577, '髖', 'kuān', '第四卷', '骨部', '髀上也。从骨，寬聲。', '骨;寬', '寬', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2578, '髕', 'bìn', '第四卷', '骨部', '厀耑也。从骨，賔聲。', '骨;賔', '賔', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2579, '䯏', 'ɡuā', '第四卷', '骨部', '骨耑也。从骨，𠯑聲。', '骨;𠯑', '𠯑', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2580, '䯣', 'kuì', '第四卷', '骨部', '厀脛閒骨也。从骨，貴聲。', '骨;貴', '貴', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2581, '骹', 'qiāo', '第四卷', '骨部', '脛也。从骨，交聲。', '骨;交', '交', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2582, '骭', 'ɡàn', '第四卷', '骨部', '骹也。从骨，干聲。', '骨;干', '干', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2583, '骸', 'hái', '第四卷', '骨部', '脛骨也。从骨，亥聲。', '骨;亥', '亥', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2584, '𩪦;髓', 'suǐ', '第四卷', '骨部', '骨中脂也。从骨，隓聲。', '骨;隓', '隓', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2585, '䯜', 'tì', '第四卷', '骨部', '骨閒黃汁也。从骨，易聲。讀若易曰「夕惕若厲」。', '骨;易;惕', '易;惕', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2586, '體', 'tǐ', '第四卷', '骨部', '總十二屬也。从骨，豊聲。', '骨;豊', '豊', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2587, '髍', 'mó', '第四卷', '骨部', '㾫病也。从骨，麻聲。', '骨;麻', '麻', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2588, '骾', 'ɡěnɡ', '第四卷', '骨部', '食骨畱咽中也。从骨，更聲。', '骨;更', '更', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2589, '骼', 'ɡé', '第四卷', '骨部', '禽獸之骨曰骼。从骨，各聲。', '骨;各', '各', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2590, '骴', 'cī', '第四卷', '骨部', '鳥獸殘骨曰骴。骴，可惡也。从骨，此聲。朙堂月令曰「掩骼薶骴[髊]」。', '骨;此;差', '此;差', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2591, '骫', 'wěi', '第四卷', '骨部', '骨耑骫奊也。从骨，丸聲。', '骨;丸', '丸', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2592, '䯤', 'kuài', '第四卷', '骨部', '骨擿之可會髮者。从骨，會聲。詩曰「䯤弁如星」。', '骨;會', '會', 'null', NULL, '骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2593, '肉', 'ròu', '第四卷', '肉部', '胾肉。象形。凡肉之屬皆从肉。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2594, '腜', 'méi', '第四卷', '肉部', '婦始孕，腜兆也。从肉，某聲。', '肉;某', '某', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2595, '肧', 'pēi', '第四卷', '肉部', '婦孕一月也。从肉，不聲。', '肉;不', '不', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2596, '胎', 'tāi', '第四卷', '肉部', '婦孕三月也。从肉，台聲。', '肉;台', '台', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2597, '肌', 'jī', '第四卷', '肉部', '肉也。从肉，几聲。', '肉;几', '几', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2598, '臚;膚', 'lú', '第四卷', '肉部', '皮也。从肉，盧聲。籒文臚[𧆭]。', '肉;盧;𧆭', '盧;𧆭', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2599, '肫', 'zhūn', '第四卷', '肉部', '面頯也。从肉，屯聲。', '肉;屯', '屯', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2600, '𦠄', 'jī', '第四卷', '肉部', '頰肉也。从肉，幾聲。讀若畿。', '肉;幾;畿', '幾;畿', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2601, '脣', 'chún', '第四卷', '肉部', '口耑也。从肉，辰聲。古文脣从頁。', '肉;辰;頁', '辰', 'null', NULL, '肉;頁', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2602, '脰', 'dòu', '第四卷', '肉部', '項也。从肉，豆聲。', '肉;豆', '豆', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2603, '肓', 'huānɡ', '第四卷', '肉部', '心下鬲上也。从肉，亡聲。春秋傳曰「病在肓之上」。', '肉;亡', '亡', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2604, '腎', 'shèn', '第四卷', '肉部', '水臧也。从肉，臤聲。', '肉;臤', '臤', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2605, '肺', 'fèi', '第四卷', '肉部', '金臧也。从肉，𣎵聲。', '肉;𣎵', '𣎵', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2606, '脾', 'pí', '第四卷', '肉部', '土藏也。从肉，卑聲。', '肉;卑', '卑', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2607, '肝', 'ɡān', '第四卷', '肉部', '木藏也。从肉，干聲。', '肉;干', '干', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2608, '膽', 'dǎn', '第四卷', '肉部', '連肝之府也。从肉，詹聲。', '肉;詹', '詹', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2609, '胃', 'wèi', '第四卷', '肉部', '榖府也。从肉，𡇒象形。', '肉;𡇒', '', 'null', NULL, '肉;𡇒', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2610, '脬', 'pāo', '第四卷', '肉部', '旁光也。从肉，孚聲。', '肉;孚', '孚', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2611, '腸', 'chánɡ', '第四卷', '肉部', '大小腸也。从肉，昜聲。', '肉;昜', '昜', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2612, '膏', 'ɡāo', '第四卷', '肉部', '肥也。从肉，高聲。', '肉;高', '高', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2613, '肪', 'fánɡ', '第四卷', '肉部', '肥也。从肉，方聲。', '肉;方', '方', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2614, '膺', 'yīnɡ', '第四卷', '肉部', '匈也。从肉，䧹聲。', '肉;䧹', '䧹', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2615, '肊;臆', 'yì', '第四卷', '肉部', '匈骨也。从肉乙[𠃉]。肊或从意。', '肉;𠃉;意', '意', 'null', NULL, '肉;𠃉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2616, '背', 'bèi', '第四卷', '肉部', '脊也。从肉，北聲。', '肉;北', '北', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2617, '脅;脇', 'xié', '第四卷', '肉部', '兩膀也。从肉，劦聲。', '肉;劦', '劦', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2618, '膀', 'pánɡ', '第四卷', '肉部', '脅也。从肉，旁聲。膀或从骨。', '肉;旁;骨', '旁', 'null', NULL, '肉;骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2619, '脟', 'liè', '第四卷', '肉部', '脅肉也。从肉，寽聲。一曰脟，腸閒肥也。一曰膫也。', '肉;寽', '寽', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2620, '肋', 'lèi', '第四卷', '肉部', '脅骨也。从肉，力聲。', '肉;力', '力', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2621, '胂', 'shēn', '第四卷', '肉部', '夾脊肉也。从肉，申聲。', '肉;申', '申', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2622, '脢', 'méi', '第四卷', '肉部', '背肉也。从肉，每聲。易曰「咸其脢」[脄[灰]]。', '肉;每;灰', '每;灰', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2623, '𦙪;肩', 'jiān', '第四卷', '肉部', '髆也。从肉。象形。俗𦙪从戸。', '肉;戸', '', 'null', NULL, '肉;戸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2624, '胳', 'ɡē', '第四卷', '肉部', '亦下也。从肉，各聲。', '肉;各', '各', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2625, '胠', 'qū', '第四卷', '肉部', '亦下也。从肉，去聲。', '肉;去', '去', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2626, '臂', 'bì', '第四卷', '肉部', '手上也。从肉，辟聲。', '肉;辟', '辟', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2627, '臑', 'nào', '第四卷', '肉部', '臂羊矢也。从肉，需聲。讀若儒。', '肉;需;儒', '需;儒', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2628, '肘', 'zhǒu', '第四卷', '肉部', '臂節也。从肉寸。寸，手寸口。', '肉;寸', '', 'null', NULL, '肉;寸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2629, '臍;䐡', 'qí', '第四卷', '肉部', '毗臍也。从肉，齊聲。', '肉;齊', '齊', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2630, '腹', 'fù', '第四卷', '肉部', '厚也。从肉，复聲。', '肉;复', '复', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2631, '腴', 'yú', '第四卷', '肉部', '腹下肥者。从肉，臾聲。', '肉;臾', '臾', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2632, '脽', 'shuí', '第四卷', '肉部', '𡱂也。从肉，隹聲。', '肉;隹', '隹', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2633, '䏐', 'jué', '第四卷', '肉部', '孔也。从肉，夬聲。讀若「決水」之決。', '肉;夬;決', '夬;決', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2634, '胯', 'kuà', '第四卷', '肉部', '股也。从肉，夸聲。', '肉;夸', '夸', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2635, '股', 'ɡǔ', '第四卷', '肉部', '髀也。从肉，殳聲。', '肉;殳', '殳', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2636, '腳', 'jiǎo', '第四卷', '肉部', '脛也。从肉，卻聲。', '肉;卻', '卻', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2637, '脛', 'jìnɡ', '第四卷', '肉部', '胻也。从肉，巠聲。', '肉;巠', '巠', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2638, '胻', 'hénɡ', '第四卷', '肉部', '脛耑也。从肉，行聲。', '肉;行', '行', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2639, '腓', 'féi', '第四卷', '肉部', '脛腨也。从肉，非聲。', '肉;非', '非', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2640, '腨', 'shuàn', '第四卷', '肉部', '腓腸也。从肉，耑聲。', '肉;耑', '耑', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2641, '胑;肢', 'zhī', '第四卷', '肉部', '體四胑也。从肉，只聲。胑或从支。', '肉;只;支', '只;支', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2642, '胲', 'ɡāi', '第四卷', '肉部', '足大指毛肉。从肉，亥聲。', '肉;亥', '亥', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2643, '肖', 'xiào', '第四卷', '肉部', '骨肉相佀也。从肉，小聲。不佀其先，故曰不肖也。', '肉;小', '小', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2644, '胤', 'yìn', '第四卷', '肉部', '子孫相承續也。从肉。从八，象其長也。㕕，亦象重絫也。古文胤[廾]。', '肉;八;㕕;廾', '', 'null', NULL, '肉;八;㕕;廾', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2645, '胄', 'zhòu', '第四卷', '肉部', '胤也。从肉，由聲。', '肉;由', '由', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2646, '䏌', 'xì', '第四卷', '肉部', '振肸也。从肉八。', '肉;八', '', 'null', NULL, '肉;八', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2647, '膻', 'dàn', '第四卷', '肉部', '肉膻也。从肉，亶聲。詩曰「膻裼暴虎」。', '肉;亶', '亶', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2648, '䑋', 'rǎnɡ', '第四卷', '肉部', '益州鄙言人盛諱其肥謂之䑋。从肉，襄聲。', '肉;襄', '襄', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2649, '肥', 'féi', '第四卷', '肉部', '多肉也。从肉卪。', '肉;卪', '', 'null', NULL, '肉;卪', 'null', 'null', 'null', 'null', '调序', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2650, '𦝨', 'jiē', '第四卷', '肉部', '臞也。从肉，皆聲。', '肉;皆', '皆', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2651, '臞', 'qú', '第四卷', '肉部', '少肉也。从肉，瞿聲。', '肉;瞿', '瞿', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2652, '脫', 'tuō', '第四卷', '肉部', '消肉臞也。从肉，兌聲。', '肉;兌', '兌', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2653, '脙', 'qiú', '第四卷', '肉部', '齊人謂臞脙也。从肉，求聲。讀若「休止」。', '肉;求;休', '求;休', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2654, '臠', 'luán', '第四卷', '肉部', '臞也。从肉，䜌聲。一曰切肉也。詩曰「棘人臠臠」。', '肉;䜌', '䜌', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2655, '膌', 'jí', '第四卷', '肉部', '𤸃也。从肉，脊聲。古文膌从疒朿，朿亦聲。', '肉;脊;疒;朿', '脊;朿', 'null', NULL, '肉;疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2656, '𦛆;脀', 'chénɡ', '第四卷', '肉部', '騃也。从肉，丞聲。讀若丞。', '肉;丞', '丞', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2657, '胗', 'zhěn', '第四卷', '肉部', '脣瘍也。从肉，㐱聲。籒文胗从疒。', '肉;㐱;疒', '㐱', 'null', NULL, '肉;疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2658, '腄', 'zhuī', '第四卷', '肉部', '跟胝也。从肉，垂聲。', '肉;垂', '垂', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2659, '胝', 'zhī', '第四卷', '肉部', '腄也。从肉，氐聲。', '肉;氐', '氐', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2660, '肬', 'yóu', '第四卷', '肉部', '贅肬也。从肉，尤聲。籒文肬从黑。', '肉;尤;黑', '尤', 'null', NULL, '肉;黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2661, '肒', 'huàn', '第四卷', '肉部', '搔生創也。从肉，丸聲。', '肉;丸', '丸', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2662, '腫', 'zhǒnɡ', '第四卷', '肉部', '癰也。从肉，重聲。', '肉;重', '重', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2663, '胅', 'dié', '第四卷', '肉部', '骨差也。从肉，失聲。讀若跌同。', '肉;失;跌', '失;跌', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2664, '脪', 'xìn', '第四卷', '肉部', '創肉反出也。从肉，希聲。', '肉;希', '希', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2665, '䏖', 'zhèn', '第四卷', '肉部', '瘢也。从肉，引聲。一曰遽也。', '肉;引', '引', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2666, '臘', 'là', '第四卷', '肉部', '冬至後三戌臘祭百神。从肉，巤聲。', '肉;巤', '巤', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2667, '膢', 'lǘ', '第四卷', '肉部', '楚俗㠯二月祭㱃食也。从肉，婁聲。一曰祈榖食新曰膢。', '肉;婁', '婁', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2668, '脁', 'tiǎo', '第四卷', '肉部', '祭也。从肉，兆聲。', '肉;兆', '兆', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2669, '胙', 'zuò', '第四卷', '肉部', '祭福肉也。从肉，乍聲。', '肉;乍', '乍', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2670, '隋', 'duò', '第四卷', '肉部', '裂肉也。从肉，隓省聲。', '肉;隓', '隓', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2671, '膳', 'shàn', '第四卷', '肉部', '具食也。从肉，善聲。', '肉;善', '善', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2672, '腬', 'róu', '第四卷', '肉部', '嘉善肉也。从肉，柔聲。', '肉;柔', '柔', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2673, '肴', 'yáo', '第四卷', '肉部', '啖也。从肉，爻聲。', '肉;爻', '爻', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2674, '腆', 'tiǎn', '第四卷', '肉部', '設膳腆腆多也。从肉，典聲。古文腆。', '肉;典', '典', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2675, '腯', 'tú', '第四卷', '肉部', '牛羊曰肥，豕曰腯。从肉，盾聲。', '肉;盾', '盾', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2676, '䏟', 'bié', '第四卷', '肉部', '肥肉也。从肉，必聲。', '肉;必', '必', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2677, '胡', 'hú', '第四卷', '肉部', '牛𩔞𠂹也。从肉，古聲。', '肉;古', '古', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2678, '胘', 'xián', '第四卷', '肉部', '牛百葉也。从肉，弦省聲。', '肉;弦', '弦', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2679, '膍', 'pí', '第四卷', '肉部', '牛百葉也。从肉，𣬉聲。一曰鳥膍胵，鳥胃也。膍或从比。', '肉;𣬉;比', '𣬉;比', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2680, '胵', 'chī', '第四卷', '肉部', '鳥膍胵也。从肉，至聲。一曰胵，五藏總名也。', '肉;至', '至', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2681, '膘', 'piǎo', '第四卷', '肉部', '牛脅後髀前合革肉也。从肉，票聲。讀若繇。', '肉;票;繇', '票;繇', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2682, '𦝭;膟', 'lǜ', '第四卷', '肉部', '血祭肉也。从肉，帥聲。𦝭或从率。', '肉;帥;率', '帥;率', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2683, '膫;膋', 'liáo', '第四卷', '肉部', '牛腸脂也。从肉，尞聲。詩曰「取其血膫」。膫或从勞省聲。', '肉;尞;勞', '尞;勞', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2684, '脯', 'fǔ', '第四卷', '肉部', '乾肉也。从肉，甫聲。', '肉;甫', '甫', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2685, '脩', 'xiū', '第四卷', '肉部', '脯也。从肉，攸聲。', '肉;攸', '攸', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2686, '膎', 'xié', '第四卷', '肉部', '脯也。从肉，奚聲。', '肉;奚', '奚', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2687, '脼', 'liǎnɡ', '第四卷', '肉部', '膎肉也。从肉，兩聲。', '肉;兩', '兩', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2688, '膊', 'pò', '第四卷', '肉部', '薄脯，脯之屋上。从肉，尃聲。', '肉;尃', '尃', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2689, '脘', 'wǎn', '第四卷', '肉部', '胃脯也。从肉，完聲。讀若患。', '肉;完;患', '完;患', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2690, '朐;胊', 'qú', '第四卷', '肉部', '脯挺也。从肉，句聲。', '肉;句', '句', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2691, '膴', 'hū', '第四卷', '肉部', '無骨腊也。楊雄說鳥腊。从肉，無聲。周禮有「膴判」。讀若謨。', '肉;無;謨', '無;謨', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2692, '腒', 'jū', '第四卷', '肉部', '北方謂鳥腊曰腒。从肉，居聲。傳曰「堯如腊，舜如腒」。', '肉;居', '居', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2693, '肍', 'qiú', '第四卷', '肉部', '乾肉醬也。从肉，九聲。讀若舊。', '肉;九;舊', '九;舊', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2694, '䐹', 'sōu', '第四卷', '肉部', '乾魚尾肅肅也。从肉，肅聲。周禮有「腒䐹」。', '肉;肅', '肅', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2695, '腝;臡', 'ní', '第四卷', '肉部', '有骨醢也。从肉，耎聲。腝或从難。', '肉;耎;難', '耎;難', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2696, '𦚺', 'shān', '第四卷', '肉部', '生肉醬也。从肉，㢟聲。', '肉;㢟', '㢟', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2697, '䏽', 'bù', '第四卷', '肉部', '豕肉醬也。从肉，㕻聲。', '肉;㕻', '㕻', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2698, '胥', 'xū', '第四卷', '肉部', '蟹醢也。从肉，疋聲。', '肉;疋', '疋', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2699, '胹', 'ér', '第四卷', '肉部', '爛也。从肉，而聲。', '肉;而', '而', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2700, '䐣', 'sǔn', '第四卷', '肉部', '切孰肉，內於血中，和也。从肉，員聲。讀若遜。', '肉;員;遜', '員;遜', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2701, '胜', 'xīnɡ', '第四卷', '肉部', '犬膏臭也。从肉，生聲。一曰不孰也。', '肉;生', '生', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2702, '臊', 'sāo', '第四卷', '肉部', '豕膏臭也。从肉，喿聲。', '肉;喿', '喿', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2703, '膮', 'xiāo', '第四卷', '肉部', '豕肉羹也。从肉，堯聲。', '肉;堯', '堯', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2704, '腥', 'xìnɡ', '第四卷', '肉部', '星見食豕。令肉中生小息肉也。从肉星。星亦聲。', '肉;星', '星', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2705, '脂', 'zhī', '第四卷', '肉部', '戴角者脂，無角者膏。从肉，旨聲。', '肉;旨', '旨', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2706, '䐝', 'suò', '第四卷', '肉部', '䑁也。从肉，𧴪聲。', '肉;𧴪', '𧴪', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2707, '䑁', 'wù', '第四卷', '肉部', '膏肥皃。从肉，學省聲。', '肉;學', '學', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '应补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2708, '膩', 'nì', '第四卷', '肉部', '上肥也。从肉，貳聲。', '肉;貳', '貳', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2709, '膜', 'mó', '第四卷', '肉部', '肉閒胲膜也。从肉，莫聲。', '肉;莫', '莫', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2710, '䐞', 'ruò', '第四卷', '肉部', '肉表革裏也。从肉，弱聲。', '肉;弱', '弱', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2711, '𦞦', 'hè', '第四卷', '肉部', '肉羹也。从肉，隺聲。', '肉;隺', '隺', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2712, '膹', 'fèn', '第四卷', '肉部', '𦞦也。从肉，賁聲。', '肉;賁', '賁', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2713, '臇', 'juǎn', '第四卷', '肉部', '𦞦也。从肉，雋聲。讀若纂。', '肉;雋;纂', '雋;纂', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2714, '胾', 'zì', '第四卷', '肉部', '大臠也。从肉，𢦒聲。', '肉;𢦒', '𢦒', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2715, '䐑', 'zhé', '第四卷', '肉部', '薄切肉也。从肉，枼聲。', '肉;枼', '枼', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2716, '膾', 'kuài', '第四卷', '肉部', '細切肉也。从肉，會聲。', '肉;會', '會', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2717, '腌', 'yān', '第四卷', '肉部', '漬肉也。从肉，奄聲。', '肉;奄', '奄', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2718, '脃;脆', 'cuì', '第四卷', '肉部', '小耎易𣃔也。从肉，絕省聲。', '肉;絕', '絕', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2719, '膬', 'cuì', '第四卷', '肉部', '耎易破也。从肉，毳聲。', '肉;毳', '毳', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2720, '散;𢿨', 'sàn', '第四卷', '肉部', '襍肉也。从肉，𢽳聲。', '肉;𢽳', '𢽳', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2721, '膞', 'zhuǎn', '第四卷', '肉部', '切肉也。从肉，專聲。', '肉;專', '專', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2722, '腏', 'chuò', '第四卷', '肉部', '挑取骨閒肉也。从肉，叕聲。讀若詩曰「啜其泣矣」。', '肉;叕;啜', '叕;啜', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2723, '𦚙;胏', 'zǐ', '第四卷', '肉部', '食所遺也。从肉，仕聲。易曰「噬乾𦚙」。楊雄說𦚙从𠂔。', '肉;仕;𠂔', '仕;𠂔', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2724, '䐄', 'xiàn', '第四卷', '肉部', '食肉不猒也。从肉，臽聲。讀若陷。', '肉;臽;陷', '臽;陷', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2725, '肰', 'rán', '第四卷', '肉部', '犬肉也。从肉犬。讀若然。古文肰[利省声]。亦古文肰[然]。', '肉;犬;利;然', '利;然', 'null', NULL, '肉;犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2726, '䐜', 'chēn', '第四卷', '肉部', '起也。从肉，眞聲。', '肉;眞', '眞', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2727, '䏙', 'tǎn', '第四卷', '肉部', '肉汁滓也。从肉，冘聲。', '肉;冘', '冘', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2728, '膠', 'jiāo', '第四卷', '肉部', '昵也。作之㠯皮。从肉，翏聲。', '肉;翏', '翏', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2729, '𣎆;𦟀', 'luó', '第四卷', '肉部', '或曰嘼名。[肉。]象形。闕。', '肉', '', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2730, '䏣', 'qū', '第四卷', '肉部', '蠅乳肉中蟲也。从肉，且聲。', '肉;且', '且', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2731, '肙', 'yuàn', '第四卷', '肉部', '小蟲也。从肉囗。一曰空也。', '肉;囗', '', 'null', NULL, '肉;囗', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2732, '腐', 'fǔ', '第四卷', '肉部', '爛也。从肉，府聲。', '肉;府', '府', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2733, '肎;肯', 'kěn', '第四卷', '肉部', '骨閒肉肎肎箸也。从肉，从冎省。一曰骨無肉也。古文肎。', '肉;冎', '', 'null', NULL, '肉;冎', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2734, '脧', 'zuī', '第四卷', '肉部', '赤子陰也。从肉，夋聲。', '肉;夋', '夋', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '应补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2735, '䏿', 'qǐ', '第四卷', '肉部', '肥腸也。从肉，啟省聲。', '肉;啟', '啟', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2736, '腔', 'qiānɡ', '第四卷', '肉部', '內空也。从肉，从空。空亦聲。', '肉;空', '空', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2737, '𦚧', 'rùn', '第四卷', '肉部', '𦚧䏰，蟲名。漢中有𦚧䏰縣，地下多此蟲，因㠯爲名。从肉，旬聲。', '肉;旬', '旬', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2738, '䏰', 'chǔn', '第四卷', '肉部', '𦚧䏰也。从肉，忍聲。', '肉;忍', '忍', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2739, '腿', 'tuǐ', '第四卷', '肉部', '足也。从肉，退聲。读若妥。', '肉;退;妥', '退;妥', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '政宇新附', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2740, '筋', 'jīn', '第四卷', '筋部', '肉之力也。从肉力，从竹。竹，物之多筋者。凡筋之屬皆从筋。', '肉;力;竹', '', 'null', NULL, '肉;力;竹', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2741, '䇟;腱', 'jiàn', '第四卷', '筋部', '筋之本也。从筋省，夗省聲。䇟或从肉建。', '筋;夗;肉;建', '夗;建', 'null', NULL, '筋;肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2742, '𥭖', 'bó', '第四卷', '筋部', '手足指節鳴也。从筋省，勺聲。𥭖或省竹[肉]。', '筋;勺;肉', '勺', 'null', NULL, '筋;肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2743, '刀', 'dāo', '第四卷', '刀部', '兵也。象形。凡刀之屬皆从刀。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2744, '㓡', 'fǒu', '第四卷', '刀部', '刀握也。从刀，缶聲。', '刀;缶', '缶', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2745, '㓵;𠟎;㗉', 'è', '第四卷', '刀部', '刀劒刃也。从刀，㖾聲。籒文𠟎从㓞各。', '刀;㖾;㓞;各', '㖾;各', 'null', NULL, '刀;㓞', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2746, '削', 'xuē', '第四卷', '刀部', '鞞也。从刀，肖聲。一曰析也。', '刀;肖', '肖', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2747, '𠛎', 'ɡōu', '第四卷', '刀部', '鎌也。从刀，句聲。', '刀;句', '句', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2748, '剴', 'ɡāi', '第四卷', '刀部', '大鎌也。一曰摩也。从刀，豈聲。', '刀;豈', '豈', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2749, '剞', 'jī', '第四卷', '刀部', '剞𠜾，曲刀也。从刀，奇聲。', '刀;奇', '奇', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2750, '𠜾', 'jué', '第四卷', '刀部', '剞𠜾也。从刀，屈聲。', '刀;屈', '屈', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2751, '利;𥝢;𥝤', 'lì', '第四卷', '刀部', '銛也。刀和然後利。从刀，和省。易曰「利者，義之和也」。古文利[刃]。', '刀;和;刃', '', 'null', NULL, '刀;和;刃', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2752, '剡', 'yǎn', '第四卷', '刀部', '銳利也。从刀，炎聲。', '刀;炎', '炎', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2753, '初', 'chū', '第四卷', '刀部', '始也。从刀衣。裁衣之始也。', '刀;衣', '', 'null', NULL, '刀;衣', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2754, '剪', 'jiǎn', '第四卷', '刀部', '齊𣃔也。从刀，歬聲。', '刀;歬', '歬', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2755, '則', 'zé', '第四卷', '刀部', '等畫物也。从刀貝。貝，古之物貨也。古文則。亦古文則。籒文則从鼎。', '刀;貝;鼎', '', 'null', NULL, '刀;貝;鼎', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2756, '剛', 'ɡānɡ', '第四卷', '刀部', '彊𣃔也。从刀，岡聲。古文剛如此[二信]。', '刀;岡;二;㐰', '岡', 'null', NULL, '刀;二;㐰', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2757, '剬', 'duān', '第四卷', '刀部', '𣃔齊也。从刀，耑聲。', '刀;耑', '耑', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2758, '劊', 'ɡuì', '第四卷', '刀部', '𣃔也。从刀，會聲。', '刀;會', '會', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2759, '切', 'qiē', '第四卷', '刀部', '刌也。从刀，七聲。', '刀;七', '七', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2760, '刌', 'cǔn', '第四卷', '刀部', '切也。从刀，寸聲。', '刀;寸', '寸', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2761, '㔎', 'xiè', '第四卷', '刀部', '𣃔也。从刀，辥聲。', '刀;辥', '辥', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2762, '刉;刏', 'jī', '第四卷', '刀部', '劃傷也。从刀，气聲。一曰𣃔也。又讀若㱯。一曰刀不利，於瓦石上刉之。', '刀;气;㱯', '气;㱯', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2763, '劌', 'ɡuì', '第四卷', '刀部', '利傷也。从刀，歲聲。', '刀;歲', '歲', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2764, '刻', 'kè', '第四卷', '刀部', '鏤也。从刀，亥聲。古文刻[吅冊]。', '刀;亥;冊', '亥', 'null', NULL, '刀;冊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2765, '副;疈', 'pì', '第四卷', '刀部', '判也。从刀，畐聲。周禮曰「副辜祭」。籒文副从㽬。', '刀;畐;㽬', '畐', 'null', NULL, '刀;㽬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2766, '剖', 'pōu', '第四卷', '刀部', '判也。从刀，㕻聲。', '刀;㕻', '㕻', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2767, '辧;辨', 'biàn', '第四卷', '刀部', '判也。从刀，辡聲。', '刀;辡', '辡', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2768, '判', 'pàn', '第四卷', '刀部', '分也。从刀，半聲。', '刀;半', '半', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2769, '剫', 'duó', '第四卷', '刀部', '判也。从刀，度聲。', '刀;度', '度', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2770, '刳', 'kū', '第四卷', '刀部', '判也。从刀，夸聲。', '刀;夸', '夸', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2771, '列', 'liè', '第四卷', '刀部', '分解也。从刀，𡿪聲。', '刀;𡿪', '𡿪', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2772, '刊', 'kān', '第四卷', '刀部', '剟也。从刀，干聲。', '刀;干', '干', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2773, '剟', 'zhuō', '第四卷', '刀部', '刊也。从刀，叕聲。', '刀;叕', '叕', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2774, '刪', 'shān', '第四卷', '刀部', '剟也。从刀冊。冊，書也。', '刀;冊', '', 'null', NULL, '刀;冊', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2775, '劈', 'pì', '第四卷', '刀部', '破也。从刀，辟聲。', '刀;辟', '辟', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2776, '剥;剝', 'bō', '第四卷', '刀部', '裂也。从刀录。录，刻也。录亦聲。一曰剝，割也。剝或从卜。', '刀;录;卜', '录;卜', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2777, '割', 'ɡē', '第四卷', '刀部', '剝也。从刀，害聲。', '刀;害', '害', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2778, '剺', 'lí', '第四卷', '刀部', '剝也。劃也。从刀，𠩺聲。', '刀;𠩺', '𠩺', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2779, '劃', 'huá', '第四卷', '刀部', '錐刀畫曰劃。从刀畫。畫亦聲。', '刀;畫', '畫', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2780, '剈', 'yuān', '第四卷', '刀部', '挑取也。从刀，肙聲。一曰窐也。', '刀;肙', '肙', '宛', '剈、剜古今字。', '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2781, '劀', 'ɡuā', '第四卷', '刀部', '𠜜去惡創肉也。从刀，矞聲。', '刀;矞', '矞', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2782, '劑', 'jì', '第四卷', '刀部', '齊也。从刀。齊聲。', '刀;齊', '齊', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2783, '刷', 'shuā', '第四卷', '刀部', '𠜜也。从刀，㕞省聲。禮有「刷[帥]巾」。', '刀;㕞;帥', '㕞;帥', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2784, '刮;𠜜', 'ɡuā', '第四卷', '刀部', '掊杷也。从刀，𠯑聲。', '刀;𠯑', '𠯑', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2785, '剽', 'piào', '第四卷', '刀部', '砭刺也。从刀，票聲。一曰剽，劫也。', '刀;票', '票', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2786, '刲', 'kuī', '第四卷', '刀部', '刺也。从刀，圭聲。易曰「士刲羊」。', '刀;圭', '圭', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2787, '剉', 'cuò', '第四卷', '刀部', '折傷也。从刀，𡋲聲。', '刀;𡋲', '𡋲', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2788, '劋', 'jiǎo', '第四卷', '刀部', '絕也。从刀，喿聲。周書曰「天用劋[巢]絕其命」。', '刀;喿;巢', '喿;巢', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2789, '刖', 'yuè', '第四卷', '刀部', '絕也。从刀，月聲。古文或从尢。', '刀;月;尢', '月', 'null', NULL, '刀;尢', 'null', 'null', 'null', 'null', '政宇补重文', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2790, '刜', 'fú', '第四卷', '刀部', '擊也。从刀，弗聲。', '刀;弗', '弗', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2791, '㓼', 'chì', '第四卷', '刀部', '傷也。从刀，桼聲。', '刀;桼', '桼', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2792, '劖', 'chán', '第四卷', '刀部', '𣃔也。从刀，毚聲。一曰剽也。釗也。', '刀;毚', '毚', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2793, '刓', 'wán', '第四卷', '刀部', '剸也。从刀，元聲。', '刀;元', '元', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2794, '釗', 'zhāo', '第四卷', '刀部', '刓也。从刀金。', '刀;金', '刀', 'null', NULL, '金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2795, '制', 'zhì', '第四卷', '刀部', '裁也。从刀未。未，物成有滋味可裁𣃔。一曰止也。古文制如此[彡]。', '刀;未;彡', '', 'null', NULL, '刀;未;彡', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2796, '㓠', 'diàn', '第四卷', '刀部', '缺也。从刀，占聲。詩曰「白圭之㓠」。', '刀;占', '占', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2797, '罰', 'fá', '第四卷', '刀部', '辠之小者。从刀詈。未㠯刀有所賊，但持刀罵詈則應罰。', '刀;詈', '', 'null', NULL, '刀;詈', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2798, '刵', 'èr', '第四卷', '刀部', '𣃔耳也。从刀耳。', '刀;耳', '耳', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2799, '㓷;劓', 'yì', '第四卷', '刀部', '刖鼻也。从刀，臬聲。易曰「天且㓷」。㓷或从鼻。', '刀;臬;鼻', '臬', 'null', NULL, '刀;鼻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2800, '刑', 'xínɡ', '第四卷', '刀部', '剄也。从刀，开聲。', '刀;开', '开', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2801, '剄', 'jǐnɡ', '第四卷', '刀部', '刑也。从刀，巠聲。', '刀;巠', '巠', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2802, '𠟃', 'zǔn', '第四卷', '刀部', '減也。从刀，尊聲。', '刀;尊', '尊', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2803, '魝', 'jié', '第四卷', '刀部', '楚人謂治魚也。从刀魚。讀若鍥。', '刀;魚;鍥', '鍥', 'null', NULL, '刀;魚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2804, '券', 'quàn', '第四卷', '刀部', '契也。从刀，𢍏聲。券別之書，㠯刀判栔其旁，故曰「書契」。', '刀;𢍏', '𢍏', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2805, '刺', 'cì', '第四卷', '刀部', '君殺大夫曰刺。刺，直傷也。从刀朿。朿亦聲。', '刀;朿', '朿', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2806, '剔', 'tī', '第四卷', '刀部', '解骨也。从刀，易聲。', '刀;易', '易', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '大徐补 | 应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2807, '刎', 'wěn', '第四卷', '刀部', '剄也。从刀，勿聲。', '刀;勿', '勿', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2808, '剜', 'wān', '第四卷', '刀部', '削也。从刀，宛聲。', '刀;宛', '宛', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2809, '劇', 'jí', '第四卷', '刀部', '尤甚也。从刀，豦聲。', '刀;豦', '豦', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2810, '刹', 'chà', '第四卷', '刀部', '柱也。从刀，殺省聲。', '刀;殺', '殺', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2811, '刃', 'rèn', '第四卷', '刃部', '刀鋻也。象刀有刃之形。凡刃之屬皆从刃。', '刀', '', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2812, '刅;創', 'chuānɡ', '第四卷', '刃部', '傷也。从刃，从一。刅或从倉。', '刃;一;倉', '倉', 'null', NULL, '刃;一', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2813, '劒;劍', 'jiàn', '第四卷', '刃部', '人所帶兵也。从刃，僉聲。籒文劒从刀。', '刃;僉;刀', '僉', 'null', NULL, '刃;刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2814, '㓞', 'qià', '第四卷', '㓞部', '巧㓞也。从刀，丯聲。凡㓞之屬皆从㓞。', '刀;丯', '丯', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2815, '𠜵', 'jiá', '第四卷', '㓞部', '齘𠜵，刮也。从㓞，夬聲。一曰𠜵，畫堅也。', '㓞;夬', '夬', 'null', NULL, '㓞', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2816, '栔', 'qì', '第四卷', '㓞部', '刻也。从㓞木。', '㓞;木', '㓞', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2817, '丯', 'jiè', '第四卷', '丯部', '艸蔡也。象艸生之散亂也。凡丯之屬皆从丯。讀若介。', '介', '介', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2818, '𠲱', 'ɡé', '第四卷', '丯部', '枝𠲱也。从丯，各聲。', '丯;各', '各', 'null', NULL, '丯', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2819, '耒', 'lěi', '第四卷', '耒部', '耕曲木也。从木推丯。古者垂作耒枱，㠯振民也。凡耒之屬皆从耒。', '木;丯', '', 'null', NULL, '木;丯', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2820, '耕', 'ɡēnɡ', '第四卷', '耒部', '𤛿也。从耒井。古者井田，故从井。', '耒;井', '井', 'null', NULL, '耒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2821, '耦', 'ǒu', '第四卷', '耒部', '耕廣五寸爲伐。二伐爲耦。从耒，禺聲。', '耒;禺', '禺', 'null', NULL, '耒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2822, '耤', 'jí', '第四卷', '耒部', '帝耤千畞也。古者使民如借，故謂之藉。从耒，昔聲。', '借;耒;昔', '借;昔', 'null', NULL, '耒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2823, '𦓯', 'ɡuī', '第四卷', '耒部', '卌㕚可㠯劃麥，河內用之。从耒，圭聲。', '耒;圭', '圭', 'null', NULL, '耒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2824, '𦔐;𦶮', 'yún', '第四卷', '耒部', '除苗閒穢也。从耒，員聲。𦔐或从芸。', '耒;員;芸', '員;芸', 'null', NULL, '耒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2825, '耡', 'chú', '第四卷', '耒部', '殷人七十而耡。耡，耤稅也。从耒，助聲。周禮曰「㠯興耡利萌」。', '耒;助', '助', 'null', NULL, '耒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2826, '角', 'jiǎo', '第四卷', '角部', '獸角也。象形。角與刀、魚相佀。凡角之屬皆从角。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2827, '䚭', 'xuān', '第四卷', '角部', '揮角皃。从角，雚聲。梁 𨻳縣有䚭亭。又讀若𦄑。', '角;雚;𦄑', '雚;𦄑', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2828, '觻', 'lù', '第四卷', '角部', '角也。从角，樂聲。張掖有觻得縣。', '角;樂', '樂', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2829, '䚡', 'sāi', '第四卷', '角部', '角中骨也。从角，思聲。', '角;思', '思', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2830, '觠', 'quán', '第四卷', '角部', '曲角也。从角，𢍏聲。', '角;𢍏', '𢍏', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2831, '觬', 'ní', '第四卷', '角部', '角觬曲也。从角，兒聲。西河有觬氏縣。', '角;兒', '兒', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2832, '觢', 'shì', '第四卷', '角部', '一角仰也。从角，㓞聲。易曰「其牛觢」。', '角;㓞', '㓞', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2833, '䚦', 'zhì', '第四卷', '角部', '角傾也。从角，虒聲。', '角;虒', '虒', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2834, '觭', 'qī', '第四卷', '角部', '角一俛一仰也。从角，奇聲。', '角;奇', '奇', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2835, '觓', 'qiú', '第四卷', '角部', '角皃。从角，丩聲。詩曰「有觓[捄]其角」。', '角;丩;捄', '丩;捄', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2836, '𧤖', 'wēi', '第四卷', '角部', '角曲中也。从角，畏聲。', '角;畏', '畏', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2837, '𧢼', 'zhuó', '第四卷', '角部', '角長皃。从角。爿聲。讀若「粗𧢼」。', '角;爿;粗', '爿;粗', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2838, '𧤼', 'jué', '第四卷', '角部', '角有所觸發也。从角，厥聲。', '角;厥', '厥', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2839, '觸', 'chù', '第四卷', '角部', '牴也。从角，蜀聲。', '角;蜀', '蜀', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2840, '觲', 'xīnɡ', '第四卷', '角部', '用角低仰便也。从羊牛角。讀若詩曰「觲觲角弓」。', '羊;牛;角', '', 'null', NULL, '羊;牛;角', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2841, '䚗', 'ɡānɡ', '第四卷', '角部', '舉角也。从角，公聲。', '角;公', '公', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2842, '觷', 'xué', '第四卷', '角部', '治角也。从角，學省聲。', '角;學', '學', 'null', NULL, '角', 'null', 'null', 'null', 'null', '大徐补 | 当删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2843, '衡', 'hénɡ', '第四卷', '角部', '牛觸，橫大木。从角大，行聲。詩曰「設其楅衡」。古文衡如此[大𠧟]。', '角;大;行;大;𠧟', '行;𠧟', 'null', NULL, '角;大;大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2844, '𧤗', 'duān', '第四卷', '角部', '角𧤗，獸也。狀佀豕，角善爲弓。出胡尸國。一曰出休尸國。从角，耑聲。', '角;耑', '耑', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2845, '觰', 'zhā', '第四卷', '角部', '觰拏，獸也。从角，者聲。一曰下大者。', '角;者', '者', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2846, '觤', 'ɡuǐ', '第四卷', '角部', '羊角不齊也。从角，危聲。', '角;危', '危', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2847, '觟', 'huà', '第四卷', '角部', '牝羊角者也。从角，圭聲。', '角;圭', '圭', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2848, '觡', 'ɡé', '第四卷', '角部', '骨角之名也。从角，各聲。', '角;各', '各', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2849, '觜', 'zuǐ', '第四卷', '角部', '鴟舊頭上角觜也。一曰觜，觿也。从角，此聲。', '角;此', '此', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2850, '解', 'jiě', '第四卷', '角部', '判也。从刀判牛角。一曰解𢊁，獸也。', '刀;牛;角', '', 'null', NULL, '刀;牛;角', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2851, '觿', 'xī', '第四卷', '角部', '佩角銳耑。可㠯解結。从角，巂聲。詩曰「童子佩觿」。', '角;巂', '巂', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2852, '觵;觥', 'ɡōnɡ', '第四卷', '角部', '兕牛角可㠯㱃者也。从角，黃聲。其狀觵觵，故謂之觵。俗觵从光。', '角;黃;光', '黃;光', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2853, '觶', 'zhì', '第四卷', '角部', '鄉飲酒觶。从角，單聲。禮曰「一人洗舉觶」。觶受四升。觶或从辰。禮經觶[氏]。', '角;單;辰;氏', '單;辰;氏', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2854, '觛', 'dàn', '第四卷', '角部', '巵也。从角，旦聲。', '角;旦', '旦', 'null', NULL, '角', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2855, '觴', 'shānɡ', '第四卷', '角部', '實曰觴，虛曰觶。从角，𥏫省聲。籒文觴。或从爵省。', '角;𥏫;爵', '𥏫', 'null', NULL, '角;爵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2856, '觚', 'ɡū', '第四卷', '角部', '鄉飲酒之爵也。一曰觴受三升者觚。从角，瓜聲。', '角;瓜', '瓜', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2857, '䚙', 'xuān', '第四卷', '角部', '角匕也。从角，亘聲。讀若讙。', '角;亘;讙', '亘;讙', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2858, '䚫', 'xí', '第四卷', '角部', '杖耑角也。从角，敫聲。', '角;敫', '敫', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2859, '觼;鐍', 'jué', '第四卷', '角部', '環之有舌者。从角，敻聲。觼或从金矞。', '角;敻;金;矞', '敻;矞', 'null', NULL, '角;金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2860, '𧣚', 'nuò', '第四卷', '角部', '調弓也。从角，弱省聲。', '角;弱', '弱', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2861, '䚨', 'fèi', '第四卷', '角部', '隿射收繳具也。从角，發聲。', '角;發', '發', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2862, '𧤕', 'qiú', '第四卷', '角部', '隿射收繳具。从角，酋聲。讀若鰌。', '角;酋;鰌', '酋;鰌', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2863, '觳', 'hú', '第四卷', '角部', '盛觵巵也。一曰射具。从角，㱿聲。讀若斛。', '角;㱿;斛', '㱿;斛', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2864, '𧥑', 'bì', '第四卷', '角部', '羌人所龡角屠𧥑。㠯驚馬也。从角，𢨋聲。𢨋，古文誖字。', '角;𢨋', '𢨋', 'null', NULL, '角', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2865, '竹', 'zhú', '第五卷', '竹部', '冬生艸也。象形。下𠂹者，箁箬也。凡竹之屬皆从竹。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2866, '箭', 'jiàn', '第五卷', '竹部', '矢竹也。从竹，前聲。', '竹;前', '前', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2867, '箘', 'jùn', '第五卷', '竹部', '箘簬，竹也。从竹，囷聲。一曰簙棊也。', '竹;囷', '囷', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2868, '簬', 'lù', '第五卷', '竹部', '箘簬也。从竹，路聲。夏書曰「惟箘簬、枯」。古文簬从輅。', '竹;路;輅', '路;輅', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2869, '筱', 'xiǎo', '第五卷', '竹部', '箭屬。小竹也。从竹，攸聲。', '竹;攸', '攸', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2870, '簜', 'dànɡ', '第五卷', '竹部', '大竹也。从竹，湯聲。夏書曰「瑤琨筱簜」。簜可爲榦，筱可爲矢。', '竹;湯', '湯', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2871, '䉠', 'wéi', '第五卷', '竹部', '竹也。从竹，微聲。籒文从微省。', '竹;微', '微', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2872, '筍', 'sǔn', '第五卷', '竹部', '竹胎也。从竹，旬聲。', '竹;旬', '旬', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2873, '䈚', 'tái', '第五卷', '竹部', '竹萌也。从竹，怠聲。', '竹;怠', '怠', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2874, '箁', 'póu', '第五卷', '竹部', '竹箬也。从竹，咅聲。', '竹;咅', '咅', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2875, '箬', 'ruò', '第五卷', '竹部', '楚謂竹皮曰箬。从竹，若聲。', '竹;若', '若', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2876, '節', 'jié', '第五卷', '竹部', '竹約也。从竹，卽聲。', '竹;卽', '卽', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2877, '筡', 'tú', '第五卷', '竹部', '析竹笢也。从竹，余聲。讀若䋈。', '竹;余;䋈', '余;䋈', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2878, '𥵸', 'mí', '第五卷', '竹部', '筡也。从竹，𦤝聲。', '竹;𦤝', '𦤝', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2879, '笢', 'mǐn', '第五卷', '竹部', '竹膚也。从竹，民聲。', '竹;民', '民', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2880, '笨', 'bèn', '第五卷', '竹部', '竹裏也。从竹，本聲。', '竹;本', '本', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2881, '䈵', 'wēnɡ', '第五卷', '竹部', '竹皃。从竹，翁聲。', '竹;翁', '翁', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2882, '篸', 'chēn', '第五卷', '竹部', '篸差也。从竹，參聲。', '竹;參', '參', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2883, '篆', 'zhuàn', '第五卷', '竹部', '引書也。从竹，彖聲。', '竹;彖', '彖', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2884, '籒', 'zhòu', '第五卷', '竹部', '讀書也。从竹，㩅聲。春秋傳曰「卜籒」云。', '竹;㩅', '㩅', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2885, '篇', 'piān', '第五卷', '竹部', '書也。一曰關西謂榜篇。从竹，扁聲。', '竹;扁', '扁', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2886, '籍', 'jí', '第五卷', '竹部', '簿也。从竹，耤聲。', '竹;耤', '耤', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2887, '篁', 'huánɡ', '第五卷', '竹部', '竹田也。从竹，皇聲。', '竹;皇', '皇', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2888, '䉃', 'jiǎnɡ', '第五卷', '竹部', '剖竹未去節謂之䉃。从竹，將聲。', '竹;將', '將', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2889, '䈎', 'yè', '第五卷', '竹部', '籥也。从竹，枼聲。', '竹;枼', '枼', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2890, '籥', 'yuè', '第五卷', '竹部', '書僮竹笘也。从竹，龠聲。', '竹;龠', '龠', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2891, '䉧', 'liú', '第五卷', '竹部', '竹聲也。从竹，劉聲。', '竹;劉', '劉', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2892, '𥳑;簡', 'jiǎn', '第五卷', '竹部', '牒也。从竹，閒聲。', '竹;閒', '閒', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2893, '笐', 'hánɡ', '第五卷', '竹部', '竹列也。从竹，亢聲。', '竹;亢', '亢', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2894, '篰', 'bù', '第五卷', '竹部', '㒼爰也。从竹，部聲。', '竹;部', '部', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2895, '等', 'děnɡ', '第五卷', '竹部', '齊𥳑也。从竹寺。寺，官曹之等平也。', '竹;寺', '', 'null', NULL, '竹;寺', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2896, '笵', 'fàn', '第五卷', '竹部', '法也。从竹，氾聲。竹，𥳑書也。古法有「竹㓝」。', '竹;氾', '氾', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2897, '箋', 'jiān', '第五卷', '竹部', '表識書也。从竹，戔聲。', '竹;戔', '戔', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2898, '符', 'fú', '第五卷', '竹部', '信也。漢制㠯竹，長六寸，分而相合。从竹，付聲。', '竹;付', '付', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2899, '筮;𥷂', 'shì', '第五卷', '竹部', '易卦用蓍也。从竹𢍮。𢍮，古文巫字。', '竹;𢍮', '', 'null', NULL, '竹;𢍮', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2900, '笄', 'jī', '第五卷', '竹部', '兂也。从竹，开聲。', '竹;开', '开', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2901, '䇫', 'jī', '第五卷', '竹部', '取蟣比也。从竹，𦣞聲。', '竹;𦣞', '𦣞', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2902, '籆;𧤽;篗', 'yuè', '第五卷', '竹部', '所㠯收絲者也。从竹，蒦聲。籆或从角閒。', '竹;蒦;角;閒', '蒦', 'null', NULL, '竹;角;閒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2903, '筳', 'tínɡ', '第五卷', '竹部', '繀絲筦也。从竹，廷聲。', '竹;廷', '廷', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2904, '筦', 'ɡuǎn', '第五卷', '竹部', '筟也。从竹，完聲。', '竹;完', '完', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2905, '筟', 'fū', '第五卷', '竹部', '筳也。从竹，孚聲。讀若春秋「魯 公子彄」。', '竹;孚;彄', '孚;彄', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2906, '簾', 'lián', '第五卷', '竹部', '堂簾也。从竹，廉聲。', '竹;廉', '廉', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2907, '笮', 'zé', '第五卷', '竹部', '迫也。在瓦之下、棼上。从竹，乍聲。', '竹;乍', '乍', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2908, '簀', 'zé', '第五卷', '竹部', '牀棧也。从竹，責聲。', '竹;責', '責', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2909, '笫', 'zǐ', '第五卷', '竹部', '簀也。从竹，𠂔聲。', '竹;𠂔', '𠂔', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2910, '筵', 'yán', '第五卷', '竹部', '竹席也。从竹，延聲。周禮曰「度堂㠯筵」。筵一丈。', '竹;延', '延', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2911, '簟', 'diàn', '第五卷', '竹部', '竹席也。从竹，覃聲。', '竹;覃', '覃', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2912, '籧', 'qú', '第五卷', '竹部', '籧篨，粗竹席也。从竹，遽聲。', '竹;遽', '遽', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2913, '篨', 'chú', '第五卷', '竹部', '籧篨也。从竹，除聲。', '竹;除', '除', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2914, '籭', 'shāi', '第五卷', '竹部', '竹器也。可㠯取麤去細。从竹，麗聲。', '竹;麗', '麗', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '麗轨', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2915, '籓', 'fān', '第五卷', '竹部', '大箕也。从竹，潘聲。一曰蔽也。', '竹;潘', '潘', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2916, '䉛', 'yù', '第五卷', '竹部', '漉米籔也。从竹，奧聲。', '竹;奧', '奧', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2917, '籔', 'sǒu', '第五卷', '竹部', '炊䉛也。从竹，數聲。', '竹;數', '數', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2918, '箅', 'bì', '第五卷', '竹部', '蔽也。所㠯蔽甑底。从竹，畀聲。', '竹;畀', '畀', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2919, '𥳓', 'shāo', '第五卷', '竹部', '飯筥也，受五升。从竹，稍聲。秦謂筥曰𥳓。', '竹;稍', '稍', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2920, '䈰', 'shāo', '第五卷', '竹部', '陳畱謂飯帚曰䈰。从竹，捎聲。一曰飯器，容五升。一曰宋、魏謂箸筩爲䈰。', '竹;捎', '捎', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2921, '筥', 'jǔ', '第五卷', '竹部', '䈰也。从竹，呂聲。', '竹;呂', '呂', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2922, '笥', 'sì', '第五卷', '竹部', '飯及衣之器也。从竹，司聲。', '竹;司', '司', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2923, '篋', 'qiè', '第五卷', '竹部', '笥也。从竹，匧聲。', '竹;匧', '匧', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '政宇应独立', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2924, '簞', 'dān', '第五卷', '竹部', '笥也。从竹，單聲。漢律令「簞，小筐也」。傳曰「簞食壺漿」。', '竹;單', '單', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2925, '簁', 'shāi', '第五卷', '竹部', '簁箄，竹器也。从竹，徙聲。', '竹;徙', '徙', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2926, '箄', 'bǐ', '第五卷', '竹部', '簁箄也。从竹，卑聲。', '竹;卑', '卑', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2927, '篿', 'tuán', '第五卷', '竹部', '圜竹器也。从竹，專聲。', '竹;專', '專', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
