INSERT INTO `shuowen_voice_revel` VALUES (2931, '籃', 'lán', '第五卷', '竹部', '大篝也。从竹，監聲。古文籃如此[艸，席省]。', '竹;監;艸;席', '監;席', 'null', NULL, '竹;艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2932, '篝', 'ɡōu', '第五卷', '竹部', '笿也。可熏衣。从竹，冓聲。宋、楚謂竹篝「牆居」也。', '竹;冓', '冓', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
