INSERT INTO `shuowen_voice_revel` VALUES (2939, '簋;匭', 'ɡuǐ', '第五卷', '竹部', '黍稷方器也。从竹皿皀。古文簋从匚食九。古文簋从匚軌。亦古文簋[木]。', '竹;皿;皀;匚;食;九;匚;軌;木', '九;軌', 'null', NULL, '竹;皿;皀;匚;食;匚;木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2940, '簠', 'fǔ', '第五卷', '竹部', '黍稷圜器也。从竹皿，甫聲。古文簠从匚夫。', '竹;皿;甫;匚;夫', '甫;夫', 'null', NULL, '竹;皿;匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2941, '𥸅;籩', 'biān', '第五卷', '竹部', '竹豆也。从竹，𨘢聲。籒文𥸅[匚𦤝]。', '竹;邊;匚;𦤝', '𨘢;𦤝', 'null', NULL, '竹;匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2942, '𥫱', 'dùn', '第五卷', '竹部', '篅也。从竹，屯聲。', '竹;屯', '屯', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2943, '篅', 'chuán', '第五卷', '竹部', '㠯判竹。圜㠯盛穀者。从竹，耑聲。', '竹;耑', '耑', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
