INSERT INTO `shuowen_voice_revel` VALUES (2950, '籱;籗', 'zhuó', '第五卷', '竹部', '罩魚者也。从竹，靃聲。籱或从寉。', '竹;靃;寉', '靃;寉', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
