INSERT INTO `shuowen_voice_revel` VALUES (2955, '箑', 'shà', '第五卷', '竹部', '扇也。从竹，疌聲。箑或从妾。', '竹;疌;妾', '疌;妾', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2956, '籠', 'lónɡ', '第五卷', '竹部', '擧土器。一曰笭也。从竹，龍聲。', '竹;龍', '龍', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2957, '䉴', 'ránɡ', '第五卷', '竹部', '袌也。从竹，襄聲。', '竹;襄', '襄', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2958, '䇘;互', 'hù', '第五卷', '竹部', '可㠯收繩者也。从竹。[互]象形。中象人手所推握也。䇘或省。', '竹;互', '互', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2959, '簝', 'liáo', '第五卷', '竹部', '宗廟盛肉竹器也。从竹，尞聲。周禮「供盆財㠯待事」。', '竹;尞', '尞', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
