INSERT INTO `shuowen_voice_revel` VALUES (2963, '籚', 'lú', '第五卷', '竹部', '積竹矛、𢧢矝也。从竹，盧聲。春秋國語曰「朱儒扶籚」。', '竹;盧', '盧', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2964, '箝', 'qián', '第五卷', '竹部', '籋也。从竹，拑聲。', '竹;拑', '拑', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2965, '籋', 'niè', '第五卷', '竹部', '箝也。从竹，爾聲。', '竹;爾', '爾', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2966, '簦', 'dēnɡ', '第五卷', '竹部', '笠葢也。从竹，登聲。', '竹;登', '登', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2967, '笠', 'lì', '第五卷', '竹部', '簦無柄也。从竹，立聲。', '竹;立', '立', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2968, '箱', 'xiānɡ', '第五卷', '竹部', '大車牝服也。从竹，相聲。', '竹;相', '相', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2969, '篚', 'fěi', '第五卷', '竹部', '車笭也。从竹，匪聲。', '竹;匪', '匪', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2970, '笭', 'línɡ', '第五卷', '竹部', '車笭也。从竹，令聲。一曰笭，籯也。', '竹;令', '令', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2971, '𥰨', 'tán', '第五卷', '竹部', '所㠯搔馬也。从竹，剡聲。', '竹;剡', '剡', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2972, '策', 'cè', '第五卷', '竹部', '馬箠也。从竹，朿聲。', '竹;朿', '朿', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2973, '箠', 'chuí', '第五卷', '竹部', '所㠯擊馬也。从竹，垂聲。', '竹;垂', '垂', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2974, '𥬲', 'zhuā', '第五卷', '竹部', '箠也。从竹，朵聲。', '竹;朵', '朵', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2975, '笍', 'zhuì', '第五卷', '竹部', '羊車騶箠也。箸箴其耑，長半分。从竹，內聲。', '竹;內', '內', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2976, '籣', 'lán', '第五卷', '竹部', '所㠯盛弩矢。人所負也。从竹，闌聲。', '竹;闌', '闌', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2977, '箙', 'fú', '第五卷', '竹部', '弩矢箙也。从竹，服聲。周禮「仲秋獻矢箙」。', '竹;服', '服', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2978, '䇬', 'zhū', '第五卷', '竹部', '栙雙也。从竹，朱聲。', '竹;朱', '朱', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2979, '笘', 'shān', '第五卷', '竹部', '折竹箠也。从竹，占聲。潁川人名小兒所書寫爲笘。', '竹;占', '占', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2980, '笪', 'dá', '第五卷', '竹部', '笞也。从竹，旦聲。', '竹;旦', '旦', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2981, '笞', 'chī', '第五卷', '竹部', '擊也。从竹，台聲。', '竹;台', '台', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2982, '籤', 'qiān', '第五卷', '竹部', '驗也。一曰𨦣也，貫也。从竹，韱聲。', '竹;韱', '韱', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2983, '𥴫', 'tún', '第五卷', '竹部', '榜也。从竹，殿聲。', '竹;殿', '殿', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2984, '箴', 'zhēn', '第五卷', '竹部', '綴衣箴也。从竹，咸聲。', '竹;咸', '咸', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2985, '箾', 'shuò', '第五卷', '竹部', '㠯竿擊人也。从竹，削聲。虞舜樂曰「箾韶」。', '竹;削', '削', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2986, '竽', 'yú', '第五卷', '竹部', '管樂也。三十六簧。从竹，虧聲。', '竹;虧', '虧', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2987, '笙', 'shēnɡ', '第五卷', '竹部', '十三簧。象鳳之身也。笙，正月之音。物生，故謂之笙。大者謂之巢。小者謂之和。从竹生。古者隨作笙。', '竹;生', '生', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2988, '簧', 'huánɡ', '第五卷', '竹部', '笙中簧也。从竹，黃聲。古者女媧作簧。', '竹;黃', '黃', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2989, '䈕', 'shí', '第五卷', '竹部', '簧屬。从竹，是聲。', '竹;是', '是', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2990, '簫', 'xiāo', '第五卷', '竹部', '參差管樂。象鳳之翼。从竹，肅聲。', '竹;肅', '肅', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2991, '筒', 'dònɡ', '第五卷', '竹部', '通簫也。从竹，同聲。', '竹;同', '同', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2992, '籟', 'lài', '第五卷', '竹部', '三孔龠也。大者謂之「笙」，其中謂之「籟」，小者謂之「箹」。从竹，賴聲。', '竹;賴', '賴', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2993, '箹', 'yuè', '第五卷', '竹部', '小籟也。从竹，約聲。', '竹;約', '約', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2994, '管;琯', 'ɡuǎn', '第五卷', '竹部', '如篪。六孔。十二月之音。物開地牙，故謂之管。从竹，官聲。古者管㠯玉。舜之時，西王母來獻其白琯。前零陵文學姓奚，於泠道 舜祠下得「笙玉琯」。夫㠯玉作音，故「神人㠯和，鳳皇來儀」也。从玉，官聲。', '竹;官;玉', '官', 'null', NULL, '竹;玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2995, '篎', 'miǎo', '第五卷', '竹部', '小管謂之篎。从竹，眇聲。', '竹;眇', '眇', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2996, '笛', 'dí', '第五卷', '竹部', '七孔筩也。从竹，由聲。羌笛三孔。', '竹;由', '由', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2997, '筑', 'zhú', '第五卷', '竹部', '㠯竹曲五弦之樂也。从巩竹。巩，持之也。竹亦聲。', '巩;竹', '竹', 'null', NULL, '巩', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2998, '箏', 'zhēnɡ', '第五卷', '竹部', '五弦筑身樂也。从竹，爭聲。', '竹;爭', '爭', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2999, '箛', 'ɡū', '第五卷', '竹部', '吹鞭也。从竹，孤聲。', '竹;孤', '孤', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3000, '篍', 'qiū', '第五卷', '竹部', '吹筩也。从竹，秋聲。', '竹;秋', '秋', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3001, '籌', 'chóu', '第五卷', '竹部', '壺矢也。从竹，壽聲。', '竹;壽', '壽', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3002, '簺', 'sài', '第五卷', '竹部', '行棊相塞謂之簺。从竹塞。塞亦聲。', '竹;塞', '塞', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3003, '簙', 'bó', '第五卷', '竹部', '局戲也。六箸十二棊也。从竹，博聲。古者烏曹作簙。', '竹;博', '博', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3004, '篳', 'bì', '第五卷', '竹部', '藩落也。从竹，畢聲。春秋傳曰「篳門圭窬」。', '竹;畢', '畢', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3005, '𥴨', 'ài', '第五卷', '竹部', '蔽不見也。从竹，愛聲。', '竹;愛', '愛', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '愛轨', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3006, '䉷', 'yán', '第五卷', '竹部', '隿射者所蔽者也。从竹，嚴聲。', '竹;嚴', '嚴', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3007, '篽;𩵎', 'yǔ', '第五卷', '竹部', '禁苑也。从竹，御聲。春秋傳曰「澤之自[舟]篽[虞]」。篽或作𩵎。从又，从魚。', '竹;御;虞;又;魚', '御;虞;魚', 'null', NULL, '竹;又', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3008, '筭', 'suàn', '第五卷', '竹部', '長六寸，所㠯計曆數者。从竹弄。言常弄乃不誤也。', '竹;弄', '', 'null', NULL, '竹;弄', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3009, '算', 'suàn', '第五卷', '竹部', '數也。从竹具。讀若筭。', '竹;具;筭', '筭', 'null', NULL, '竹;具', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3010, '𥬇;笑', 'xiào', '第五卷', '竹部', '喜也。从竹，从犬。', '竹;犬', '', 'null', NULL, '竹;犬', 'null', 'null', 'null', 'null', '大徐补', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3011, '第', 'dì', '第五卷', '竹部', '次也。从竹弟。', '竹;弟', '弟', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '应补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3012, '簃', 'yí', '第五卷', '竹部', '閣邊小屋也。从竹，移聲。', '竹;移', '移', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3013, '筠', 'yún', '第五卷', '竹部', '竹皮也。从竹，均聲。', '竹;均', '均', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3014, '笏', 'hù', '第五卷', '竹部', '公及士所搢也。从竹，勿聲。', '竹;勿', '勿', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3015, '篦', 'bì', '第五卷', '竹部', '導也。从竹，𣬉聲。', '竹;𣬉', '𣬉', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3016, '篙', 'ɡāo', '第五卷', '竹部', '所㠯進船也。从竹，高聲。', '竹;高', '高', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3017, '箍', 'gū', '第五卷', '竹部', '㠯篾固匌之。从竹。从手。匝，匡也。俗帀。', '竹;手;匝;帀', '', 'null', NULL, '竹;手;匝;帀', 'null', 'null', 'null', 'null', '政宇新附', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3018, '箕;其', 'jī', '第五卷', '箕部', '所㠯簸者也。从竹。𠀠，象形。丌，其下也。凡箕之屬皆从箕。古文箕。亦古文箕[廾]。亦古文箕。籒文箕。籒文箕[匚]。', '竹;𠀠;丌;廾;其;匚', '', 'null', NULL, '竹;𠀠;丌;廾;匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3019, '簸', 'bǒ', '第五卷', '箕部', '揚米去𥹺也。从箕，皮聲。', '箕;皮', '皮', 'null', NULL, '箕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3020, '丌', 'jī', '第五卷', '丌部', '下基也。荐物之丌。象形。凡丌之屬皆从丌。讀若箕同。', '箕', '箕', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3021, '䢋', 'jì', '第五卷', '丌部', '古之遒人㠯木鐸記詩言。从辵丌。丌亦聲。讀與記同。', '辵;丌;記', '丌;記', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3022, '典', 'diǎn', '第五卷', '丌部', '五帝之書也。从冊在丌上，尊閣之也。莊都說，典，大冊也。古文典从竹。', '冊;丌;竹', '', 'null', NULL, '冊;丌;竹', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3023, '畀', 'bì', '第五卷', '丌部', '相付与之。約在閣上也。从丌，甶聲。', '丌;甶', '甶', 'null', NULL, '丌', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3024, '巽', 'xùn', '第五卷', '丌部', '具也。从丌，𠨖聲。古文巽。篆文巽。', '丌;𠨖;开', '𠨖', 'null', NULL, '丌;开', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3025, '顨', 'xùn', '第五卷', '丌部', '巽也。从丌，从𩔊。此易「顨[巽]卦爲長女、爲風」者。', '丌;𩔊;巽', '巽', 'null', NULL, '丌;𩔊', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3026, '奠', 'diàn', '第五卷', '丌部', '置祭也。从酋。酋，酒也。丌，其下也。禮有「奠祭」。', '酋;丌', '', 'null', NULL, '酋;丌', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3027, '左', 'zuǒ', '第五卷', '左部', '𠂇手相左也。从𠂇工。凡左之屬皆从左。', '𠂇;工', '𠂇', 'null', NULL, '工', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3028, '差', 'chā', '第五卷', '左部', '貳也。差不相值也。从左𠂹。籒文差从二。', '左;𠂹;二', '左', 'null', NULL, '𠂹;二', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3029, '工', 'ɡōnɡ', '第五卷', '工部', '巧飾也。象人有規榘。與巫同意。凡工之屬皆从工。古文工从彡。', '彡', '', 'null', NULL, '彡', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3030, '式', 'shì', '第五卷', '工部', '法也。从工，弋聲。', '工;弋', '弋', 'null', NULL, '工', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3031, '巧', 'qiǎo', '第五卷', '工部', '技也。从工，丂聲。', '工;丂', '丂', 'null', NULL, '工', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3032, '巨;榘;矩', 'jù', '第五卷', '工部', '規巨也。从工。象手持之。巨或从木矢。矢者，其中正也。古文巨。', '工;木;矢', '', 'null', NULL, '工;木;矢', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3033, '㠭', 'zhǎn', '第五卷', '㠭部', '極巧視之也。从四工。凡㠭之屬皆从㠭。', '工', '', 'null', NULL, '工', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3034, '𡫳', 'sè', '第五卷', '㠭部', '窒也。从㠭，从廾，窒宀中。㠭猶齊也。', '㠭;廾;宀', '', 'null', NULL, '㠭;廾;宀', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3035, '巫;𢍮', 'wū', '第五卷', '巫部', '祝女也。能事無形，㠯舞降神者也。[工]象人㒳褎舞形。與工同意。古者巫咸初作巫。凡巫之屬皆从巫。古文巫[吅廾]。', '工;吅;廾', '', 'null', NULL, '工;吅;廾', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3036, '覡', 'xí', '第五卷', '巫部', '能齊肅事神明者。在男曰覡，在女曰巫。从巫見。', '巫;見', '', 'null', NULL, '巫;見', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3037, '甘', 'ɡān', '第五卷', '甘部', '美也。从口含一。一，道也。凡甘之屬皆从甘。', '口;一', '', 'null', NULL, '口;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3038, '甜;甛', 'tián', '第五卷', '甘部', '美也。从甘舌[㐁]。舌，知甘者。', '甘;㐁', '㐁', 'null', NULL, '甘', 'null', 'null', 'null', 'null', '侵部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3039, '𤯌', 'ɡān', '第五卷', '甘部', '和也。从甘厤。厤，調也。甘亦聲。讀若圅。', '甘;厤;圅', '甘;圅', 'null', NULL, '厤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3040, '猒', 'yān', '第五卷', '甘部', '飽也。足也。从甘肰。猒或从㠯。', '甘;肰;㠯', '', 'null', NULL, '甘;肰;㠯', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3041, '甚', 'shèn', '第五卷', '甘部', '尤安樂也。从甘匹。匹，耦也。古文甚[口]。', '甘;匹;口', '', 'null', NULL, '甘;匹;口', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3042, '旨', 'zhǐ', '第五卷', '旨部', '美也。从甘，匕聲。凡旨之屬皆从旨。古文旨[千]。', '甘;匕;千', '匕', 'null', NULL, '甘;千', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3043, '嘗;甞', 'chánɡ', '第五卷', '旨部', '口味之也。从旨，尙聲。', '旨;尙', '尙', 'null', NULL, '旨', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3044, '曰', 'yuē', '第五卷', '曰部', '䛐也。从口。𠃊，象口气出也。凡曰之屬皆从曰。', '口;𠃊', '', 'null', NULL, '口;𠃊', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3045, '𣆑', 'cè', '第五卷', '曰部', '告也。从曰，从冊。冊亦聲。', '曰;冊', '冊', 'null', NULL, '曰', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3046, '曷', 'hé', '第五卷', '曰部', '何也。从曰，匃聲。', '曰;匃', '匃', 'null', NULL, '曰', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3047, '曶', 'hū', '第五卷', '曰部', '出气䛐也。从曰，象气出形。春秋傳曰「鄭大子曶[忽]」。籒文曶。一曰佩也。象形。', '曰;忽', '忽', 'null', NULL, '曰', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3048, '朁', 'cǎn', '第五卷', '曰部', '曾也。从曰，兓聲。詩曰「朁不畏朙」。', '曰;兓', '兓', 'null', NULL, '曰', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3049, '沓', 'tà', '第五卷', '曰部', '語多沓沓也。从水曰。遼東有沓縣。', '水;曰', '', 'null', NULL, '水;曰', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3050, '曹', 'cáo', '第五卷', '曰部', '獄㒳曹也。从㯥，在廷東也。从曰，治事者也。', '㯥;曰', '', 'null', NULL, '㯥;曰', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3051, '乃', 'nǎi', '第五卷', '乃部', '申䛐之難也。象气之出難也。凡乃之屬皆从乃。古文乃。籒文乃。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3052, '𠧟', 'rénɡ', '第五卷', '乃部', '驚聲也。从乃省，卤[西]聲。籒文𠧟不省。或曰𠧟，往也。讀若仍。古文𠧟。', '乃;西;仍', '西;仍', 'null', NULL, '乃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3053, '逌', 'yóu', '第五卷', '乃部', '气行皃。从乃，𠧪聲。讀若攸。', '乃;𠧪;攸', '𠧪;攸', 'null', NULL, '乃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3054, '丂', 'kǎo', '第五卷', '丂部', '气欲舒出，ㄅ上礙於一也。丂，古文㠯爲亏字。又㠯爲巧字。凡丂之屬皆从丂。', '亏;巧', '亏;巧', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3055, '甹', 'pīnɡ', '第五卷', '丂部', '亟䛐也。从丂，从由。或曰甹，俠也。三輔謂輕財者爲甹。', '丂;由', '', 'null', NULL, '丂;由', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3056, '寧', 'nínɡ', '第五卷', '丂部', '願䛐也。从丂，寍聲。', '丂;寍', '寍', 'null', NULL, '丂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3057, '𠀀', 'hē', '第五卷', '丂部', '反丂也。讀若呵[可]。', '丂;可', '可', 'null', NULL, '丂', 'null', 'null', 'null', 'null', '⚙️△', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3058, '可', 'kě', '第五卷', '可部', '肎也。从口𠀀。𠀀亦聲。凡可之屬皆从可。', '口;𠀀', '𠀀', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3059, '奇', 'qí', '第五卷', '可部', '異也。一曰不耦。从大，从可。', '大;可', '可', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3060, '哿', 'ɡě', '第五卷', '可部', '可也。从可，加聲。詩曰「哿矣富人」。', '可;加', '可;加', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3061, '哥', 'ɡē', '第五卷', '可部', '聲也。从二可。古文㠯爲歌字。', '可;歌', '可;歌', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3062, '叵', 'pǒ', '第五卷', '可部', '不可也。从反可。', '可', '', 'null', NULL, '可', 'null', 'null', 'null', 'null', '新', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3063, '兮', 'xī', '第五卷', '兮部', '語所稽也。从丂八。象气越亏也。凡兮之屬皆从兮。', '丂;八', '', 'null', NULL, '丂;八', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3064, '𠣬;𠣤', 'sǔn', '第五卷', '兮部', '驚䛐也。从兮，旬聲。𠣤或从心。', '兮;旬;心', '旬', 'null', NULL, '兮;心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3065, '羲', 'xī', '第五卷', '兮部', '气也。从兮，義聲。', '兮;義', '義', 'null', NULL, '兮', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3066, '乎', 'hū', '第五卷', '兮部', '語之餘也。从兮。象聲上越揚之形也。', '兮', '', 'null', NULL, '兮', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3067, '号', 'háo', '第五卷', '号部', '痛聲也。从口在丂上。凡号之屬皆从号。', '口;丂', '丂', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3068, '號', 'háo', '第五卷', '号部', '嘑也。从号，从虎。', '号;虎', '号', 'null', NULL, '虎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3069, '亏;于', 'yú', '第五卷', '亏部', '於也。象气之舒亏。从丂，从一。一者，其气平也。凡亏之屬皆从亏。', '丂;一', '', 'null', NULL, '丂;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3070, '虧;𧇾', 'kuī', '第五卷', '亏部', '气損也。从亏，雐聲。虧或从兮。', '亏;雐;兮', '雐', 'null', NULL, '亏;兮', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3071, '粵;粤', 'yuè', '第五卷', '亏部', '亏也。宷愼之䛐也。从宷亏。周書曰「粤[越]三日丁亥」。', '宷;亏;越', '越', 'null', NULL, '宷;亏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3072, '吁', 'xū', '第五卷', '亏部', '驚語也。从口亏。亏亦聲。', '口;亏', '亏', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3073, '平', 'pínɡ', '第五卷', '亏部', '語平舒也。从亏八。八，分也。爰禮說。古文平如此。', '亏;八', '', 'null', NULL, '亏;八', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3074, '喜', 'xǐ', '第五卷', '喜部', '樂也。从壴，从口。凡喜之屬皆从喜。古文喜从欠。與歡同。', '壴;口;欠', '', 'null', NULL, '壴;口;欠', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3075, '憙', 'xǐ', '第五卷', '喜部', '說也。从心喜。喜亦聲。', '心;喜', '喜', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3076, '嚭', 'pǐ', '第五卷', '喜部', '大也。从喜，否聲。春秋傳吳有大宰嚭。', '喜;否', '否', 'null', NULL, '喜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3077, '壴', 'zhù', '第五卷', '壴部', '陳樂，立而上見也。从屮豆。凡壴之屬皆从壴。', '屮;豆', '豆', 'null', NULL, '屮', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3078, '尌', 'shù', '第五卷', '壴部', '立也。从壴，从寸。寸，持之也。讀若駐。', '壴;寸;駐', '壴;駐', 'null', NULL, '寸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3079, '𧒕', 'qì', '第五卷', '壴部', '夜戒守𡔷也。从壴。蚤聲。禮「昏𡔷四通爲『大𡔷』，夜半三通爲『戒晨』，旦五通爲『發朙』」。讀若戚。', '壴;蚤;戚', '蚤;戚', 'null', NULL, '壴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3080, '彭', 'pénɡ', '第五卷', '壴部', '𡔷聲也。从壴。从彡。', '壴;彡', '', 'null', NULL, '壴;彡', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3081, '嘉', 'jiā', '第五卷', '壴部', '美也。从壴，加聲。', '壴;加', '加', 'null', NULL, '壴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3082, '𡔷;鼓', 'ɡǔ', '第五卷', '𡔷部', '郭也。春分之音，萬物郭皮甲而出，故曰𡔷。从壴，从屮又。屮，象𠂹飾。又，象其手擊之也。周禮「六𡔷」——靁𡔷八面；靈𡔷六面；路𡔷四面；鼖𡔷、皋𡔷、晉𡔷皆㒳面。凡𡔷之屬皆从𡔷。籒文𡔷从古。', '壴;屮;又;古', '壴;古', 'null', NULL, '屮;又', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3083, '鼛', 'ɡāo', '第五卷', '𡔷部', '大𡔷也。从𡔷，咎聲。詩曰「鼛𡔷不勝」。', '𡔷;咎', '咎', 'null', NULL, '𡔷', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3084, '鼖', 'fén', '第五卷', '𡔷部', '大𡔷謂之鼖。鼖，八尺而㒳面，㠯𡔷軍事。从𡔷，卉聲。鼖或从革，賁聲。', '𡔷;卉;革;賁', '卉;賁', 'null', NULL, '𡔷;革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3085, '鼙', 'pí', '第五卷', '𡔷部', '騎𡔷也。从𡔷，卑聲。', '𡔷;卑', '卑', 'null', NULL, '𡔷', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3086, '𪔳', 'lónɡ', '第五卷', '𡔷部', '𡔷聲也。从𡔷，隆聲。', '𡔷;隆', '隆', 'null', NULL, '𡔷', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3087, '鼘', 'yuān', '第五卷', '𡔷部', '鼘鼘，𡔷聲也。从𡔷，𣶒聲。詩曰「鼗𡔷鼘鼘」。', '𡔷;𣶒', '𣶒', 'null', NULL, '𡔷', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3088, '鼞', 'tānɡ', '第五卷', '𡔷部', '𡔷聲也。从𡔷，堂聲。詩曰「擊𡔷其鼞」。', '𡔷;堂', '堂', 'null', NULL, '𡔷', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3089, '䶀', 'tà', '第五卷', '𡔷部', '鼙聲也。从𡔷，合聲。古文䶀从革。', '𡔷;合;革', '合', 'null', NULL, '𡔷;革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3090, '𪔪', 'qì', '第五卷', '𡔷部', '𡔷無聲也。从𡔷，咠聲。', '𡔷;咠', '咠', 'null', NULL, '𡔷', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3091, '𪔕', 'tà', '第五卷', '𡔷部', '𡔷鼙聲。从𡔷，缶聲。', '𡔷;缶', '缶', 'null', NULL, '𡔷', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3092, '豈', 'qǐ', '第五卷', '豈部', '還師振旅樂也。一曰欲登也。从豆，𢼸省聲。凡豈之屬皆从豈。', '豆;𢼸', '𢼸', 'null', NULL, '豆', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3093, '愷', 'kǎi', '第五卷', '豈部', '康也。从豈心。豈亦聲。', '豈;心', '豈', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3094, '𧰙', 'qí', '第五卷', '豈部', '汔也。訖事之樂也。从豈，幾聲。', '豈;幾', '幾', 'null', NULL, '豈', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3095, '豆', 'dòu', '第五卷', '豆部', '古食肉器也。从囗。象形。凡豆之屬皆从豆。古文豆。', '囗', '', 'null', NULL, '囗', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3096, '梪', 'dòu', '第五卷', '豆部', '木豆謂之梪。从木豆。', '木;豆', '豆', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3097, '𧯷', 'jǐn', '第五卷', '豆部', '蠡也。从豆，蒸省聲。', '豆;蒸', '蒸', 'null', NULL, '豆', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3098, '𧯦', 'juàn', '第五卷', '豆部', '豆屬。从豆，𢍏聲。', '豆;𢍏', '𢍏', 'null', NULL, '豆', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3099, '𧯡', 'wān', '第五卷', '豆部', '豆飴也。从豆，夗聲。', '豆;夗', '夗', 'null', NULL, '豆', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3100, '𢍪', 'dēnɡ', '第五卷', '豆部', '禮器也。从廾持肉，在豆上。讀若鐙同。', '廾;肉;豆;鐙', '鐙', 'null', NULL, '廾;肉;豆', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3101, '豊', 'lǐ', '第五卷', '豊部', '行禮之器也。从豆。象形。凡豊之屬皆从豊。讀與禮同。', '豆;禮', '禮', 'null', NULL, '豆', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3102, '豑', 'zhì', '第五卷', '豊部', '爵之次弟也。从豊弟。虞書曰「平豑[秩]東作」。', '豊;弟;秩', '弟;秩', 'null', NULL, '豊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3103, '豐', 'fēnɡ', '第五卷', '豐部', '豆之豐滿也。从豆。象形。一曰鄉飲酒有「豐矦」者。凡豐之屬皆从豐。古文豐。', '豆', '', 'null', NULL, '豆', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3104, '豓;豔;𧰚', 'yàn', '第五卷', '豐部', '好而長也。从豐。豐，大也。盇聲。春秋傳曰「美而豓」。', '豐;盇', '盇', 'null', NULL, '豐', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3105, '䖒', 'xī', '第五卷', '䖒部', '古陶器也。从豆，虍聲。凡䖒之屬皆从䖒。', '豆;虍', '虍', 'null', NULL, '豆', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3106, '𧇼', 'hào', '第五卷', '䖒部', '土鍪也。从䖒，号聲。讀若鎬。', '䖒;号;鎬', '号;鎬', 'null', NULL, '䖒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3107, '𧈚', 'zhù', '第五卷', '䖒部', '器也。从䖒㿾。㿾亦聲。闕。', '䖒;㿾', '㿾', 'null', NULL, '䖒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3108, '虍', 'hū', '第五卷', '虍部', '虎文也。象形。凡虍之屬皆从虍。讀若春秋傳曰「盱有餘」。', '盱', '盱', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3109, '虞', 'yú', '第五卷', '虍部', '騶虞也。白虎黑文，尾長於身，仁獸也。食自死之肉。从虍，吳聲。詩曰「于嗟乎！騶虞」。', '虍;吳', '吳', 'null', NULL, '虍', 'null', 'null', 'null', 'null', '经', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3110, '虙', 'fú', '第五卷', '虍部', '虎皃。从虍，必聲。', '虍;必', '必', 'null', NULL, '虍', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3111, '虔', 'qián', '第五卷', '虍部', '虎行皃。从虍，文聲。讀若矝。', '虍;文;矝', '矝', 'null', NULL, '虍;文', 'null', 'null', 'null', 'null', '', 0x31, 'null', '文非聲', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3112, '虘', 'cuó', '第五卷', '虍部', '虎不柔不信也。从虍，且聲。讀若「䣜縣」。', '虍;且;䣜', '且;䣜', 'null', NULL, '虍', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3113, '虖', 'hū', '第五卷', '虍部', '哮虖也。从虍，乎聲。', '虍;乎', '乎', 'null', NULL, '虍', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3114, '虐;𧆩', 'nüè', '第五卷', '虍部', '殘也。从虍爪人。虎足反爪人也。古文虐如此[口]。', '虍;爪;人;口', '', 'null', NULL, '虍;爪;人;口', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3115, '虨', 'bīn', '第五卷', '虍部', '虎文彪也。从虍，彬聲。', '虍;彬', '彬', 'null', NULL, '虍', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3116, '𧇽;鐻;虡', 'jù', '第五卷', '虍部', '鐘𡔷之柎也。飾爲猛獸。从虍，[廾]。象形。[丌]其下足。𧇽或从金豦。篆文𧇽。', '虍;廾;丌;金;豦', '豦', 'null', NULL, '虍;廾;丌;金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3117, '虎', 'hǔ', '第五卷', '虎部', '山獸之君。从虍，从儿。虎足象人足也。凡虎之屬皆从虎。古文虎[爲]。亦古文虎。', '虍;儿;爲', '爲', 'null', NULL, '虍;儿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3118, '𧈖', 'ɡé', '第五卷', '虎部', '虎聲也。从虎，毄聲。讀若隔。', '虎;毄;隔', '毄;隔', 'null', NULL, '虎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3119, '䖑', 'mì', '第五卷', '虎部', '白虎也。从虎，昔[冥]省聲。讀若鼏。', '虎;冥;鼏', '冥;鼏', 'null', NULL, '虎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3120, '䖔', 'kǎn', '第五卷', '虎部', '䖑屬。从虎，厺聲。', '虎;厺', '厺', 'null', NULL, '虎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3121, '虪', 'shù', '第五卷', '虎部', '黑虎也。从虎，儵聲。', '虎;儵', '儵', 'null', NULL, '虎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3122, '虦', 'zhàn', '第五卷', '虎部', '虎竊毛謂之「虦苗」。从虎，戔聲。竊，淺也。', '虎;戔', '戔', 'null', NULL, '虎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3123, '彪', 'biāo', '第五卷', '虎部', '虎文也。从虎彡。彡，象其文也。', '虎;彡', '', 'null', NULL, '虎;彡', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3124, '䖊', 'yì', '第五卷', '虎部', '虎皃。从虎，乂聲。', '虎;乂', '乂', 'null', NULL, '虎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3125, '𧆦', 'yì', '第五卷', '虎部', '虎皃。从虎，气聲。', '虎;气', '气', 'null', NULL, '虎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3126, '虓', 'xiāo', '第五卷', '虎部', '虎鳴也。从虎，九聲。一曰師子。', '虎;九', '九', 'null', NULL, '虎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3127, '䖐', 'yín', '第五卷', '虎部', '虎聲也。从虎，斤聲。', '虎;斤', '斤', 'null', NULL, '虎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3128, '虩', 'xì', '第五卷', '虎部', '易「履虎尾虩虩[愬]」。虩虩，恐懼也。一曰蠅虎也。从虎，𡭴聲。', '愬;虎;𡭴', '愬;𡭴', 'null', NULL, '虎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3129, '虢', 'ɡuó', '第五卷', '虎部', '虎所攫畫明文也。从虎寽。', '虎;寽', '', 'null', NULL, '虎;寽', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3130, '虒', 'sī', '第五卷', '虎部', '委虒，虎之有角者也。从虎，𠂆聲。', '虎;𠂆', '𠂆', 'null', NULL, '虎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3131, '𧈜', 'ténɡ', '第五卷', '虎部', '黑虎也。从虎，騰聲。', '虎;騰', '騰', 'null', NULL, '虎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3132, '𧇭', 'bào', '第五卷', '虎部', '虐也，急也。从虎，从武。', '虎;武', '', 'null', NULL, '虎;武', 'null', 'null', 'null', 'null', '新', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3133, '䖘', 'tú', '第五卷', '虎部', '楚人謂虎爲「烏䖘」。从虎，兔聲。', '虎;兔', '兔', 'null', NULL, '虎', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3134, '虤', 'yán', '第五卷', '虤部', '虎怒也。从二虎。凡虤之屬皆从虤。', '虎', '', 'null', NULL, '虎', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3135, '䖜', 'yín', '第五卷', '虤部', '㒳虎爭聲。从虤，从曰。讀若憖。', '虤;曰;憖', '憖', 'null', NULL, '虤;曰', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3136, '贙', 'xuàn', '第五卷', '虤部', '分別也。从虤對爭貝。讀若回。', '虤;貝;回', '回', 'null', NULL, '虤;貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3137, '皿', 'mǐn', '第五卷', '皿部', '飯食之用器也。象形。與豆同意。凡皿之屬皆从皿。讀若猛。', '猛', '猛', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3138, '盂', 'yú', '第五卷', '皿部', '飲器也。从皿，于聲。', '皿;于', '于', 'null', NULL, '皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3139, '盌', 'wǎn', '第五卷', '皿部', '小盂也。从皿，夗聲。', '皿;夗', '夗', 'null', NULL, '皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3140, '盛', 'chénɡ', '第五卷', '皿部', '黍稷在器中㠯祀者也。从皿，成聲。', '皿;成', '成', 'null', NULL, '皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3141, '齍', 'zī', '第五卷', '皿部', '黍稷器。所㠯祀者。从皿，齊聲。', '皿;齊', '齊', 'null', NULL, '皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3142, '䀁;𥁓', 'yòu', '第五卷', '皿部', '小甌也。从皿，有聲。讀若灰。一曰若賄。䀁或从右。', '皿;有;灰;賄;右', '有;灰;賄;右', 'null', NULL, '皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3143, '盧', 'lú', '第五卷', '皿部', '盧，飯器也。从皿，𧆭聲。籒文盧[鹵省聲]。', '皿;𧆭;鹵', '𧆭;鹵', 'null', NULL, '皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3144, '䀇', 'ɡǔ', '第五卷', '皿部', '器也。从缶皿，古聲。', '缶;皿;古', '古', 'null', NULL, '缶;皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3145, '盄', 'zhāo', '第五卷', '皿部', '器也。从皿，弔聲。', '皿;弔', '弔', 'null', NULL, '皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3146, '盎', 'ànɡ', '第五卷', '皿部', '盆也。从皿，央聲。盎或从瓦。', '皿;央;瓦', '央', 'null', NULL, '皿;瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3147, '盆', 'pén', '第五卷', '皿部', '盎也。从皿，分聲。', '皿;分', '分', 'null', NULL, '皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3148, '㿾', 'zhù', '第五卷', '皿部', '器也。从皿，宁聲。', '皿;宁', '宁', 'null', NULL, '皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3149, '盨', 'xǔ', '第五卷', '皿部', '㯯盨，負戴器也。从皿，須聲。', '皿;須', '須', 'null', NULL, '皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3150, '䀊', 'jiǎo', '第五卷', '皿部', '器也。从皿，漻聲。', '皿;漻', '漻', 'null', NULL, '皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3151, '𥁑', 'mì', '第五卷', '皿部', '拭器也。从皿，必聲。', '皿;必', '必', 'null', NULL, '皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3152, '醯', 'xī', '第五卷', '皿部', '酸也。作醯㠯𩱙、㠯酒。从𩱙、酒竝省，从皿。皿，器也。', '𩱙;酒;皿', '', 'null', NULL, '𩱙;酒;皿', 'null', 'null', 'null', 'null', '△', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3153, '盉', 'hé', '第五卷', '皿部', '調味也。从皿，禾聲。', '皿;禾', '禾', 'null', NULL, '皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3154, '益', 'yì', '第五卷', '皿部', '饒也。从水皿。水皿，益之意也。', '水;皿', '', 'null', NULL, '水;皿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3155, '盈', 'yínɡ', '第五卷', '皿部', '滿器也。从皿夃。', '皿;夃', '', 'null', NULL, '皿;夃', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3156, '盡', 'jìn', '第五卷', '皿部', '器中空也。从皿，㶳聲。', '皿;㶳', '㶳', 'null', NULL, '皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3157, '盅', 'chōnɡ', '第五卷', '皿部', '器虛也。从皿，中聲。老子曰「道盅而用之」。', '皿;中', '中', 'null', NULL, '皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3158, '盦', 'ān', '第五卷', '皿部', '覆葢也。从皿，酓聲。', '皿;酓', '酓', 'null', NULL, '皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3159, '𥁕;昷', 'wēn', '第五卷', '皿部', '仁也。从皿，㠯食囚也。官溥說。', '皿;囚', '', 'null', NULL, '皿;囚', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3160, '盥', 'ɡuàn', '第五卷', '皿部', '澡手也。从𦥑，水臨皿也。春秋傳曰「奉匜沃盥」。', '𦥑;水;皿', '', 'null', NULL, '臼;水;皿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3161, '盪', 'dànɡ', '第五卷', '皿部', '滌器也。从皿，湯聲。', '皿;湯', '湯', 'null', NULL, '皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3162, '盋;鉢', 'bō', '第五卷', '皿部', '盋器。盂屬。从皿，犮聲。[鉢]或从金，从本。', '皿;犮;金;本', '犮;本', 'null', NULL, '皿;金', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3163, '𠙴;𥬔', 'qū', '第五卷', '𠙴部', '𠙴盧，飯器，㠯桺作之。象形。凡𠙴之屬皆从𠙴。𠙴或从竹，去聲。', '竹;去', '去', 'null', NULL, '竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3164, '去;厺', 'qù', '第五卷', '去部', '人相違也。从大，𠙴聲。凡去之屬皆从去。', '大;𠙴', '𠙴', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3165, '朅', 'qiè', '第五卷', '去部', '去也。从去，曷聲。', '去;曷', '曷', 'null', NULL, '去', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3166, '𡕮', 'línɡ', '第五卷', '去部', '去也。从去，夌聲。讀若棘、陵。', '去;夌;棘;陵', '夌;棘;陵', 'null', NULL, '去', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3167, '血', 'xuè', '第五卷', '血部', '祭所薦牲血也。从皿。一，象血形。凡血之屬皆从血。', '皿;一', '', 'null', NULL, '皿;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3168, '衁', 'huānɡ', '第五卷', '血部', '血也。从血，亡聲。春秋傳曰「士刲羊，亦無衁也」。', '血;亡', '亡', 'null', NULL, '血', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3169, '衃', 'pēi', '第五卷', '血部', '凝血也。从血，不聲。', '血;不', '不', 'null', NULL, '血', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3170, '𧗁;𧗃', 'jīn', '第五卷', '血部', '气液也。从血，𦘔聲。', '血;𦘔', '𦘔', 'null', NULL, '血', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3171, '𧖨', 'tínɡ', '第五卷', '血部', '定息也。从血，甹省聲。讀若亭。', '血;甹;亭', '甹;亭', 'null', NULL, '血', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3172, '衄;衂', 'nǜ', '第五卷', '血部', '鼻出血也。从血，丑聲。', '血;丑', '丑', 'null', NULL, '血', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3173, '𧗕;膿', 'nónɡ', '第五卷', '血部', '腫血也。从血，農省聲。俗𧗕。从肉，農聲。', '血;農;肉', '農', 'null', NULL, '血;肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3174, '𧖺', 'tǎn', '第五卷', '血部', '血醢也。从血，䏙聲。禮有「𧖺醢」，㠯牛乾脯、粱、𥶶、鹽、酒也。', '血;䏙', '䏙', 'null', NULL, '血', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3175, '𧗎', 'zú', '第五卷', '血部', '醢也。从血，菹聲。𧗎或从缶。', '血;菹;缶', '菹', 'null', NULL, '血;缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3176, '𧗓', 'jī', '第五卷', '血部', '㠯血有所刏涂祭也。从血，幾聲。', '血;幾', '幾', 'null', NULL, '血', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3177, '卹', 'xù', '第五卷', '血部', '憂也。从血，卪聲。一曰鮮少也。', '血;卪', '卪', 'null', NULL, '血', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3178, '衋', 'xì', '第五卷', '血部', '傷痛也。从血聿。皕聲。周書曰「民罔不衋傷心」。讀若譆。', '血;聿;皕;譆', '皕;譆', 'null', NULL, '血;聿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3179, '䘓', 'kàn', '第五卷', '血部', '羊凝血也。从血，臽聲。䘓或从贛。', '血;臽;贛', '臽;贛', 'null', NULL, '血', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3180, '盇;𥁋;盍', 'hé', '第五卷', '血部', '覆也。从血，大聲。', '血;大', '大', 'null', NULL, '血', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3181, '衊', 'miè', '第五卷', '血部', '污血也。从血，蔑聲。', '血;蔑', '蔑', 'null', NULL, '血', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3182, '丶', 'zhǔ', '第五卷', '丶部', '有所絕止，丶而識之也。凡丶之屬皆从丶。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3183, '主', 'zhǔ', '第五卷', '丶部', '鐙中火主也。，象形。从丶，丶亦聲。', '丶', '丶', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3184, '咅;㕻', 'pǒu', '第五卷', '丶部', '相與語，唾而不受也。从丶，从否。丶亦聲。咅或从豆欠。', '丶;否;豆;欠', '丶;豆', 'null', NULL, '否;欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3185, '丹', 'dān', '第五卷', '丹部', '巴、越之赤石也。象采丹井。丶，象丹形。凡丹之屬皆从丹。古文丹。亦古文丹[彤]。', '丶;彤', '', 'null', NULL, '丶;彤', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3186, '雘', 'wò', '第五卷', '丹部', '善丹也。从丹，蒦聲。讀與靃同。周書曰「惟其𢾅丹雘」。', '丹;蒦;靃', '蒦;靃', 'null', NULL, '丹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3187, '彤', 'tónɡ', '第五卷', '丹部', '丹飾也。从丹彡。彡，其畫也。彡亦聲。', '丹;彡', '彡', 'null', NULL, '丹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3188, '靑;青', 'qīnɡ', '第五卷', '靑部', '東方色也。木生火。从生丹。丹靑之信，言必然。凡靑之屬皆从靑。古文靑。', '生;丹', '生', 'null', NULL, '丹', 'null', 'null', 'null', 'null', '耕部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3189, '靜', 'jìnɡ', '第五卷', '靑部', '宷也。从靑，爭聲。', '靑;爭', '靑;爭', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3190, '井', 'jǐnɡ', '第五卷', '井部', '八家爲一井。象構𩏑形。丶，𦉥象也。古者伯益初作井。凡井之屬皆从井。', '丶', '', 'null', NULL, '丶', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3191, '𤌌', 'yǐnɡ', '第五卷', '井部', '澤地也。从井，瑩省聲。', '井;瑩', '瑩', 'null', NULL, '井', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3192, '阱;穽', 'jǐnɡ', '第五卷', '井部', '陷也。从𨸏井。井亦聲。阱或从穴。古文阱从水。', '𨸏;井;穴;水', '井', 'null', NULL, '𨸏;穴;水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3193, '㓝', 'xínɡ', '第五卷', '井部', '罰辠也。从刀井。易曰「井者，法也」。井亦聲。', '刀;井', '井', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3194, '刱', 'chuànɡ', '第五卷', '井部', '造法刱業也。从井，刅聲。讀若創。', '井;刅;創', '刅;創', 'null', NULL, '井', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3195, '皀', 'bī', '第五卷', '皀部', '穀之馨香也。象嘉穀在裹中之形。匕，所㠯扱之。或說皀，一粒也。凡皀之屬皆从皀。又讀若香。', '匕;香', '香', 'null', NULL, '匕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3196, '卽;即', 'jí', '第五卷', '皀部', '卽食也。从皀，卪聲。', '皀;卪', '卪', 'null', NULL, '皀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3197, '旣;既', 'jì', '第五卷', '皀部', '小食也。从皀，旡聲。論語曰「不使勝食旣」。', '皀;旡', '旡', 'null', NULL, '皀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3198, '冟', 'shì', '第五卷', '皀部', '飯剛柔不調相箸。从皀，冂聲。讀若適。', '皀;冂;適', '冂;適', 'null', NULL, '皀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3199, '鬯', 'chànɡ', '第五卷', '鬯部', '㠯𩰤釀𩰪艸。芬芳攸服㠯降神也。从𠙴。𠙴，器也。中象米。匕，所㠯扱之。易曰「不喪匕鬯」。凡鬯之屬皆从鬯。', '𠙴;匕', '', 'null', NULL, '𠙴;匕', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3200, '𩰪', 'yù', '第五卷', '鬯部', '芳艸也。十葉爲貫，百廿貫，築㠯煮之爲𩰪。从𦥑缶冂鬯。彡，其飾也。一曰𩰪鬯，百艸之華，遠方鬱人所貢芳艸，合釀之㠯降神。鬱，今鬱林郡也。', '𦥑;缶;冂;鬯;彡', '', 'null', NULL, '臼;缶;冂;鬯;彡', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3201, '爵', 'jué', '第五卷', '鬯部', '禮器也。𠋓，象雀之形。中有鬯酒，又持之也。皆㠯飲器象雀者。取其鳴節節足足也。古文爵如此，象形。', '鬯;又', '', 'null', NULL, '鬯;又', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3202, '𩰤;秬', 'jù', '第五卷', '鬯部', '黑黍也。一稃二米㠯釀。从鬯，矩聲。𩰤或从禾。', '鬯;矩;禾', '矩', 'null', NULL, '鬯;禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3203, '𩰢', 'shǐ', '第五卷', '鬯部', '列也。从鬯，吏聲。讀若迅。', '鬯;吏;迅', '吏;迅', 'null', NULL, '鬯', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3204, '食;𩚁', 'shí', '第五卷', '食部', '亼米也。从皀，亼聲。或說亼，皀也。凡食之屬皆从食。', '皀;亼', '亼', 'null', NULL, '皀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3205, '𩞑;饙', 'fēn', '第五卷', '食部', '脩飯也。从食，𠦪聲。𩞑或从賁。𩞑或从奔。', '食;𠦪;賁;奔', '𠦪;賁;奔', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3206, '𩞷;餾', 'liù', '第五卷', '食部', '飯气流也。从食，畱聲。', '食;畱', '畱', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3207, '飪;餁', 'rèn', '第五卷', '食部', '大孰也。从食，壬聲。古文飪[肉]。亦古文飪[恁]。', '食;壬;肉;恁', '壬;恁', 'null', NULL, '食;肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3208, '𩟷;饔', 'yōnɡ', '第五卷', '食部', '孰食也。从食，雝聲。', '食;雝', '雝', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3209, '飴', 'yí', '第五卷', '食部', '米糱煎者也。从食，台聲。籒文飴从異省。', '食;台;異', '台;異', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3210, '餳', 'xínɡ', '第五卷', '食部', '飴和饊者也。从食，昜聲。', '食;昜', '昜', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3211, '饊', 'sǎn', '第五卷', '食部', '熬稻粻餭[皇]也。从食，散聲。', '食;散', '散', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3212, '餅', 'bǐnɡ', '第五卷', '食部', '麪餈也。从食，并聲。', '食;并', '并', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3213, '餈;粢;䭣', 'cí', '第五卷', '食部', '稻餅也。从食，次聲。餈或从齊。餈或从米。', '食;次;齊;米', '次;齊', 'null', NULL, '食;米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3214, '饘', 'zhān', '第五卷', '食部', '糜也。从食，亶聲。周謂之饘，宋 衛謂之𩜾。', '食;亶', '亶', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3215, '餱', 'hóu', '第五卷', '食部', '乾食也。从食，矦聲。周書曰「歭乃餱[糗]粻」。', '食;矦;糗', '矦;糗', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3216, '餥', 'fěi', '第五卷', '食部', '餱也。从食，非聲。陳、楚之閒相謁而食麥飯曰餥。', '食;非', '非', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3217, '饎;𩝮;糦', 'chì', '第五卷', '食部', '酒食也。从食，喜聲。詩曰「可㠯饙饎」。饎或从巸。饎或从米。', '食;喜;巸;米', '喜;巸', 'null', NULL, '食;米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3218, '籑;饌;䉵', 'zhuàn', '第五卷', '食部', '具食也。从食，算聲。䉵或从巽。', '食;算;巽', '算;巽', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3219, '養', 'yǎnɡ', '第五卷', '食部', '足食也。从食，羊聲。古文養[攴]。', '食;羊;攴', '羊', 'null', NULL, '食;攴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3220, '飯', 'fàn', '第五卷', '食部', '食也。从食，反聲。', '食;反', '反', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3221, '𩚖', 'niù', '第五卷', '食部', '襍飯也。从食，丑聲。', '食;丑', '丑', 'null', NULL, '食', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3222, '飤', 'sì', '第五卷', '食部', '糧也。从人仰食。', '人;食', '食', 'null', NULL, '人', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3223, '饡', 'zàn', '第五卷', '食部', '㠯羹澆飯也。从食，贊聲。', '食;贊', '贊', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3224, '𩞧', 'shǎnɡ', '第五卷', '食部', '晝食也。从食，象聲。𩞧或从昜。', '食;象;昜', '象;昜', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3225, '飧', 'sūn', '第五卷', '食部', '餔也。从夕食。', '夕;食', '', 'null', NULL, '夕;食', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3226, '餔', 'bū', '第五卷', '食部', '日加申時食也。从食，甫聲。籒文餔从皿，浦聲。', '食;甫;皿;浦', '甫;浦', 'null', NULL, '食;皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3227, '餐;湌', 'cān', '第五卷', '食部', '吞也。从食，𣦼聲。餐或从水。', '食;𣦼;水', '𣦼', 'null', NULL, '食;水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3228, '䭑', 'lián', '第五卷', '食部', '嘰也。从食，兼聲。讀若「風溓溓」。一曰廉絜也。', '食;兼;溓;廉', '兼;溓;廉', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3229, '饁;𩝉', 'yè', '第五卷', '食部', '餉田也。从食，𥁋聲。詩曰「𩝉彼南𠭇」。', '食;𥁋', '𥁋', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3230, '饟', 'xiǎnɡ', '第五卷', '食部', '周人謂餉曰饟。从食，襄聲。', '食;襄', '襄', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3231, '餉', 'xiǎnɡ', '第五卷', '食部', '饋也。从食，向聲。', '食;向', '向', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3232, '饋', 'kuì', '第五卷', '食部', '餉也。从食，貴聲。', '食;貴', '貴', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3233, '饗', 'xiǎnɡ', '第五卷', '食部', '鄉人㱃酒也。从鄉，从食。鄉亦聲。', '鄉;食', '鄉', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3234, '饛', 'ménɡ', '第五卷', '食部', '盛器滿皃。从食，蒙聲。詩曰「有饛簋飧」。', '食;蒙', '蒙', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3235, '飵', 'zuò', '第五卷', '食部', '楚人相謁食麥曰飵。从食，乍聲。', '食;乍', '乍', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3236, '䬯', 'nián', '第五卷', '食部', '相謁食麥也。从食，占聲。', '食;占', '占', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3237, '䭡', 'wèn', '第五卷', '食部', '秦人謂相謁而食麥曰「䭡䭓」。从食，㥯聲。', '食;㥯', '㥯', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3238, '䭓', 'èn', '第五卷', '食部', '䭡䭓也。从食，豈聲。', '食;豈', '豈', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3239, '餬', 'hú', '第五卷', '食部', '寄食也。从食，胡聲。', '食;胡', '胡', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3240, '飶', 'bì', '第五卷', '食部', '食之香也。从食，必聲。詩曰「有飶其香」。', '食;必', '必', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3241, '𩜈', 'yù', '第五卷', '食部', '燕食也。从食，芺聲。詩曰「㱃酒之𩜈[醧]」。', '食;芺;醧', '芺;醧', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3242, '飽', 'bǎo', '第五卷', '食部', '猒也。从食，包聲。古文飽从𤓽[孚]聲。亦古文飽，从卯聲。', '食;包;孚;卯', '包;孚;卯', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3243, '䬼', 'yuàn', '第五卷', '食部', '猒也。从食，肙聲。', '食;肙', '肙', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3244, '饒', 'ráo', '第五卷', '食部', '飽也。从食，堯聲。', '食;堯', '堯', 'null', NULL, '食', 'null', 'null', 'null', 'null', '餘 優 益', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3245, '餘', 'yú', '第五卷', '食部', '饒也。从食，余聲。', '食;余', '余', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3246, '餀', 'hài', '第五卷', '食部', '食臭也。从食，艾聲。爾雅曰「餀謂之喙」。', '食;艾', '艾', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3247, '餞', 'jiàn', '第五卷', '食部', '送去食也。从食，戔聲。詩曰「顯父餞之」。', '食;戔', '戔', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3248, '餫', 'yùn', '第五卷', '食部', '野饋曰餫。从食，軍聲。', '食;軍', '軍', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3249, '館', 'ɡuǎn', '第五卷', '食部', '客舍也。从食，宮聲。周禮「五十里有市，市有館，館有積，㠯待朝聘之客」。', '食;官', '官', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3250, '饕;叨', 'tāo', '第五卷', '食部', '貪也。从食，號聲。俗饕。从口，刀聲。籒文饕从號省。', '食;號;口;刀', '號;刀', 'null', NULL, '食;口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3251, '飻', 'tiè', '第五卷', '食部', '貪也。从食，㐱聲。春秋傳曰謂之「饕飻」。', '食;㐱', '㐱', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3252, '饖', 'wèi', '第五卷', '食部', '飯傷熱也。从食，歲聲。', '食;歲', '歲', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3253, '饐', 'yì', '第五卷', '食部', '飯傷溼也。从食，壹聲。', '食;壹', '壹', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3254, '餲', 'ài', '第五卷', '食部', '飯餲也。从食，曷聲。論語曰「食饐而餲」。', '食;曷', '曷', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3255, '饑', 'jī', '第五卷', '食部', '榖不孰爲饑。从食，幾聲。', '食;幾', '幾', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3256, '饉', 'jǐn', '第五卷', '食部', '蔬不孰爲饉。从食，堇聲。', '食;堇', '堇', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3257, '𩚬', 'è', '第五卷', '食部', '飢也。从食，戹聲。讀若楚人言「恚人」。', '食;戹;恚', '戹;恚', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3258, '餒;餧', 'něi', '第五卷', '食部', '飢也。从食，妥聲。一曰魚敗曰餒。', '食;妥', '妥', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3259, '飢', 'jī', '第五卷', '食部', '餓也。从食，几聲。', '食;几', '几', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3260, '餓', 'è', '第五卷', '食部', '飢也。从食，我聲。', '食;我', '我', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3261, '餽', 'ɡuì', '第五卷', '食部', '吳人謂祭曰餽。从食鬼。鬼亦聲。', '食;鬼', '鬼', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3262, '餟', 'zhuì', '第五卷', '食部', '祭酹也。从食，叕聲。', '食;叕', '叕', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3263, '䬽', 'shuì', '第五卷', '食部', '小餟也。从食，兌聲。', '食;兌', '兌', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3264, '𩜁', 'línɡ', '第五卷', '食部', '馬食穀多，气流四下也。从食，夌聲。', '食;夌', '夌', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3265, '䬴', 'mò', '第五卷', '食部', '食馬穀也。从食，末聲。', '食;末', '末', 'null', NULL, '食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3266, '餕', 'jùn', '第五卷', '食部', '食之餘也。从食，夋聲。', '食;夋', '夋', 'null', NULL, '食', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3267, '餻', 'ɡāo', '第五卷', '食部', '餌屬。从食，羔聲。', '食;羔', '羔', 'null', NULL, '食', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3268, '亼;𠓛', 'jí', '第五卷', '亼部', '三合也。从入一。象三合之形。凡亼之屬皆从亼。讀若集。', '入;一;集', '集', 'null', NULL, '入;一', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3269, '合', 'hé', '第五卷', '亼部', '亼口也。从亼口。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3270, '僉', 'qiān', '第五卷', '亼部', '皆也。从亼，从吅，从从。虞書曰「僉曰『伯夷』」。', '亼;吅;从', '', 'null', NULL, '亼;吅;从', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3271, '侖', 'lún', '第五卷', '亼部', '思也。从亼冊。籒文侖。', '亼;冊', '', 'null', NULL, '亼;冊', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3272, '今', 'jīn', '第五卷', '亼部', '是時也。从亼乁。乁，古文及。', '亼;乁', '乁', 'null', NULL, '亼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3273, '舍', 'shè', '第五卷', '亼部', '市居曰舍。从亼屮囗。屮，象屋也。囗，象築也。', '亼;屮;囗', '', 'null', NULL, '亼;屮;囗', 'null', 'null', 'null', 'null', '舍≌落', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3274, '會', 'huì', '第五卷', '會部', '合也。从亼，曾省。曾，益也。凡會之屬皆从會。古文會如此[彳合]。', '亼;曾;彳;合', '', 'null', NULL, '亼;曾;彳;合', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3275, '朇', 'pí', '第五卷', '會部', '益也。从會，卑聲。', '會;卑', '卑', 'null', NULL, '會', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3276, '䢈', 'chén', '第五卷', '會部', '日月合宿爲䢈。从會辰。會亦聲。', '會;辰', '辰', 'null', NULL, '會', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3277, '倉', 'cānɡ', '第五卷', '倉部', '穀藏也。蒼黃取而臧之，故謂之倉。从食省。囗，象倉形。凡倉之屬皆从倉。奇字倉[巨]。', '食;囗;巨', '', 'null', NULL, '食;囗;巨', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3278, '牄', 'qiānɡ', '第五卷', '倉部', '鳥獸來食聲也。从倉。爿聲。虞書曰「鳥獸牄牄」。', '倉;爿', '爿', 'null', NULL, '倉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3279, '入', 'rù', '第五卷', '入部', '內也。象從上俱下也。凡入之屬皆从入。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3280, '內', 'nèi', '第五卷', '入部', '入也。从冂入。自外而入也。', '冂;入', '', 'null', NULL, '冂;入', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3281, '㞥', 'cén', '第五卷', '入部', '入山之突也。从山，从入。闕。', '山;入', '', 'null', NULL, '山;入', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3282, '糴', 'dí', '第五卷', '入部', '市榖也。从入䊮。', '入;䊮', '', 'null', NULL, '入;䊮', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3283, '㒰;全', 'quán', '第五卷', '入部', '完也。从入，从工。篆文㒰从玉。純玉曰全。古文㒰[未详]。', '入;工;玉', '', 'null', NULL, '入;工;玉', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3284, '𠓜', 'liǎnɡ', '第五卷', '入部', '二入也。㒳从此。闕。', '入;㒳', '㒳', 'null', NULL, '入', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3285, '缶', 'fǒu', '第五卷', '缶部', '瓦器所㠯盛酒𤖅。秦人鼔之㠯節謌。象形。凡缶之屬皆从缶。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3286, '䍍', 'kòu', '第五卷', '缶部', '未燒瓦器也。从缶，㱿聲。讀若「筩莩」同。', '缶;㱿;莩', '㱿;莩', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3287, '匋', 'táo', '第五卷', '缶部', '作瓦器也。从缶，包省聲。古者昆吾作匋。案，史篇讀與缶同。', '缶;包;缶', '包;缶', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3288, '罌', 'yīnɡ', '第五卷', '缶部', '缶也。从缶，賏聲。', '缶;賏', '賏', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3289, '𦉈', 'chuí', '第五卷', '缶部', '小口罌也。从缶，𠂹聲。', '缶;𠂹', '𠂹', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3290, '䍌', 'bù', '第五卷', '缶部', '小缶也。从缶，㕻聲。', '缶;㕻', '㕻', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3291, '缾;瓶', 'pínɡ', '第五卷', '缶部', '𦉥也。从缶，并聲。缾或从瓦。', '缶;并;瓦', '并', 'null', NULL, '缶;瓦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3292, '𦉥', 'wènɡ', '第五卷', '缶部', '汲缾也。从缶，雝聲。', '缶;雝', '雝', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3293, '䍇', 'tà', '第五卷', '缶部', '下平缶也。从缶，乏聲。讀若「簿引𦐇」。', '缶;乏;𦐇', '乏;𦐇', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3294, '罃', 'yīnɡ', '第五卷', '缶部', '備火。長頸缾也。从缶，熒省聲。', '缶;熒', '熒', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3295, '缸', 'ɡānɡ', '第五卷', '缶部', '瓨也。从缶，工聲。', '缶;工', '工', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '政宇应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3296, '𦈸', 'yù', '第五卷', '缶部', '瓦器也。从缶，或聲。', '缶;或', '或', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3297, '䍎', 'cùn', '第五卷', '缶部', '瓦器也。从缶，薦聲。', '缶;薦', '薦', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3298, '䍃', 'yóu', '第五卷', '缶部', '瓦器也。从缶，肉聲。', '缶;肉', '肉', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3299, '𦉢', 'línɡ', '第五卷', '缶部', '瓦器也。从缶，霝聲。', '缶;霝', '霝', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3300, '䍄', 'diǎn', '第五卷', '缶部', '缺也。从缶，占聲。', '缶;占', '占', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3301, '缺', 'quē', '第五卷', '缶部', '器破也。从缶，夬聲。', '缶;夬', '夬', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3302, '罅', 'xià', '第五卷', '缶部', '裂也。从缶，虖聲。缶燒善裂也。', '缶;虖', '虖', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3303, '罄', 'qìnɡ', '第五卷', '缶部', '器中空也。从缶，殸聲。殸，古文磬字。詩曰「缾之罄矣」。', '缶;殸', '殸', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3304, '罊', 'qì', '第五卷', '缶部', '器中盡也。从缶，毄聲。', '缶;毄', '毄', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3305, '缿', 'xiànɡ', '第五卷', '缶部', '受錢器也。从缶，后聲。古㠯瓦，今㠯竹。', '缶;后', '后', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3306, '罐', 'ɡuàn', '第五卷', '缶部', '器也。从缶，雚聲。', '缶;雚', '雚', 'null', NULL, '缶', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3307, '矢;𠂕;𥎨', 'shǐ', '第五卷', '矢部', '弓弩矢也。从入。象鏑栝羽之形。古者夷牟初作矢。凡矢之屬皆从矢。', '入', '', 'null', NULL, '入', 'null', 'null', 'null', 'null', '脂部', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3308, '䠶;射', 'shè', '第五卷', '矢部', '弓弩發於身而中於遠也。从矢，从身。篆文䠶。从寸。寸，法度也，亦手也。', '矢;身;寸', '', 'null', NULL, '矢;身;寸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3309, '矯', 'jiǎo', '第五卷', '矢部', '揉箭箝也。从矢，喬聲。', '矢;喬', '喬', 'null', NULL, '矢', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3310, '矰', 'zēnɡ', '第五卷', '矢部', '隿射矢也。从矢，曾聲。', '矢;曾', '曾', 'null', NULL, '矢', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3311, '矦;侯', 'hóu', '第五卷', '矢部', '春饗所射矦也。从人。从厂。象張布。矢在其下。天子射熊虎豹，服猛也。諸矦射熊虎。大夫射麋，麋，惑也。士射鹿豕，爲田除害也。其祝曰「母若不寧侯，不朝于王所，故伉而射汝也」。古文矦。', '人;厂;矢', '', 'null', NULL, '人;厂;矢', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3312, '𥏻', 'shānɡ', '第五卷', '矢部', '傷也。从矢，傷省聲。', '矢;傷', '傷', 'null', NULL, '矢', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3313, '短', 'duǎn', '第五卷', '矢部', '有所長短，㠯矢爲正。从矢，豆聲。', '矢;豆', '', 'null', NULL, '矢;豆', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3314, '矤', 'shěn', '第五卷', '矢部', '況䛐也。从矢，引省聲。从矢，取䛐之所之如矢也。', '矢;引', '引', 'null', NULL, '矢', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3315, '知', 'zhī', '第五卷', '矢部', '識䛐也。从口矢。', '口;矢', '矢', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3316, '矣', 'yǐ', '第五卷', '矢部', '語巳䛐也。从矢，㠯聲。', '矢;㠯', '㠯', 'null', NULL, '矢', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3317, '矮', 'ǎi', '第五卷', '矢部', '短人也。从矢，委聲。', '矢;委', '委', 'null', NULL, '矢', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3318, '高', 'ɡāo', '第五卷', '高部', '崇也。[入]象臺觀高之形。从冂囗。與倉、舍同意。凡高之屬皆从高。', '入;冂;囗', '', 'null', NULL, '入;冂;囗', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3319, '䯧;廎', 'qǐnɡ', '第五卷', '高部', '小堂也。从高省，冋聲。䯧或从广，頃聲。', '高;冋;广;頃', '冋;頃', 'null', NULL, '高;广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3320, '亭', 'tínɡ', '第五卷', '高部', '民所安定也。亭有樓。从高省，丁聲。', '高;丁', '丁', 'null', NULL, '高', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3321, '亳', 'bó', '第五卷', '高部', '京兆 杜陵亭也。从高省，乇聲。', '高;乇', '乇', 'null', NULL, '高', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3322, '𫡃;冋;坰', 'jiōnɡ', '第五卷', '𫡃部', '邑外謂之郊。郊外謂之野。野外謂之林。林外謂之𫡃。象遠介也。凡𫡃之屬皆从𫡃。古文𫡃从囗。象國邑。𫡃或从土。', '囗;土', '', 'null', NULL, '囗;土', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3323, '市', 'shì', '第五卷', '𫡃部', '買賣所之也。市有垣。从𫡃，从乁。象物相及也。乁，古文及字。之省聲。', '𫡃;乁;之', '之', 'null', NULL, '𫡃;乁', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3324, '冘', 'yín', '第五卷', '𫡃部', '冘冘，行皃。从儿出𫡃。', '儿;𫡃', '', 'null', NULL, '儿;𫡃', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3325, '央', 'yānɡ', '第五卷', '𫡃部', '央，中也。从大在𫡃之內。大，人也。央、旁同意。一曰久也。', '大;𫡃', '', 'null', NULL, '大;𫡃', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3326, '隺;寉', 'hú', '第五卷', '𫡃部', '高至也。从隹上欲出𫡃。易曰「夫乾隺然」。', '隹;𫡃', '', 'null', NULL, '隹;𫡃', 'null', 'null', 'null', 'null', '经', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3327, '𩫖;𩫏', 'ɡuō', '第五卷', '𩫖部', '度也。民所度居也。从回。象城𩫖之重。㒳亭相對也。或但从囗。凡𩫖之屬皆从𩫖。', '回;囗', '', 'null', NULL, '回;囗', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3328, '𩫠', 'quē', '第五卷', '𩫖部', '缺也。古者城闕其南方謂之𩫠。从𩫖，夬聲。讀若「拔物爲決引」也。', '𩫖;夬;決', '夬;決', 'null', NULL, '𩫖', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3329, '京', 'jīnɡ', '第五卷', '京部', '人所爲絕高丘也。从高省。丨，象高形。凡京之屬皆从京。', '高;丨', '', 'null', NULL, '高;丨', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3330, '就', 'jiù', '第五卷', '京部', '就，高也。从京尤。尤，異於凡也。籒文就。', '京;尤', '', 'null', NULL, '京;尤', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3331, '亯;亨;享', 'xiǎnɡ', '第五卷', '亯部', '獻也。从高省。日，象孰物形。孝經曰「祭則鬼亯之」。凡亯之屬皆从亯。篆文亯。', '高;日', '', 'null', NULL, '高;日', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3332, '𦎫', 'chún', '第五卷', '亯部', '孰也。从亯羊。讀若純。一曰鬻也。篆文𦎫。', '亯;羊;純', '純', 'null', NULL, '亯;羊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3333, '䈞', 'dǔ', '第五卷', '亯部', '㫗也。从亯，竹聲。讀若篤。', '亯;竹;篤', '竹;篤', 'null', NULL, '亯', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3334, '𦤘', 'yōnɡ', '第五卷', '亯部', '用也。从亯，从自。自，知臭。亯，所食也。讀若庸同。', '亯;自;庸', '庸', 'null', NULL, '亯;自', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3335, '㫗', 'hòu', '第五卷', '㫗部', '厚也。从反亯。凡㫗之屬皆从㫗。', '亯', '', 'null', NULL, '亯', 'null', 'null', 'null', 'null', '⚙️', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3336, '覃;𪉲', 'tán', '第五卷', '㫗部', '長味也。从㫗，鹹省聲。詩曰「實覃實吁」。古文覃[鹵]。篆文覃省。', '㫗;鹹;鹵', '鹹', 'null', NULL, '㫗;鹵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3337, '厚', 'hòu', '第五卷', '㫗部', '山陵之㫗也。从厂，从㫗。古文厚从后土。', '厂;㫗;后;土', '㫗;后', 'null', NULL, '厂;㫗;土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3338, '畗;畐', 'fú', '第五卷', '畗部', '滿也。从高省。象高厚之形。凡畐之屬皆从畐。讀若伏。', '高;伏', '伏', 'null', NULL, '高', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3339, '良', 'liánɡ', '第五卷', '畗部', '善也。从畗省，亡聲。古文良。亦古文良[㞋]。亦古文良[𠓜]。', '畗;亡;㞋;𠓜', '亡', 'null', NULL, '畗;㞋;𠓜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3340, '㐭;廩', 'lǐn', '第五卷', '㐭部', '穀所振入也。宗廟秶盛，蒼黃㐭而取之，故謂之㐭。从入，从回。象屋形。中有戸牖。凡㐭之屬皆从㐭。㐭或从广稟。', '入;回;广;稟', '稟', 'null', NULL, '入;回;广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3341, '稟', 'bǐnɡ', '第五卷', '㐭部', '賜榖也。从㐭禾。', '㐭;禾', '', 'null', NULL, '㐭;禾', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3342, '亶', 'dǎn', '第五卷', '㐭部', '多榖也。从㐭，旦聲。', '㐭;旦', '旦', 'null', NULL, '㐭', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3343, '啚', 'bǐ', '第五卷', '㐭部', '嗇也。从囗，从㐭。㐭，受也。古文啚如此。', '囗;㐭', '', 'null', NULL, '囗;㐭', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3344, '嗇;𠾂;𠻮', 'sè', '第五卷', '嗇部', '愛濇也。从來㐭。來者㐭而臧之，故田夫謂之「嗇夫」。一曰棘省聲。凡𠾂之屬皆从𠾂。古文𠾂从田。', '來;㐭;棘;田', '棘', 'null', NULL, '來;㐭;田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3345, '牆', 'qiánɡ', '第五卷', '嗇部', '垣蔽也。从𠾂，爿聲。籒文从二禾。籒文亦从二來。', '𠾂;爿;禾;來', '爿', 'null', NULL, '𠾂;禾;來', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3346, '來', 'lái', '第五卷', '來部', '周所受瑞麥來麰也。二麥一夆，象其芒朿之形。天所來也，故爲行來之來。詩曰「詒我來麰」。凡來之屬皆从來。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3347, '𥏳', 'sì', '第五卷', '來部', '詩曰「不𥏳[㠯]」，不來。从來，矣聲。𥏳或从彳。', '㠯;來;矣;彳', '㠯;矣', 'null', NULL, '來;彳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3348, '麥', 'mài', '第五卷', '麥部', '芒穀。秋穜厚薶，故謂之麥。麥，金也，金王而生，火王而死。从來。有穗者也。从夊。凡麥之屬皆从麥。', '來;夊', '薶', 'null', NULL, '來;夊', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3349, '麰', 'móu', '第五卷', '麥部', '來麰，麥也。从麥，牟聲。麰或从艸。', '麥;牟;艸', '牟', 'null', NULL, '麥;艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3350, '麧', 'hé', '第五卷', '麥部', '堅麥也。从麥，气聲。', '麥;气', '气', 'null', NULL, '麥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3351, '䵀', 'suǒ', '第五卷', '麥部', '小麥㞕之覆。从麥，𧴪聲。', '麥;𧴪', '𧴪', 'null', NULL, '麥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3352, '䴾', 'cuó', '第五卷', '麥部', '䃺麥也。从麥，差聲。一曰𢶈也。', '麥;差', '差', 'null', NULL, '麥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3353, '麩', 'fū', '第五卷', '麥部', '小麥㞕皮也。从麥，夫聲。麩或从甫。', '麥;夫;甫', '夫;甫', 'null', NULL, '麥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3354, '麪;麫', 'miàn', '第五卷', '麥部', '麥㞕末也。从麥，丏聲。', '麥;丏', '丏', 'null', NULL, '麥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3355, '䵂', 'zhí', '第五卷', '麥部', '麥覈㞕也。十斤爲三斗。从麥，啻聲。', '麥;啻', '啻', 'null', NULL, '麥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3356, '麷', 'fēnɡ', '第五卷', '麥部', '䰞麥也。从麥，豐聲。讀若馮。', '麥;豐;馮', '豐;馮', 'null', NULL, '麥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3357, '麮', 'qù', '第五卷', '麥部', '麥甘鬻也。从麥，去聲。', '麥;去', '去', 'null', NULL, '麥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3358, '𪍠', 'kū', '第五卷', '麥部', '餅𥶶也。从麥，㱿聲。讀若庫。', '麥;㱿;庫', '㱿;庫', 'null', NULL, '麥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3359, '䴳', 'huá', '第五卷', '麥部', '餅𥶶也。从麥，穴聲。', '麥;穴', '穴', 'null', NULL, '麥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3360, '䴭', 'cái', '第五卷', '麥部', '餅𥶶也。从麥，才聲。', '麥;才', '才', 'null', NULL, '麥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3361, '夊', 'suī', '第五卷', '夊部', '行遲曳夊夊也。象人㒳脛有所躧也。凡夊之屬皆从夊。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3362, '夋', 'qūn', '第五卷', '夊部', '行夋夋也。一曰倨也。从夊，允聲。', '夊;允', '允', 'null', NULL, '夊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3363, '复', 'fù', '第五卷', '夊部', '行故道也。从夊，畗省聲。', '夊;畐', '畗', 'null', NULL, '夊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3364, '夌', 'línɡ', '第五卷', '夊部', '越也。从夊𡴆。𡴆，高大也。一曰夌，𢕌也。', '夊;𡴆', '', 'null', NULL, '夊;𡴆', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3365, '致', 'zhì', '第五卷', '夊部', '送詣也。从夊，从至。', '夊;至', '至', 'null', NULL, '夊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3366, '憂', 'yōu', '第五卷', '夊部', '和之行也。从夊，𢝊聲。詩曰「布政憂憂」。', '夊;𢝊', '𢝊', 'null', NULL, '夊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3367, '愛', 'ài', '第五卷', '夊部', '行皃也。从夊，㤅聲。', '夊;㤅', '㤅', 'null', NULL, '夊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3368, '𡕡', 'pú', '第五卷', '夊部', '行𡕡𡕡也。从夊，闕。讀若僕。', '夊;僕', '僕', 'null', NULL, '夊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3369, '竷', 'kǎn', '第五卷', '夊部', '繇也。舞也。从夊，从章。樂有章也。夅聲。詩曰「竷竷[坎欿]𡔷我」。', '夊;章;夅;坎;欿', '夅;坎;欿', 'null', NULL, '夊;章', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3370, '𡕢', 'wǎn', '第五卷', '夊部', '匘葢也。象皮包覆匘，下有㒳臂，而夊在下。讀若范。', '夊;范', '范', 'null', NULL, '夊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3371, '夏', 'xià', '第五卷', '夊部', '中國之人也。从夊，从頁，从𦥑。𦥑，㒳手。夊，㒳足也。古文夏[亼目足]。', '夊;頁;𦥑;亼;目;足', '', 'null', NULL, '夊;頁;臼;亼;目;足', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3372, '畟', 'cè', '第五卷', '夊部', '治稼畟畟進也。从田儿，从夊。詩曰「畟畟良耜」。', '田;儿;夊', '', 'null', NULL, '田;儿;夊', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3373, '㚇', 'zōnɡ', '第五卷', '夊部', '斂足也。䧿、𩀎醜，其飛也㚇。从夊，兇聲。', '夊;兇', '兇', 'null', NULL, '夊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3374, '夒', 'náo', '第五卷', '夊部', '貪獸也。一曰母㺅，佀人。从頁。巳、止、夊，其手足。', '頁;巳;止;夊', '', 'null', NULL, '頁;巳;止;夊', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3375, '夔', 'kuí', '第五卷', '夊部', '卽魖也。如龍，一足。从夊。象有角手人面之形。', '夊', '', 'null', NULL, '夊', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3376, '夎', 'cuò', '第五卷', '夊部', '拜失容也。从夊，坐聲。', '夊;坐', '坐', 'null', NULL, '夊', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3377, '舛;踳', 'chuǎn', '第五卷', '舛部', '對臥也。从夊㐄相背。凡舛之屬皆从舛。楊雄作舛，从足春。', '夊;㐄;足;春', '春', 'null', NULL, '夊;㐄;足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3378, '舞', 'wǔ', '第五卷', '舛部', '樂也。用足相背。从舛，𣞤聲。古文舞从羽亡。', '舛;𣞤;羽;亡', '𣞤;亡', 'null', NULL, '舛;羽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3379, '舝', 'xiá', '第五卷', '舛部', '車軸耑鍵也。㒳穿相背。从舛，𥝁省聲。𥝁，古文禼字。舝、害同意。', '舛;𥝁', '𥝁', 'null', NULL, '舛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3380, '舜', 'shùn', '第五卷', '舜部', '舜艸也。楚謂之葍。秦謂之藑。蔓地生而連華。象形。从舛。舛亦聲。凡舜之屬皆从舜。古文舜[赤]。', '舛;赤', '舛', 'null', NULL, '赤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3381, '𦨁;葟', 'huánɡ', '第五卷', '舜部', '華榮也。从舜，㞷聲。讀若皇。爾雅曰「𦨁，華也」。𦨁或从艸皇。', '舜;㞷;艸;皇', '㞷;皇', 'null', NULL, '舜;艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3382, '韋', 'wéi', '第五卷', '韋部', '相背也。从舛，囗聲。獸皮之韋，可㠯束物枉戾相韋背，故借㠯爲「皮韋」。凡韋之屬皆从韋。古文韋。', '舛;囗', '囗', 'null', NULL, '舛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3383, '韠', 'bì', '第五卷', '韋部', '韍也。所㠯蔽前者。㠯韋，下廣二尺，上廣一尺，其頸五寸。一命縕韠，再命赤韠。从韋，畢聲。', '韋;畢', '畢', 'null', NULL, '韋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3384, '韎', 'mèi', '第五卷', '韋部', '茅蒐染韋也。一入曰韎。从韋，末聲。', '韋;末', '末', 'null', NULL, '韋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3385, '韢', 'suìxì', '第五卷', '韋部', '櫜紐也。从韋，惠聲。一曰盛虜頭橐也。', '韋;惠', '惠', 'null', NULL, '韋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3386, '韜', 'tāo', '第五卷', '韋部', '劒衣也。从韋，舀聲。', '韋;舀', '舀', 'null', NULL, '韋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3387, '韝', 'ɡōu', '第五卷', '韋部', '臂衣也。从韋，冓聲。', '韋;冓', '冓', 'null', NULL, '韋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3388, '韘', 'shè', '第五卷', '韋部', '射玦也。所㠯拘弦，㠯象骨，韋系，箸右巨指。从韋，枼聲。詩曰「童子佩韘」。韘或从弓。', '韋;枼;弓', '枼', 'null', NULL, '韋;弓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3389, '韣', 'zhú', '第五卷', '韋部', '弓衣也。从韋，蜀聲。', '韋;蜀', '蜀', 'null', NULL, '韋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3390, '韔', 'chànɡ', '第五卷', '韋部', '弓衣也。从韋，長聲。詩曰「交韔二弓」。', '韋;長', '長', 'null', NULL, '韋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3391, '䪗', 'xiá', '第五卷', '韋部', '履也。从韋，叚聲。', '韋;叚', '叚', 'null', NULL, '韋', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3392, '𩏇;緞', 'duàn', '第五卷', '韋部', '履後帖也。从韋，段聲。𩏇或从糸。', '韋;段;糸', '段', 'null', NULL, '韋;糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3393, '韤', 'wà', '第五卷', '韋部', '足衣也。从韋，蔑聲。', '韋;蔑', '蔑', 'null', NULL, '韋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3394, '䪙', 'pò', '第五卷', '韋部', '軶裹也。从韋，尃聲。', '韋;尃', '尃', 'null', NULL, '韋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3395, '韏', 'quàn', '第五卷', '韋部', '革中辨謂之韏。从韋，𢍏聲。', '韋;𢍏', '𢍏', 'null', NULL, '韋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3396, '𩏷;揫;揪;𢱀;𩏶', 'jiū', '第五卷', '韋部', '收束也。从韋，𥼚聲。讀若酋。𩏶或从要。揫𩏶或从秋手。', '韋;𥼚;酋;要;秋;手', '𥼚;酋;秋', 'null', NULL, '韋;要;手', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3397, '𩏑;韓', 'hán', '第五卷', '韋部', '井橋也。从韋，取其帀也。倝聲。', '韋;倝', '倝', 'null', NULL, '韋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3398, '韌', 'rèn', '第五卷', '韋部', '柔而固也。从韋，刃聲。', '韋;刃', '刃', 'null', NULL, '韋', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3399, '弟', 'dì', '第五卷', '弟部', '韋束之次弟也。从古文之象。凡弟之屬皆从弟。古文弟，从古文韋省，丿[𠂆]聲。', '韋;𠂆', '𠂆', 'null', NULL, '韋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3400, '𥊽', 'kūn', '第五卷', '弟部', '周人謂兄曰𥊽。从弟眔。', '弟;眔', '眔', 'null', NULL, '弟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3401, '夂', 'zhǐ', '第五卷', '夂部', '從後至也。象人㒳脛後有致之者。凡夂之屬皆从夂。讀若黹。', '黹', '黹', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3402, '𡕗', 'hài', '第五卷', '夂部', '相遮要害也。从夂，丯聲。南陽 新野有𡕗亭。', '夂;丯', '丯', 'null', NULL, '夂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3403, '夆', 'fēnɡ', '第五卷', '夂部', '啎也。从夂，丰聲。讀若縫。', '夂;丰;縫', '丰;縫', 'null', NULL, '夂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3404, '夅', 'xiánɡ', '第五卷', '夂部', '服也。从夂㐄。相承不敢竝也。', '夂;㐄', '', 'null', NULL, '夂;㐄', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3405, '夃', 'ɡǔ', '第五卷', '夂部', '秦人市買多得爲夃。从乃，从夂。益至也。詩曰「我夃[姑]酌彼金罍」。', '乃;夂;姑', '姑', 'null', NULL, '乃;夂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3406, '㐄', 'kuǎ', '第五卷', '夂部', '跨步也。从反夂。𩰫从此。', '夂;𩰫', '𩰫', 'null', NULL, '夂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3407, '久', 'jiǔ', '第五卷', '久部', '從後灸之也。象人㒳脛後有歫也。周禮曰「久諸牆㠯觀其橈」。凡久之屬皆从久。', '灸', '灸', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3408, '桀', 'jié', '第五卷', '桀部', '磔也。从舛，在木上也。凡桀之屬皆从桀。', '舛;木', '', 'null', NULL, '舛;木', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3409, '磔', 'zhé', '第五卷', '桀部', '辜也。从桀，石聲。', '桀;石', '石', 'null', NULL, '桀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3410, '椉;乘', 'chénɡ', '第五卷', '桀部', '覆也。从入桀。桀，黠也。軍法「入桀曰椉」。古文椉从几。', '入;桀;几', NULL, 'null', NULL, '入;桀;几', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3411, '木', 'mù', '第六卷', '木部', '冒也。冒地而生，東方之行。从屮，下象其根。凡木之屬皆从木。', '屮', '', 'null', NULL, '屮', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3412, '橘', 'jú', '第六卷', '木部', '橘果，出江南。从木，矞聲。', '木;矞', '矞', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3413, '橙', 'chénɡ', '第六卷', '木部', '橘屬。从木，登聲。', '木;登', '登', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3414, '柚', 'yòu', '第六卷', '木部', '條也。佀橙而酢。从木，由聲。夏書曰「厥苞橘柚」。', '木;由', '由', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3415, '樝', 'zhā', '第六卷', '木部', '樝果佀棃而酢。从木，虘聲。', '木;虘', '虘', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3416, '棃', 'lí', '第六卷', '木部', '棃果也。从木，𥝢聲。𥝢，古文利。', '木;𥝢', '𥝢', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3417, '梬', 'yǐnɡ', '第六卷', '木部', '梬棗也。似柹而小。一曰㮕。从木，甹聲。', '木;甹', '甹', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3418, '柹', 'shì', '第六卷', '木部', '赤實果。从木，𠂔聲。', '木;𠂔', '𠂔', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3419, '柟', 'nán', '第六卷', '木部', '梅也。从木，冉聲。', '木;冉', '冉', '南', '俗作楠。', '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3420, '梅', 'méi', '第六卷', '木部', '柟也。可食。从木，每聲。或从某。', '木;每;某', '每;某', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3421, '杏', 'xìnɡ', '第六卷', '木部', '杏果也。从木，向省聲。', '木;向', '向', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3422, '柰', 'nài', '第六卷', '木部', '柰果也。从木，示聲。', '木;示', '示', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3423, '李;杍', 'lǐ', '第六卷', '木部', '李果也。从木，子聲。古文。', '木;子', '子', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3424, '桃', 'táo', '第六卷', '木部', '桃果也。从木，兆聲。', '木;兆', '兆', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3425, '㮘', 'mào', '第六卷', '木部', '冬桃。从木，敄聲。讀若髦。', '木;敄;髦', '敄;髦', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3426, '𣓀;𣐽', 'zhēn', '第六卷', '木部', '𣓀實如小栗。从木，辛聲。春秋傳曰「女摯不過𣓀栗」。', '木;辛', '辛', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3427, '楷', 'jiē', '第六卷', '木部', '楷木也。孔子冢葢樹之者。从木，皆聲。', '木;皆', '皆', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3428, '梫', 'qǐn', '第六卷', '木部', '桂也。从木，侵省聲。', '木;侵', '侵', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3429, '桂', 'ɡuì', '第六卷', '木部', '江南木。百藥之長。从木，圭聲。', '木;圭', '圭', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3430, '棠', 'tánɡ', '第六卷', '木部', '牡曰棠，牝曰杜。从木，尙聲。', '木;尙', '尙', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3431, '杜', 'dù', '第六卷', '木部', '甘棠也。从木，土聲。', '木;土', '土', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3432, '槢', 'xí', '第六卷', '木部', '槢木也。从木，習聲。', '木;習', '習', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3433, '樿', 'zhǎn', '第六卷', '木部', '樿木也。可㠯爲櫛。从木，單聲。', '木;單', '單', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3434, '椲', 'wěi', '第六卷', '木部', '椲木也。可屈爲杅者。从木，韋聲。', '木;韋', '韋', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3435, '楢', 'yóu', '第六卷', '木部', '柔木也。工官㠯爲耎輪。从木，酋聲。讀若糗。', '木;酋;糗', '酋;糗', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3436, '桏', 'qiónɡ', '第六卷', '木部', '椶椐木也。从木，邛聲。', '木;邛', '邛', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3437, '棆', 'lún', '第六卷', '木部', '母杶也。从木，侖聲。讀若易「卦屯」之屯。', '木;侖;屯', '侖;屯', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3438, '楈', 'xū', '第六卷', '木部', '楈木也。从木，胥聲。讀若「芟刈」之芟。', '木;胥;芟', '胥;芟', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3439, '柍', 'yǎnɡ', '第六卷', '木部', '柍梅也。从木，央聲。一曰江南 橦材，其實謂之柍。', '木;央', '央', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3440, '楑', 'kuí', '第六卷', '木部', '楑木也。从木，癸聲。一曰度也。', '木;癸', '癸', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3441, '𣓌', 'ɡǎo', '第六卷', '木部', '𣓌木也。从木，咎聲。讀若晧。', '木;咎;晧', '咎;晧', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3442, '椆', 'chóu', '第六卷', '木部', '椆木也。从木，周聲。讀若丩。', '木;周;丩', '周;丩', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3443, '樕', 'sù', '第六卷', '木部', '樸樕，小木也。从木，欶聲。', '木;欶', '欶', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3444, '㰘', 'yí', '第六卷', '木部', '㰘木也。从木，彝聲。', '木;彝', '彝', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3445, '梣', 'cén', '第六卷', '木部', '靑皮木。从木，岑聲。或从𡨞省。𡨞，籒文寑。', '木;岑;𡨞', '岑;𡨞', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3446, '棳', 'zhuō', '第六卷', '木部', '棳木也。从木，叕聲。益州有棳縣。', '木;叕', '叕', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3447, '𣘫', 'háo', '第六卷', '木部', '𣘫木也。从木，號省聲。', '木;號', '號', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3448, '棪', 'yǎn', '第六卷', '木部', '遬其也。从木，炎聲。讀若「三年導服」之導。', '木;炎;導', '炎;導', 'null', NULL, '木', 'null', 'null', 'null', 'null', '谈部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3449, '𣛹', 'chuán', '第六卷', '木部', '𣛹木也。从木，遄聲。', '木;遄', '遄', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3450, '椋', 'liánɡ', '第六卷', '木部', '卽來也。从木，京聲。', '木;京', '京', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3451, '檍', 'yì', '第六卷', '木部', '杶也。从木，意聲。', '木;意', '意', 'null', NULL, '木', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3452, '𣙿', 'fèi', '第六卷', '木部', '𣙿木也。从木，費聲。', '木;費', '費', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3453, '樗', 'chū', '第六卷', '木部', '樗木也。从木，雩聲。', '木;雩', '雩', 'null', NULL, '木', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3454, '楀', 'yǔ', '第六卷', '木部', '楀木也。从木，禹聲。', '木;禹', '禹', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3455, '蘽', 'lěi', '第六卷', '木部', '蘽木也。从木，藟聲。籒文[靁]。', '木;藟;靁', '藟;靁', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3456, '桋', 'yí', '第六卷', '木部', '赤栜也。从木，夷聲。詩曰「隰有杞桋」。', '木;夷', '夷', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3457, '栟', 'bīnɡ', '第六卷', '木部', '栟櫚，椶也。从木，并聲。', '木;并', '并', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3458, '椶', 'zōnɡ', '第六卷', '木部', '栟櫚也。从木，㚇聲。可作萆。', '木;㚇', '㚇', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3459, '椅', 'yī', '第六卷', '木部', '梓也。从木，奇聲。', '木;奇', '奇', 'null', NULL, '木', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3460, '檟', 'jiǎ', '第六卷', '木部', '楸也。从木，賈聲。春秋傳曰「樹六檟於蒲圃」。', '木;賈', '賈', '夏', '爾雅：「槐小葉曰榎。」「大而皵，楸；小而皵，榎。」段氏云，榎者，檟之或字。', '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3461, '梓', 'zǐ', '第六卷', '木部', '楸也。从木，宰省聲。或不省。', '木;宰', '宰', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3462, '楸', 'qiū', '第六卷', '木部', '梓也。从木，秋聲。', '木;秋', '秋', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3463, '𣚍', 'yì', '第六卷', '木部', '梓屬。大者可爲棺椁，小者可爲弓材。从木，𠶷聲。', '木;𠶷', '𠶷', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3464, '柀', 'bǐ', '第六卷', '木部', '煔也。从木，皮聲。一曰析也。', '木;皮', '皮', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3465, '檆', 'shān', '第六卷', '木部', '木也。从木，煔聲。', '木;煔', '煔', 'null', NULL, '木', 'null', 'null', 'null', 'null', '大徐补 | 应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3466, '榛', 'zhēn', '第六卷', '木部', '榛木也。从木，秦聲。一曰叢木也。', '木;秦', '秦', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3467, '𣐊', 'kǎo', '第六卷', '木部', '山樗也。从木，尻聲。讀若糗。', '木;尻;糗', '尻;糗', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3468, '杶', 'chūn', '第六卷', '木部', '杶木也。从木，屯聲。夏書曰「杶榦桰柏」。或从熏。古文杶。', '木;屯;熏', '屯;熏', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3469, '橁', 'chūn', '第六卷', '木部', '杶也。从木，筍聲。', '木;筍', '筍', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
