INSERT INTO `shuowen_voice_revel` VALUES (3554, '榙', 'tā', '第六卷', '木部', '榙𣝋，果似李。从木，荅聲。讀若嚃。', '木;荅;嚃', '荅;嚃', 'null', NULL, '木', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
