INSERT INTO `shuowen_voice_revel` VALUES (3556, '某', 'méi', '第六卷', '木部', '酸果也。从木甘。闕。古文某从口。', '木;甘;口', '', 'null', NULL, '木;甘;口', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
