INSERT INTO `shuowen_voice_revel` VALUES (3558, '樹', 'shù', '第六卷', '木部', '木生植之總名也。从木，尌聲。籒文。', '木;尌', '尌', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
