INSERT INTO `shuowen_voice_revel` VALUES (3607, '柔', 'róu', '第六卷', '木部', '木曲直也。从木，矛聲。', '木;矛', '矛', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3608, '柝', 'tuò', '第六卷', '木部', '判也。從木，㡿聲。易曰「重門擊柝[𣟄]」。', '木;㡿;𣟄', '㡿;𣟄', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3609, '朸', 'lè', '第六卷', '木部', '木之理也。从木，力聲。平原有朸縣。', '木;力', '力', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3610, '材', 'cái', '第六卷', '木部', '木梃也。从木，才聲。', '木;才', '才', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3611, '柴', 'chái', '第六卷', '木部', '小木散材。从木，此聲。', '木;此', '此', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3612, '榑', 'fú', '第六卷', '木部', '榑桑，神木。日所出也。从木，尃聲。', '木;尃', '尃', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3613, '杲', 'ɡǎo', '第六卷', '木部', '朙也。从日在木上。讀若槀。', '槀', '槀', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3614, '杳', 'yǎo', '第六卷', '木部', '冥也。一曰木下白也。从日在木下。', '日;木', '', 'null', NULL, '日;木', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3615, '㮝', 'hé', '第六卷', '木部', '角械也。从木，郤聲。', '木;郤', '郤', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3616, '栽', 'zāi', '第六卷', '木部', '築牆長版也。从木，𢦒聲。春秋傳曰「楚圍蔡，里而栽」。', '木;𢦒', '𢦒', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3617, '築', 'zhù', '第六卷', '木部', '所㠯𢶈也。从木，筑聲。古文[土䈞]。', '木;筑;土;䈞', '筑;䈞', 'null', NULL, '木;土', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3618, '榦', 'ɡàn', '第六卷', '木部', '築牆耑木也。从木，倝聲。一曰本也。', '木;倝', '倝', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3619, '檥', 'yǐ', '第六卷', '木部', '榦也。从木，義聲。', '木;義', '義', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3620, '構', 'ɡòu', '第六卷', '木部', '葢也。从木，冓聲。杜林㠯爲「椽桷」字。', '木;冓;桷', '冓;桷', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3621, '模', 'mú', '第六卷', '木部', '法也。从木，莫聲。讀若「嫫母」之嫫。', '木;莫;嫫', '莫;嫫', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3622, '桴', 'fú', '第六卷', '木部', '眉棟也。从木，孚聲。', '木;孚', '孚', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3623, '棟', 'dònɡ', '第六卷', '木部', '極也。从木，東聲。', '木;東', '東', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3624, '極', 'jí', '第六卷', '木部', '棟也。从木，亟聲。', '木;亟', '亟', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3625, '柱', 'zhù', '第六卷', '木部', '楹也。从木，主聲。', '木;主', '主', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3626, '楹', 'yínɡ', '第六卷', '木部', '柱也。从木，盈聲。春秋傳曰「丹桓宮楹」。', '木;盈', '盈', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3627, '樘', 'chēnɡ', '第六卷', '木部', '柱也。从木，堂聲。', '木;堂', '堂', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3628, '𣝍', 'bì', '第六卷', '木部', '壁柱也。从木，薄省聲。', '木;薄', '薄', 'null', NULL, '木', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3629, '榰', 'zhī', '第六卷', '木部', '柱氐也。古用木，今㠯石。从木，耆聲。易曰「榰[震]恆，凶」是。', '木;耆;震', '耆;震', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3630, '㮞', 'jié', '第六卷', '木部', '欂櫨也。从木，咨聲。', '木;咨', '咨', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3631, '欂', 'bó', '第六卷', '木部', '欂櫨，柱上枅也。从木，薄聲。', '木;薄', '薄', 'null', NULL, '木', 'null', 'null', 'null', 'null', '应补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3632, '櫨', 'lú', '第六卷', '木部', '欂櫨也。从木，盧聲。伊尹曰「果之美者，箕山之東，靑鳧之所，有甘櫨焉，夏孰也」。一曰宅櫨木，出弘農山也。', '木;盧', '盧', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3633, '枅', 'jī', '第六卷', '木部', '屋欂櫨也。从木，开聲。', '木;开', '开', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3634, '栵', 'liè', '第六卷', '木部', '栭也。从木，列聲。詩曰「其灌其栵」。', '木;列', '列', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3635, '栭', 'ér', '第六卷', '木部', '屋枅上標也。从木，而聲。爾雅曰「栭謂之㮞」。', '木;而', '而', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3636, '檼', 'yìn', '第六卷', '木部', '棼也。从木，㥯聲。', '木;㥯', '㥯', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3637, '橑', 'lǎo', '第六卷', '木部', '椽也。从木，尞聲。', '木;尞', '尞', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3638, '桷', 'jué', '第六卷', '木部', '榱也。从木，角聲。椽方曰桷。春秋傳曰「刻桓宮之桷」。', '木;角', '角', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3639, '椽', 'chuán', '第六卷', '木部', '榱也。从木，彖聲。', '木;彖', '彖', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3640, '榱', 'cuī', '第六卷', '木部', '椽也。秦名屋椽也，周謂之椽，齊、魯謂之桷。从木，衰聲。', '木;衰', '衰', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3641, '楣', 'méi', '第六卷', '木部', '秦名屋𣝼䏈也。齊謂之厃，楚謂之梠。从木，眉聲。', '木;眉', '眉', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3642, '梠', 'lǚ', '第六卷', '木部', '楣也。从木，呂聲。', '木;呂', '呂', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3643, '㮰', 'pí', '第六卷', '木部', '梠也。从木，𣬉聲。讀若「枇杷」之枇。', '木;𣬉;枇', '𣬉;枇', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3644, '𣝼;櫋', 'mián', '第六卷', '木部', '屋櫋䏈也。从木，𦤝聲。', '木;𦤝', '𦤝', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3645, '檐', 'yán', '第六卷', '木部', '㮰也。从木，詹聲。', '木;詹', '詹', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3646, '橝', 'tán', '第六卷', '木部', '屋梠前也。从木，覃聲。', '木;覃', '覃', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3647, '樀', 'dí', '第六卷', '木部', '戸樀也。从木，啻聲。爾雅曰「檐謂之樀」。樀，朝門。讀與滴同。', '木;啻;滴', '啻;滴', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3648, '植', 'zhí', '第六卷', '木部', '戸植也。从木，直聲。一曰蠶槌。或从置。', '木;直;置', '直;置', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3649, '樞', 'shū', '第六卷', '木部', '戸樞也。从木，區聲。', '木;區', '區', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3650, '槏', 'qiǎn', '第六卷', '木部', '戸也。从木，兼聲。', '木;兼', '兼', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3651, '樓', 'lóu', '第六卷', '木部', '重屋也。从木，婁聲。', '木;婁', '婁', 'null', NULL, '木', 'null', 'null', 'null', 'null', '麗轨', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3652, '㰍', 'lónɡ', '第六卷', '木部', '房室之疏也。从木，龍聲。', '木;龍', '龍', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3653, '楯', 'shǔn', '第六卷', '木部', '闌檻也。从木，盾聲。', '木;盾', '盾', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3654, '櫺', 'línɡ', '第六卷', '木部', '楯閒子也。从木，霝聲。', '木;霝', '霝', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3655, '杗', 'mánɡ', '第六卷', '木部', '棟也。从木，亡聲。爾雅曰「杗𢊺謂之梁」。', '木;亡', '亡', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3656, '梀', 'cù', '第六卷', '木部', '短椽也。从木，束聲。', '木;束', '束', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3657, '杇;杅', 'wū', '第六卷', '木部', '所㠯涂也。秦謂之杇。關東謂之槾。从木，亏聲。', '木;亏', '亏', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3658, '槾', 'màn', '第六卷', '木部', '杇也。从木，曼聲。', '木;曼', '曼', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3659, '椳', 'wēi', '第六卷', '木部', '門樞謂之椳。从木，畏聲。', '木;畏', '畏', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3660, '𣔺', 'mào', '第六卷', '木部', '門樞之横梁。从木，冒聲。', '木;冒', '冒', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3661, '梱', 'kǔn', '第六卷', '木部', '門橜也。从木，困聲。', '木;困', '困', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3662, '榍', 'xiè', '第六卷', '木部', '限也。从木，㞕聲。', '木;㞕', '㞕', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3663, '柤', 'zhā', '第六卷', '木部', '木閑也。从木，且聲。', '木;且', '且', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3664, '槍', 'qiānɡ', '第六卷', '木部', '歫也。从木，倉聲。一曰槍，攘也。', '木;倉', '倉', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3665, '楗', 'jiàn', '第六卷', '木部', '歫門也。从木，建聲。', '木;建', '建', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3666, '櫼', 'jiān', '第六卷', '木部', '楔也。从木，韱聲。', '木;韱', '韱', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3667, '楔', 'xiè', '第六卷', '木部', '櫼也。从木，契聲。', '木;契', '契', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3668, '柵', 'zhà', '第六卷', '木部', '編豎木也。从木，冊聲。', '木;冊', '冊', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3669, '杝', 'lí', '第六卷', '木部', '落也。从木，也聲。讀又若阤。', '木;也;阤', '也;阤', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3670, '𣟄', 'tuò', '第六卷', '木部', '行夜所擊木。从木，橐聲。易曰「重門擊𣟄」。', '木;橐', '橐', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3671, '桓', 'huán', '第六卷', '木部', '亭郵表也。从木，亘聲。', '木;亘', '亘', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3672, '楃', 'wò', '第六卷', '木部', '木帳也。从木，屋聲。', '木;屋', '屋', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3673, '橦', 'chuánɡ', '第六卷', '木部', '帳極也。从木。童聲。', '木;童', '童', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3674, '杠', 'ɡānɡ', '第六卷', '木部', '牀前横木也。从木，工聲。', '木;工', '工', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3675, '桯', 'tīnɡ', '第六卷', '木部', '牀前几。从木，呈聲。', '木;呈', '呈', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3676, '桱', 'jīnɡ', '第六卷', '木部', '桱，桯也。東方謂之蕩。从木，巠聲。', '木;巠', '巠', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3677, '牀', 'chuánɡ', '第六卷', '木部', '安身之几坐也。从木，爿聲。', '木;爿', '爿', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3678, '枕', 'zhěn', '第六卷', '木部', '臥頭薦也。从木，冘聲。', '木;冘', '冘', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3679, '楲', 'wēi', '第六卷', '木部', '楲窬，褻器也。从木，威聲。', '木;威', '威', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3680, '櫝', 'dú', '第六卷', '木部', '匱也。从木，𧷏聲。一曰木名。又曰櫝，木枕也。', '木;𧷏', '𧷏', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3681, '櫛', 'zhì', '第六卷', '木部', '梳比之總名也。从木，節聲。', '木;節', '節', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3682, '梳', 'shū', '第六卷', '木部', '所㠯理髮也。从木，疏省聲。', '木;疏', '疏', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3683, '㭘', 'ɡé', '第六卷', '木部', '劒柙也。从木，合聲。', '木;合', '合', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3684, '槈;鎒', 'nòu', '第六卷', '木部', '薅器也。从木，辱聲。或作从金。', '木;辱', '辱', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3685, '𣚏', 'xū', '第六卷', '木部', '㭉臿也。从木人。象形。䀠聲。', '木;人;䀠', '䀠', 'null', NULL, '木;人', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3686, '㭉;釫', 'huá', '第六卷', '木部', '㒳刃臿也。从木。𦫳，象形。宋 魏曰㭉也。或从金亏。', '木;𦫳;金;亏', '亏', 'null', NULL, '木;𦫳;金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3687, '㭒;梩;耜', 'sì', '第六卷', '木部', '臿也。从木，㠯聲。一曰徙土輂。齊人語也。或从里。', '木;㠯;里', '㠯;里', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3688, '枱;鈶', 'yí', '第六卷', '木部', '耒耑也。从木，台聲。或从金台聲。籒文从辝。', '木;台;金;台;辝', '台;台;辝', 'null', NULL, '木;金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3689, '楎', 'hún', '第六卷', '木部', '六㕚𤛿。一曰𤛿上曲木𤛿轅。从木，軍聲。讀若緯。或如「渾天」之渾。', '木;軍;緯;渾', '軍;緯;渾', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3690, '櫌', 'yōu', '第六卷', '木部', '摩田器也。从木，憂聲。論語曰「櫌而不輟」。', '木;憂', '憂', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3691, '欘', 'zhú', '第六卷', '木部', '斫也。齊謂之「茲箕」。一曰斤柄性自曲者。从木，屬聲。', '木;屬', '屬', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3692, '櫡', 'zhuó', '第六卷', '木部', '斫謂之櫡。从木。箸聲。', '木;箸', '箸', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3693, '杷', 'pá', '第六卷', '木部', '收麥器。从木，巴聲。', '木;巴', '巴', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3694, '𣒃', 'yì', '第六卷', '木部', '穜樓也。一曰燒麥柃𣒃也。从木，役聲。', '木;役', '役', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3695, '柃', 'línɡ', '第六卷', '木部', '木也。从木，令聲。', '木;令', '令', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3696, '柫', 'fú', '第六卷', '木部', '擊禾連枷也。从木，弗聲。', '木;弗', '弗', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3697, '枷', 'jiā', '第六卷', '木部', '柫也。从木，加聲。淮南謂之柍。', '木;加', '加', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3698, '杵', 'chǔ', '第六卷', '木部', '舂柄也。从木，午聲。', '木;午', '午', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3699, '概;槩;槪;㮣', 'ɡài', '第六卷', '木部', '所㠯𣏙斗斛也。从木，旣聲。', '木;旣', '旣', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3700, '杚;𣏙', 'ɡài', '第六卷', '木部', '平也。从木，气聲。', '木;气', '气', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3701, '㮐', 'shěnɡ', '第六卷', '木部', '木參交，㠯支炊䉛者也。从木，省聲。讀若「驪駕」。', '木;省;驪', '省;驪', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3702, '柶', 'sì', '第六卷', '木部', '禮有「柶」。柶，匕也。从木，四聲。', '木;四', '四', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3703, '桮', 'bēi', '第六卷', '木部', '㔶也。从木，否聲。籒文桮[匚]。', '木;否;匚', '否', 'null', NULL, '木;匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3704, '槃;盤', 'pán', '第六卷', '木部', '承槃也。从木，般聲。古文从金。籒文从皿。', '木;般;金;皿', '般', 'null', NULL, '木;金;皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3705, '榹', 'sī', '第六卷', '木部', '槃也。从木，虒聲。', '木;虒', '虒', '枼;是', '榹，今作碟。方言作㼵。', '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3706, '案', 'àn', '第六卷', '木部', '几屬。从木，安聲。', '木;安', '安', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3707, '檈', 'xuán', '第六卷', '木部', '圜案也。从木，瞏聲。', '木;瞏', '瞏', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3708, '椷', 'jiān', '第六卷', '木部', '篋也。从木，咸聲。', '木;咸', '咸', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3709, '枓', 'zhǔ', '第六卷', '木部', '勺也。从木，斗聲。', '木;斗', '斗', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3710, '杓', 'biāo', '第六卷', '木部', '枓柄也。从木，勺聲。', '木;勺', '勺', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3711, '櫑;罍', 'léi', '第六卷', '木部', '龜目酒尊。刻木作雲靁象，象施不竆也。从木，从畾。畾亦聲。櫑或从缶。櫑或从皿。籒文櫑从缶囘。', '木;畾;缶;皿;缶', '畾', 'null', NULL, '木;缶;皿;缶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3712, '椑', 'pí', '第六卷', '木部', '圜𣖁也。从木，卑聲。', '木;卑', '卑', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3713, '榼;𣖁', 'kē', '第六卷', '木部', '酒器也。从木，盇聲。', '木;盇', '盇', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3714, '橢', 'tuǒ', '第六卷', '木部', '車笭中橢橢器也。从木，隋聲。', '木;隋', '隋', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3715, '槌', 'zhuì', '第六卷', '木部', '關東謂之槌。關西謂之㭙。从木，追聲。', '木;追', '追', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3716, '㭙', 'zhé', '第六卷', '木部', '槌也。从木，寺聲。', '木;寺', '寺', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3717, '栚', 'zhèn', '第六卷', '木部', '槌之横者也。關西謂之㯢。从木，灷聲。', '木;灷', '灷', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3718, '槤', 'liǎn', '第六卷', '木部', '胡槤也。从木，連聲。', '木;連', '連', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3719, '櫎', 'huǎnɡ', '第六卷', '木部', '所㠯几器。从木，廣聲。一曰帷屏屬。', '木;廣', '廣', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3720, '㮂', 'jú', '第六卷', '木部', '所㠯舉食者。从木，具聲。', '木;具', '具', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3721, '檕', 'jì', '第六卷', '木部', '繘耑木也。从木，毄聲。', '木;毄', '毄', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3722, '檷', 'nǐ', '第六卷', '木部', '絡絲柎也。从木，爾聲。讀若昵。易曰「繫於金檷」。', '木;爾;昵', '爾;昵', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3723, '機', 'jī', '第六卷', '木部', '主發謂之機。从木，幾聲。', '木;幾', '幾', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3724, '榺;𣚗', 'shènɡ', '第六卷', '木部', '機持經者。从木，朕聲。', '木;朕', '朕', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3725, '杼', 'zhù', '第六卷', '木部', '機持緯者。从木，予聲。', '木;予', '予', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3726, '椱', 'fù', '第六卷', '木部', '機持繒者。从木，复聲。', '木;复', '复', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3727, '楥', 'xuàn', '第六卷', '木部', '履法也。从木，爰聲。讀若「指撝」。', '木;爰;撝', '爰;撝', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3728, '核', 'ɡāi', '第六卷', '木部', '蠻夷㠯木皮爲匧。狀如籢尊之形也。从木，亥聲。', '木;亥', '亥', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3729, '棚', 'pénɡ', '第六卷', '木部', '棧也。从木，朋聲。', '木;朋', '朋', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3730, '棧', 'zhàn', '第六卷', '木部', '棚也。竹木之車曰棧。从木，戔聲。', '木;戔', '戔', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3731, '栫', 'jiàn', '第六卷', '木部', '㠯柴木雝也。从木，存聲。', '木;存', '存', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3732, '槶', 'ɡuì', '第六卷', '木部', '匡當也。从木，國聲。', '木;國', '國', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3733, '梯', 'tī', '第六卷', '木部', '木階也。从木，弟聲。', '木;弟', '弟', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3734, '棖', 'chénɡ', '第六卷', '木部', '杖也。从木，長聲。一曰法也。', '木;長', '長', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3735, '桊', 'juàn', '第六卷', '木部', '牛鼻環也。从木，𢍏聲。', '木;𢍏', '𢍏', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3736, '椯', 'duǒ', '第六卷', '木部', '箠也。从木，耑聲。一曰度也。一曰椯，剟也。', '木;耑', '耑', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3737, '橜;橛', 'jué', '第六卷', '木部', '弋也。从木，厥聲。一曰門梱也。', '木;厥', '厥', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3738, '樴', 'zhí', '第六卷', '木部', '弋也。从木，戠聲。', '木;戠', '戠', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3739, '杖', 'zhànɡ', '第六卷', '木部', '持也。从木，丈聲。', '木;丈', '丈', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3740, '柭', 'bā', '第六卷', '木部', '棓也。从木，犮聲。', '木;犮', '犮', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3741, '棓', 'bànɡ', '第六卷', '木部', '梲也。从木，咅聲。', '木;咅', '咅', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3742, '梲', 'tuō', '第六卷', '木部', '木杖也。从木，兌聲。', '木;兌', '兌', 'null', NULL, '木', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3743, '椎', 'chuí', '第六卷', '木部', '所㠯擊也。齊謂之「終葵」。从木，隹聲。', '木;隹', '隹', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3744, '柯', 'kē', '第六卷', '木部', '斧柄也。从木，可聲。', '木;可', '可', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3745, '柄', 'bǐnɡ', '第六卷', '木部', '柯也。从木，丙聲。或从秉。', '木;丙;秉', '丙;秉', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3746, '柲', 'bì', '第六卷', '木部', '欑也。从木，必聲。', '木;必', '必', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3747, '欑', 'cuán', '第六卷', '木部', '積竹杖。从木，贊聲。一曰穿也。一曰叢木。', '木;贊', '贊', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3748, '杘;柅', 'chì', '第六卷', '木部', '篗柄也。从木，尸聲。杘或从木，尼聲。', '木;尸;尼', '尸;尼', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3749, '榜', 'bǎng', '第六卷', '木部', '所㠯輔弓弩也。从木，旁聲。', '木;旁', '旁', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3750, '㯳', 'qínɡ', '第六卷', '木部', '榜也。从木，敬聲。', '木;敬', '敬', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3751, '𨽌;檃', 'yǐn', '第六卷', '木部', '桰也。从木，隱省聲。', '木;隱', '隱', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3752, '桰', 'kuò', '第六卷', '木部', '檃也。从木，𠯑聲。一曰矢桰，檃弦處。', '木;𠯑', '𠯑', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3753, '棐', 'fěi', '第六卷', '木部', '輔也。从木，非聲。', '木;非', '非', 'null', NULL, '木', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3754, '棊', 'qí', '第六卷', '木部', '簙棊。从木，其聲。', '木;其', '其', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3755, '椄', 'jiē', '第六卷', '木部', '續木也。从木，妾聲。', '木;妾', '妾', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3756, '栙', 'xiánɡ', '第六卷', '木部', '栙雙也。从木，夅聲。讀若鴻。', '木;夅;鴻', '夅;鴻', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3757, '栝', 'tiǎn', '第六卷', '木部', '炊竈木。从木，㐁聲。', '木;㐁', '㐁', 'null', NULL, '木', 'null', 'null', 'null', 'null', '侵部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3758, '槽', 'cáo', '第六卷', '木部', '嘼之食器。从木，曹聲。', '木;曹', '曹', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3759, '臬', 'niè', '第六卷', '木部', '射埻旳也。从木，自聲。', '木;自', '自', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3760, '桶', 'tǒnɡ', '第六卷', '木部', '方木器也。受十六斗。从木，甬聲。', '木;甬', '甬', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3761, '櫓;樐', 'lǔ', '第六卷', '木部', '大盾也。从木，魯聲。或从鹵。', '木;魯;鹵', '魯;鹵', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3762, '樂', 'yuè', '第六卷', '木部', '五聲八音總名。象𡔷鞞。木，𧇽也。', '木', '', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3763, '柎', 'fū', '第六卷', '木部', '闌足也。从木，付聲。', '木;付', '付', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3764, '枹', 'fú', '第六卷', '木部', '擊𡔷柄也。从木，包聲。', '木;包', '包', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3765, '椌', 'qiānɡ', '第六卷', '木部', '柷。樂也。从木，空聲。', '木;空', '空', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3766, '柷', 'zhù', '第六卷', '木部', '樂。木椌也。所㠯止音爲節。从木，祝省聲。', '木;祝', '祝', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3767, '槧', 'qiàn', '第六卷', '木部', '牘樸也。从木，斬聲。', '木;斬', '斬', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3768, '札', 'zhá', '第六卷', '木部', '牒也。从木，乙[𠄌]聲。', '木;𠄌', '𠄌', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3769, '檢', 'jiǎn', '第六卷', '木部', '書署也。从木，僉聲。', '木;僉', '僉', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3770, '檄', 'xí', '第六卷', '木部', '尺二書。从木，敫聲。', '木;敫', '敫', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3771, '棨', 'qǐ', '第六卷', '木部', '傳信也。从木，啟省聲。', '木;啟', '啟', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3772, '楘', 'mù', '第六卷', '木部', '車𠪱録。束文也。从木，敄聲。詩曰「五楘梁輈」。', '木;敄', '敄', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3773, '梐', 'bì', '第六卷', '木部', '梐枑，行馬也。从木，陛省聲。周禮曰「設梐枑再重」。', '木;陛', '陛', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3774, '枑', 'hù', '第六卷', '木部', '梐枑也。从木，互聲。', '木;互', '互', 'null', NULL, '木', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3775, '极', 'jí', '第六卷', '木部', '驢上負也。从木，及聲。或讀若急。', '木;及;急', '及;急', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3776, '㭕', 'qū', '第六卷', '木部', '极也。从木，去聲。', '木;去', '去', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3777, '槅', 'ɡé', '第六卷', '木部', '大車枙。从木，鬲聲。', '木;鬲', '鬲', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3778, '橾', 'shū', '第六卷', '木部', '車轂中空也。从木，喿聲。讀若藪。', '木;喿;藪', '喿;藪', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3779, '楇', 'huò', '第六卷', '木部', '盛膏器。从木，咼聲。', '木;咼', '咼', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3780, '枊', 'ànɡ', '第六卷', '木部', '馬柱也。从木，卬聲。一曰豎也。', '木;卬', '卬', 'null', NULL, '木', 'null', 'null', 'null', 'null', '阳部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3781, '棝', 'ɡù', '第六卷', '木部', '棝斗，可射鼠。从木，固聲。', '木;固', '固', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3782, '欙', 'léi', '第六卷', '木部', '山行所乘者。从木，纍聲。虞書曰「予乘四載」。水行乘舟，陸行乘車，山行乘欙，澤行乘䡅。', '木;纍', '纍', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3783, '榷', 'què', '第六卷', '木部', '水上横木，所㠯渡者。从木，隺聲。', '木;隺', '隺', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3784, '橋', 'qiáo', '第六卷', '木部', '水梁也。从木，喬聲。', '木;喬', '喬', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3785, '梁', 'liánɡ', '第六卷', '木部', '水橋也。从木水。刅聲。古文。', '木;水;刅', '刅', 'null', NULL, '木;水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3786, '𣔱', 'sōu', '第六卷', '木部', '船總名。从木，叜聲。', '木;叜', '叜', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3787, '橃', 'fá', '第六卷', '木部', '海中大船。从木，發聲。', '木;發', '發', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3788, '楫', 'jí', '第六卷', '木部', '所㠯擢舟也。从木，咠聲。', '木;咠', '咠', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3789, '欚', 'lǐ', '第六卷', '木部', '江中大船也。从木，蠡聲。', '木;蠡', '蠡', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3790, '校', 'jiào', '第六卷', '木部', '木囚也。从木，交聲。', '木;交', '交', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3791, '樔', 'cháo', '第六卷', '木部', '澤中守艸樓。从木，巢聲。', '木;巢', '巢', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3792, '采', 'cǎi', '第六卷', '木部', '捋取也。从木，从爪。', '木;爪', '', 'null', NULL, '木;爪', 'null', 'null', 'null', 'null', '之部', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3793, '杮', 'fèi', '第六卷', '木部', '削木朴也。从木，𣎵聲。陳、楚謂之「札杮」。', '木;𣎵', '𣎵', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3794, '横;橫', 'hénɡ', '第六卷', '木部', '闌木也。从木，黃聲。', '木;黃', '黃', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3795, '梜', 'jiā', '第六卷', '木部', '檢柙也。从木，夾聲。', '木;夾', '夾', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3796, '桄', 'ɡuànɡ', '第六卷', '木部', '充也。从木，光聲。', '木;光', '光', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3797, '檇', 'zuì', '第六卷', '木部', '㠯木有所𢶈也。从木，雋聲。春秋傳曰「越敗吳於檇李」。', '木;雋', '雋', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3798, '椓', 'zhuó', '第六卷', '木部', '擊也。从木，豖聲。', '木;豖', '豖', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3799, '朾', 'chénɡ', '第六卷', '木部', '撞也。从木，丁聲。', '木;丁', '丁', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3800, '柧', 'ɡū', '第六卷', '木部', '棱也。从木，瓜聲。又柧棱，殿堂上冣高之處也。', '木;瓜', '瓜', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3801, '棱', 'lénɡ', '第六卷', '木部', '柧也。从木，夌聲。', '木;夌', '夌', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3802, '𣡌;櫱', 'niè', '第六卷', '木部', '伐木餘也。从木，獻聲。商書曰「若顚木之有㽕𣡌」。𣡌或从木，辥聲。古文𣡌，从木無頭。亦古文𣡌[羍]。', '木;獻;辥;羍', '獻;辥;羍', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3803, '枰', 'pínɡ', '第六卷', '木部', '平也。从木平。平亦聲。', '木;平', '平', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3804, '柆', 'lā', '第六卷', '木部', '折木也。从木，立聲。', '木;立', '立', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3805, '槎', 'chá', '第六卷', '木部', '衺斫也。从木，差聲。春秋傳曰「山不槎」。', '木;差', '差', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3806, '檮;𣚑', 'dǎo', '第六卷', '木部', '𣚑柮，𣃔木也。从木，𠷎聲。春秋傳曰「𣚑柮」。', '木;𠷎', '𠷎', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3807, '柮', 'wù', '第六卷', '木部', '檮柮也。从木，出聲。讀若爾雅「貀無前足」之貀。[兀]', '木;出;貀;兀', '出;貀;兀', 'null', NULL, '木', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3808, '析', 'xī', '第六卷', '木部', '破木也。一曰折也。从木，从斤。', '木;斤', '', 'null', NULL, '木;斤', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3809, '棷', 'zōu', '第六卷', '木部', '木薪也。从木，取聲。', '木;取', '取', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3810, '梡', 'hùn', '第六卷', '木部', '㮯。木薪也。从木，完聲。', '木;完', '完', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3811, '㮯', 'hún', '第六卷', '木部', '梡。木未析也。从木，圂聲。', '木;圂', '圂', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3812, '楄', 'pián', '第六卷', '木部', '楄部，方木也。从木，扁聲。春秋傳曰「楄部薦榦」。', '木;扁', '扁', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3813, '楅', 'bī', '第六卷', '木部', '㠯木有所畐束也。从木，畐聲。詩曰「夏而楅衡」。', '木;畐', '畐', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3814, '枼', 'yè', '第六卷', '木部', '楄也。枼，薄也。从木，世聲。', '木;世', '世', 'null', NULL, '木', 'null', 'null', 'null', 'null', '盍部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3815, '槱', 'yǒu', '第六卷', '木部', '積木燎之也。从木火，酉聲。詩曰「薪之槱之」。周禮「㠯槱燎祠司中、司命」。槱或从示。柴祭天神也。', '木;火;酉;示', '酉', 'null', NULL, '木;火;示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3816, '休;庥', 'xiū', '第六卷', '木部', '息止也。从人依木。休或从广。', '人;木;广', '', 'null', NULL, '人;木;广', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3817, '㮓;𣘰;𣕲;𠄭;亙;𠄣;𠀕', 'ɡèn', '第六卷', '木部', '竟也。从木，恆聲。古文㮓。', '木;恆', '恆', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3818, '械', 'xiè', '第六卷', '木部', '桎梏也。从木，戒聲。一曰械，器之總名。一曰械，治也。一曰有所盛曰器。無所盛曰械。', '木;戒', '戒', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3819, '杽', 'chǒu', '第六卷', '木部', '械也。从木手。手亦聲。', '木;手', '手', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3820, '桎', 'zhì', '第六卷', '木部', '足械也。所㠯質地。从木，至聲。', '木;至', '至', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3821, '梏', 'ɡù', '第六卷', '木部', '手械也。所㠯告天。从木，告聲。', '木;告', '告', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3822, '櫪', 'lì', '第六卷', '木部', '櫪㯕，柙指也。从木，𠪱聲。', '木;𠪱', '𠪱', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3823, '㯕', 'xī', '第六卷', '木部', '櫪㯕也。从木，斯聲。', '木;斯', '斯', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3824, '檻', 'jiàn', '第六卷', '木部', '櫳也。从木，監聲。一曰圈。', '木;監', '監', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3825, '櫳', 'lónɡ', '第六卷', '木部', '檻也。从木，龍聲。', '木;龍', '龍', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3826, '柙', 'xiá', '第六卷', '木部', '檻也。所㠯臧虎兕也。从木，甲聲。古文柙[未详]。', '木;甲', '甲', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3827, '棺', 'ɡuān', '第六卷', '木部', '關也。所㠯掩屍。从木，官聲。', '木;官', '官', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3828, '櫬', 'chèn', '第六卷', '木部', '棺也。从木，親聲。春秋傳曰「士輿櫬」。', '木;親', '親', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3829, '槥', 'huì', '第六卷', '木部', '棺櫝也。从木，彗聲。', '木;彗', '彗', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3830, '椁', 'ɡuǒ', '第六卷', '木部', '葬有木𩫖也。从木𩫖。𩫖亦聲。', '木;𩫖', '𩫖', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3831, '楬', 'jié', '第六卷', '木部', '桀也。从木，曷聲。春秋傳曰「楬而書之」。', '木;曷', '曷', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3832, '䲷;梟', 'xiāo', '第六卷', '木部', '不孝鳥也。故日至，捕梟磔之。从鳥在木上。', '鳥;木', '', 'null', NULL, '鳥;木', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3833, '栀;梔', 'zhī', '第六卷', '木部', '木實可染。从木，卮聲。', '木;卮', '卮', 'null', NULL, '木', 'null', 'null', 'null', 'null', '新 | 应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3834, '榭', 'xiè', '第六卷', '木部', '臺有屋也。从木，射聲。', '木;射', '射', 'null', NULL, '木', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3835, '槊', 'shuò', '第六卷', '木部', '矛也。从木，朔聲。', '木;朔', '朔', 'null', NULL, '木', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3836, '椸', 'yí', '第六卷', '木部', '衣架也。从木，施聲。', '木;施', '施', 'null', NULL, '木', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3837, '榻', 'tà', '第六卷', '木部', '牀也。从木，𦐇聲。', '木;𦐇', '𦐇', 'null', NULL, '木', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3838, '櫍', 'zhì', '第六卷', '木部', '柎也。从木，質聲。', '木;質', '質', 'null', NULL, '木', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3839, '櫂;棹', 'zhào', '第六卷', '木部', '所㠯進舩也。从木，翟聲。[棹]或从卓。', '木;翟;卓', '翟;卓', 'null', NULL, '木', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3840, '槹', 'ɡāo', '第六卷', '木部', '桔槹，汲水器也。从木，臯聲。', '木;臯', '臯', 'null', NULL, '木', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3841, '樁', 'zhuānɡ', '第六卷', '木部', '橛杙也。从木，舂聲。', '木;舂', '舂', 'null', NULL, '木', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3842, '櫻', 'yīnɡ', '第六卷', '木部', '果也。从木，嬰聲。', '木;嬰', '嬰', 'null', NULL, '木', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3843, '栜', 'sè', '第六卷', '木部', '桋也。从木，策省聲。', '木;策', '策', 'null', NULL, '木', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3844, '東', 'dōnɡ', '第六卷', '東部', '動也。从木。官溥說，从日在木中。凡東之屬皆从東。', '日;木', '', 'null', NULL, '日;木', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3845, '㯥', 'cáo', '第六卷', '東部', '二東。曹從此。闕。讀若束。', '東;曹;束', '曹;束', 'null', NULL, '東', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3846, '林', 'lín', '第六卷', '林部', '平土有叢木曰林。从二木。凡林之屬皆从林。', '木', '', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3847, '𣞤;𣟒;𡙻;橆;𣚨', 'wú', '第六卷', '林部', '豐也。从林𡘲。𡘲，或說「規模」字。從大𠦜。𠦜，數之積也。林者，木之多也。𣞤與庶同意。商書曰「庶艸䋣𣞤[廡]」。', '林;𡘲;大;𠦜;廡', '廡', 'null', NULL, '林;𡘲;大;𠦜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3848, '鬱', 'yù', '第六卷', '林部', '木叢者。从林，𩰪省聲。', '林;𩰪', '𩰪', 'null', NULL, '林', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3849, '楚', 'chǔ', '第六卷', '林部', '叢木。一名荊也。从林，疋聲。', '林;疋', '疋', 'null', NULL, '林', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3850, '棽', 'chēn', '第六卷', '林部', '木枝條棽儷也。从林，今聲。', '林;今', '今', 'null', NULL, '林', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3851, '楙', 'mào', '第六卷', '林部', '木盛也。从木，矛聲。', '木;矛', '矛', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3852, '麓', 'lù', '第六卷', '林部', '守山林吏也。从林，鹿聲。一曰林屬於山爲麓。春秋傳曰「沙麓崩」。古文从录。', '林;鹿;录', '鹿;录', 'null', NULL, '林', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3853, '棼', 'fén', '第六卷', '林部', '複屋棟也。从林，分聲。', '林;分', '分', 'null', NULL, '林', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3854, '森', 'sēn', '第六卷', '林部', '木多皃。从林，从木。讀若「曾參」之參。', '林;木;參', '參', 'null', NULL, '林;木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3855, '梵', 'fàn', '第六卷', '林部', '[从林，凡聲。]出自西域釋書，未詳意義。', '林;凡', '凡', 'null', NULL, '林', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3856, '才', 'cái', '第六卷', '才部', '艸木之初也。從丨上貫一。將生枝葉也。一，地也。凡才之屬皆从才。', '丨;一', '', 'null', NULL, '丨;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3857, '叒', 'ruò', '第六卷', '叒部', '日初出東方湯谷所登榑桑，叒木也。象形。凡叒之屬皆从叒。籒文[口卪]。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '️', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3858, '桑', 'sānɡ', '第六卷', '叒部', '蠶所食葉木。从叒木。', '叒;木', '', 'null', NULL, '叒;木', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3859, '之', 'zhī', '第六卷', '之部', '出也。象艸過屮。枝莖漸益大。有所之也。一者地也。凡之之屬皆从之。', '屮;一', '', 'null', NULL, '屮;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3860, '㞷', 'huánɡ', '第六卷', '之部', '艸木妄生也。从之在土上。讀若皇。古文[𡈼]。', '之;土;皇;𡈼', '皇', 'null', NULL, '之;土;𡈼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3861, '帀', 'zā', '第六卷', '帀部', '𠣘也。从反之而帀也。凡帀之屬皆从帀。周盛說。', '之', '', 'null', NULL, '之', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3862, '師', 'shī', '第六卷', '帀部', '二千五百人爲師。从帀，从𠂤。𠂤，四帀衆意也。古文師[未详]。', '帀;𠂤', '', 'null', NULL, '帀;𠂤', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3863, '出;岀', 'chū', '第六卷', '出部', '進也。象艸木益茲，上出𨔶也。凡出之屬皆从出。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '🔴', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3864, '敖;𢾕', 'áo', '第六卷', '出部', '游也。从出，从放。', '出;放', '', 'null', NULL, '出;放', 'null', 'null', 'null', 'null', '重出应删', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3865, '賣;𧷓', 'mài', '第六卷', '出部', '出物貨也。从出，从買。', '出;買', '', 'null', NULL, '出;買', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3866, '糶', 'tiào', '第六卷', '出部', '出穀也。从出，从䊮。䊮亦聲。', '出;䊮', '䊮', 'null', NULL, '出', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3867, '𦤙', 'niè', '第六卷', '出部', '槷𦤙，不安也。从出，臬聲。易曰「槷𦤙」。', '出;臬', '出', 'null', NULL, '臬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3868, '𣎵', 'pō', '第六卷', '𣎵部', '艸木盛𣎵𣎵然。象形。八聲。凡𣎵之屬皆从𣎵。讀若輩。[巿（参索字）]', '八;輩;巿', '八;輩;巿', 'null', NULL, '', 'null', 'null', 'null', 'null', '🤰潑', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3869, '𢍚', 'wèi', '第六卷', '𣎵部', '艸木𢍚孛之皃。从𣎵，畀聲。', '𣎵;畀', '畀', 'null', NULL, '𣎵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3870, '孛', 'bèi', '第六卷', '𣎵部', '𢍚孛也。从𣎵，从子。人色也，故从子。論語曰「色孛如也」。', '𣎵;子', '', 'null', NULL, '𣎵;子', 'null', 'null', 'null', 'null', '调序', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3871, '索', 'suǒ', '第六卷', '𣎵部', '艸有莖葉可作繩索。从𣎵糸。杜林說，𣎵，亦朱巿字。', '𣎵;糸', '', 'null', NULL, '𣎵;糸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3872, '𠂔', 'zǐ', '第六卷', '𣎵部', '止也。从𣎵盛而一橫止之也。', '𣎵;一', '', 'null', NULL, '𣎵;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3873, '南', 'nán', '第六卷', '𣎵部', '艸木至南方有枝任也。从𣎵，𢆉聲。古文。', '𣎵;𢆉;任', '𢆉;任', 'null', NULL, '𣎵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3874, '生;𤯓', 'shēnɡ', '第六卷', '生部', '進也。象艸木生出土上。凡生之屬皆从生。', '土', '', 'null', NULL, '土', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3875, '丰', 'fēnɡ', '第六卷', '生部', '艸盛丰丰也。从𤯓。上下𨔶也。', '𤯓', '', 'null', NULL, '𤯓', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3876, '産;產', 'chǎn', '第六卷', '生部', '生也。从生，彥省聲。', '生;彥', '彥', 'null', NULL, '生', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3877, '隆', 'lónɡ', '第六卷', '生部', '豐大也。从生，降聲。', '生;降', '降', 'null', NULL, '生', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3878, '甤', 'ruí', '第六卷', '生部', '艸木實甤甤也。从生，豕聲。讀若綏。', '生;豕;綏', '豕;綏', 'null', NULL, '生', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3879, '甡', 'shēn', '第六卷', '生部', '衆生竝立之皃。从二生。詩曰「甡甡其鹿」。', '生', '', 'null', NULL, '生', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3880, '乇', 'zhé', '第六卷', '乇部', '艸葉也。上毌一，下有根。象形字。凡乇之屬皆从乇。', '一', '', 'null', NULL, '一', 'null', 'null', 'null', 'null', '⚙️', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3881, '𠂹;𢏒;𠣔', 'chuí', '第六卷', '𠂹部', '艸木華葉𠂹。象形。凡𠂹之屬皆从𠂹。古文[未详]。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3882, '𠌶;荂', 'huā', '第六卷', '𠌶部', '艸木華也。从𠂹，亏聲。凡𠌶之屬皆从𠌶。𠌶或从艸，从夸。', '𠂹;亏;艸;夸', '亏;夸', 'null', NULL, '𠂹;艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3883, '韡', 'wěi', '第六卷', '𠌶部', '盛也。从𠌶，韋聲。詩曰「㖾不韡韡」。', '𠌶;韋', '韋', 'null', NULL, '𠌶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3884, '華', 'huā', '第六卷', '華部', '榮也。从艸𠌶。凡華之屬皆从華。', '艸;𠌶', '𠌶', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3885, '皣', 'yè', '第六卷', '華部', '艸木白華皃。从華，从白。', '華;白', '', 'null', NULL, '華;白', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3886, '𥝌', 'jī', '第六卷', '𥝌部', '木之曲頭止，不能上也。凡𥝌之屬皆从𥝌。', '木', '', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3887, '䅩', 'zhǐ', '第六卷', '𥝌部', '䅩䅓，多小意而止也。从𥝌，从支，只聲。一曰木也。', '𥝌;支;只', '只', 'null', NULL, '𥝌;支', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3888, '䅓', 'jǔ', '第六卷', '𥝌部', '䅩䅓也。从𥝌，从又，句聲。一曰木名。', '又;句', '句', 'null', NULL, '又', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3889, '稽', 'jī', '第六卷', '稽部', '畱止也。从𥝌，从尤，旨聲。凡稽之屬皆从稽。', '𥝌;尤;旨', '旨', 'null', NULL, '𥝌;尤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3890, '𥢔', 'zhuó', '第六卷', '稽部', '特止也。从稽省，卓聲。', '稽;卓', '卓', 'null', NULL, '稽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3891, '𥢑', 'ɡǎo', '第六卷', '稽部', '𥢑䅓而止也。从稽省，咎聲。讀若晧。賈侍中說，稽、𥢔、𥢑三字皆木名。', '稽;咎;晧', '咎;晧', 'null', NULL, '稽', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3892, '巢', 'cháo', '第六卷', '巢部', '鳥在木上曰巢。在穴曰窠。从木，象形。凡巢之屬皆从巢。', '木', '', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3893, '𦥘', 'biǎn', '第六卷', '巢部', '傾覆也。从寸𦥑覆之。寸，人手也。𦥑，从巢省。杜林說，㠯爲「貶損」之貶。', '寸;𦥑;巢;貶', '貶', 'null', NULL, '寸;臼;巢', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3894, '桼', 'qī', '第六卷', '桼部', '木汁。可㠯䰍物。从木。象形。桼如水滴而下也。凡桼之屬皆从桼。', '木', '', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3895, '䰍', 'xiū', '第六卷', '桼部', '桼也。从桼，髟聲。', '桼;髟', '髟', 'null', NULL, '桼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3896, '㯡', 'pào', '第六卷', '桼部', '桼垸巳，復桼之。从桼，包聲。', '桼;包', '包', 'null', NULL, '桼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3897, '束', 'shù', '第六卷', '束部', '縛也。从囗木。凡束之屬皆从束。', '囗;木', '', 'null', NULL, '囗;木', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3898, '柬', 'jiǎn', '第六卷', '束部', '分別𥳑之也。从束八。八，分別也。', '束;八', '', 'null', NULL, '束;八', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3899, '𢆚;𢆞', 'jiǎn', '第六卷', '束部', '小束也。从束，开聲。讀若繭。', '束;开;繭', '开;繭', 'null', NULL, '束', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3900, '剌', 'là', '第六卷', '束部', '戾也。从束，从刀。刀束者，剌之也。', '束;刀', '', 'null', NULL, '束;刀', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3901, '㯻', 'ɡǔn', '第六卷', '㯻部', '橐也。从束，圂聲。凡㯻之屬皆从㯻。', '束;圂', '圂', 'null', NULL, '束', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3902, '橐;槖;𢄿', 'tuó', '第六卷', '㯻部', '囊也。从㯻省，石聲。', '㯻;石', '石', 'null', NULL, '㯻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3903, '囊', 'nánɡ', '第六卷', '㯻部', '橐也。从㯻省，𤕦聲。', '㯻;𤕦', '𤕦', 'null', NULL, '㯻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3904, '櫜', 'ɡāo', '第六卷', '㯻部', '車上大橐。从㯻省，咎聲。詩曰「載櫜弓矢」。', '㯻;咎', '咎', 'null', NULL, '㯻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3905, '㯱', 'pāo', '第六卷', '㯻部', '囊張大皃。从㯻省，缶聲。', '㯻;缶', '缶', 'null', NULL, '㯻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3906, '囗', 'wéi', '第六卷', '囗部', '回也。象回帀之形。凡囗之屬皆从囗。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3907, '圜', 'yuán', '第六卷', '囗部', '天體也。从囗，瞏聲。', '囗;瞏', '瞏', 'null', NULL, '囗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3908, '團', 'tuán', '第六卷', '囗部', '圜也。从囗，專聲。', '囗;專', '專', 'null', NULL, '囗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3909, '㘣', 'xuán', '第六卷', '囗部', '規也。从囗，肙聲。', '囗;肙', '肙', 'null', NULL, '囗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3910, '囩', 'yún', '第六卷', '囗部', '回也。从囗，云聲。', '囗;云', '云', 'null', NULL, '囗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3911, '圓', 'yuán', '第六卷', '囗部', '圜全也。从囗，員聲。讀若員。', '囗;員', '員', 'null', NULL, '囗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3912, '回;囘', 'huí', '第六卷', '囗部', '轉也。从囗。中象回轉之形。古文。', '囗', '', 'null', NULL, '囗', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3913, '圖', 'tú', '第六卷', '囗部', '畫計難也。从囗，从啚。啚，難意也。', '囗;啚', '', 'null', NULL, '囗;啚', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3914, '圛', 'yì', '第六卷', '囗部', '回行也。从囗，睪聲。商書曰「曰圛」。圛者，升雲半有半無。讀若驛。', '囗;睪;驛', '睪;驛', 'null', NULL, '囗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3915, '國', 'ɡuó', '第六卷', '囗部', '邦也。从囗，从或。', '囗;或', '或', 'null', NULL, '囗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3916, '壼;𡈋', 'kǔn', '第六卷', '囗部', '宮中道。从囗。[从束省]象宮垣，道上之形。詩曰「室家之壼」。', '囗;束', '', 'null', NULL, '囗;束', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3917, '囷', 'qūn', '第六卷', '囗部', '廩之圜者。从禾在囗中。圜謂之囷，方謂之京。', '禾;囗', '', 'null', NULL, '禾;囗', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3918, '圈', 'juàn', '第六卷', '囗部', '養畜之閑也。从囗，卷聲。', '囗;卷', '卷', 'null', NULL, '囗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3919, '囿;𡈹', 'yòu', '第六卷', '囗部', '苑有垣也。从囗，有聲。一曰所㠯養禽獸曰囿。籒文囿[𡈹]。', '囗;有;𡈹', '有', 'null', NULL, '囗;𡈹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3920, '園', 'yuán', '第六卷', '囗部', '所㠯樹果也。从囗，袁聲。', '囗;袁', '袁', 'null', NULL, '囗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3921, '圃', 'pǔ', '第六卷', '囗部', '所㠯穜菜曰圃。从囗，甫聲。', '囗;甫', '甫', 'null', NULL, '囗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3922, '因', 'yīn', '第六卷', '囗部', '就也。从囗大。', '囗;大', '', 'null', NULL, '囗;大', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3923, '㘝', 'nà', '第六卷', '囗部', '下取物縮臧之。从又，从囗。讀若聶。', '又;囗;聶', '聶', 'null', NULL, '又;囗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3924, '囹', 'línɡ', '第六卷', '囗部', '獄也。从囗，令聲。', '囗;令', '令', 'null', NULL, '囗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3925, '圄', 'yǔ', '第六卷', '囗部', '守之也。从囗，吾聲。', '囗;吾', '吾', 'null', NULL, '囗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3926, '囚', 'qiú', '第六卷', '囗部', '繫也。从人在囗中。', '人;囗', '', 'null', NULL, '人;囗', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3927, '固', 'ɡù', '第六卷', '囗部', '四塞也。从囗，古聲。', '囗;古', '古', 'null', NULL, '囗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3928, '圍', 'wéi', '第六卷', '囗部', '守也。从囗，韋聲。', '囗;韋', '韋', 'null', NULL, '囗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3929, '困;𣏔', 'kùn', '第六卷', '囗部', '故廬也。从木在囗中。古文困[止木]。', '木;囗;止;木', '', 'null', NULL, '木;囗;止;木', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3930, '圂', 'hùn', '第六卷', '囗部', '豕廁也。从囗。象豕在囗中也。會意。', '囗;豕', '', 'null', NULL, '囗;豕', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3931, '囮;㘥', 'é', '第六卷', '囗部', '譯也。从囗，化聲。率烏者繫生烏㠯來之，名曰囮。讀若譌。囮或从䌛。', '囗;化;譌;䌛', '化;譌;䌛', 'null', NULL, '囗', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3932, '員', 'yuán', '第六卷', '員部', '物數也。从貝，囗聲。凡員之屬皆从員。籒文从鼎。', '貝;囗;鼎', '囗', 'null', NULL, '貝;鼎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3933, '𧶊', 'yún', '第六卷', '員部', '物數紛𧶊亂也。从員，云聲。讀若春秋傳曰「宋 皇鄖」。', '員;云;鄖', '云;鄖', 'null', NULL, '員', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3934, '貝', 'bèi', '第六卷', '貝部', '海介蟲也。居陸名猋。在水名蜬。象形。古者貨貝而寶龜，周而有泉，至秦廢貝行錢。凡貝之屬皆从貝。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3935, '𧴪', 'suǒ', '第六卷', '貝部', '貝聲也。从小貝。', '小;貝', '', 'null', NULL, '小;貝', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3936, '賄', 'huì', '第六卷', '貝部', '財也。从貝，有聲。', '貝;有', '有', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3937, '財', 'cái', '第六卷', '貝部', '人所寶也。从貝，才聲。', '貝;才', '才', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3938, '貨', 'huò', '第六卷', '貝部', '財也。从貝，化聲。', '貝;化', '化', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3939, '䞈', 'ɡuì', '第六卷', '貝部', '資也。从貝，爲聲。或曰此古貨字。[讀若貴。]', '貝;爲;貨;貴', '爲;貨;貴', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3940, '資', 'zī', '第六卷', '貝部', '貨也。从貝，次聲。', '貝;次', '次', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3941, '贎', 'wàn', '第六卷', '貝部', '贈貨也。从貝，萬聲。', '貝;萬', '萬', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
