INSERT INTO `shuowen_voice_revel` VALUES (3944, '賁', 'bì', '第六卷', '貝部', '飾也。从貝，卉聲。', '貝;卉', '卉', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3945, '賀', 'hè', '第六卷', '貝部', '㠯禮物相奉慶也。从貝，加聲。', '貝;加', '加', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3946, '貢', 'ɡònɡ', '第六卷', '貝部', '獻功也。从貝，工聲。', '貝;工', '工', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3947, '贊', 'zàn', '第六卷', '貝部', '見禮也。从貝，从兟。', '貝;兟', '兟', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3948, '賮', 'jìn', '第六卷', '貝部', '會禮也。从貝，㶳聲。', '貝;㶳', '㶳', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3949, '齎', 'jī', '第六卷', '貝部', '持遺也。从貝，齊聲。', '貝;齊', '齊', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3950, '貸', 'dài', '第六卷', '貝部', '施也。从貝，代聲。', '貝;代', '代', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3951, '貣', 'tè', '第六卷', '貝部', '從人求物也。从貝，弋聲。', '貝;弋', '弋', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3952, '賂', 'lù', '第六卷', '貝部', '遺也。从貝，各聲。', '貝;各', '各', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3953, '賸', 'shènɡ', '第六卷', '貝部', '物相增加也。从貝，朕聲。一曰送也，副也。', '貝;朕', '朕', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3954, '贈', 'zènɡ', '第六卷', '貝部', '玩好相送也。从貝，曾聲。', '貝;曾', '曾', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3955, '貱', 'bì', '第六卷', '貝部', '迻予也。从貝，皮聲。', '貝;皮', '皮', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3956, '𥫔;贛', 'ɡònɡ', '第六卷', '貝部', '賜也。从貝，竷省聲。籒文𥫔。', '貝;竷', '竷', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3957, '賚', 'lài', '第六卷', '貝部', '賜也。从貝，來聲。周書曰「賚尒秬鬯」。', '貝;來', '來', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3958, '賞', 'shǎnɡ', '第六卷', '貝部', '賜有功也。从貝，尙聲。', '貝;尙', '尙', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3959, '賜', 'cì', '第六卷', '貝部', '予也。从貝，易聲。', '貝;易', '易', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3960, '貤', 'yì', '第六卷', '貝部', '重次弟物也。从貝，也聲。', '貝;也', '也', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3961, '贏', 'yínɡ', '第六卷', '貝部', '賈有餘利也。从貝，𣎆聲。', '貝;𣎆', '𣎆', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3962, '賴', 'lài', '第六卷', '貝部', '贏也。从貝，剌聲。', '貝;剌', '剌', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3963, '負', 'fù', '第六卷', '貝部', '恃也。从人守貝有所恃也。一曰受貸不償。', '人;貝', '', 'null', NULL, '人;貝', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3964, '貯', 'zhù', '第六卷', '貝部', '積也。从貝，宁聲。', '貝;宁', '宁', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3965, '貳', 'èr', '第六卷', '貝部', '副也，益也。从貝，弍聲。弍，古文二。', '貝;弍', '弍', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3966, '賓;賔', 'bīn', '第六卷', '貝部', '所敬也。从貝，𡧍聲。古文[元]。', '貝;𡧍;元', '𡧍', 'null', NULL, '貝;元', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3967, '賖', 'shē', '第六卷', '貝部', '貰買也。从貝，余聲。', '貝;余', '余', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3968, '貰', 'shì', '第六卷', '貝部', '貸也。从貝，世聲。', '貝;世', '世', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3969, '贅', 'zhuì', '第六卷', '貝部', '㠯物質錢。从敖貝。敖者猶放，謂貝當復取之。一曰最。', '敖;貝', '', 'null', NULL, '敖;貝', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3970, '質', 'zhì', '第六卷', '貝部', '㠯物相贅。从貝，从斦，闕。', '貝;斦', '', 'null', NULL, '貝;斦', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3971, '貿', 'mào', '第六卷', '貝部', '易財也。从貝，卯聲。', '貝;卯', '卯', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3972, '贖', 'shú', '第六卷', '貝部', '貿也。从貝，𧷏聲。', '貝;𧷏', '𧷏', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3973, '費', 'fèi', '第六卷', '貝部', '散財用也。从貝，弗聲。', '貝;弗', '弗', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3974, '責', 'zé', '第六卷', '貝部', '求也。从貝，朿聲。', '貝;朿', '朿', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3975, '賈', 'ɡǔ', '第六卷', '貝部', '賈，市也。从貝，襾聲。一曰坐賣售也。', '貝;襾', '襾', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3976, '𧶜', 'shānɡ', '第六卷', '貝部', '行賈也。从貝，商省聲。', '貝;商', '商', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3977, '販', 'fàn', '第六卷', '貝部', '買賤賣貴者。从貝，反聲。', '貝;反', '反', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3978, '買', 'mǎi', '第六卷', '貝部', '市也。从网貝。孟子曰「登壟𣃔而网市利」。', '网;貝', '', 'null', NULL, '网;貝', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3979, '貴;𡭙', 'ɡuì', '第六卷', '貝部', '物不賤也。从貝，臾聲。臾，古文蕢。', '貝;臾', '臾', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3980, '賤', 'jiàn', '第六卷', '貝部', '賈少也。从貝，戔聲。', '貝;戔', '戔', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3981, '賦', 'fù', '第六卷', '貝部', '斂也。从貝，武聲。', '貝;武', '武', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3982, '貪', 'tān', '第六卷', '貝部', '欲也。从貝，今聲。', '貝;今', '今', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3983, '貶', 'biǎn', '第六卷', '貝部', '損也。从貝，乏聲。', '貝;乏', '乏', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3984, '貧', 'pín', '第六卷', '貝部', '財分少也。从貝分。分亦聲。古文从宀分。', '貝;分;宀', '分', 'null', NULL, '貝;宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3985, '賃', 'lìn', '第六卷', '貝部', '庸也。从貝，任聲。', '貝;任', '任', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3986, '賕', 'qiú', '第六卷', '貝部', '㠯財物枉法相謝也。从貝，求聲。一曰載質也。', '貝;求', '求', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3987, '購', 'ɡòu', '第六卷', '貝部', '㠯財有所求也。从貝，冓聲。', '貝;冓', '冓', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3988, '䝪', 'shǔ', '第六卷', '貝部', '齎財卜問爲䝪。从貝，疋聲。讀若所。', '貝;疋;所', '疋;所', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3989, '貲', 'zī', '第六卷', '貝部', '小罰㠯財自贖也。从貝，此聲。漢律「民不繇貲錢二十三」。', '貝;此', '此', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3990, '賨;賩', 'cónɡ', '第六卷', '貝部', '南蠻賦也。从貝，宗聲。', '貝;宗', '宗', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3991, '𧷏;𧶠', 'yù', '第六卷', '貝部', '𧗳也。从貝，𡍬聲。𡍬，古文睦。讀若育。', '貝;𡍬;育', '𡍬;育', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3992, '賏', 'yīnɡ', '第六卷', '貝部', '頸飾也。从二貝。', '貝', '', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3993, '貺', 'kuànɡ', '第六卷', '貝部', '賜也。从貝，兄聲。', '貝;兄', '兄', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3994, '賵;𠙒', 'fènɡ', '第六卷', '貝部', '贈死者。从貝，从冒。[𠙒，从貝，凡聲。]', '貝;凡', '凡', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3995, '賭', 'dǔ', '第六卷', '貝部', '博簺也。从貝，者聲。', '貝;者', '者', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3996, '貼', 'tiē', '第六卷', '貝部', '㠯物爲質也。从貝，占聲。', '貝;占', '占', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3997, '貽', 'yí', '第六卷', '貝部', '贈遺也。从貝，台聲。', '貝;台', '台', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3998, '𧸖', 'zhuàn', '第六卷', '貝部', '重買也，錯也。从貝，廉聲。', '貝;廉', '廉', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3999, '賽', 'sài', '第六卷', '貝部', '報也。从貝，塞省聲。', '貝;塞', '塞', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4000, '賻', 'fù', '第六卷', '貝部', '助也。从貝，尃聲。', '貝;尃', '尃', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4001, '贍', 'shàn', '第六卷', '貝部', '給所求也。从貝，詹聲。', '貝;詹', '詹', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4002, '邑', 'yì', '第六卷', '邑部', '國也。从囗。先王之制，尊卑有大小。从卪。凡邑之屬皆从邑。', '囗;卪', '', 'null', NULL, '囗;卪', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4003, '邦', 'bānɡ', '第六卷', '邑部', '國也。从邑，丰聲。古文[之田]。', '邑;丰;之;田', '丰', 'null', NULL, '邑;之;田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4004, '郡', 'jùn', '第六卷', '邑部', '周制「天子地方千里，分爲百縣，縣有四郡」。故春秋傳曰「上大夫受縣，下大夫受郡」是也。至秦初，天下置三十六郡㠯監縣。从邑，君聲。', '邑;君', '君', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4005, '都', 'dū', '第六卷', '邑部', '有先君之舊宗廟曰都。从邑，者聲。周禮「歫國五百里爲都」。', '邑;者', '者', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4006, '鄰', 'lín', '第六卷', '邑部', '五家爲鄰。从邑，粦聲。', '邑;粦', '粦', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4007, '酇', 'zuǎn', '第六卷', '邑部', '百家爲酇。酇，聚也。从邑，贊聲。南陽有酇縣。', '邑;贊', '贊', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4008, '鄙', 'bǐ', '第六卷', '邑部', '五酇爲鄙。从邑，啚聲。', '邑;啚', '啚', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4009, '郊', 'jiāo', '第六卷', '邑部', '歫國百里爲郊。从邑，交聲。', '邑;交', '交', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4010, '邸', 'dǐ', '第六卷', '邑部', '屬國舍也。从邑，氐聲。', '邑;氐', '氐', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4011, '郛', 'fú', '第六卷', '邑部', '𩫏也。从邑，孚聲。', '邑;孚', '孚', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4012, '郵', 'yóu', '第六卷', '邑部', '竟上行書舍。从邑垂。垂，邊也。', '邑;垂', '', 'null', NULL, '邑;垂', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4013, '𨛍', 'shào', '第六卷', '邑部', '國甸。大夫稍稍所食邑。从邑，肖聲。周禮曰「任𨛍地在天子三百里之內」。', '邑;肖', '肖', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4014, '鄯', 'shàn', '第六卷', '邑部', '鄯善，西胡國也。从邑善。善亦聲。', '邑;善', '善', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4015, '𥨪', 'qiónɡ', '第六卷', '邑部', '夏后時諸矦夷羿國也。从邑，竆省聲。', '邑;竆', '竆', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4016, '𨜒', 'jì', '第六卷', '邑部', '周封黃帝之後於𨜒也。从邑，契聲。讀若薊。上谷有𨜒縣。', '邑;契;薊', '契;薊', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4017, '邰', 'tái', '第六卷', '邑部', '炎帝之後姜姓所封。周棄外家國。从邑，台聲。右扶風 斄縣是也。詩曰「有邰家室」。', '邑;台', '台', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4018, '𨙸;岐', 'qí', '第六卷', '邑部', '周文王所封。在右扶風 美陽 中水鄉。从邑，支聲。岐𨙸或从山，支聲。因岐山㠯名之也。古文𨙸从枝，从山。', '邑;支;山;支;枝;山', '支;支;枝', 'null', NULL, '邑;山;山', 'null', 'null', 'null', 'null', '重文应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4019, '邠;豳', 'bīn', '第六卷', '邑部', '周大王國，在右扶風 美陽。从邑，分聲。美陽亭，卽豳也。民俗㠯夜市有豳山。从山，从豩，闕。', '邑;分;山;豩', '分;豩', 'null', NULL, '邑;山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4020, '郿', 'méi', '第六卷', '邑部', '右扶風縣。从邑，眉聲。', '邑;眉', '眉', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4021, '郁', 'yù', '第六卷', '邑部', '右扶風 郁夷也。从邑，有聲。', '邑;有', '有', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4022, '鄠', 'hù', '第六卷', '邑部', '右扶風縣也。从邑，雩聲。', '邑;雩', '雩', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4023, '扈', 'hù', '第六卷', '邑部', '夏后同姓，所封「戰於甘」者。在鄠，有扈谷、甘亭。从邑，戸聲。古文扈从山𢎘[𠘧]。', '邑;戸;山;𠘧', '戸;𠘧', 'null', NULL, '邑;山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4024, '䣙', 'péi', '第六卷', '邑部', '右扶風 鄠鄉。从邑，崩聲。沛 城父有䣙鄉。讀若陪。', '邑;崩;陪', '崩;陪', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4025, '䢸', 'jū', '第六卷', '邑部', '右扶風 鄠鄉。从邑，且聲。', '邑;且', '且', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4026, '郝', 'hǎo', '第六卷', '邑部', '右扶風 鄠、盩庢鄉。从邑，赤聲。', '邑;赤', '赤', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4027, '酆', 'fēnɡ', '第六卷', '邑部', '周文王所都。在京兆 杜陵西南。从邑，豐聲。', '邑;豐', '豐', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4028, '鄭', 'zhènɡ', '第六卷', '邑部', '京兆縣。周厲王子友所封。从邑，奠聲。宗周之滅，鄭徙潧、洧之上，今新鄭是也。', '邑;奠', '奠', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
