INSERT INTO `shuowen_voice_revel` VALUES (4054, '郤', 'xì', '第六卷', '邑部', '晉大夫叔虎邑也。从邑，𧮫聲。', '邑;𧮫', '𧮫', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
