INSERT INTO `shuowen_voice_revel` VALUES (4064, '邯', 'hán', '第六卷', '邑部', '趙 邯鄲縣。从邑，甘聲。', '邑;甘', '甘', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
