INSERT INTO `shuowen_voice_revel` VALUES (4141, '䣡', 'yí', '第六卷', '邑部', '臨淮 徐地。从邑，義聲。春秋傳曰「徐 䣡楚」。', '邑;義', '義', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
