INSERT INTO `shuowen_voice_revel` VALUES (4146, '鄫', 'cénɡ', '第六卷', '邑部', '姒姓國。在東海。从邑，曾聲。', '邑;曾', '曾', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
