INSERT INTO `shuowen_voice_revel` VALUES (4148, '邞', 'fū', '第六卷', '邑部', '琅邪縣也。一名純德。从邑，夫聲。', '邑;夫', '夫', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4149, '䣛', 'qī', '第六卷', '邑部', '齊地也。从邑，桼聲。', '邑;桼', '桼', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4150, '郭', 'ɡuō', '第六卷', '邑部', '齊之郭氏虛。善善不能進，惡惡不能退，是㠯亡國也。从邑，𩫖聲。', '邑;𩫖', '𩫖', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4151, '郳', 'ní', '第六卷', '邑部', '齊地。从邑，兒聲。春秋傳曰「齊 高厚定郳田」。', '邑;兒', '兒', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4152, '郣', 'bó', '第六卷', '邑部', '郣地。从邑，孛聲。一曰地之起者曰郣。', '邑;孛', '孛', 'null', NULL, '邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
