INSERT INTO `shuowen_voice_revel` VALUES (4184, '𨛜', 'xiànɡ', '第六卷', '𨛜部', '鄰道也。从邑，从𨙨。凡𨛜之屬皆从𨛜。闕。', '邑;𨙨', '', 'null', NULL, '邑;𨙨', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4185, '鄉', 'xiānɡ', '第六卷', '𨛜部', '國離邑。民所封鄉也。𠾂夫別治。从𨛜，皀聲。封圻之內六鄉，六卿治之。', '𨛜;皀', '皀', 'null', NULL, '𨛜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4186, '𨞠;巷;䢽;衖', 'xiànɡ', '第六卷', '𨛜部', '里中道也。从𨛜共。言在邑中所共。篆文从邑省。', '𨛜;共;邑', '共', 'null', NULL, '𨛜;邑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4187, '日', 'rì', '第七卷', '日部', '實也。大昜之精不虧。从○一。象形。古文。象形。凡日之屬皆从日。', '○;一', '', 'null', NULL, '○;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4188, '旻', 'mín', '第七卷', '日部', '秋天也。从日，文聲。虞書說「仁覆閔下，則偁旻天」。', '日;文', '文', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4189, '時', 'shí', '第七卷', '日部', '四時也。从日，寺聲。古文時从日之作。', '日;寺;之', '寺;之', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4190, '早', 'zǎo', '第七卷', '日部', '䢅也。从日在甲上。', '日;甲', '', 'null', NULL, '日;甲', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4191, '㫚;昒', 'hū', '第七卷', '日部', '尙冥也。从日，勿聲。', '日;勿', '勿', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4192, '昧', 'mèi', '第七卷', '日部', '昧爽，且朙也。从日，未聲。一曰闇也。', '日;未', '未', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4193, '暏', 'dǔ', '第七卷', '日部', '且朙也。从日，者聲。', '日;者', '者', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4194, '晢;晣', 'zhé', '第七卷', '日部', '昭晢，朙也。从日，㪿聲。禮曰「晢[質]朙行事」。', '日;㪿;質', '㪿;質', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4195, '曉', 'xiǎo', '第七卷', '日部', '朙也。从日，堯聲。', '日;堯', '堯', 'null', NULL, '日', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4196, '昕', 'xīn', '第七卷', '日部', '且朙也。日將出也。从日，斤聲。讀若希。', '日;斤;希', '斤;希', 'null', NULL, '日', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4197, '昭', 'zhāo', '第七卷', '日部', '日朙也。从日，召聲。', '日;召', '召', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4198, '晤', 'wù', '第七卷', '日部', '朙也。从日，吾聲。詩曰「晤辟有摽」。', '日;吾', '吾', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4199, '旳', 'dì', '第七卷', '日部', '朙也。从日，勺聲。易曰「爲旳顙」。', '日;勺', '勺', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4200, '晃', 'huǎnɡ', '第七卷', '日部', '朙也。从日，光聲。', '日;光', '光', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4201, '曠', 'kuànɡ', '第七卷', '日部', '朙也。从日，廣聲。', '日;廣', '廣', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4202, '旭', 'xù', '第七卷', '日部', '日旦出皃。从日，九聲。讀若好。一曰朙也。', '日;九;好', '九;好', 'null', NULL, '日', 'null', 'null', 'null', 'null', '旭≌煦≌熹≌熙', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4203, '晉', 'jìn', '第七卷', '日部', '進也。日出而萬物進。从日，从臸。易曰「朙出地上晉」。', '日;臸', '', 'null', NULL, '日;臸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4204, '暘', 'yánɡ', '第七卷', '日部', '日出也。从日，昜聲。虞書曰「曰暘谷」。', '日;昜', '昜', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4205, '晵', 'qǐ', '第七卷', '日部', '雨而晝夝也。从日，啟省聲。', '日;啟', '啟', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4206, '晹', 'yì', '第七卷', '日部', '日覆雲，暫見也。从日，易聲。', '日;易', '易', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4207, '昫', 'xū', '第七卷', '日部', '日出𥁕也。从日，句聲。北地有昫衍縣。', '日;句', '句', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4208, '晛', 'xiàn', '第七卷', '日部', '日見也。从日見。見亦聲。詩曰「見晛[㬫]曰消」。', '日;見;㬫', '見;㬫', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
