INSERT INTO `shuowen_voice_revel` VALUES (4278, '朓', 'tiǎo', '第七卷', '月部', '晦而月見西，謂之朓。从月，兆聲。', '月;兆', '兆', 'null', NULL, '月', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4279, '朒', 'nǜ', '第七卷', '月部', '朔而月見東方，謂之「縮朒」。从月，肉聲。', '月;肉', '肉', 'null', NULL, '月', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4280, '期', 'qī', '第七卷', '月部', '會也。从月，其聲。古文从日丌。', '月;其;日;丌', '其;丌', 'null', NULL, '月;日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4281, '朦', 'ménɡ', '第七卷', '月部', '月朦朧也。从月，蒙聲。', '月;蒙', '蒙', 'null', NULL, '月', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4282, '朧', 'lónɡ', '第七卷', '月部', '朦朧也。从月，龍聲。', '月;龍', '龍', 'null', NULL, '月', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4283, '旦', 'dàn', '第七卷', '旦部', '朙也。从日見一上。一，地也。凡旦之屬皆从旦。', '日;一', '', 'null', NULL, '日;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4284, '暨;曁', 'jì', '第七卷', '旦部', '日頗見也。从旦，旣聲。', '旦;旣', '旣', 'null', NULL, '旦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4285, '倝', 'ɡàn', '第七卷', '倝部', '日始出，光倝倝也。从旦，㫃聲。凡倝之屬皆从倝。', '旦;㫃', '㫃', 'null', NULL, '旦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4286, '𠐱', 'ɡàn', '第七卷', '倝部', '闕且。从三日，在㫃中。', '日;㫃', '㫃', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4287, '朝', 'zhāo', '第七卷', '倝部', '旦也。从倝，舟聲。', '倝;舟', '舟', 'null', NULL, '倝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4288, '㫃', 'yǎn', '第七卷', '㫃部', '旌旗之游，㫃蹇之皃。从屮，曲而垂下㫃相出入也。讀若偃。古人名㫃字子遊。凡㫃之屬皆从㫃。古文㫃字，象旌旗之游及㫃之形。', '屮;偃', '偃', 'null', NULL, '屮', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4289, '旗', 'qí', '第七卷', '㫃部', '熊旗五游，㠯象伐星。士卒㠯爲期。从㫃，其聲。周禮曰「率都建旗」。', '㫃;其', '其', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4290, '旐', 'zhào', '第七卷', '㫃部', '龜蛇四游，㠯象營室。攸攸而長也。从㫃，兆聲。周禮曰「縣鄙建旐」。', '㫃;兆', '兆', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4291, '旆', 'pèi', '第七卷', '㫃部', '繼旐之旗也。沛然而垂。从㫃，𣎵聲。', '㫃;𣎵', '𣎵', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4292, '旌', 'jīnɡ', '第七卷', '㫃部', '遊車載旌。析羽注旄首也。所㠯精進士卒也。从㫃，生聲。', '㫃;生', '生', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4293, '旟', 'yú', '第七卷', '㫃部', '錯革鳥其上，所㠯進士衆。旟旟，衆也。从㫃，與聲。周禮曰「州里建旟」。', '㫃;與', '與', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4294, '旂', 'qí', '第七卷', '㫃部', '旗有衆鈴。㠯令衆也。从㫃，斤聲。', '㫃;斤', '斤', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4295, '旞', 'suì', '第七卷', '㫃部', '導車所載。全羽㠯爲允。允，進也。从㫃，遂聲。或从遺作。', '㫃;遂;遺', '遂;遺', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4296, '旝', 'kuài', '第七卷', '㫃部', '旌旗也。从㫃，會聲。詩曰「其旝如林」。春秋傳曰「旝動而𡔷」。一曰建大木，置石其上，發㠯機，㠯槌敵。', '㫃;會', '會', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4297, '旃', 'zhān', '第七卷', '㫃部', '旗曲柄也。所㠯旃表士衆。从㫃，丹聲。周禮曰「通帛爲旃」。或从亶。', '㫃;丹;亶', '丹;亶', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4298, '㫍', 'yóu', '第七卷', '㫃部', '旌旗之流也。从㫃，攸聲。', '㫃;攸', '攸', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4299, '㫏', 'yǎo', '第七卷', '㫃部', '旗屬。从㫃，要聲。', '㫃;要', '要', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4300, '旖', 'yǐ', '第七卷', '㫃部', '旖施，旗皃。从㫃，奇聲。', '㫃;奇', '奇', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4301, '施', 'shī', '第七卷', '㫃部', '旗。旖施也。从㫃，也聲。齊 欒施字子旗。知施者，旗也。', '㫃;也', '也', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4302, '旚', 'piāo', '第七卷', '㫃部', '旌旗旚繇也。从㫃，票聲。', '㫃;票', '票', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4303, '𣄠', 'biāo', '第七卷', '㫃部', '旌旗飛揚皃。从㫃，猋聲。', '㫃;猋', '猋', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4304, '游;遊', 'yóu', '第七卷', '㫃部', '旌旗之流也。从㫃，汓聲。古文游[辵子]。', '㫃;汓;辵;子', '汓;子', 'null', NULL, '㫃;辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4305, '旇', 'pī', '第七卷', '㫃部', '旌旗披靡也。从㫃，皮聲。', '㫃;皮', '皮', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4306, '旋', 'xuán', '第七卷', '㫃部', '周旋，旗之指𪎮也。从㫃疋。疋，足也。', '㫃;疋', '', 'null', NULL, '㫃;疋', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4307, '旄', 'máo', '第七卷', '㫃部', '幢也。从㫃，毛聲。', '㫃;毛', '毛', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4308, '旛', 'fān', '第七卷', '㫃部', '旛胡也。謂旗幅之下垂者。从㫃，番聲。', '㫃;番', '番', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4309, '旅;𣥐', 'lǚ', '第七卷', '㫃部', '軍之五百人。从㫃，从从。从，俱也。古文旅[止]。古文㠯爲「魯 衛」之魯。', '㫃;从;止;魯', '魯', 'null', NULL, '㫃;从;止', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4310, '族', 'zú', '第七卷', '㫃部', '矢鏠也。束之族族也。从㫃，从矢。㫃，所㠯標衆，衆矢之所集。', '㫃;矢', '', 'null', NULL, '㫃;矢', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4311, '冥', 'mínɡ', '第七卷', '冥部', '窈也。从日六，从冂。日數十，十六日而月始虧，冥也。冂亦聲。凡冥之屬皆从冥。', '日;六;冂', '冂', 'null', NULL, '日;六', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4312, '鼆', 'ménɡ', '第七卷', '冥部', '冥也。从冥，黽聲。讀若「黽蛙」之黽。', '冥;黽', '黽', 'null', NULL, '冥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4313, '晶', 'jīnɡ', '第七卷', '晶部', '精光也。从三日。凡晶之屬皆从晶。', '日', '', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4314, '曐;星', 'xīnɡ', '第七卷', '晶部', '萬物之精。上爲列星。从晶，生聲。一曰象形，从○，古○復注中，故與日同。古文。或省。', '晶;生', '生', 'null', NULL, '晶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4315, '曑;參;参', 'shēn', '第七卷', '晶部', '商星也。从晶，㐱聲。參或省。', '晶;㐱', '㐱', 'null', NULL, '晶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4316, '曟;晨', 'chén', '第七卷', '晶部', '房星。爲民田時者。从晶，辰聲。曟或省。', '晶;辰', '辰', 'null', NULL, '晶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4317, '曡;疊;叠;㬪', 'dié', '第七卷', '晶部', '楊雄說，㠯爲古理官決罪，三日得其宜乃行之。从晶宜。亡新㠯从三日大盛，改爲三田。', '晶;宜', '', 'null', NULL, '晶;宜', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4318, '有', 'yǒu', '第七卷', '有部', '不宜有也。春秋傳曰「日月有食之」。从月，又聲。凡有之屬皆从有。', '月;又', '又', 'null', NULL, '月', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4319, '㦽;𢒰', 'yù', '第七卷', '有部', '有彣彰也。从有，𡿿聲。', '有;𡿿', '𡿿', 'null', NULL, '有', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4320, '龓', 'lónɡ', '第七卷', '有部', '兼有也。从有，龍聲。讀若聾。', '有;龍;聾', '龍;聾', 'null', NULL, '有', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4321, '朙;明', 'mínɡ', '第七卷', '朙部', '照也。从月囧。凡朙之屬皆从朙。古文从日。', '月;囧;日', '囧', 'null', NULL, '月;日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4322, '朚', 'huānɡ', '第七卷', '朙部', '翌也。从朙，亡聲。', '朙;亡', '亡', 'null', NULL, '朙', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4323, '囧', 'jiǒnɡ', '第七卷', '囧部', '窗牖麗廔，闓朙也。象形。凡囧之屬皆从囧。讀若獷。賈侍中說，讀與朙同。', '獷;朙', '獷;朙', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4324, '𥁰;盟', 'ménɡ', '第七卷', '囧部', '周禮曰「國有疑則𥁰」。諸侯再相與會，十二嵗一𥁰。北面詔天之司愼、司命。𥁰，殺牲歃血，朱盤玉敦，㠯立牛耳。从囧，皿聲。篆文从朙。古文从明。', '囧;皿;朙;明', '皿', 'null', NULL, '囧;朙;明', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4325, '夕', 'xī', '第七卷', '夕部', '莫也。从月半見。凡夕之屬皆从夕。', '月', '', 'null', NULL, '月', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4326, '夜', 'yè', '第七卷', '夕部', '舍也。天下休舍。从夕，亦省聲。', '夕;亦', '亦', 'null', NULL, '夕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4327, '夢', 'ménɡ', '第七卷', '夕部', '不朙也。从夕，瞢省聲。', '夕;瞢', '瞢', 'null', NULL, '夕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4328, '夗', 'yuàn', '第七卷', '夕部', '轉臥也。从夕卪。臥有卪也。', '夕;卪', '', 'null', NULL, '夕;卪', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4329, '夤', 'yín', '第七卷', '夕部', '敬惕也。从夕，寅聲。易曰「夕惕若厲」。籒文[未详]。', '夕;寅', '寅', 'null', NULL, '夕', 'null', 'null', 'null', 'null', '真部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4330, '夝', 'qínɡ', '第七卷', '夕部', '雨而夜除星見也。从夕，生聲。', '夕;生', '生', 'null', NULL, '夕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4331, '外', 'wài', '第七卷', '夕部', '遠也。卜尙平旦，今若夕卜，於事外矣。古文。', '夕;卜', '', 'null', NULL, '夕;卜', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4332, '夙;𠈇;𡖊', 'sù', '第七卷', '夕部', '早敬也。从丮夕。持事雖夕不休，早敬者也。古文[𠀬]。古文[㐁]。', '丮;夕;𠀬;㐁', '𠀬;㐁', 'null', NULL, '丮;夕', 'null', 'null', 'null', 'null', '觉部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4333, '𡖶', 'mò', '第七卷', '夕部', '𡧯也。从夕，莫聲。', '夕;莫', '莫', 'null', NULL, '夕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4334, '多', 'duō', '第七卷', '多部', '緟也。从緟夕。夕者，相繹也，故爲多。緟夕爲多，緟日爲曡。凡多之屬皆从多。古文竝夕。', '夕', '', 'null', NULL, '夕', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4335, '夥;㚌', 'huǒ', '第七卷', '多部', '齋謂多也。从多，果聲。', '多;果', '果', 'null', NULL, '多', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4336, '𡌪', 'ɡuài', '第七卷', '多部', '大也。从多，圣聲。', '多;圣', '圣', 'null', NULL, '多', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4337, '㗬', 'zhā', '第七卷', '多部', '厚脣皃。从多尙。', '多;尙', '多', 'null', NULL, '尙', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4338, '毌', 'ɡuàn', '第七卷', '毌部', '穿物持之也。从一橫𠮚。𠮚，象寶貨之形。凡毌之屬皆从毌。讀若冠。', '一;𠮚;冠', '冠', 'null', NULL, '一;𠮚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4339, '貫', 'ɡuàn', '第七卷', '毌部', '錢貝之毌也。从毌貝。', '毌;貝', '毌', 'null', NULL, '貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4340, '虜', 'lǔ', '第七卷', '毌部', '獲也。从毌，从力，虍聲。', '毌;力;虍', '虍', 'null', NULL, '毌;力', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4341, '𢎘', 'hàn', '第七卷', '𢎘部', '嘾也。艸木之𠌶未發圅然。象形。凡𢎘之屬皆从𢎘。讀若含。', '含', '含', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4342, '圅;函;肣', 'hán', '第七卷', '𢎘部', '舌也。舌體𢎘𢎘。从𢎘，象形，𢎘亦聲。俗圅从肉今。', '𢎘;肉;今', '𢎘;今', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4343, '㽕', 'yóu', '第七卷', '𢎘部', '木生條也。从𢎘，由聲。商書曰「若顚木之有㽕枿」。古文言「由枿」。', '𢎘;由', '由', 'null', NULL, '𢎘', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4344, '甬', 'yǒnɡ', '第七卷', '𢎘部', '艸木𠌶甬甬然也。从𢎘，用聲。', '𢎘;用', '用', 'null', NULL, '𢎘', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4345, '𢎙', 'xián', '第七卷', '𢎘部', '艸木𢎘盛也。从二𢎘。', '𢎘', '𢎘', 'null', NULL, '', 'null', 'null', 'null', 'null', '应删 | 政宇不应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4346, '米', 'mǐ', '第七卷', '米部', '粟實也。象禾黍之形。凡米之屬皆从米。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '政宇调部', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4347, '粱', 'liánɡ', '第七卷', '米部', '禾米也。从米，梁省聲。', '米;梁', '梁', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4348, '𥼚', 'zhuō', '第七卷', '米部', '早取穀也。从米，焦聲。一曰小。', '米;焦', '焦', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4349, '粲', 'càn', '第七卷', '米部', '稻重一䄷。爲粟二十斗。爲米十斗。曰毇。爲米六斗大半斗曰粲。从米，𣦼聲。', '米;𣦼', '𣦼', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4350, '䊪', 'lì', '第七卷', '米部', '粟重一䄷，爲十六斗大半斗。舂爲米一斛曰䊪。从米，萬聲。', '米;萬', '萬', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4351, '精', 'jīnɡ', '第七卷', '米部', '擇米也。从米，靑聲。', '米;靑', '靑', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4352, '粺', 'bài', '第七卷', '米部', '毇也。从米，卑聲。', '米;卑', '卑', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4353, '粗', 'cū', '第七卷', '米部', '疏也。从米，且聲。', '米;且', '且', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4354, '粊', 'bì', '第七卷', '米部', '惡米也。从米，比聲。周書有「粊誓」。', '米;比', '比', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4355, '糱', 'niè', '第七卷', '米部', '牙米也。从米，辥聲。', '米;辥', '辥', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4356, '粒', 'lì', '第七卷', '米部', '糂也。从米，立聲。古文从食。', '米;立;食', '立', 'null', NULL, '米;食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4357, '𥼶', 'shì', '第七卷', '米部', '漬米也。从米，睪聲。', '米;睪', '睪', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4358, '糂;糝', 'sǎn', '第七卷', '米部', '㠯米和羹也。从米，甚聲。一曰粒也。籒文糂从朁。古文糂从參。', '米;甚;朁;參', '甚;朁;參', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4359, '糪', 'bò', '第七卷', '米部', '炊。米者謂之糪。从米，辟聲。', '米;辟', '辟', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4360, '糜', 'mí', '第七卷', '米部', '糝糜也。从米，麻聲。黃帝初敎作糜。', '米;麻', '麻', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4361, '䊤', 'tán', '第七卷', '米部', '糜和也。从米，覃聲。讀若譚。', '米;覃;譚', '覃;譚', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4362, '𥹆', 'mí', '第七卷', '米部', '潰米也。从米，尼聲。交止有𥹆泠縣。', '米;尼', '尼', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4363, '𥶶', 'qū', '第七卷', '米部', '酒母也。从米，𥷚省聲。𥶶或从麥，鞠省聲。', '米;𥷚;麥;鞠', '𥷚;鞠', 'null', NULL, '米;麥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4364, '糟', 'zāo', '第七卷', '米部', '酒滓也。从米，曹聲。籒文从酉。', '米;曹;酉', '曹;酉', 'null', NULL, '米;酉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4365, '𥼓', 'bèi', '第七卷', '米部', '乾飯也。从米，𤰈聲。', '米;𤰈', '𤰈', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4366, '糗', 'qiǔ', '第七卷', '米部', '熬米麥也。从米，臭聲。', '米;臭', '臭', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4367, '䊆', 'jiù', '第七卷', '米部', '舂糗也。从米臼。', '米;臼', '臼', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4368, '糈', 'xǔ', '第七卷', '米部', '糧也。从米，胥聲。', '米;胥', '胥', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4369, '糧', 'liánɡ', '第七卷', '米部', '穀食也。从米，量聲。', '米;量', '量', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4370, '粈', 'róu', '第七卷', '米部', '襍飯也。从米，丑聲。', '米;丑', '丑', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4371, '䊮', 'dí', '第七卷', '米部', '穀也。从米，翟聲。', '米;翟', '翟', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4372, '𥽘', 'mò', '第七卷', '米部', '末也。从米，蔑聲。', '米;蔑', '蔑', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4373, '粹', 'cuì', '第七卷', '米部', '不襍也。从米，卒聲。', '米;卒', '卒', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4374, '氣;餼', 'xì', '第七卷', '米部', '饋客之芻米也。从米，气聲。春秋傳曰「齊人來氣諸侯」。氣或从旣。氣或从食。', '米;气;旣;食', '气;旣', 'null', NULL, '米;食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4375, '䉺', 'hónɡ', '第七卷', '米部', '陳臭米。从米，工聲。', '米;工', '工', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4376, '粉', 'fěn', '第七卷', '米部', '所㠯傅面者也。从米，分聲。', '米;分', '分', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4377, '䊎', 'quǎn', '第七卷', '米部', '粉也。从米，卷聲。', '米;卷', '卷', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4378, '䊝', 'xiè', '第七卷', '米部', '䊝𥻦也。从米，悉聲。', '米;悉', '悉', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4379, '𥻦', 'sà', '第七卷', '米部', '䊝𥻦，散之也。从米，殺聲。', '米;殺', '殺', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4380, '䊳', 'mí', '第七卷', '米部', '碎也。从米，靡聲。', '米;靡', '靡', 'null', NULL, '米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4381, '竊', 'qiè', '第七卷', '米部', '盜自中出曰竊。从穴米。禼、廿皆聲也。廿，古文疾。禼，偰字也。', '穴;米;廿;禼;偰', '廿;禼;偰', 'null', NULL, '穴;米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4382, '粻', 'zhānɡ', '第七卷', '米部', '食米也。从米，長聲。', '米;長', '長', 'null', NULL, '米', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4383, '粕', 'pò', '第七卷', '米部', '糟粕，酒滓也。从米，白聲。', '米;白', '白', 'null', NULL, '米', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4384, '粔', 'jù', '第七卷', '米部', '粔籹，膏環也。从米，巨聲。', '米;巨', '巨', 'null', NULL, '米', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4385, '籹', 'nǚ', '第七卷', '米部', '粔籹也。从米，女聲。', '米;女', '女', 'null', NULL, '米', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4386, '糉', 'zònɡ', '第七卷', '米部', '蘆葉裹米也。从米，㚇聲。', '米;㚇', '㚇', 'null', NULL, '米', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4387, '糖', 'tánɡ', '第七卷', '米部', '飴也。从米，唐聲。', '米;唐', '唐', 'null', NULL, '米', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4388, '𣐺', 'hàn', '第七卷', '𣎺部', '艸木垂𠌶實也。从木𢎙。𢎘亦聲。凡𣐺之屬皆从𣐺。闕。', '木;𢎙', '𢎙', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4389, '𩏏', 'wéi', '第七卷', '𣎺部', '束也。从𣐺。韋聲。', '𣐺;韋', '韋', 'null', NULL, '𣎺', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4390, '𠧪', 'tiáo', '第七卷', '𠧪部', '艸木實垂𠧪𠧪然。象形。凡𠧪之屬皆从𠧪。讀若調。籒文从三𠧪作。', '調', '調', 'null', NULL, '', 'null', 'null', 'null', 'null', '𠧪≈髫≈絛≈條', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4391, '栗', 'lì', '第七卷', '𠧪部', '栗木也。从𠧪木。其實下垂，故从𠧪。古文栗从西，从二𠧪。徐巡說，木至西方，戰栗也。', '𠧪;木;西;𠧪', '', 'null', NULL, '𠧪;木;西;𠧪', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4392, '粟;𥻆', 'sù', '第七卷', '𠧪部', '嘉穀實也。从𠧪，从米。孔子曰「粟之爲言續也」。籒文粟。', '𠧪;米', '', 'null', NULL, '𠧪;米', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4393, '齊', 'qí', '第七卷', '齊部', '禾麥吐穗上平也。象形。凡齊之屬皆从齊。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4394, '𪗍', 'qí', '第七卷', '齊部', '等也。从齊，妻聲。', '齊;妻', '妻', 'null', NULL, '齊', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4395, '朿', 'cì', '第七卷', '朿部', '木芒也。象形。凡朿之屬皆从朿。讀若刺。', '刺', '刺', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4396, '棗', 'zǎo', '第七卷', '朿部', '羊棗也。从重朿。', '朿', '', 'null', NULL, '朿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4397, '棘', 'jí', '第七卷', '朿部', '小棗叢生者。从竝朿。', '朿', '', 'null', NULL, '朿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4398, '片', 'piàn', '第七卷', '片部', '判木也。从半木。凡片之屬皆从片。', '木', '', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4399, '版', 'bǎn', '第七卷', '片部', '片也。从片，反聲。', '片;反', '反', 'null', NULL, '片', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4400, '𤗚', 'bì', '第七卷', '片部', '片也。从片，畐聲。', '片;畐', '畐', 'null', NULL, '片', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4401, '牘', 'dú', '第七卷', '片部', '書版也。从片，𧷏聲。', '片;𧷏', '𧷏', 'null', NULL, '片', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4402, '牒', 'dié', '第七卷', '片部', '札也。从片，枼聲。', '片;枼', '枼', 'null', NULL, '片', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4403, '牑', 'biān', '第七卷', '片部', '牀版也。从片，扁聲。讀若邊。', '片;扁;邊', '扁;邊', 'null', NULL, '片', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4404, '牖', 'yǒu', '第七卷', '片部', '穿壁㠯木爲交窗也。从片戸，甫聲。譚長㠯爲甫上，日也，非戸也，牖，所㠯見日。', '片;戸;甫;日', '甫', 'null', NULL, '片;戸;日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4405, '牏', 'tóu', '第七卷', '片部', '築牆短版也。从片，俞聲。讀若俞。一曰若紐。', '片;俞;紐', '俞;紐', 'null', NULL, '片', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4406, '爿', 'qiáng', '第七卷', '片部', '反片爲爿，讀若牆。', '片', '牆', 'null', NULL, '', 'null', 'null', 'null', 'null', '应补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4407, '鼎', 'dǐnɡ', '第七卷', '鼎部', '三足㒳耳。和五味之寶器也。象析木㠯炊。貞省聲。昔禹收九牧之金，鑄鼎荆山之下，入山林川澤者，离魅、蛧蜽莫能逢之，㠯協承天休。易卦「巽木於下者爲鼎」。古文㠯貝爲鼎。籒文㠯鼎爲貝。凡鼎之屬皆从鼎。', '貞', '貞', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4408, '鼒;鎡', 'zī', '第七卷', '鼎部', '鼎之圜掩上者。从鼎，才聲。詩曰「鼐鼎及鼒」。俗鼒。从金，玆聲。', '鼎;才;金;玆', '才;玆', 'null', NULL, '鼎;金', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4409, '鼐', 'nài', '第七卷', '鼎部', '鼎之絕大者。从鼎，乃聲。魯詩說「鼐，小鼎」。', '鼎;乃', '乃', 'null', NULL, '鼎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4410, '𪔃', 'jiōng', '第七卷', '鼎部', '㠯木橫貫鼎耳舉之。从鼎，𫡃聲。周禮「廟門容大𪔃[扃]七箇」。卽易「玉鉉大吉」也。', '鼎;𫡃;扃', '𫡃;扃', 'null', NULL, '鼎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4411, '鼏', 'mì', '第七卷', '鼎部', '鼎覆也。从鼎冂。冂亦聲。', '鼎;冂', '冂', 'null', NULL, '鼎', 'null', 'null', 'null', 'null', '应补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4412, '克', 'kè', '第七卷', '克部', '肩也。象屋下刻木之形。凡克之屬皆从克。古文克。亦古文克。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4413, '录', 'lù', '第七卷', '录部', '刻木录录也。象形。凡录之屬皆从录。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4414, '禾', 'hé', '第七卷', '禾部', '嘉穀也。㠯二月始生，八月而孰，得之中和，故謂之禾。禾，木也。木王而生，金王而死。从木。象其穗。凡禾之屬皆从禾。', '木', '', 'null', NULL, '木', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4415, '秀', 'xiù', '第七卷', '禾部', '上諱。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4416, '稼', 'jià', '第七卷', '禾部', '禾之秀實爲稼，莖節爲禾。从禾，家聲。一曰稼，家事也。一曰在野曰稼。', '禾;家', '家', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4417, '穡', 'sè', '第七卷', '禾部', '穀可收曰穡。从禾，嗇聲。', '禾;嗇', '嗇', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4418, '穜', 'zhònɡ', '第七卷', '禾部', '埶也。从禾，童聲。', '禾;童', '童', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4419, '稙', 'zhí', '第七卷', '禾部', '早穜也。从禾。直聲。詩曰「稙稚尗麥」。', '禾;直', '直', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4420, '種', 'chónɡ', '第七卷', '禾部', '先穜後孰也。从禾，重聲。', '禾;重', '重', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4421, '稑;穋', 'lù', '第七卷', '禾部', '疾孰也。从禾，坴聲。詩曰「黍稷種稑」。稑或从翏。', '禾;坴;翏', '坴;翏', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4422, '稺', 'zhì', '第七卷', '禾部', '幼禾也。从禾，屖聲。', '禾;屖', '屖', '稚', '通作稚', '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4423, '稹', 'zhěn', '第七卷', '禾部', '穜穊也。从禾，眞聲。周禮曰「稹理而堅」。', '禾;眞', '眞', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4424, '稠', 'chóu', '第七卷', '禾部', '多也。从禾，周聲。', '禾;周', '周', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4425, '穊', 'jì', '第七卷', '禾部', '稠也。从禾，旣聲。', '禾;旣', '旣', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4426, '稀', 'xī', '第七卷', '禾部', '疏也。从禾，希聲。', '禾;希', '希', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4427, '𥣫', 'miè', '第七卷', '禾部', '禾也。从禾，蔑聲。', '禾;蔑', '蔑', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4428, '穆', 'mù', '第七卷', '禾部', '禾也。从禾，㣎聲。', '禾;㣎', '㣎', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4429, '私', 'sī', '第七卷', '禾部', '禾也。从禾，厶聲。北道名禾主人曰「私主人」。', '禾;厶', '厶', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4430, '䆏', 'fèi', '第七卷', '禾部', '稻紫莖不黏者也。从禾，糞聲。讀若靡。', '禾;糞;靡', '糞;靡', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4431, '稷', 'jì', '第七卷', '禾部', '𪗉也。五穀之長。从禾，畟聲。古文稷。', '禾;畟', '畟', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4432, '𪗉;秶', 'zī', '第七卷', '禾部', '稷也。从禾，齊聲。𪗉或从次作。', '禾;齊;次', '齊;次', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4433, '秫;朮;术', 'shú', '第七卷', '禾部', '稷之黏者。从禾。朮，象形。秫或省禾。', '禾;朮', '朮', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4434, '穄', 'jì', '第七卷', '禾部', '𪎭也。从禾，祭聲。', '禾;祭', '祭', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4435, '稻', 'dào', '第七卷', '禾部', '稌也。从禾，舀聲。', '禾;舀', '舀', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4436, '稌', 'tú', '第七卷', '禾部', '稻也。从禾，余聲。周禮曰「牛宜稌」。', '禾;余', '余', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4437, '稬', 'nuò', '第七卷', '禾部', '沛國謂稻曰稬。从禾，耎聲。', '禾;耎', '耎', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4438, '稴', 'xián', '第七卷', '禾部', '稻不黏者。从禾，兼聲。讀若「風廉」之廉。', '禾;兼;廉', '兼;廉', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4439, '秔;稉', 'jīnɡ', '第七卷', '禾部', '稻屬。从禾，亢聲。俗秔[更]。', '禾;亢;更', '亢;更', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4440, '秏', 'hào', '第七卷', '禾部', '稻屬。从禾，毛聲。伊尹曰「飯之美者，玄山之禾，南海之秏」。', '禾;毛', '毛', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4441, '穬', 'kuànɡ', '第七卷', '禾部', '芒粟也。从禾，廣聲。', '禾;廣', '廣', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4442, '秜', 'lí', '第七卷', '禾部', '稻今年落、來年自生，謂之秜。从禾，尼聲。', '禾;尼', '尼', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4443, '䅘', 'lái', '第七卷', '禾部', '齊謂麥䅘也。从禾，來聲。', '禾;來', '來', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '政宇调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4444, '稗', 'bài', '第七卷', '禾部', '禾別也。从禾，卑聲。琅邪有稗縣。', '禾;卑', '卑', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4445, '移', 'yí', '第七卷', '禾部', '禾相倚移也。从禾，多聲。一曰禾名。', '禾;多', '多', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4446, '穎', 'yǐnɡ', '第七卷', '禾部', '禾末也。从禾，頃聲。詩曰「禾穎穟穟」。[役]', '禾;頃;役', '頃;役', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4447, '𥝩;穗;穂', 'suì', '第七卷', '禾部', '禾成秀，人所收者也。从爪禾。俗从禾，惠聲。', '爪;禾;禾;惠', '惠', 'null', NULL, '爪;禾;禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4448, '䄪', 'diǎo', '第七卷', '禾部', '禾危𥝩也。从禾，勺聲。', '禾;勺', '勺', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4449, '穟', 'suì', '第七卷', '禾部', '禾𥝩之皃。从禾，遂聲。詩曰「禾穎穟穟」。穟或从艸。', '禾;遂;艸', '遂', 'null', NULL, '禾;艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4450, '𥠄', 'duān', '第七卷', '禾部', '禾𠂹皃。从禾，耑聲。讀若端。', '禾;耑;端', '耑;端', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4451, '䅥', 'jié', '第七卷', '禾部', '禾舉出苗也。从禾，曷聲。', '禾;曷', '曷', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4452, '秒', 'miǎo', '第七卷', '禾部', '禾芒也。从禾，少聲。', '禾;少', '少', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4453, '穖', 'jǐ', '第七卷', '禾部', '禾穖也。从禾，幾聲。', '禾;幾', '幾', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4454, '秠', 'pī', '第七卷', '禾部', '一稃二米。从禾，丕聲。詩曰「誕降嘉穀，惟秬惟秠」。天賜后稷之嘉穀也。', '禾;丕', '丕', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4455, '稃', 'fū', '第七卷', '禾部', '𥢶也。从禾，孚聲。稃或从米，付聲。', '禾;孚;米;付', '孚;付', 'null', NULL, '禾;米', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4456, '𥢶', 'kuài', '第七卷', '禾部', '穅也。从禾，會聲。讀若裹。', '禾;會;裹', '會;裹', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4457, '穅;康;𥹺', 'kānɡ', '第七卷', '禾部', '穀之皮也。从禾米，庚聲。穅或省作。', '禾;米;庚', '庚', 'null', NULL, '禾;米', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4458, '秨', 'zuó', '第七卷', '禾部', '禾繇皃。从禾，乍聲。讀若昨。', '禾;乍;昨', '乍;昨', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4459, '穮', 'biāo', '第七卷', '禾部', '槈鉏田也。从禾，麃聲。春秋傳曰「是穮是袞」。', '禾;麃', '麃', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4460, '䅁', 'àn', '第七卷', '禾部', '轢禾也。从禾，安聲。', '禾;安', '安', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4461, '秄', 'zǐ', '第七卷', '禾部', '雝禾本。从禾，子聲。', '禾;子', '子', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4462, '穧', 'jì', '第七卷', '禾部', '穫刈也。一曰撮也。从禾，齊聲。', '禾;齊', '齊', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4463, '穫', 'huò', '第七卷', '禾部', '刈穀也。从禾，蒦聲。', '禾;蒦', '蒦', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4464, '䆅', 'zī', '第七卷', '禾部', '積禾也。从禾，資聲。詩曰「䆅之秩秩」。', '禾;資', '資', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4465, '積', 'jī', '第七卷', '禾部', '聚也。从禾，責聲。', '禾;責', '責', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4466, '秩', 'zhì', '第七卷', '禾部', '積皃。从禾，失聲。詩曰「䆅之秩秩」。', '禾;失', '失', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4467, '稛', 'kǔn', '第七卷', '禾部', '絭束也。从禾，囷聲。', '禾;囷', '囷', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4468, '稞', 'huà', '第七卷', '禾部', '穀之善者。从禾，果聲。一曰無皮穀。', '禾;果', '果', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4469, '秳;𥞸', 'huó', '第七卷', '禾部', '舂粟不潰也。从禾，𠯑聲。', '禾;𠯑', '𠯑', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4470, '𥝖', 'hé', '第七卷', '禾部', '𥞸也。从禾，气聲。', '禾;气', '气', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4471, '䅵', 'zhuó', '第七卷', '禾部', '禾皮也。从禾，羙聲。', '禾;羙', '羙', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4472, '稭;秸', 'jiá', '第七卷', '禾部', '禾稾去其皮，祭天㠯爲席也。从禾，皆聲。', '禾;皆', '皆', '吉', '秸𪈓', '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4473, '稈;秆', 'ɡǎn', '第七卷', '禾部', '禾莖也。从禾，旱聲。春秋傳曰「或投一秉稈」。秆稈或从干作。', '禾;旱;干', '旱;干', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4474, '稾', 'ɡǎo', '第七卷', '禾部', '稈也。从禾，高聲。', '禾;高', '高', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4475, '秕', 'bǐ', '第七卷', '禾部', '不成粟也。从禾，比聲。', '禾;比', '比', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4476, '䅌', 'juān', '第七卷', '禾部', '麥莖也。从禾，肙聲。', '禾;肙', '肙', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4477, '𥞥;䅀', 'liè', '第七卷', '禾部', '黍穰也。从禾，列聲。', '禾;列', '列', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4478, '穰', 'ránɡ', '第七卷', '禾部', '黍𥞥巳治者。从禾，襄聲。', '禾;襄', '襄', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4479, '秧', 'yānɡ', '第七卷', '禾部', '禾若秧穰也。从禾，央聲。', '禾;央', '央', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4480, '䅭', 'pánɡ', '第七卷', '禾部', '䅭䅣，穀名。从禾，旁聲。', '禾;旁', '旁', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4481, '䅣', 'huánɡ', '第七卷', '禾部', '䅭䅣也。从禾，皇聲。', '禾;皇', '皇', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4482, '年', 'nián', '第七卷', '禾部', '穀孰也。从禾，千聲。春秋傳曰「大有年」。', '禾;千', '千', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4483, '穀', 'ɡǔ', '第七卷', '禾部', '續也。百穀之總名也。从禾，㱿聲。', '禾;㱿', '㱿', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4484, '稔', 'rěn', '第七卷', '禾部', '穀孰也。从禾，念聲。春秋傳曰「不五稔」是。', '禾;念', '念', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4485, '租', 'zū', '第七卷', '禾部', '田賦也。从禾，且聲。', '禾;且', '且', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4486, '稅', 'shuì', '第七卷', '禾部', '租也。从禾，兌聲。', '禾;兌', '兌', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4487, '䆃', 'dào', '第七卷', '禾部', '䆃米也。从禾，道聲。司馬相如曰「䆃一莖六穗」也。', '禾;道', '道', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4488, '𥡃', 'huānɡ', '第七卷', '禾部', '虛無食也。从禾，荒聲。', '禾;荒', '荒', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4489, '穌', 'sū', '第七卷', '禾部', '杷取禾若也。从禾，魚聲。', '禾;魚', '魚', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4490, '稍', 'shāo', '第七卷', '禾部', '出物有漸也。从禾，肖聲。', '禾;肖', '肖', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4491, '秋', 'qiū', '第七卷', '禾部', '禾穀孰也。从禾，𪚰省聲。籒文不省。', '禾;𪚰', '𪚰', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4492, '秦', 'qín', '第七卷', '禾部', '伯益之後所封國，地宜禾。从禾，舂省。一曰秦，禾名。籒文秦从秝。', '禾;舂;秝', '', 'null', NULL, '禾;舂;秝', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4493, '稱', 'chēnɡ', '第七卷', '禾部', '銓也。从禾，爯聲。春分而禾生。日夏至晷景可度，禾有秒。秋分而秒定。律數十二，十二秒而當一分。十分而寸。其㠯爲重。十二粟爲一分，十二分爲一銖，故諸程品皆从禾。', '禾;爯', '爯', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4494, '科', 'kē', '第七卷', '禾部', '程也。从禾斗。斗者，量也。', '禾;斗', '', 'null', NULL, '禾;斗', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4495, '程', 'chénɡ', '第七卷', '禾部', '程品也。十髮爲程，一程爲分，十分爲寸。从禾，呈聲。', '禾;呈', '呈', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4496, '稯', 'zōnɡ', '第七卷', '禾部', '布之八十縷爲稯。从禾，㚇聲。籒文稯省[兇]。', '禾;㚇;兇', '㚇;兇', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4497, '秭', 'zǐ', '第七卷', '禾部', '五稯爲秭。从禾，𠂔聲。一曰數𢡃至萬曰秭。', '禾;𠂔', '𠂔', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4498, '秅', 'chá', '第七卷', '禾部', '二秭爲秅。从禾，乇聲。周禮曰「二百四十斤爲秉。四秉曰筥，十筥曰稯，十稯曰秅，四百秉爲一秅」。', '禾;乇', '乇', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4499, '䄷', 'shí', '第七卷', '禾部', '百二十斤也。稻一䄷爲粟二十斗。禾黍一䄷爲粟十六斗大半斗。从禾，石聲。', '禾;石', '石', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4500, '稘', 'jī', '第七卷', '禾部', '復其時也。从禾，其聲。唐書曰「稘三百有六旬」。', '禾;其', '其', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4501, '穩', 'wěn', '第七卷', '禾部', '蹂榖聚也。一曰安也。从禾，隱省。', '禾;隱', '隱', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4502, '稕', 'zhùn', '第七卷', '禾部', '束稈也。从禾，𦎫聲。', '禾;𦎫', '𦎫', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4503, '秝', 'lì', '第七卷', '秝部', '稀疏適秝也。从二禾。凡秝之屬皆从秝。讀若歷。', '禾;歷', '歷', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4504, '兼', 'jiān', '第七卷', '秝部', '并也。从又持秝。兼持二禾。秉持一禾。', '又;秝', '', 'null', NULL, '又;秝', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4505, '尗', 'shú', '第七卷', '尗部', '豆也。尗，象豆生之形也。凡尗之屬皆从尗。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '政宇调部', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4506, '敊;豉;𢻃', 'chǐ', '第七卷', '尗部', '配鹽幽尗也。从尗，支聲。俗𢻃从豆。', '尗;支;豆', '支', 'null', NULL, '尗;豆', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4507, '黍', 'shǔ', '第七卷', '黍部', '禾屬而黏者也。㠯大暑而穜，故謂之黍。从禾，雨省聲。孔子曰「黍可爲酒」，故从禾入水也。凡黍之屬皆从黍。', '禾;雨', '雨', 'null', NULL, '禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4508, '𪎭;𪐎', 'méi', '第七卷', '黍部', '穄也。从黍，麻聲。', '黍;麻', '麻', 'null', NULL, '黍', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4509, '𪐄', 'bǐ', '第七卷', '黍部', '黍屬也。从黍，卑聲。', '黍;卑', '卑', 'null', NULL, '黍', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4510, '黏', 'nián', '第七卷', '黍部', '相箸也。从黍，占聲。', '黍;占', '占', 'null', NULL, '黍', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4511, '𪏻;䊀', 'hú', '第七卷', '黍部', '黏也。从黍，古聲。𪏻或从米作。', '黍;古;米', '古', 'null', NULL, '黍;米', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4512, '䵒', 'nì', '第七卷', '黍部', '黏也。从黍，日聲。春秋傳曰「不義不䵒[暱]」。䵒或从刃。', '黍;日;暱;刃', '日;暱;刃', 'null', NULL, '黍', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4513, '黎', 'lí', '第七卷', '黍部', '履黏也。从黍，𥝢省聲。𥝢，古文利，作履黏㠯黍米也。', '黍;𥝢', '𥝢', 'null', NULL, '黍', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4514, '䵗', 'bó', '第七卷', '黍部', '治黍禾豆下潰葉也。从黍，畐聲。', '黍;畐', '畐', 'null', NULL, '黍', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4515, '𪏸', 'nǐ', '第七卷', '黍部', '從黍，尼聲。', '黍;尼', '尼', 'null', NULL, '黍', 'null', 'null', 'null', 'null', '小徐 | 应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4516, '香', 'xiānɡ', '第七卷', '香部', '芳也。从黍，从甘。春秋傳曰「黍稷馨香」。凡香之屬皆从香。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4517, '馨', 'xīn', '第七卷', '香部', '香之遠聞也。从香，殸聲。殸，籒文磬。', '香;殸', '殸', 'null', NULL, '香', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4518, '馥', 'fù', '第七卷', '香部', '香气芬馥也。从香，复聲。', '香;复', '复', 'null', NULL, '香', 'null', 'null', 'null', 'null', '新附应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4519, '毇', 'huǐ', '第七卷', '毇部', '䊪米一斛舂爲九斗也。从臼米。从殳。凡毇之屬皆从毇。', '臼;米;殳', '', 'null', NULL, '臼;米;殳', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4520, '糳', 'zuò', '第七卷', '毇部', '䊪米一斛舂爲八斗曰糳。从毇，丵省聲。', '毇;丵', '丵', 'null', NULL, '毇', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4521, '臼', 'jiù', '第七卷', '臼部', '舂臼也。古者掘地爲臼，其後穿木石。象形。中象米也。凡臼之屬皆从臼。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4522, '舂', 'chōnɡ', '第七卷', '臼部', '𢶈粟也。从廾持杵㠯臨臼。杵省。古者雝父初作舂。', '廾;杵;臼', '', 'null', NULL, '廾;杵;臼', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4523, '𦥭', 'pò', '第七卷', '臼部', '齊謂舂曰𦥭。从臼，屰聲。讀若膊。', '臼;屰;膊', '屰;膊', 'null', NULL, '臼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4524, '臿', 'chā', '第七卷', '臼部', '舂去麥皮也。从臼，干聲。一曰干，所㠯臿之。', '臼;干', '干', 'null', NULL, '臼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4525, '舀;抭', 'yǎo', '第七卷', '臼部', '抒臼也。从爪臼。詩曰「或簸或舀[揄]」。舀或从手宂。舀或从臼宂。', '爪;臼;揄;手;宂', '揄;宂', 'null', NULL, '爪;臼;手', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4526, '臽', 'xiàn', '第七卷', '臼部', '小阱也。从人在臼上。', '人;臼', '', '凵', '甲骨文从凵，当为声符。', '人;臼', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4527, '凶', 'xiōnɡ', '第七卷', '凶部', '惡也。象地穿交陷其中也。凡凶之屬皆从凶。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4528, '兇', 'xiōnɡ', '第七卷', '凶部', '𢺕恐也。从儿在凶下。春秋傳曰「曹人兇懼」。', '儿;凶', '凶', 'null', NULL, '儿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4529, '𣎳', 'pìn', '第七卷', '𣎳部', '分枲莖皮也。从屮，八象枲皮。凡𣎳之屬皆从𣎳。讀若髕。', '屮;八;髕', '髕', 'null', NULL, '屮;八', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4530, '枲', 'xǐ', '第七卷', '𣎳部', '麻也。从𣎳，台聲。籒文枲从𣏟，从辝。', '𣎳;台;𣏟;辝', '台;辝', 'null', NULL, '𣎳;𣏟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4531, '𣏟', 'pài', '第七卷', '𣏟部', '萉之總名也。𣏟之爲言微也。微纖爲功。象形。凡𣏟之屬皆从𣏟。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4532, '檾', 'qǐnɡ', '第七卷', '𣏟部', '枲屬。从𣏟，熒省聲。詩曰「衣錦檾[褧]衣」。', '𣏟;熒;褧', '熒;褧', 'null', NULL, '𣏟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4533, '𢽳', 'sàn', '第七卷', '𣏟部', '分離也。从𣏟，从攴。𣏟，分𢽳之意也。', '𣏟;攴', '', 'null', NULL, '𣏟;攴', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4534, '麻', 'má', '第七卷', '麻部', '枲也。从𣏟，从广。𣏟，人所治也，在屋下。凡麻之屬皆从麻。', '𣏟;广', '', 'null', NULL, '𣏟;广', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4535, '䵈', 'kù', '第七卷', '麻部', '未練治纑也。从麻，後聲。', '麻;後', '後', 'null', NULL, '麻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4536, '黀', 'zōu', '第七卷', '麻部', '麻䕸也。从麻，取聲。', '麻;取', '取', 'null', NULL, '麻', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4537, '𪎨', 'tóu', '第七卷', '麻部', '檾屬。从麻，俞聲。', '麻;俞', '俞', 'null', NULL, '麻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4538, '耑', 'duān', '第七卷', '耑部', '物初生之題也。上象生形，下象根也。凡耑之屬皆从耑。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4539, '韭', 'jiǔ', '第七卷', '韭部', '韭菜也。一種而久生者也，故謂之韭。象形。在一之上。一，地也，此與耑同意。凡韭之屬皆从韭。', '一;久', '久', 'null', NULL, '一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4540, '𩐌', 'duì', '第七卷', '韭部', '韲也。从韭，隊聲。', '韭;隊', '隊', 'null', NULL, '韭', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4541, '䪢;𩐊;䪣;䪡;䪠;𠬘;齏;𩐐;韲', 'jī', '第七卷', '韭部', '𩐌也。从韭。次、𠂔皆聲。䪣或从齊。', '韭;次;𠂔;齊', '次;𠂔;齊', 'null', NULL, '韭', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4542, '䪥', 'xiè', '第七卷', '韭部', '䪥菜也。似韭。从韭，㕢聲。', '韭;㕢', '㕢', 'null', NULL, '韭', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4543, '韱', 'xiān', '第七卷', '韭部', '山韭也。从韭，㦰聲。', '韭;㦰', '㦰', 'null', NULL, '韭', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4544, '䪤', 'fán', '第七卷', '韭部', '小蒜。从韭，番聲。', '韭;番', '番', 'null', NULL, '韭', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4545, '瓜', 'ɡuā', '第七卷', '瓜部', '蓏也。象形。凡瓜之屬皆从瓜。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4546, '㼎', 'bó', '第七卷', '瓜部', '小瓜也。从瓜，交聲。', '瓜;交', '交', 'null', NULL, '瓜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4547, '瓞', 'dié', '第七卷', '瓜部', '㼎也。从瓜，失聲。詩曰「緜緜瓜瓞」。瓞或从弗[弟]。', '瓜;失;弟', '失;弟', 'null', NULL, '瓜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4548, '𤬐', 'xínɡ', '第七卷', '瓜部', '小瓜也。从瓜，熒省聲。', '瓜;熒', '熒', 'null', NULL, '瓜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4549, '𤬖', 'yáo', '第七卷', '瓜部', '瓜也。从瓜，䌛省聲。', '瓜;䌛', '䌛', 'null', NULL, '瓜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4550, '瓣', 'bàn', '第七卷', '瓜部', '瓜中實也。从瓜，辡聲。', '瓜;辡', '辡', 'null', NULL, '瓜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4551, '㼌', 'yǔ', '第七卷', '瓜部', '本不勝末，微弱也。从二瓜。讀若庾。', '瓜;庾', '庾', 'null', NULL, '瓜', 'null', 'null', 'null', 'null', '⚙️', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4552, '瓠', 'hù', '第七卷', '瓠部', '匏也。从瓜，夸聲。凡瓠之屬皆从瓠。', '瓜;夸', '夸', 'null', NULL, '瓜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4553, '瓢', 'piáo', '第七卷', '瓠部', '蠡也。从瓠省。票聲。', '瓠;票', '票', 'null', NULL, '瓠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4554, '宀', 'mián', '第七卷', '宀部', '交覆𥥍屋也。象形。凡宀之屬皆从宀。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4555, '家', 'jiā', '第七卷', '宀部', '凥也。从宀，豭省聲。古文家[𢑓]。', '宀;豭;𢑓', '豭;𢑓', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4556, '宅;㡯', 'zhái', '第七卷', '宀部', '人所託凥也。从宀，乇聲。古文宅[土]。亦古文宅。', '宀;乇;土', '乇', 'null', NULL, '宀;土', 'null', 'null', 'null', 'null', '铎部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4557, '室', 'shì', '第七卷', '宀部', '實也。从宀，至聲。室屋皆从至，所止也。', '宀;至', '至', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4558, '宣', 'xuān', '第七卷', '宀部', '天子宣室也。从宀，亘聲。', '宀;亘', '亘', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4559, '向', 'xiànɡ', '第七卷', '宀部', '北出牖也。从宀，从囗。詩曰「塞向墐戸」。', '宀;囗', '', 'null', NULL, '宀;囗', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4560, '宧', 'yí', '第七卷', '宀部', '養也。室之東北隅。食所居。从宀，𦣞聲。', '宀;𦣞', '𦣞', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4561, '𡧮', 'yǎo', '第七卷', '宀部', '戸樞聲也。室之東南隅。从宀，㫐聲。', '宀;㫐', '㫐', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4562, '奥;奧', 'ào', '第七卷', '宀部', '宛也。室之西南隅。从宀，𢍏聲。', '宀;𢍏', '𢍏', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4563, '宸', 'chén', '第七卷', '宀部', '屋宇也。从宀，辰聲。', '宀;辰', '辰', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4564, '宇', 'yǔ', '第七卷', '宀部', '屋邊也。从宀，于聲。易曰「上棟下宇」。籒文宇从禹。', '宀;于;禹', '于;禹', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4565, '寷', 'fēnɡ', '第七卷', '宀部', '大屋也。从宀，豐聲。易曰「豐其屋」。', '宀;豐', '豐', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4566, '寏;院', 'huán', '第七卷', '宀部', '周垣也。从宀，奐聲。寏或从𨸏，完聲。', '宀;奐;𨸏;完', '奐;完', 'null', NULL, '宀;𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4567, '宏', 'hónɡ', '第七卷', '宀部', '屋𥥍也。从宀，厷聲。', '宀;厷', '厷', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4568, '宖', 'hónɡ', '第七卷', '宀部', '屋響也。从宀，弘聲。', '宀;弘', '弘', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4569, '寪', 'wěi', '第七卷', '宀部', '屋皃。从宀，爲聲。', '宀;爲', '爲', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4570, '㝩', 'kānɡ', '第七卷', '宀部', '屋㝩㝗也。从宀，康聲。', '宀;康', '康', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4571, '㝗', 'lánɡ', '第七卷', '宀部', '㝩㝗也。从宀，良聲。音「良久」。', '宀;良', '良', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4572, '宬', 'chénɡ', '第七卷', '宀部', '屋所容受也。从宀，成聲。', '宀;成', '成', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4573, '寍', 'nínɡ', '第七卷', '宀部', '安也。从宀，心在皿上。皿，人之飲食器，所㠯安人也。', '宀;心;皿', '', 'null', NULL, '宀;心;皿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4574, '定', 'dìnɡ', '第七卷', '宀部', '安也。从宀，正聲。', '宀;正', '正', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4575, '寔', 'shí', '第七卷', '宀部', '正也。从宀，是聲。', '宀;是', '是', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4576, '安', 'ān', '第七卷', '宀部', '竫也。从女在宀中。', '女;宀', '', 'null', NULL, '女;宀', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4577, '宓', 'mì', '第七卷', '宀部', '安也。从宀，必聲。', '宀;必', '必', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4578, '㝣', 'yì', '第七卷', '宀部', '竫也。从宀，契聲。', '宀;契', '契', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4579, '宴', 'yàn', '第七卷', '宀部', '安也。从宀，妟聲。', '宀;妟', '妟', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4580, '𡧯', 'jì', '第七卷', '宀部', '無人聲也。从宀，尗聲。𡧯或从言。', '宀;尗;言', '尗', 'null', NULL, '宀;言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4581, '察', 'chá', '第七卷', '宀部', '覆審也。从宀，祭聲。', '宀;祭', '祭', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4582, '寴', 'qīn', '第七卷', '宀部', '至也。从宀，親聲。', '宀;親', '親', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4583, '完', 'wán', '第七卷', '宀部', '全也。从宀，元聲。古文㠯爲寬字。', '宀;元;寬', '元;寬', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4584, '富', 'fù', '第七卷', '宀部', '僃也。一曰厚也。从宀，畐聲。', '宀;畐', '畐', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4585, '實', 'shí', '第七卷', '宀部', '富也。从宀貫。貫爲貨物。', '宀;貫', '', 'null', NULL, '宀;貫', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4586, '宲', 'bǎo', '第七卷', '宀部', '藏也。从宀，呆聲。呆，古文保。周書曰「陳宲[寶]赤刀」。', '宀;呆;寶', '呆;寶', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4587, '容', 'rónɡ', '第七卷', '宀部', '盛也。从宀，谷聲。古文容从公。', '宀;谷;公', '谷;公', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4588, '宂', 'rǒnɡ', '第七卷', '宀部', '𢽳也。从宀儿。人在屋下，無田事也。周書曰「宮中之宂食」。', '宀;儿', '', 'null', NULL, '宀;儿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4589, '𡫛;㝰', 'mián', '第七卷', '宀部', '㝰㝰不見也。一曰㝰㝰，不省人。从宀，𦤝聲。', '宀;𦤝', '𦤝', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4590, '寶', 'bǎo', '第七卷', '宀部', '珍也。从宀玉貝。缶聲。古文寶省貝。', '宀;玉;貝;缶', '缶', 'null', NULL, '宀;玉;貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4591, '宭', 'qún', '第七卷', '宀部', '羣凥也。从宀，君聲。', '宀;君', '君', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4592, '宦', 'huàn', '第七卷', '宀部', '仕也。从宀臣。', '宀;臣', '', 'null', NULL, '宀;臣', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4593, '宰', 'zǎi', '第七卷', '宀部', '辠人在屋下執事者。从宀，从辛。辛，辠也。', '宀;辛', '', 'null', NULL, '宀;辛', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4594, '守', 'shǒu', '第七卷', '宀部', '守官也。从宀，从寸。从宀，寺府之事也；从寸，法度也。', '宀;寸', '', 'null', NULL, '宀;寸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4595, '寵', 'chǒnɡ', '第七卷', '宀部', '尊凥也。从宀，龍聲。', '宀;龍', '龍', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4596, '宥', 'yòu', '第七卷', '宀部', '寬也。从宀，有聲。', '宀;有', '有', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4597, '宜;宐;冝', 'yí', '第七卷', '宀部', '所安也。从宀之下，一之上。多省聲。古文宜。亦古文宜。', '宀;一;多', '多', 'null', NULL, '宀;一', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4598, '寫', 'xiě', '第七卷', '宀部', '置物也。从宀，舄聲。', '宀;舄', '舄', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4599, '宵', 'xiāo', '第七卷', '宀部', '夜也。从宀。宀下冥也。肖聲。', '宀;肖', '肖', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4600, '宿', 'sù', '第七卷', '宀部', '止也。从宀，𠈇聲。𠈇，古文夙。', '宀;𠈇', '𠈇', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '觉部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4601, '寑;𡨞', 'qǐn', '第七卷', '宀部', '臥也。从宀，侵聲。籒文寑省。', '宀;侵', '侵', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '侵部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4602, '𡧍', 'miàn', '第七卷', '宀部', '冥合也。从宀，丏聲。讀若書曰「藥不瞑眩」。', '宀;丏;瞑', '丏;瞑', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4603, '寬;寛', 'kuān', '第七卷', '宀部', '屋寬大也。从宀，萈聲。', '宀;萈', '萈', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4604, '𡨂', 'wù', '第七卷', '宀部', '寤也。从宀，吾聲。', '宀;吾', '吾', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4605, '寁', 'zǎn', '第七卷', '宀部', '凥之速也。从宀，疌聲。', '宀;疌', '疌', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4606, '寡', 'ɡuǎ', '第七卷', '宀部', '少也。从宀頒。頒，分也。宀分，故爲少也。', '宀;頒', '', 'null', NULL, '宀;頒', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4607, '宛;惌', 'wǎn', '第七卷', '宀部', '屈艸自覆也。从宀，夗聲。宛或从心。', '宀;夗;心', '夗', 'null', NULL, '宀;心', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4608, '客', 'kè', '第七卷', '宀部', '寄也。从宀，各聲。', '宀;各', '各', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4609, '寄', 'jì', '第七卷', '宀部', '託也。从宀，奇聲。', '宀;奇', '奇', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4610, '寓', 'yù', '第七卷', '宀部', '寄也。从宀，禺聲。寓或从广作。', '宀;禺;广', '禺', 'null', NULL, '宀;广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4611, '寠', 'jù', '第七卷', '宀部', '無禮凥也。从宀，婁聲。', '宀;婁', '婁', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4612, '㝌;疚', 'jiù', '第七卷', '宀部', '貧病也。从宀。久聲。詩曰「煢煢在㝌」。', '宀;久', '久', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4613, '寒', 'hán', '第七卷', '宀部', '凍也。从人在宀下，从茻。上下爲覆，下有仌也。', '人;宀;茻;仌', '', 'null', NULL, '人;宀;茻;仌', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4614, '害', 'hài', '第七卷', '宀部', '傷也。从宀口。言从家起也。丯聲。', '宀;口;丯', '丯', 'null', NULL, '宀;口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4615, '𡩡', 'suǒ', '第七卷', '宀部', '入家𢯱也。从宀，索聲。', '宀;索', '索', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4616, '𡫬', 'jū', '第七卷', '宀部', '竆也。从宀，𥱩聲。𡫬或从穴。', '宀;𥱩;穴', '𥱩', 'null', NULL, '宀;穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4617, '宄', 'ɡuǐ', '第七卷', '宀部', '姦也。外爲盜，內爲宄。从宀，九聲。讀若軌。古文宄[又]。亦古文宄[心]。', '宀;九;軌;又;心', '九;軌', 'null', NULL, '宀;又;心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4618, '㝮', 'cuì', '第七卷', '宀部', '塞也。从宀，𠭥聲。讀若虞書曰「竄三苗」之竄。', '宀;𠭥;竄', '𠭥;竄', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4619, '宕', 'dànɡ', '第七卷', '宀部', '過也。一曰洞屋。从宀，碭省聲。汝南 項有宕鄉。', '宀;碭', '碭', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4620, '宋', 'sònɡ', '第七卷', '宀部', '凥也。从宀木。讀若送。', '宀;木;送', '送', 'null', NULL, '宀;木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4621, '𡪐;㝪', 'diàn', '第七卷', '宀部', '屋傾下也。从宀，𡘺聲。', '宀;𡘺', '𡘺', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4622, '宗', 'zōnɡ', '第七卷', '宀部', '尊祖廟也。从宀示。', '宀;示', '', 'null', NULL, '宀;示', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4623, '宔', 'zhǔ', '第七卷', '宀部', '宗廟宔祏也。从宀，主聲。', '宀;主', '主', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4624, '宙', 'zhòu', '第七卷', '宀部', '舟輿所極覆也。从宀，由聲。', '宀;由', '由', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4625, '寘', 'zhì', '第七卷', '宀部', '置也。从宀，眞聲。', '宀;眞', '眞', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4626, '寰', 'huán', '第七卷', '宀部', '王者封畿內縣也。从宀，瞏聲。', '宀;瞏', '瞏', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4627, '寀', 'cǎi', '第七卷', '宀部', '同地爲寀。从宀，采聲。', '宀;采', '采', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4628, '宮', 'ɡōnɡ', '第七卷', '宮部', '室也。从宀，躳省聲。凡宮之屬皆从宮。', '宀;躳', '躳', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4629, '營', 'yínɡ', '第七卷', '宮部', '帀居也。从宮，熒省聲。', '宮;熒', '熒', 'null', NULL, '宮', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4630, '呂;膂;吕', 'lǚ', '第七卷', '呂部', '脊骨也。象形。昔大嶽爲禹心呂之臣，故封呂矦。凡呂之屬皆从呂。篆文呂从肉，旅聲。', '肉;旅', '旅', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4631, '躳;躬', 'ɡōnɡ', '第七卷', '呂部', '身也。从呂，从身。俗从弓身。', '呂;身;弓;身', '弓', 'null', NULL, '呂;身;身', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4632, '穴', 'xué', '第七卷', '穴部', '土室也。从宀，八聲。凡穴之屬皆从穴。', '宀;八', '八', 'null', NULL, '宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4633, '𥥊', 'mǐnɡ', '第七卷', '穴部', '北方謂地空因㠯爲土穴爲𥥊戸。从穴，皿聲。讀若猛。', '穴;皿;猛', '皿;猛', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4634, '窨', 'yìn', '第七卷', '穴部', '地室也。从穴，音聲。', '穴;音', '音', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4635, '𥨍', 'fù', '第七卷', '穴部', '地室也。从穴，復聲。詩曰「陶𥨍陶穴」。', '穴;復', '復', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4636, '竈;𥨫;𫁘', 'zào', '第七卷', '穴部', '炊𥨫也。周禮㠯𥨫祠祝融。从穴，鼀省聲。竈或不省作。', '穴;鼀', '鼀', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4637, '窯', 'yáo', '第七卷', '穴部', '燒瓦窯𫁘也。从穴，羔聲。', '穴;羔', '羔', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4638, '窐', 'wā', '第七卷', '穴部', '空也。从穴，圭聲。', '穴;圭', '圭', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4639, '𥥍', 'shēn', '第七卷', '穴部', '深也。一曰𫁘突。从穴火，求省。讀若禮「三年導服」之導。', '穴;火;求;導', '導', 'null', NULL, '穴;火;求', 'null', 'null', 'null', 'null', '侵部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4640, '穿', 'chuān', '第七卷', '穴部', '通也。从牙在穴中。', '牙;穴', '', 'null', NULL, '牙;穴', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4641, '竂;寮', 'liáo', '第七卷', '穴部', '穿也。从穴，尞聲。論語有公伯寮。', '穴;尞', '尞', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4642, '䆕', 'yuè', '第七卷', '穴部', '穿也。从穴，夬聲。', '穴;夬', '夬', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4643, '䆢', 'yuè', '第七卷', '穴部', '深抉也。从穴抉。', '穴;抉', '抉', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4644, '竇', 'dòu', '第七卷', '穴部', '空也。从穴，𧷏聲。', '穴;𧷏', '𧷏', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4645, '竅', 'qiào', '第七卷', '穴部', '空也。从穴，敫聲。', '穴;敫', '敫', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4646, '空', 'kōnɡ', '第七卷', '穴部', '竅也。从穴，工聲。', '穴;工', '工', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4647, '𥥻', 'qìnɡ', '第七卷', '穴部', '空也。从穴，巠聲。詩曰「瓶之𥥻[罄]矣」。', '穴;巠;罄', '巠;罄', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4648, '穵', 'yà', '第七卷', '穴部', '空也。从穴，𠃉聲。', '穴;𠃉', '𠃉', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4649, '䆷', 'xuè', '第七卷', '穴部', '空皃。从穴，矞聲。', '穴;矞', '矞', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4650, '窠', 'kē', '第七卷', '穴部', '空也。从穴，果聲。一曰鳥巢也。在樹曰巢，在穴曰窠。', '穴;果', '果', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4651, '窻;䆫', 'chuānɡ', '第七卷', '穴部', '通孔也。从穴，悤聲。', '穴;悤', '悤', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4652, '窊', 'wā', '第七卷', '穴部', '污衺，下也。从穴，瓜聲。', '穴;瓜', '瓜', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4653, '窳', 'yǔ', '第七卷', '穴部', '污窬也。从穴，㼌聲。朔方有窳渾縣。', '穴;㼌', '㼌', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4654, '窞', 'dàn', '第七卷', '穴部', '坎中更有坎也。从穴臽。臽亦聲。易曰「入于坎窞」。一曰旁入也。', '穴;臽', '臽', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4655, '𥥺;窌', 'jiào', '第七卷', '穴部', '窖也。从穴，丣聲。', '穴;丣', '丣', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4656, '窖', 'jiào', '第七卷', '穴部', '地臧也。从穴，告聲。', '穴;告', '告', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4657, '窬', 'yú', '第七卷', '穴部', '穿木戸也。从穴，俞聲。一曰空中也。', '穴;俞', '俞', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4658, '窵', 'diào', '第七卷', '穴部', '窵窅，𥥍也。从穴，鳥聲。', '穴;鳥', '鳥', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4659, '窺', 'kuī', '第七卷', '穴部', '小視也。从穴，規聲。', '穴;規', '規', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4660, '竀', 'chēnɡ', '第七卷', '穴部', '正視也。从穴中正見。正亦聲。', '穴;正;見', '正', 'null', NULL, '穴;見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4661, '窡', 'zhuó', '第七卷', '穴部', '穴中見也。从穴，叕聲。', '穴;叕', '叕', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4662, '窋', 'zhuó', '第七卷', '穴部', '物在穴中皃。从穴，出聲。', '穴;出', '出', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4663, '窴', 'tián', '第七卷', '穴部', '𡫳也。从穴，眞聲。', '穴;眞', '眞', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4664, '窒', 'zhì', '第七卷', '穴部', '𡫳也。从穴，至聲。', '穴;至', '至', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4665, '突', 'tū', '第七卷', '穴部', '犬從穴中暫出也。从犬在穴中。一曰搰也、匪突也。', '犬;穴', '', 'null', NULL, '犬;穴', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4666, '竄', 'cuàn', '第七卷', '穴部', '匿也。从鼠在穴中。', '鼠;穴', '', 'null', NULL, '鼠;穴', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4667, '窣', 'sū', '第七卷', '穴部', '從穴中卒出。从穴，卒聲。', '穴;卒', '卒', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4668, '窘', 'jiǒnɡ', '第七卷', '穴部', '迫也。从穴，君聲。', '穴;君', '君', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4669, '窕;𠩓;庣', 'tiǎo', '第七卷', '穴部', '𥥍肆極也。从穴，兆聲。讀若挑。', '穴;兆;挑', '兆;挑', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', '𣂀从𠩓聲，不见许书，玉裁以为即窕之异体。今暂从之。', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4670, '穹', 'qiónɡ', '第七卷', '穴部', '竆也。从穴，弓聲。', '穴;弓', '弓', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4671, '究', 'jiù', '第七卷', '穴部', '竆也。从穴，九聲。', '穴;九', '九', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4672, '竆;窮', 'qiónɡ', '第七卷', '穴部', '極也。从穴，躳聲。', '穴;躳', '躳', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4673, '䆞', 'yǎo', '第七卷', '穴部', '冥也。从穴，㫐聲。', '穴;㫐', '㫐', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4674, '窔', 'yào', '第七卷', '穴部', '䆞窔，𥥍也。从穴，交聲。', '穴;交', '交', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4675, '邃;䆳', 'suì', '第七卷', '穴部', '𥥍遠也。从穴，遂聲。', '穴;遂', '遂', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4676, '窈', 'yǎo', '第七卷', '穴部', '𥥍遠也。从穴，幼聲。', '穴;幼', '幼', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4677, '窱', 'tiǎo', '第七卷', '穴部', '杳窱也。从穴，條聲。', '穴;條', '條', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4678, '竁', 'cuì', '第七卷', '穴部', '穿地也。从穴，毳聲。一曰小鼠聲。周禮曰「大喪甫竁」。', '穴;毳', '毳', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4679, '窆', 'biǎn', '第七卷', '穴部', '葬下棺也。从穴，乏聲。周禮曰「及窆執斧」。', '穴;乏', '乏', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4680, '窀', 'zhūn', '第七卷', '穴部', '窀穸，葬之厚夕也。从穴，屯聲。春秋傳曰「窀穸從先君於地下」。', '穴;屯', '屯', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4681, '穸', 'xī', '第七卷', '穴部', '窀穸也。从穴，夕聲。', '穴;夕', '夕', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4682, '䆘', 'yā', '第七卷', '穴部', '入𧖴刺穴謂之䆘。从穴，甲聲。', '穴;甲', '甲', 'null', NULL, '穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4683, '㝱', 'mènɡ', '第七卷', '㝱部', '寐而覺者也。从宀，从疒，夢聲。周禮「㠯日月星辰占『六㝱』之吉凶——一曰正㝱；二曰㖾㝱；三曰思㝱。四曰寤㝱；五曰喜㝱；六曰懼㝱」。凡㝱之屬皆从㝱。', '疒;夢', '夢', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4684, '寢', 'qǐn', '第七卷', '㝱部', '病臥也。从㝱省，𡨞省聲。', '㝱;𡨞', '𡨞', 'null', NULL, '㝱', 'null', 'null', 'null', 'null', '侵部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4685, '寐', 'mèi', '第七卷', '㝱部', '臥也。从㝱省，未聲。', '㝱;未', '未', 'null', NULL, '㝱', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4686, '寤', 'wù', '第七卷', '㝱部', '寐覺而有言曰寤。从㝱省，吾聲。一曰晝見而夜㝱也。籒文寤。', '㝱;吾', '吾', 'null', NULL, '㝱', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4687, '𡫽', 'rǔ', '第七卷', '㝱部', '楚人謂寐曰𡫽。从㝱省，女聲。', '㝱;女', '女', 'null', NULL, '㝱', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4688, '𡬍', 'mǐ', '第七卷', '㝱部', '寐而厭也。从㝱省，米聲。', '㝱;米', '米', 'null', NULL, '㝱', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4689, '𡬄', 'jì', '第七卷', '㝱部', '孰寐也。从㝱省，水聲。讀若悸。', '㝱;水;悸', '水;悸', 'null', NULL, '㝱', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4690, '寎', 'bìnɡ', '第七卷', '㝱部', '臥驚病也。从㝱省，丙聲。', '㝱;丙', '丙', 'null', NULL, '㝱', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4691, '寱', 'yì', '第七卷', '㝱部', '瞑言也。从㝱省，臬聲。', '㝱;臬', '臬', 'null', NULL, '㝱', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4692, '寣', 'hū', '第七卷', '㝱部', '臥驚也。一曰小兒號寣寣。一曰河內相𧦝也。从㝱省，从言。', '㝱;言', '', 'null', NULL, '㝱;言', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4693, '疒;𤕫', 'nè', '第七卷', '疒部', '倚也。人有疾痛也，象倚箸之形。凡疒之屬皆从疒。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4694, '疾;廿', 'jí', '第七卷', '疒部', '病也。从疒，矢聲。籒文疾[𥏼省]。古文。', '疒;矢;𥏼', '矢;𥏼', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4695, '病', 'bìnɡ', '第七卷', '疒部', '疾加也。从疒，丙聲。', '疒;丙', '丙', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4696, '痡', 'pū', '第七卷', '疒部', '病也。从疒，甫聲。詩曰「我僕痡矣」。', '疒;甫', '甫', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '政宇调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4697, '痛', 'tònɡ', '第七卷', '疒部', '病也。从疒，甬聲。', '疒;甬', '甬', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '政宇调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4698, '瘣', 'huì', '第七卷', '疒部', '病也。从疒，鬼聲。詩曰「譬彼瘣[壞]木」。一曰腫旁出也。', '疒;鬼;壞', '鬼;壞', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4699, '疴', 'kē', '第七卷', '疒部', '病也。从疒，可聲。五行傳曰「時卽有口疴」。', '疒;可', '可', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4700, '瘽', 'qín', '第七卷', '疒部', '病也。从疒，堇聲。', '疒;堇', '堇', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4701, '瘵', 'zhài', '第七卷', '疒部', '病也。从疒，祭聲。', '疒;祭', '祭', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4702, '瘨', 'diān', '第七卷', '疒部', '病也。从疒，眞聲。一曰腹張。', '疒;眞', '眞', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4703, '瘼', 'mò', '第七卷', '疒部', '病也。从疒，莫聲。', '疒;莫', '莫', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4704, '㽱', 'jiǎo', '第七卷', '疒部', '腹中急痛也。从疒，丩聲。', '疒;丩', '丩', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4705, '𤸫', 'yùn', '第七卷', '疒部', '病也。从疒，員聲。', '疒;員', '員', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4706, '癇;癎', 'xián', '第七卷', '疒部', '病也。从疒，閒聲。', '疒;閒', '閒', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4707, '㽾', 'wù', '第七卷', '疒部', '病也。从疒，出聲。', '疒;出', '出', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4708, '疵', 'cī', '第七卷', '疒部', '病也。从疒，此聲。', '疒;此', '此', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4709, '癈', 'fèi', '第七卷', '疒部', '固病也。从疒，發聲。', '疒;發', '發', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4710, '瘏', 'tú', '第七卷', '疒部', '病也。从疒，者聲。詩曰「我馬瘏矣」。', '疒;者', '者', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4711, '瘲', 'zònɡ', '第七卷', '疒部', '病也。从疒，從聲。', '疒;𨑹', '𨑹', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4712, '㾕', 'shěn', '第七卷', '疒部', '寒病也。从疒，辛聲。', '疒;辛', '辛', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4713, '𤷇', 'xù', '第七卷', '疒部', '頭痛也。从疒，或聲。讀若「溝洫」之洫。', '疒;或;洫', '或;洫', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4714, '痟', 'xiāo', '第七卷', '疒部', '酸痟，頭痛也。从疒，肖聲。周禮曰「春時有痟首疾」。', '疒;肖', '肖', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4715, '疕', 'bǐ', '第七卷', '疒部', '頭瘍也。从疒，匕聲。', '疒;匕', '匕', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4716, '瘍', 'yánɡ', '第七卷', '疒部', '頭創也。从疒，昜聲。', '疒;昜', '昜', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4717, '痒', 'yánɡ', '第七卷', '疒部', '瘍也。从疒，羊聲。', '疒;羊', '羊', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4718, '㾺', 'mà', '第七卷', '疒部', '目病。一曰惡气箸身也。一曰蝕創。从疒，馬聲。', '疒;馬', '馬', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4719, '𤺊', 'xī', '第七卷', '疒部', '散聲也。从疒，斯聲。', '疒;斯', '斯', '沙', '或借作沙，禮記：鳥皫色而沙鳴。', '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4720, '𤺉', 'wěi', '第七卷', '疒部', '口咼也。从疒，爲聲。', '疒;爲', '爲', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4721, '疦', 'jué', '第七卷', '疒部', '𤺉也。从疒，夬聲。', '疒;夬', '夬', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4722, '瘖', 'yīn', '第七卷', '疒部', '不能言也。从疒，音聲。', '疒;音', '音', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4723, '癭', 'yǐnɡ', '第七卷', '疒部', '頸癅也。从疒，嬰聲。', '疒;嬰', '嬰', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4724, '瘻', 'lòu', '第七卷', '疒部', '頸腫也。从疒，婁聲。', '疒;婁', '婁', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4725, '𤴨', 'yòu', '第七卷', '疒部', '顫也。从疒，又聲。', '疒;又', '又', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4726, '瘀', 'yū', '第七卷', '疒部', '積血也。从疒，於聲。', '疒;於', '於', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4727, '疝', 'shàn', '第七卷', '疒部', '腹痛也。从疒，山聲。', '疒;山', '山', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4728, '疛', 'zhǒu', '第七卷', '疒部', '小腹病。从疒，肘省聲。', '疒;肘', '肘', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4729, '𤼤', 'pì', '第七卷', '疒部', '滿也。从疒，𡚤聲。', '疒;𡚤', '𡚤', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4730, '㾈', 'fù', '第七卷', '疒部', '俛病也。从疒，付聲。', '疒;付', '付', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4731, '痀', 'jū', '第七卷', '疒部', '曲脊也。从疒，句聲。', '疒;句', '句', 'null', NULL, '疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4732, '瘚;欮', 'jué', '第七卷', '疒部', '屰气也。从疒，从屰欠。瘚或省疒[欮]。', '疒;屰;欠;欮', '欮', 'null', NULL, '疒;屰;欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
