INSERT INTO `shuowen_voice_revel` VALUES (4801, '同', 'tónɡ', '第七卷', '𠔼部', '合會也。从𠔼口。', '𠔼;口', '', 'null', NULL, '𠔼;口', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4802, '𡉉', 'qiānɡ', '第七卷', '𠔼部', '𢅂帳之象。从𠔼。㞢，其飾也。', '𠔼;㞢', '', 'null', NULL, '𠔼;㞢', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4803, '冡', 'ménɡ', '第七卷', '𠔼部', '覆也。从𠔼豕。', '𠔼;豕', '', 'null', NULL, '𠔼;豕', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4804, '冃', 'mào', '第七卷', '冃部', '小兒及蠻夷頭衣也。从冂。二，其飾也。凡冃之屬皆从冃。', '冂;二', '', 'null', NULL, '冂;二', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4805, '冕;絻', 'miǎn', '第七卷', '冃部', '大夫㠯上冠也。䆳延、垂瑬、紞纊。从冃，免聲。古者黃帝初作冕。冕或从糸作。', '冃;免;糸', '免', 'null', NULL, '冃;糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4806, '冑', 'zhòu', '第七卷', '冃部', '兜鍪也。从冃，由聲。司馬法冑从革。', '冃;由;革', '由', 'null', NULL, '冃;革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4807, '冒', 'mào', '第七卷', '冃部', '冡而前也。从冃目。古文冒。', '冃;目', '冃;目', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4808, '最', 'zuì', '第七卷', '冃部', '犯取也。[又曰會。]从冃取。', '冃;取', '', 'null', NULL, '冃;取', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4809, '㒻', 'mào', '第七卷', '冃部', '犯而見也。从冃，从見。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '小徐本有、大徐本无', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4810, '㒳', 'liǎnɡ', '第七卷', '㒳部', '再也。从冂。从𠓜。从丨。易曰「參天㒳地」。凡㒳之屬皆从㒳。', '冂;𠓜;丨', '', 'null', NULL, '冂;𠓜;丨', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4811, '兩', 'liǎnɡ', '第七卷', '㒳部', '二十四銖爲一兩。从一㒳。㒳，平分也。㒳亦聲。', '一;㒳', '㒳', 'null', NULL, '一', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4812, '㒼', 'mán', '第七卷', '㒳部', '平也。从廿。五行之數，二十分爲一辰。从㒳。㒳，平也。讀若蠻。', '廿;㒳;蠻', '蠻', 'null', NULL, '廿;㒳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4813, '网;罔;網;𦉾', 'wǎnɡ', '第七卷', '网部', '庖犧氏所結繩，㠯田㠯漁也。从冂。下象网交文。凡网之屬皆从网。网或加亡。或从糸。古文网从冂，亡聲。籒文从𠔼。', '冂;亡;糸;𠔼', '亡', 'null', NULL, '冂;糸;𠔼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4814, '罨', 'yǎn', '第七卷', '网部', '䍐也。从网，奄聲。', '网;奄', '奄', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4815, '罕;䍑;䍐', 'hǎn', '第七卷', '网部', '网也。从网，干聲。', '网;干', '干', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4816, '𦌾;𦍆', 'juàn', '第七卷', '网部', '网也。从网𦇏。𦇏亦聲。一曰綰也。', '网;𦇏', '𦇏', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4817, '䍙', 'méi', '第七卷', '网部', '网也。从网，每聲。', '网;每', '每', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4818, '𦌔;𦋭;𫅈;𨂳;𨃿;䠣', 'xuǎn', '第七卷', '网部', '网也。从网，巽聲。𦌔或从足巽。逸周書曰「不卵不䠣，㠯成鳥獸」。𦌔者，𦍆獸足，故从足。', '网;巽;足', '巽', 'null', NULL, '网;足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4819, '䍘;𥹄', 'mí', '第七卷', '网部', '网也。从网，米聲。䍘或从𣦵。', '网;米;𣦵', '米', 'null', NULL, '网;𣦵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4820, '罩', 'zhào', '第七卷', '网部', '捕魚器也。从网，卓聲。', '网;卓', '卓', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4821, '罾', 'zēnɡ', '第七卷', '网部', '魚网也。从网，曾聲。', '网;曾', '曾', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4822, '罪', 'zuì', '第七卷', '网部', '捕魚竹网。从网，非聲。秦㠯爲辠字。', '网;非;辠', '非;辠', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4823, '罽', 'jì', '第七卷', '网部', '魚网也。从网，㓹聲。㓹，籒文銳。', '网;㓹', '㓹', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4824, '罛', 'ɡū', '第七卷', '网部', '魚罟也。从网，瓜聲。詩曰「施罛濊濊」。', '网;瓜', '瓜', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4825, '罟', 'ɡǔ', '第七卷', '网部', '网也。从网，古聲。', '网;古', '古', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4826, '羀;罶;𦌁', 'liǔ', '第七卷', '网部', '曲梁寡婦之笱。魚所畱也。从网畱。畱亦聲。羀或从婁。春秋國語曰「溝罛𦌁」。', '网;畱;婁', '畱;婁', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4827, '罜', 'zhǔ', '第七卷', '网部', '罜䍡，小魚罟也。从网，主聲。', '网;主', '主', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4828, '䍡', 'lù', '第七卷', '网部', '罜䍡也。从网，鹿聲。', '网;鹿', '鹿', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4829, '罧', 'shèn', '第七卷', '网部', '積柴水中㠯聚魚也。从网，林聲。', '网;林', '林', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4830, '罠', 'mín', '第七卷', '网部', '所㠯釣也。从网，民聲。', '网;民', '民', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4831, '羅', 'luó', '第七卷', '网部', '㠯絲罟鳥也。从网，从維。古者芒氏初作羅。', '网;維', '', 'null', NULL, '网;維', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4832, '罬;輟', 'zhuó', '第七卷', '网部', '捕鳥覆車也。从网，叕聲。罬或从車作。', '网;叕;車', '叕', 'null', NULL, '网;車', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4833, '罿', 'chōnɡ', '第七卷', '网部', '罬也。从网，童聲。', '网;童', '童', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4834, '䍖;罦', 'fú', '第七卷', '网部', '覆車也。从网，包聲。詩曰「雉離于䍖」。䍖或从孚作。', '网;包;孚', '包;孚', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4835, '罻', 'wèi', '第七卷', '网部', '捕鳥网也。从网，𤈫聲。', '网;𤈫', '𤈫', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4836, '𦊾;罘', 'fú', '第七卷', '网部', '兔罟也。从网，否聲。', '网;否', '否', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4837, '𦊍;𦊂', 'hù', '第七卷', '网部', '罟也。从网，互聲。', '网;互', '互', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4838, '罝', 'jū', '第七卷', '网部', '兔网也。从网，且聲。罝或从組作。籒文从虘。', '网;且;組;虘', '且;組;虘', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4839, '𦌬', 'wǔ', '第七卷', '网部', '牖中网也。从网，舞聲。', '网;舞', '舞', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4840, '署', 'shǔ', '第七卷', '网部', '部署也。各有所网屬也。从网，者聲。', '网;者', '者', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4841, '罷', 'bà', '第七卷', '网部', '遣有辠也。从网能。网，辠网也。言有賢能而入网。卽貰遣之。周禮曰「議能之辟」是也。', '网;能', '', 'null', NULL, '网;能', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4842, '置', 'zhì', '第七卷', '网部', '赦也。从网直。', '网;直', '直', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4843, '罯', 'ǎn', '第七卷', '网部', '覆也。从网，音聲。', '网;音', '音', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4844, '詈', 'lì', '第七卷', '网部', '罵也。从网言。', '网;言', '', 'null', NULL, '网;言', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4845, '罵', 'mà', '第七卷', '网部', '詈也。从网，馬聲。', '网;馬', '馬', 'null', NULL, '网', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4846, '𦌭;羈', 'jī', '第七卷', '网部', '馬落頭也。从网𩡳。𩡳，絆也。𦌭或从革。', '网;𩡳;革', '', '奇', '俗作羇', '网;𩡧;革', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4847, '罭', 'yù', '第七卷', '网部', '魚網也。从网，或聲。', '网;或', '或', 'null', NULL, '网', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4848, '罳', 'sī', '第七卷', '网部', '罘罳，屏也。从网，思聲。', '网;思', '思', 'null', NULL, '网', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4849, '罹', 'lí', '第七卷', '网部', '心憂也。从网[惟]。', '网;惟', '', 'null', NULL, '网;惟', 'null', 'null', 'null', 'null', '新 | 罹≌羅', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4850, '襾', 'yà', '第七卷', '襾部', '覆也。从冂，上下覆之。凡襾之屬皆从襾。讀若𣉩。', '冂;𣉩', '𣉩', 'null', NULL, '冂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4851, '覂', 'fěnɡ', '第七卷', '襾部', '覆也。从襾，乏聲。', '襾;乏', '乏', 'null', NULL, '襾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4852, '覆', 'fù', '第七卷', '襾部', '覂也。从襾，復聲。一曰葢也。', '襾;復', '復', 'null', NULL, '襾', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4853, '覈', 'hé', '第七卷', '襾部', '實也。攷事，襾笮邀[徼]遮，其辭得實曰覈。从襾，敫聲。覈或从雨。', '襾;敫;雨', '敫', 'null', NULL, '襾;雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4854, '巾', 'jīn', '第七卷', '巾部', '佩巾也。从冂。丨，象系也。凡巾之屬皆从巾。', '冂;丨', '', 'null', NULL, '冂;丨', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4855, '𢁥', 'fēn', '第七卷', '巾部', '楚謂大巾曰𢁥。从巾，分聲。', '巾;分', '分', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4856, '帥;帨', 'shuài', '第七卷', '巾部', '佩巾也。从巾，𠂤聲。帥或从兌聲。', '巾;𠂤;兌', '𠂤;兌', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4857, '𢄢', 'shuì', '第七卷', '巾部', '禮巾也。从巾，埶聲。', '巾;埶', '埶', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4858, '帗', 'bō', '第七卷', '巾部', '一幅巾也。从巾，犮聲。讀若撥。', '巾;犮;撥', '犮;撥', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4859, '㠴', 'rèn', '第七卷', '巾部', '枕巾也。从巾，刃聲。', '巾;刃', '刃', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4860, '幋', 'pán', '第七卷', '巾部', '覆衣大巾也。从巾，般聲。或㠯爲首幋。', '巾;般', '般', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4861, '帤', 'rú', '第七卷', '巾部', '巾帤也。从巾，如聲。一曰幣巾。', '巾;如', '如', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4862, '幣', 'bì', '第七卷', '巾部', '帛也。从巾，敝聲。', '巾;敝', '敝', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4863, '幅', 'fú', '第七卷', '巾部', '布帛廣也。从巾，畐聲。', '巾;畐', '畐', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4864, '㡆', 'huānɡ', '第七卷', '巾部', '設色之工治絲練者。从巾，巟聲。一曰㡆，隔也。讀若荒。', '巾;巟', '巟', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4865, '帶', 'dài', '第七卷', '巾部', '紳也。男子鞶帶，婦人帶絲。象繫佩之形。佩必有巾。从重巾。', '巾', '', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4866, '幘', 'zé', '第七卷', '巾部', '髮有巾曰幘。从巾，責聲。', '巾;責', '責', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4867, '㡄', 'xún', '第七卷', '巾部', '領耑也。从巾，旬聲。', '巾;旬', '旬', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4868, '帔', 'pèi', '第七卷', '巾部', '弘農謂帬帔也。从巾，皮聲。', '巾;皮', '皮', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4869, '帬;裙', 'qún', '第七卷', '巾部', '繞領也。从巾，君聲。帬或从衣。', '巾;君;衣', '君', 'null', NULL, '巾;衣', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4870, '常;裳', 'chánɡ', '第七卷', '巾部', '下帬也。从巾，尙聲。常或从衣。', '巾;尙;衣', '尙', 'null', NULL, '巾;衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4871, '帴', 'sàn', '第七卷', '巾部', '帬也。一曰帗也。一曰婦人脅衣。从巾，戔聲。讀若「末殺」之殺。', '巾;戔;殺', '戔;殺', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4872, '㡓;褌', 'kūn', '第七卷', '巾部', '幒也。从巾，軍聲。㡓或从衣。', '巾;軍;衣', '軍', 'null', NULL, '巾;衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4873, '𢃭;幒;𢃓', 'zhōnɡ', '第七卷', '巾部', '㡓也。从巾，悤聲。一曰帙。幒或从松。', '巾;悤;松', '悤;松', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4874, '𢅡', 'lán', '第七卷', '巾部', '楚謂無緣衣也。从巾，監聲。', '巾;監', '監', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4875, '幎', 'mì', '第七卷', '巾部', '幔也。从巾，冥聲。周禮有「幎人」。', '巾;冥', '冥', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4876, '幔', 'màn', '第七卷', '巾部', '幎也。从巾，曼聲。', '巾;曼', '曼', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4877, '幬;𢅂;𢄛', 'chóu', '第七卷', '巾部', '襌帳也。从巾，𠷎聲。', '巾;𠷎', '𠷎', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4878, '㡘', 'lián', '第七卷', '巾部', '帷也。从巾，兼聲。', '巾;兼', '兼', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4879, '帷', 'wéi', '第七卷', '巾部', '在旁曰帷。从巾，隹聲。古文帷[匚韋]。', '巾;隹;匚;韋', '隹;韋', 'null', NULL, '巾;匚', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4880, '帳', 'zhànɡ', '第七卷', '巾部', '張也。从巾，長聲。', '巾;長', '長', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4881, '幕', 'mù', '第七卷', '巾部', '帷在上曰幕。从巾，莫聲。', '巾;莫', '莫', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4882, '㠲', 'bǐ', '第七卷', '巾部', '㡜裂也。从巾，匕聲。', '巾;匕', '匕', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4883, '㡜', 'xiè', '第七卷', '巾部', '殘帛也。从巾，祭聲。', '巾;祭', '祭', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4884, '㡏', 'shū', '第七卷', '巾部', '正褍裂也。从巾，俞聲。', '巾;俞', '俞', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4885, '帖', 'tiè', '第七卷', '巾部', '帛書署也。从巾，占聲。', '巾;占', '占', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4886, '帙;袟;袠', 'zhì', '第七卷', '巾部', '書衣也。从巾，失聲。帙或从衣。', '巾;失;衣', '失', 'null', NULL, '巾;衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4887, '㡐;𢃬', 'jiān', '第七卷', '巾部', '幡幟也。从巾，前聲。', '巾;前', '前', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4888, '幑', 'huī', '第七卷', '巾部', '幑識也。㠯絳帛。箸於背。从巾，微省聲。春秋傳曰「揚幑者公徒」。若今救火衣然也。', '巾;微', '微', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4889, '幖;𢅽', 'biāo', '第七卷', '巾部', '𢅽識也。从巾，票聲。', '巾;票', '票', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4890, '㠾', 'yuān', '第七卷', '巾部', '幡也。从巾，夗聲。', '巾;夗', '夗', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4891, '幡', 'fān', '第七卷', '巾部', '書兒拭觚布也。从巾，番聲。', '巾;番', '番', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4892, '𢃴', 'là', '第七卷', '巾部', '刜也。从巾，剌聲。', '巾;剌', '剌', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4893, '㡨', 'jiān', '第七卷', '巾部', '拭也。从巾，韱聲。', '巾;韱', '韱', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4894, '飾', 'shì', '第七卷', '巾部', '㕞也。从巾，从人，从𩚁聲。讀若式。一曰襐飾。', '巾;人;𩚁;式', '𩚁;式', 'null', NULL, '巾;人', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4895, '幝', 'chǎn', '第七卷', '巾部', '車敝皃。从巾，單聲。詩曰「檀車幝幝[𦆀]」。', '巾;單;𦆀', '單;𦆀', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4896, '𢄐', 'ménɡ', '第七卷', '巾部', '葢衣也。从巾，冡聲。', '巾;冡', '冡', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4897, '幭', 'miè', '第七卷', '巾部', '葢幭也。从巾，蔑聲。一曰襌被。', '巾;蔑', '蔑', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4898, '幠', 'hū', '第七卷', '巾部', '覆也。从巾，𣞤聲。', '巾;𣞤', '𣞤', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4899, '幃', 'wéi', '第七卷', '巾部', '囊也。从巾，韋聲。', '巾;韋', '韋', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4900, '帣', 'juàn', '第七卷', '巾部', '囊也。今鹽官三斛爲一帣。从巾，𢍏聲。', '巾;𢍏', '𢍏', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4901, '帚', 'zhǒu', '第七卷', '巾部', '所㠯糞也。从又持巾埽𫡃內。古者少康初作箕、帚、秫酒。少康，杜康也，葬長垣。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4902, '席', 'xí', '第七卷', '巾部', '藉也。禮「天子、諸矦席，有黼繡純飾」。从巾，庶省聲。古文席从石省[㐁]。', '巾;庶;石;㐁', '庶;石', 'null', NULL, '巾;㐁', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4903, '幐', 'ténɡ', '第七卷', '巾部', '囊也。从巾，朕聲。', '巾;朕', '朕', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4904, '𢅯', 'fèn', '第七卷', '巾部', '㠯囊盛穀大滿而裂也。从巾，奮聲。', '巾;奮', '奮', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4905, '㡒', 'zhūn', '第七卷', '巾部', '載米𤲑也。从巾，盾聲。讀若易「屯卦」之屯。', '巾;盾;屯', '盾;屯', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4906, '㠷', 'ɡé', '第七卷', '巾部', '蒲席𤳊也。从巾，及聲。讀若蛤。', '巾;及;蛤', '及;蛤', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4907, '幩', 'fén', '第七卷', '巾部', '馬纏鑣扇汗也。从巾，賁聲。詩曰「朱幩儦儦」。', '巾;賁', '賁', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4908, '㡪;𢅼', 'néi', '第七卷', '巾部', '𡎰地也。㠯巾𢵢之。从巾，𡖀[婚]聲。讀若「水溫㬮」。一曰箸也。', '巾;𡖀;婚;㬮', '𡖀;婚;㬮', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4909, '帑', 'tǎnɡ', '第七卷', '巾部', '金幣所藏也。从巾，奴聲。', '巾;奴', '奴', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4910, '布', 'bù', '第七卷', '巾部', '枲織也。从巾，父聲。', '巾;父', '父', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4911, '幏', 'jià', '第七卷', '巾部', '南郡 蠻夷賨布也。从巾，家聲。', '巾;家', '家', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4912, '㡉', 'xián', '第七卷', '巾部', '㡉布也。出東萊。从巾，弦聲。', '巾;弦', '弦', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4913, '㡔', 'mù', '第七卷', '巾部', '䰍布也。一曰車衡上衣。从巾，敄聲。讀若頊。', '巾;敄;頊', '敄;頊', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4914, '幦', 'mì', '第七卷', '巾部', '車覆軾也。从巾，辟聲。周禮曰「駹車犬幦」。', '巾;辟', '辟', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4915, '㡇', 'zhé', '第七卷', '巾部', '領耑也。从巾，耴聲。', '巾;耴', '耴', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4916, '幢', 'chuánɡ', '第七卷', '巾部', '旌旗之屬。从巾，童聲。', '巾;童', '童', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4917, '幟', 'zhì', '第七卷', '巾部', '旌旗之屬。从巾，戠聲。', '巾;戠', '戠', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4918, '帟', 'yì', '第七卷', '巾部', '在上曰帟。从巾，亦聲。', '巾;亦', '亦', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4919, '幗', 'ɡuó', '第七卷', '巾部', '婦人首飾。从巾，國聲。', '巾;國', '國', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4920, '幧', 'qiāo', '第七卷', '巾部', '斂髮也。从巾，喿聲。', '巾;喿', '喿', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4921, '帒;袋', 'dài', '第七卷', '巾部', '囊也。从巾，代聲。[袋]或从衣。', '巾;代;衣', '代', 'null', NULL, '巾;衣', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4922, '帊', 'pà', '第七卷', '巾部', '帛三幅曰帊。从巾，巴聲。', '巾;巴', '巴', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4923, '幞', 'fú', '第七卷', '巾部', '帊也。从巾，菐聲。', '巾;菐', '菐', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4924, '幰', 'xiǎn', '第七卷', '巾部', '車幔也。从巾，憲聲。', '巾;憲', '憲', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4925, '巿;韍;紱', 'fú', '第七卷', '巿部', '韠也。上古衣蔽前而巳，巿㠯象之。天子朱巿，諸矦赤巿，卿大夫蔥衡。从巾。象連帶之形。凡巿之屬皆从巿。篆文巿，从韋，从犮。俗作紱。', '巾;韋;犮', '犮', 'null', NULL, '巾;韋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4926, '𢂷;韐', 'jiá', '第七卷', '巿部', '士無巿有𢂷。制如𣖁，缺四角。爵弁服，其色韎，賤不得與裳同。从巿，合聲。𢂷或从韋。', '巿;合;韋', '合', 'null', NULL, '巿;韋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4927, '帛', 'bó', '第七卷', '帛部', '繒也。从巾，白聲。凡帛之屬皆从帛。', '巾;白', '白', 'null', NULL, '巾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4928, '錦', 'jǐn', '第七卷', '帛部', '襄邑織文也。从帛，金聲。', '帛;金', '金', 'null', NULL, '帛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4929, '白', 'bái', '第七卷', '白部', '西方色也。侌用事。物色白。从入合二。二，侌數。凡白之屬皆从白。古文白。', '入;二', '', 'null', NULL, '入;二', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4930, '皎', 'jiǎo', '第七卷', '白部', '月之白也。从白，交聲。詩曰「月出皎兮」。', '白;交', '交', 'null', NULL, '白', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4931, '皢', 'xiǎo', '第七卷', '白部', '日之白也。从白，堯聲。', '白;堯', '堯', 'null', NULL, '白', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
