INSERT INTO `shuowen_voice_revel` VALUES (4939, '皛', 'xiào', '第七卷', '白部', '顯也。通白曰皛。从三白。讀若皎。', '白;皎', '皎', 'null', NULL, '白', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
