INSERT INTO `shuowen_voice_revel` VALUES (4946, '𪓌', 'zuì', '第七卷', '黹部', '會五采繒也。从黹，卒聲。', '黹;卒', '卒', 'null', NULL, '黹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4947, '黺', 'fěn', '第七卷', '黹部', '袞衣山、龍、華、蟲。黺，畫粉也。从黹，分聲。衛宏說。', '黹;分', '分', 'null', NULL, '黹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4948, '人', 'rén', '第八卷', '人部', '天地之性冣貴者也。此籒文。象臂脛之形。凡人之屬皆从人。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4949, '僮', 'tónɡ', '第八卷', '人部', '未冠也。从人，童聲。', '人;童', '童', 'null', NULL, '人', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4950, '保;𣎼;呆;𠌀', 'bǎo', '第八卷', '人部', '養也。从人，𤔍省聲。𤔍，古文孚。古文不省。古文。', '人;𤔍', '𤔍', 'null', NULL, '人', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
