INSERT INTO `shuowen_voice_revel` VALUES (5216, '匙', 'chí', '第八卷', '匕部', '匕也。从匕，是聲。', '匕;是', '是', 'null', NULL, '匕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5217, '𠤏', 'bǎo', '第八卷', '匕部', '相次也。从匕十。鴇从此。', '匕;十;鴇', '鴇', 'null', NULL, '匕;十', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5218, '𢺵', 'qì', '第八卷', '匕部', '頃也。从匕，支聲。匕，頭頃也。詩曰「𢺵彼織女」。', '匕;支', '支', 'null', NULL, '匕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5219, '頃', 'qīnɡ', '第八卷', '匕部', '頭不正也。从匕頁。', '匕;頁', '', 'null', NULL, '匕;頁', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5220, '匘', 'nǎo', '第八卷', '匕部', '頭𩪦也。从匕。匕，相匕箸也。川，㠯象髮。𦥓，象𦥓形。', '匕;川;𦥓', '', 'null', NULL, '匕;川;𦥓', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5221, '卬', 'yǎnɡ', '第八卷', '匕部', '望也。欲有所庶及也。从匕卪。詩曰「高山卬止」。', '匕;卪', '', 'null', NULL, '匕;卪', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5222, '卓', 'zhuō', '第八卷', '匕部', '高也。早匕爲卓，匕卪爲卬，皆同意。古文卓。', '早;匕', '', 'null', NULL, '早;匕', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5223, '艮', 'ɡèn', '第八卷', '匕部', '很也。从匕目。匕目，猶目相匕，不相下也。易曰「艮其限」。匕目爲艮，𠤎目爲眞。', '匕;目', '', 'null', NULL, '匕;目', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5224, '从', 'cónɡ', '第八卷', '从部', '相聽也。从二人。凡从之屬皆从从。', '人', '', 'null', NULL, '人', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5225, '從;𨑹', 'cónɡ', '第八卷', '从部', '隨行也。从从辵。从亦聲。', '从;辵', '从', 'null', NULL, '辵', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5226, '并', 'bìnɡ', '第八卷', '从部', '相从也。从从，开聲。一曰从持二干爲并。', '开;干', '开', 'null', NULL, '干', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5227, '比', 'bǐ', '第八卷', '比部', '密也。二人爲从，反从爲比。凡此之屬皆从比。古文比。', '从', '', 'null', NULL, '从', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5228, '毖', 'bì', '第八卷', '比部', '愼也。从比，必聲。周書曰「無毖于卹」。', '比;必', '必', 'null', NULL, '比', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5229, '北', 'bèi', '第八卷', '北部', '乖也。从二人相背。凡北之屬皆从北。', '人', '', 'null', NULL, '人', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5230, '冀', 'jì', '第八卷', '北部', '北方州也。从北，異聲。', '北;異', '異', 'null', NULL, '北', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5231, '丘', 'qiū', '第八卷', '丘部', '土之高也。非人所爲也。从北，从一。一，地也。人凥在丘南，故从北。中邦之凥，在昆侖東南。一曰四方高、中央下爲丘。象形。凡丘之屬皆从丘。古文从土。', '北;一;土', '', 'null', NULL, '北;一;土', 'null', 'null', 'null', 'null', '经', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5232, '虛;虚;虗', 'xū', '第八卷', '丘部', '大丘也。昆侖丘謂之昆侖虚。古者九夫爲井，四井爲邑，四邑爲丘，丘謂之虗。从丘，虍聲。', '丘;虍', '虍', 'null', NULL, '丘', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5233, '屔', 'ní', '第八卷', '丘部', '反頂受水丘也。从丘，从泥省。泥亦聲。', '丘;泥', '泥', 'null', NULL, '丘', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5234, '㐺', 'yín', '第八卷', '㐺部', '眾立也。从三人。凡㐺之屬皆从㐺。讀若「欽崟」。', '人;崟', '崟', 'null', NULL, '人', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5235, '眾;衆', 'zhònɡ', '第八卷', '㐺部', '多也。从㐺目。衆意。', '㐺;目', '', 'null', NULL, '㐺;目', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5236, '聚', 'jù', '第八卷', '㐺部', '會也。从㐺，取聲。一曰邑落曰聚。', '㐺;取', '取', 'null', NULL, '㐺', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5237, '臮', 'jì', '第八卷', '㐺部', '衆與䛐也。从㐺，自聲。虞書曰「臮[暨]咎繇」。古文臮[悉]。', '㐺;自;暨;悉', '自;暨;悉', 'null', NULL, '㐺', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5238, '𡈼', 'tǐnɡ', '第八卷', '𡈼部', '善也。从人士。士，事也。一曰象物出地挺生也。凡𡈼之屬皆从𡈼。', '人;士', '', 'null', NULL, '人;士', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5239, '徵', 'zhēnɡ', '第八卷', '𡈼部', '召也。从𡈼，从微省。𡈼微爲徵。行於微而聞達者，卽徵也。古文[从微省，从各]。', '𡈼;微;各', '', 'null', NULL, '𡈼;微;各', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5240, '朢', 'wànɡ', '第八卷', '𡈼部', '月滿也。與日相望，佀朝君。从月，从臣，从𡈼。𡈼，朝廷也。古文朢省。', '月;臣;𡈼', '', 'null', NULL, '月;臣;𡈼', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5241, '㸒', 'yín', '第八卷', '𡈼部', '近求也。从爪𡈼。𡈼，憿幸也。', '爪;𡈼', '', 'null', NULL, '爪;𡈼', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5242, '重', 'zhònɡ', '第八卷', '重部', '厚也。从𡈼，東聲。凡重之屬皆从重。', '𡈼;東', '東', 'null', NULL, '𡈼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5243, '量', 'liánɡ', '第八卷', '重部', '稱輕重也。从重省。曏省聲。古文。', '重;曏', '曏', 'null', NULL, '重', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5244, '臥', 'wò', '第八卷', '臥部', '伏也。从人。臣，取其伏也。凡臥之屬皆从臥。', '人;臣', '', 'null', NULL, '人;臣', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5245, '監', 'jiān', '第八卷', '臥部', '臨下也。从臥，䘓省聲。古文監从言。', '臥;䘓;言', '䘓', 'null', NULL, '臥;言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5246, '臨', 'lín', '第八卷', '臥部', '監也。从臥，品聲。', '臥;品', '品', 'null', NULL, '臥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5247, '䭆', 'nè', '第八卷', '臥部', '楚謂小兒嬾䭆。从臥食。', '臥;食', '', 'null', NULL, '臥;食', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5248, '身', 'shēn', '第八卷', '身部', '躳也。从人，申省聲。凡身之屬皆从身。', '人;申', '申', 'null', NULL, '人', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5249, '軀', 'qū', '第八卷', '身部', '體也。从身，區聲。', '身;區', '區', 'null', NULL, '身', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5250, '㐆', 'yī', '第八卷', '㐆部', '歸也。从反身。凡㐆之屬皆从㐆。', '身', '', 'null', NULL, '身', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5251, '殷', 'yīn', '第八卷', '㐆部', '作樂之盛偁殷。从㐆殳。易曰「殷薦之上帝」。', '㐆;殳', '', 'null', NULL, '㐆;殳', 'null', 'null', 'null', 'null', '文部', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5252, '衣', 'yī', '第八卷', '衣部', '依也。上曰衣，下曰常。象覆二人之形。凡衣之屬皆从衣。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5253, '裁', 'cái', '第八卷', '衣部', '制衣也。从衣，𢦒聲。', '衣;𢦒', '𢦒', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5254, '袞', 'ɡǔn', '第八卷', '衣部', '天子亯先王。卷龍繡於下常。幅一龍。蟠阿上鄉。从衣，㕣聲。', '衣;㕣', '㕣', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5255, '𧝑', 'zhàn', '第八卷', '衣部', '丹縠衣也。从衣，㠭聲。', '衣;㠭', '㠭', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5256, '褕', 'yú', '第八卷', '衣部', '褕翟，羽飾衣。从衣，俞聲。一曰直裾謂之「襜褕」。', '衣;俞', '俞', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5257, '袀', 'jūn', '第八卷', '衣部', '玄服也。从衣，匀聲。讀若均。', '衣;匀;均', '匀;均', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '应补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5258, '袗', 'zhěn', '第八卷', '衣部', '襌衣也。一曰盛服。从衣，㐱聲。', '衣;㐱', '㐱', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5259, '表;𧘝', 'biǎo', '第八卷', '衣部', '上衣也。从衣毛。古者衣裘，故㠯毛爲表。古文表从麃。', '衣;毛;麃', '毛;麃', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5260, '裏', 'lǐ', '第八卷', '衣部', '衣內也。从衣，里聲。', '衣;里', '里', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5261, '襁', 'qiǎnɡ', '第八卷', '衣部', '負兒衣。从衣，強聲。', '衣;強', '強', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5262, '襋', 'jí', '第八卷', '衣部', '衣領也。从衣，棘聲。詩曰「要之襋之」。', '衣;棘', '棘', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5263, '襮', 'bó', '第八卷', '衣部', '黼領也。从衣，㬥聲。詩曰「素衣朱襮」。', '衣;㬥', '㬥', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5264, '褗', 'yǎn', '第八卷', '衣部', '褗領也。从衣，匽聲。', '衣;匽', '匽', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5265, '裺', 'yǎn', '第八卷', '衣部', '褗謂之裺。从衣，奄聲。', '衣;奄', '奄', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5266, '衽', 'rèn', '第八卷', '衣部', '衣䘳也。从衣，壬聲。', '衣;壬', '壬', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5267, '褸', 'lǚ', '第八卷', '衣部', '衽也。从衣，婁聲。', '衣;婁', '婁', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5268, '𧚨', 'qì', '第八卷', '衣部', '䘳緣也。从衣，疌聲。', '衣;疌', '疌', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5269, '䘳', 'jīn', '第八卷', '衣部', '交衽也。从衣，金聲。', '衣;金', '金', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5270, '褘', 'huī', '第八卷', '衣部', '蔽厀也。从衣，韋聲。周禮曰「王后之服褘衣」，謂畫袍。', '衣;韋', '韋', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5271, '衭', 'fū', '第八卷', '衣部', '襲衭也。从衣，夫聲。', '衣;夫', '夫', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5272, '襲', 'xí', '第八卷', '衣部', '𠂇衽袍。从衣，龖省聲。籒文襲不省。', '衣;龖', '龖', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5273, '袍', 'páo', '第八卷', '衣部', '襺也。从衣，包聲。論語曰「衣敝緼袍」。', '衣;包', '包', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5274, '襺', 'jiǎn', '第八卷', '衣部', '袍衣也。从衣，繭聲。㠯絮曰襺。㠯縕曰袍。春秋傳曰「盛夏重襺」。', '衣;繭', '繭', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5275, '褋', 'dié', '第八卷', '衣部', '南楚謂襌衣曰褋。从衣，枼聲。', '衣;枼', '枼', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5276, '袤', 'mào', '第八卷', '衣部', '衣帶㠯上。从衣，矛聲。一曰南北曰袤。東西曰廣。籒文袤从楙。', '衣;矛;楙', '矛;楙', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5277, '襘', 'ɡuì', '第八卷', '衣部', '帶所結也。从衣，會聲。春秋傳曰「衣有襘」。', '衣;會', '會', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5278, '褧', 'jiǒnɡ', '第八卷', '衣部', '檾衣也。詩曰「衣錦褧衣」。示反古。从衣，耿聲。', '衣;耿', '耿', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5279, '袛', 'dī', '第八卷', '衣部', '袛裯，短衣也。从衣，氐聲。', '衣;氐', '氐', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5280, '裯', 'dāo', '第八卷', '衣部', '衣袂袛裯。从衣，周聲。', '衣;周', '周', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5281, '襤', 'lán', '第八卷', '衣部', '裯謂之襤褸。襤，無緣衣也。从衣，監聲。', '衣;監', '監', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5282, '䙃', 'duò', '第八卷', '衣部', '無袂衣謂之䙃。从衣，惰省聲。', '衣;惰', '惰', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5283, '𧛔', 'dú', '第八卷', '衣部', '衣躳縫。从衣，毒聲。讀若督。', '衣;毒;督', '毒;督', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5284, '袪', 'qū', '第八卷', '衣部', '衣袂也。从衣，去聲。一曰袪，褱也。褱者，袌也。袪尺二寸。春秋傳曰「披斬其袪」。', '衣;去', '去', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5285, '褎;袖', 'xiù', '第八卷', '衣部', '袂也。从衣，𥝩聲。俗褎从由。', '衣;𥝩;由', '𥝩;由', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5286, '袂', 'mèi', '第八卷', '衣部', '褎也。从衣，夬聲。', '衣;夬', '夬', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5287, '褢', 'huái', '第八卷', '衣部', '褎也。一曰臧也。从衣，鬼聲。', '衣;鬼', '鬼', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5288, '袌', 'bào', '第八卷', '衣部', '褱也。从衣，包聲。', '衣;包', '包', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5289, '褱', 'huái', '第八卷', '衣部', '俠也。从衣，眔聲。一曰橐。', '衣;眔', '眔', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5290, '襜', 'chān', '第八卷', '衣部', '衣蔽歬。从衣，詹聲。', '衣;詹', '詹', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5291, '袥', 'tuō', '第八卷', '衣部', '衣衸。从衣，石聲。', '衣;石', '石', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5292, '衸', 'xiè', '第八卷', '衣部', '袥也。从衣，介聲。', '衣;介', '介', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5293, '袉', 'tuó', '第八卷', '衣部', '裾也。从衣，它聲。論語曰「朝服袉紳」。', '衣;它', '它', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5294, '裾', 'jū', '第八卷', '衣部', '衣袌也。从衣，居聲。讀與居同。', '衣;居', '居', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5295, '衧', 'yú', '第八卷', '衣部', '諸衧也。从衣，于聲。', '衣;于', '于', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5296, '襗', 'duó', '第八卷', '衣部', '絝也。从衣，睪聲。', '衣;睪', '睪', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5297, '褰', 'qiān', '第八卷', '衣部', '絝也。从衣，寒省聲。春秋傳曰「徵褰與襦」。', '衣;寒', '寒', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5298, '襱', 'lónɡ', '第八卷', '衣部', '絝踦也。从衣，龍聲。襱或从𧷏。', '衣;龍;𧷏', '龍;𧷏', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5299, '袑', 'shào', '第八卷', '衣部', '絝上也。从衣，召聲。', '衣;召', '召', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5300, '襑', 'tǎn', '第八卷', '衣部', '衣博大也。从衣，尋聲。', '衣;尋', '尋', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5301, '褒;襃', 'bāo', '第八卷', '衣部', '衣博裾。从衣，𠌀省聲。𠌀，古文保。', '衣;𠌀', '𠌀', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5302, '𧝐;䙗', 'tì', '第八卷', '衣部', '緥也。从衣，啻聲。詩曰「載衣之𧝐[裼]」。', '衣;啻;裼', '啻;裼', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5303, '褍', 'duān', '第八卷', '衣部', '衣正幅。从衣，耑聲。', '衣;耑', '耑', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5304, '䙟', 'wéi', '第八卷', '衣部', '重衣皃。从衣，圍聲。爾雅曰「䙟䙟[洄]，䙡䙡」。', '衣;圍;洄', '圍;洄', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5305, '複', 'fù', '第八卷', '衣部', '重衣也。从衣，复聲。一曰褚衣。', '衣;复', '复', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5306, '褆', 'tí', '第八卷', '衣部', '衣厚褆褆。从衣，是聲。', '衣;是', '是', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5307, '襛', 'nónɡ', '第八卷', '衣部', '衣厚皃。从衣，農聲。詩曰「何彼襛矣」。', '衣;農', '農', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5308, '裻', 'dú', '第八卷', '衣部', '新衣聲。一曰背縫。从衣，叔聲。', '衣;叔', '叔', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5309, '袳', 'chǐ', '第八卷', '衣部', '衣張也。从衣，多聲。春秋傳曰「公會齊侯于袳」。', '衣;多', '多', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '经', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5310, '裔', 'yì', '第八卷', '衣部', '衣裙也。从衣。㕯聲。古文裔[几]。', '衣;㕯;几', '㕯;几', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5311, '衯', 'fēn', '第八卷', '衣部', '長衣皃。从衣，分聲。', '衣;分', '分', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5312, '裵;裴', 'péi', '第八卷', '衣部', '長衣皃。从衣，非聲。', '衣;非', '非', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5313, '袁', 'yuán', '第八卷', '衣部', '長衣皃。从衣，叀省聲。', '衣;叀', '叀', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5314, '𧜣', 'diāo', '第八卷', '衣部', '短衣也。从衣，鳥聲。春秋傳曰有空𧜣。', '衣;鳥', '鳥', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5315, '褺', 'dié', '第八卷', '衣部', '重衣也。从衣，執聲。巴郡有褺江縣。', '衣;執', '執', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5316, '襡', 'shǔ', '第八卷', '衣部', '裋衣也。从衣，蜀聲。讀若屬。', '衣;屬', '屬', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5317, '𧞐', 'zhuó', '第八卷', '衣部', '衣至地也。从衣，斲聲。', '衣;斲', '斲', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5318, '襦', 'rú', '第八卷', '衣部', '裋衣也。从衣，需聲。一曰㬮衣。', '衣;需', '需', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5319, '褊', 'biǎn', '第八卷', '衣部', '衣小也。从衣，扁聲。', '衣;扁', '扁', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5320, '袷', 'jiā', '第八卷', '衣部', '衣無絮。从衣，合聲。', '衣;合', '合', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5321, '襌', 'dān', '第八卷', '衣部', '衣不重。从衣，單聲。', '衣;單', '單', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5322, '襄', 'xiānɡ', '第八卷', '衣部', '漢令「解衣而耕謂之襄」。从衣，𤕦聲。古文襄[爲古女]。', '衣;𤕦;爲;古;女', '𤕦', 'null', NULL, '衣;爲;古;女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5323, '被', 'bèi', '第八卷', '衣部', '寑衣。長一身有半。从衣，皮聲。', '衣;皮', '皮', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5324, '衾', 'qīn', '第八卷', '衣部', '大被。从衣，今聲。', '衣;今', '今', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5325, '襐', 'xiànɡ', '第八卷', '衣部', '襐飾也。从衣，象聲。', '衣;象', '象', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5326, '衵', 'yì', '第八卷', '衣部', '日日所常衣。从衣，从日。日亦聲。', '衣;日', '日', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5327, '褻', 'xiè', '第八卷', '衣部', '私服。从衣，埶聲。詩曰「是褻[紲]絆也」。', '衣;埶;紲', '埶;紲', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5328, '衷', 'zhōnɡ', '第八卷', '衣部', '裏褻衣。从衣，中聲。春秋傳曰「皆衷其衵服」。', '衣;中', '中', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5329, '袾', 'zhū', '第八卷', '衣部', '好佳也。从衣，朱聲。詩曰「靜女其袾」。', '衣;朱', '朱', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5330, '袓', 'jù', '第八卷', '衣部', '事好也。从衣，且聲。', '衣;且', '且', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5331, '裨', 'bì', '第八卷', '衣部', '接也。益也。从衣，卑聲。', '衣;卑', '卑', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5332, '袢', 'fán', '第八卷', '衣部', '衣無色也。从衣，半聲。一曰詩曰「是紲袢也」。讀若普。', '衣;半;普', '半;普', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5333, '雜;襍', 'zá', '第八卷', '衣部', '五采相合也。从衣，集聲。', '衣;集', '集', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5334, '褽', 'wèi', '第八卷', '衣部', '衽也。从衣，熨聲。', '衣;熨', '熨', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5335, '裕', 'yù', '第八卷', '衣部', '衣物饒也。从衣，谷聲。易曰「有孚裕無咎」。', '衣;谷', '谷', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5336, '襞', 'bì', '第八卷', '衣部', '韏衣也。从衣，辟聲。', '衣;辟', '辟', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5337, '衦', 'ɡǎn', '第八卷', '衣部', '摩展衣也。从衣，干聲。', '衣;干', '干', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5338, '裂', 'liè', '第八卷', '衣部', '繒餘也。从衣，列聲。', '衣;列', '列', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5339, '𧘽', 'ná', '第八卷', '衣部', '敝衣。从衣，奴聲。', '衣;奴', '奴', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5340, '袒', 'zhàn', '第八卷', '衣部', '衣縫解也。从衣，旦聲。', '衣;旦', '旦', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5341, '補', 'bǔ', '第八卷', '衣部', '完衣也。从衣，甫聲。', '衣;甫', '甫', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5342, '𧝉', 'zhǐ', '第八卷', '衣部', '紩衣也。从衣黹。黹亦聲。', '衣;黹', '黹', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5343, '褫', 'chǐ', '第八卷', '衣部', '奪衣也。从衣，虒聲。讀若池。', '衣;虒;池', '虒;池', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5344, '𧝹;裸', 'luǒ', '第八卷', '衣部', '但也。从衣，𦟀聲。𧝹或从果。', '衣;𦟀;果', '𦟀;果', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5345, '裎', 'chénɡ', '第八卷', '衣部', '但也。从衣，呈聲。', '衣;呈', '呈', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5346, '裼', 'xī', '第八卷', '衣部', '但也。从衣，易聲。', '衣;易', '易', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5347, '衺', 'xié', '第八卷', '衣部', '𩎮也。从衣，牙聲。', '衣;牙', '牙', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5348, '襭;擷', 'xié', '第八卷', '衣部', '㠯衣衽扱物謂之襭。从衣，頡聲。襭或从手。', '衣;頡;手', '頡', 'null', NULL, '衣;手', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5349, '袺', 'jié', '第八卷', '衣部', '執衽謂之袺。从衣，吉聲。', '衣;吉', '吉', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5350, '褿', 'cáo', '第八卷', '衣部', '帴也。从衣，曹聲。', '衣;曹', '曹', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5351, '裝', 'zhuānɡ', '第八卷', '衣部', '裹也。从衣，壯聲。', '衣;壯', '壯', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5352, '裹', 'ɡuǒ', '第八卷', '衣部', '纏也。从衣，果聲。', '衣;果', '果', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5353, '裛', 'yè', '第八卷', '衣部', '纏也。从衣，邑聲。', '衣;邑', '邑', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5354, '𪗋', 'zī', '第八卷', '衣部', '緶也。裳下緝。从衣，齊聲。', '衣;齊', '齊', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5355, '裋', 'shù', '第八卷', '衣部', '豎使布長襦。从衣，豆聲。', '衣;豆', '豆', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5356, '䙔', 'yǔ', '第八卷', '衣部', '編枲衣。一曰頭䙔。一曰㳄裹衣。从衣，區聲。', '衣;區', '區', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5357, '褐', 'hè', '第八卷', '衣部', '編枲韤。一曰粗衣。从衣，曷聲。', '衣;曷', '曷', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5358, '衰', 'suō', '第八卷', '衣部', '艸雨衣。秦謂之萆。从衣。象形。古文衰。', '衣', '', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5359, '卒', 'zú', '第八卷', '衣部', '隷人給事者爲卒。古㠯染衣題識，故从衣一。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5360, '褚', 'chǔ', '第八卷', '衣部', '卒也。从衣，者聲。一曰裝也。', '衣;者', '者', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5361, '製', 'zhì', '第八卷', '衣部', '裁衣也。从衣，制聲。', '衣;制', '制', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5362, '袚', 'bō', '第八卷', '衣部', '蠻夷衣。从衣，犮聲。一曰蔽厀。', '衣;犮', '犮', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5363, '襚', 'suì', '第八卷', '衣部', '衣死人也。从衣，遂聲。春秋傳曰「楚使公親襚」。', '衣;遂', '遂', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5364, '䘟', 'diāo', '第八卷', '衣部', '棺中縑裏也。从衣，弔聲。讀若雕。', '衣;弔;雕', '弔;雕', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5365, '裞', 'shuì', '第八卷', '衣部', '贈終者衣被曰裞。从衣，兌聲。', '衣;兌', '兌', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5366, '褮', 'yínɡ', '第八卷', '衣部', '鬼衣也。从衣，熒省聲。讀若詩曰「葛藟縈之」，一曰若「靜女其袾」之袾。', '衣;熒;縈;靜', '熒;縈;靜', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5367, '䘰', 'shān', '第八卷', '衣部', '車溫也。从衣，延聲。', '衣;延', '延', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5368, '褭', 'niǎo', '第八卷', '衣部', '㠯組帶馬也。从衣，从馬。', '衣;馬', '', 'null', NULL, '衣;馬', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5369, '袨', 'xuàn', '第八卷', '衣部', '盛服也。从衣，玄聲。', '衣;玄', '玄', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5370, '衫', 'shān', '第八卷', '衣部', '衣也。从衣，彡聲。', '衣;彡', '彡', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5371, '襖', 'ǎo', '第八卷', '衣部', '𧚍屬。从衣，奥聲。', '衣;奥', '奥', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5372, '褪', 'tuì', '第八卷', '衣部', '卸衣也。从衣，退聲。讀若吞。', '衣;退;吞', '退;吞', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '政宇新附', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5373, '裘;求;𧚍', 'qiú', '第八卷', '裘部', '皮衣也。从衣。象形。與衰同意。凡𧚍之屬皆从𧚍。古文𧚍。', '衣', '', 'null', NULL, '衣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5374, '𩱘', 'kè', '第八卷', '裘部', '𧚍裏也。从𧚍，鬲聲。讀若擊。', '𧚍;鬲;擊', '鬲;擊', 'null', NULL, '𧚍', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5375, '老', 'lǎo', '第八卷', '老部', '考也。七十曰老。从人毛𠤎。言須髮變白也。凡老之屬皆从老。', '毛;𠤎', '', 'null', NULL, '毛;𠤎', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5376, '耊;耋', 'dié', '第八卷', '老部', '年八十曰耊。从老省。至聲。', '老;至', '至', 'null', NULL, '老', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5377, '𦽡', 'mào', '第八卷', '老部', '年九十曰𦽡。从老，蒿省聲。', '老;蒿', '蒿', 'null', NULL, '老', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5378, '耆', 'qí', '第八卷', '老部', '老也。从老省，旨聲。', '老;旨', '旨', 'null', NULL, '老', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5379, '耇;耈;耉', 'ɡǒu', '第八卷', '老部', '老人面凍黎若垢。从老省，句聲。', '老;句', '句', 'null', NULL, '老', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5380, '𦒻', 'diàn', '第八卷', '老部', '老人面如點處。从老省，占聲。讀若「耿介」之耿。', '老;占;耿', '占;耿', 'null', NULL, '老', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5381, '𦒶', 'shù', '第八卷', '老部', '老人行才相逮。从老省。勿，象形。讀若樹。', '老;勿;樹', '樹', 'null', NULL, '老;勿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5382, '壽', 'shòu', '第八卷', '老部', '久也。从老省，𠷎聲。', '老;𠷎', '𠷎', 'null', NULL, '老', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5383, '考', 'kǎo', '第八卷', '老部', '老也。从老省，丂聲。', '老;丂', '丂', 'null', NULL, '老', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5384, '孝', 'xiào', '第八卷', '老部', '善事父母者。从老省。从子，子承老也。', '老;子', '', 'null', NULL, '老;子', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5385, '毛', 'máo', '第八卷', '毛部', '眉髮之屬及獸毛也。象形。凡毛之屬皆从毛。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5386, '𣯍', 'rǔn', '第八卷', '毛部', '毛盛也。从毛，隼聲。虞書曰「鳥獸𣯍[矞]髦」。', '毛;隼;矞', '隼;矞', 'null', NULL, '毛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5387, '㲦', 'hàn', '第八卷', '毛部', '獸豪也。从毛，倝聲。', '毛;倝', '倝', 'null', NULL, '毛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5388, '毨', 'xiǎn', '第八卷', '毛部', '選也。仲秋鳥獸毛盛，可選取㠯爲器。从毛，先聲。讀若選。', '毛;先;選', '先;選', 'null', NULL, '毛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5389, '𣯩', 'mén', '第八卷', '毛部', '㠯毳爲𦇧。色如虋，故謂之𣯩。虋，禾之赤苗也。从毛，㒼聲。詩曰「毳衣如𣯩」。', '毛;㒼', '㒼', 'null', NULL, '毛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5390, '氊', 'zhān', '第八卷', '毛部', '撚毛也。从毛，亶聲。', '毛;亶', '亶', 'null', NULL, '毛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5391, '毦', 'ěr', '第八卷', '毛部', '羽毛飾也。从毛，耳聲。', '毛;耳', '耳', 'null', NULL, '毛', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5392, '氍', 'qú', '第八卷', '毛部', '氍毹，毾㲪。从毛，瞿聲。', '毛;瞿', '瞿', 'null', NULL, '毛', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5393, '毹', 'yú', '第八卷', '毛部', '氍毹也。从毛，俞聲。', '毛;俞', '俞', 'null', NULL, '毛', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5394, '毾', 'tà', '第八卷', '毛部', '毾㲪也。从毛，𦐇聲。', '毛;𦐇', '𦐇', 'null', NULL, '毛', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5395, '㲪', 'dēnɡ', '第八卷', '毛部', '毾㲪也。从毛，登聲。', '毛;登', '登', 'null', NULL, '毛', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5396, '毬', 'qiú', '第八卷', '毛部', '鞠丸也。从毛，求聲。', '毛;求', '求', 'null', NULL, '毛', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5397, '氅', 'chǎnɡ', '第八卷', '毛部', '析鳥羽爲旗纛之屬。从毛，敞聲。', '毛;敞', '敞', 'null', NULL, '毛', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5398, '毳', 'cuì', '第八卷', '毳部', '獸細毛也。从三毛。凡毳之屬皆从毳。', '毛', '', 'null', NULL, '毛', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5399, '䩁', 'fēi', '第八卷', '毳部', '毛紛紛也。从毳，非聲。', '毳;非', '非', 'null', NULL, '毳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5400, '尸', 'shī', '第八卷', '尸部', '陳也。象臥之形。凡尸之屬皆从尸。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5401, '㞟', 'diàn', '第八卷', '尸部', '偫也。从尸，奠聲。', '尸;奠', '奠', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5402, '居', 'jū', '第八卷', '尸部', '蹲也。从尸，古聲。俗居从足。', '尸;古;足', '古', 'null', NULL, '尸;足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5403, '㞒', 'xiè', '第八卷', '尸部', '臥息也。从尸自。', '尸;自', '自', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5404, '㞕', 'xiè', '第八卷', '尸部', '動作切切也。从尸，䏌聲。', '尸;䏌', '䏌', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5405, '展;㞡', 'zhǎn', '第八卷', '尸部', '轉也。从尸。𧝑省聲。', '尸;𧝑', '𧝑', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5406, '屆;届', 'jiè', '第八卷', '尸部', '行不便也。一曰極也。从尸，凷聲。', '尸;凷', '凷', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5407, '尻', 'kāo', '第八卷', '尸部', '𦞠也。从尸，九聲。', '尸;九', '九', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5408, '𡱂;𦞠;𩪔;𩪡;臀', 'tún', '第八卷', '尸部', '髀也。从尸下丌凥几。𡱂或从肉隼。𡱂或从骨，殿聲。', '尸;丌;几;肉;隼;骨;殿', '隼;殿', 'null', NULL, '尸;丌;几;肉;骨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5409, '㞓', 'qì', '第八卷', '尸部', '尻也。从尸，旨聲。', '尸;旨', '旨', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5410, '尼', 'ní', '第八卷', '尸部', '從後近之。从尸，匕聲。', '尸;匕', '匕', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5411, '㞚', 'qì', '第八卷', '尸部', '㞚㞏。從後相躡也。从尸，臿聲。', '尸;臿', '臿', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5412, '㞏', 'zhé', '第八卷', '尸部', '㞚㞏也。从尸，乏聲。', '尸;乏', '乏', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5413, '㞋;𡰫', 'niǎn', '第八卷', '尸部', '柔皮也。从尸又。又，申尸之後也。', '尸;又', '', 'null', NULL, '尸;又', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5414, '屒', 'zhěn', '第八卷', '尸部', '伏皃。一曰屋宇也。从尸，辰聲。', '尸;辰', '辰', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5415, '屖', 'xī', '第八卷', '尸部', '屖遟[遲]也。从尸，辛聲。', '尸;辛', '辛', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5416, '屝', 'fèi', '第八卷', '尸部', '履屬。从尸，非聲。', '尸;非', '非', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5417, '屍', 'shī', '第八卷', '尸部', '終主也。从尸死。', '尸;死', '尸;死', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5418, '屠', 'tú', '第八卷', '尸部', '刳也。从尸，者聲。', '尸;者', '者', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5419, '屟', 'xiè', '第八卷', '尸部', '履之荐也。从尸，枼聲。', '尸;枼', '枼', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5420, '屋', 'wū', '第八卷', '尸部', '凥也。从尸。尸，所主也。一曰尸象屋形。从至。至，所止也。屋、室皆从至。籒文屋从厂。古文屋。', '尸;至;厂', '', 'null', NULL, '尸;至;厂', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5421, '屏', 'pínɡ', '第八卷', '尸部', '屏，蔽也。从尸，并聲。', '尸;并', '并', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5422, '層', 'cénɡ', '第八卷', '尸部', '重屋也。从尸，曾聲。', '尸;曾', '曾', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5423, '屢', 'lǚ', '第八卷', '尸部', '數也。从尸[婁]。', '尸;婁', '婁', 'null', NULL, '尸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5424, '尺', 'chǐ', '第八卷', '尺部', '十寸也。人手卻十分動脈爲寸口。十寸爲尺。尺，所㠯指尺規榘事也。从尸，从乙。乙，所識也。周制寸、尺、咫、尋、常、仞諸度量，皆㠯人之體爲法。凡尺之屬皆从尺。', '尸;乙', '', 'null', NULL, '尸;乙', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5425, '咫', 'zhǐ', '第八卷', '尺部', '中婦人手長八寸謂之咫。周尺也。从尺，只聲。', '尺;只', '只', 'null', NULL, '尺', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5426, '尾', 'wěi', '第八卷', '尾部', '微也。从到毛在尸後。古人或飾系尾。西南夷皆然。凡尾之屬皆从尾。', '毛;尸', '', 'null', NULL, '毛;尸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5427, '屬', 'zhǔ', '第八卷', '尾部', '連也。从尾。蜀聲。', '尾;蜀', '蜀', 'null', NULL, '尾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5428, '屈;𡲬', 'qū', '第八卷', '尾部', '無尾也。从尾，出聲。', '尾;出', '出', 'null', NULL, '尾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5429, '尿;𡲘', 'niào', '第八卷', '尾部', '人小便也。从尾水。', '尾;水', '', 'null', NULL, '尾;水', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5430, '履', 'lǚ', '第八卷', '履部', '足所依也。从尸。服履者也。从彳夊。从舟，象履形。一曰尸聲。凡履之屬皆从履。古文履从頁，从足。', '尸;彳;夊;舟;頁;足', '尸', 'null', NULL, '彳;夊;舟;頁;足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5431, '屨', 'jù', '第八卷', '履部', '履也。从履省，婁聲。一曰鞮也。', '履;婁', '婁', 'null', NULL, '履', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5432, '𡳸', 'lì', '第八卷', '履部', '履下也。从履省，歷聲。', '履;歷', '歷', 'null', NULL, '履', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5433, '𡱣', 'xù', '第八卷', '履部', '履屬。从履省，予聲。', '履;予', '予', 'null', NULL, '履', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5434, '屩', 'juē', '第八卷', '履部', '履也。从履省，喬聲。', '履;喬', '喬', 'null', NULL, '履', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5435, '屐', 'jī', '第八卷', '履部', '屩也。从履省，支聲。', '履;支', '支', 'null', NULL, '履', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5436, '舟', 'zhōu', '第八卷', '舟部', '船也。古者共𡔷、貨狄刳木爲舟，剡木爲楫，㠯濟不通。象形。凡舟之屬皆从舟。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5437, '俞', 'yú', '第八卷', '舟部', '空中木爲舟也。从亼，从舟，从巜。巜，水也。', '亼;舟;巜', '', 'null', NULL, '亼;舟;巜', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5438, '船;舩', 'chuán', '第八卷', '舟部', '舟也。从舟，㕣聲。', '舟;㕣', '㕣', 'null', NULL, '舟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5439, '䑣', 'chēn', '第八卷', '舟部', '船行也。从舟，彡聲。', '舟;彡', '彡', 'null', NULL, '舟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5440, '舳', 'zhú', '第八卷', '舟部', '舳艫也。从舟，由聲。漢律名船方長爲舳艫。一曰船尾。', '舟;由', '由', 'null', NULL, '舟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5441, '艫', 'lú', '第八卷', '舟部', '舳艫也。从舟，盧聲。一曰船頭。', '舟;盧', '盧', 'null', NULL, '舟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5442, '𦨉', 'wù', '第八卷', '舟部', '船行不安也。从舟，刖省聲。讀若兀。', '舟;刖;兀', '刖;兀', 'null', NULL, '舟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5443, '艐', 'zōnɡ', '第八卷', '舟部', '船箸沙不行也。从舟，㚇聲。讀若䔂。', '舟;㚇;䔂', '㚇;䔂', 'null', NULL, '舟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5444, '朕', 'zhèn', '第八卷', '舟部', '我也。闕。[从舟，灷聲。]', '舟;灷', '灷', 'null', NULL, '舟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5445, '舫', 'fǎnɡ', '第八卷', '舟部', '船也。朙堂月令曰「舫人」。舫人，習水者。从舟，方聲。', '舟;方', '方', 'null', NULL, '舟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5446, '般', 'bān', '第八卷', '舟部', '辟也。象舟之旋。从舟，从殳。殳，令舟旋者也。古文般从攴。', '舟;殳;攴', '', '班', '公输班，一作公输般。', '舟;殳;攴', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5447, '服', 'fú', '第八卷', '舟部', '用也。一曰車右騑所㠯舟旋。从舟，𠬝聲。古文服从人。', '舟;𠬝;人', '𠬝', 'null', NULL, '舟;人', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5448, '𦩍', 'dāo', '第八卷', '舟部', '小船也。从舟，周聲。', '舟;周', '周', 'null', NULL, '舟', 'null', 'null', 'null', 'null', '应补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5449, '舸', 'ɡě', '第八卷', '舟部', '舟也。从舟，可聲。', '舟;可', '可', 'null', NULL, '舟', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5450, '艇', 'tǐnɡ', '第八卷', '舟部', '小舟也。从舟，廷聲。', '舟;廷', '廷', 'null', NULL, '舟', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5451, '艅', 'yú', '第八卷', '舟部', '艅艎，舟名。从舟，余聲。', '舟;余', '余', 'null', NULL, '舟', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5452, '艎', 'huánɡ', '第八卷', '舟部', '艅艎也。从舟，皇聲。', '舟;皇', '皇', 'null', NULL, '舟', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5453, '方;汸', 'fānɡ', '第八卷', '方部', '倂船也。象兩舟省總頭形。凡方之屬皆从方。方或从水。', '舟;水', '', 'null', NULL, '舟;水', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5454, '𣃚', 'hánɡ', '第八卷', '方部', '方舟也。从方，亢聲。禮「天子造舟，諸侯維舟，大夫方舟，士特舟」。', '方;亢', '亢', 'null', NULL, '方', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5455, '儿', 'rén', '第八卷', '儿部', '古文奇字人也。象形。孔子曰「儿在下，故詰詘」。凡儿之屬皆从儿。', '人', '人', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5456, '兀', 'wù', '第八卷', '儿部', '高而上平也。从一在儿上。讀若敻。茂陵有兀桑里。', '一;儿;敻', '敻', 'null', NULL, '一;儿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5457, '兒', 'ér', '第八卷', '儿部', '孺子也。从儿。象小兒頭𦥓未合。', '儿', '', 'null', NULL, '儿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5458, '允', 'yǔn', '第八卷', '儿部', '信也。从㠯儿。', '㠯;儿', '', 'null', NULL, '㠯;儿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5459, '兌;兑', 'duì', '第八卷', '儿部', '說也。从儿，㕣聲。', '儿;㕣', '㕣', 'null', NULL, '儿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5460, '充', 'chōnɡ', '第八卷', '儿部', '長也。高也。从儿，育省聲。', '儿;育', '育', 'null', NULL, '儿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5461, '亮', 'liànɡ', '第八卷', '儿部', '朙也。从儿，高省。', '儿;高', '', 'null', NULL, '儿;高', 'null', 'null', 'null', 'null', '应补', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5462, '兄', 'xiōnɡ', '第八卷', '兄部', '長也。从儿，从口。凡兄之屬皆从兄。', '儿;口', '', 'null', NULL, '儿;口', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5463, '兢', 'jīnɡ', '第八卷', '兄部', '競也。从二兄。二兄，競意。从丯聲。讀若矝。一曰兢，敬也。', '兄;丯;矝', '丯;矝', 'null', NULL, '兄', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5464, '兂;簪', 'zēn', '第八卷', '兂部', '首笄也。从儿。匸象形。凡兂之屬皆从兂。俗兂。从竹，从朁。', '儿;匸;竹;朁', '朁', 'null', NULL, '儿;匸;竹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5465, '兓', 'jīn', '第八卷', '兂部', '兓兓，銳意也。从二兂。', '兂', '兂', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5466, '皃;䫉;貌', 'mào', '第八卷', '皃部', '頌儀也。从儿。白象面形。凡皃之屬皆从皃。皃或从頁，豹省聲。籒文皃从豸。', '儿;白;頁;豹;豸', '豹', 'null', NULL, '儿;白;頁;豸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5467, '㝸;𢍍;弁;𡭪;覍', 'biàn', '第八卷', '皃部', '冕也。周曰𡭪，殷曰吁，夏曰收。从皃。象形。或覍字。籒文覍从廾，上象形。', '皃;廾', '', 'null', NULL, '皃;廾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5468, '𠑹', 'ɡǔ', '第八卷', '𠑹部', '廱蔽也。从儿。象左右皆蔽形。凡𠑹之屬皆从𠑹。讀若瞽。', '儿;瞽', '瞽', 'null', NULL, '儿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5469, '兜', 'dōu', '第八卷', '𠑹部', '兜鍪，首鎧也。从𠑹，从皃省。皃，象人頭形也。', '𠑹;皃', '', 'null', NULL, '𠑹;皃', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5470, '先', 'xiān', '第八卷', '先部', '前進也。从儿之。凡先之屬皆从先。', '儿;之', '', 'null', NULL, '儿;之', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5471, '兟', 'shēn', '第八卷', '先部', '進也。从二先。贊从此。闕。', '先', '', 'null', NULL, '先', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5472, '禿;秃', 'tū', '第八卷', '禿部', '無髮也。从儿。上象禾粟之形。[秀，]取其聲。凡禿之屬皆从禿。王育說，倉頡出見禿人伏禾中，因㠯制字。未知其審。', '儿;秀', '秀', 'null', NULL, '儿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5473, '穨', 'tuí', '第八卷', '禿部', '禿皃。从禿，貴聲。', '禿;貴', '貴', 'null', NULL, '禿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5474, '見', 'jiàn', '第八卷', '見部', '視也。从目儿。凡見之屬皆从見。', '目;儿', '', 'null', NULL, '目;儿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5475, '視;眡', 'shì', '第八卷', '見部', '瞻也。从見，示聲。古文視。亦古文視[氐]。', '見;示;氐', '示;氐', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5476, '䚕', 'lì', '第八卷', '見部', '求視也。从見，麗聲。讀若池。', '見;麗;池', '麗;池', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5477, '覣', 'wēi', '第八卷', '見部', '好視也。从見，委聲。', '見;委', '委', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5478, '𧡎', 'nì', '第八卷', '見部', '旁視也。从見，兒聲。', '見;兒', '兒', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5479, '覶', 'luó', '第八卷', '見部', '好視也。从見，𤔔聲。', '見;𤔔', '𤔔', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5480, '䚄', 'lù', '第八卷', '見部', '笑視也。从見，录聲。', '見;录', '录', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5481, '𧡩', 'xuǎn', '第八卷', '見部', '大視也。从見，爰聲。', '見;爰', '爰', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5482, '覝', 'lián', '第八卷', '見部', '察視也。从見，𤇇聲。讀若鎌。', '見;𤇇;鎌', '𤇇;鎌', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5483, '䚋', 'yùn', '第八卷', '見部', '外博眾視也。从見，員聲。讀若運。', '見;員;運', '員;運', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5484, '觀', 'ɡuān', '第八卷', '見部', '諦視也。从見，雚聲。', '見;雚', '雚', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5485, '䙷', 'dé', '第八卷', '見部', '取也。从見寸。寸，度之，亦手也。', '見;寸', '', 'null', NULL, '見;寸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5486, '覽', 'lǎn', '第八卷', '見部', '觀也。从見監。監亦聲。', '見;監', '監', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5487, '䚅', 'lài', '第八卷', '見部', '內視也。从見，來聲。', '見;來', '來', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5488, '𧡨', 'tí', '第八卷', '見部', '顯也。从見，是聲。', '見;是', '是', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5489, '𧢄', 'piǎo', '第八卷', '見部', '目有察省見也。从見，票聲。', '見;票', '票', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5490, '䙻;𧠥', 'cī', '第八卷', '見部', '𧠥覰，闚觀也。从見，𠂔聲。', '見;𠂔', '𠂔', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5491, '覰', 'qù', '第八卷', '見部', '𧠥覰也。一曰拘覰，未致密也。从見，虘聲。', '見;虘', '虘', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5492, '覭', 'mínɡ', '第八卷', '見部', '小見也。从見，冥聲。爾雅曰「覭髳，弗離」。', '見;冥', '冥', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5493, '𧡪', 'dān', '第八卷', '見部', '內視也。从見，甚聲。', '見;甚', '甚', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5494, '覯', 'ɡòu', '第八卷', '見部', '遇見也。从見，冓聲。', '見;冓', '冓', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5495, '𧢦', 'kuī', '第八卷', '見部', '注目視也。从見，歸聲。', '見;歸', '歸', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5496, '覘', 'chān', '第八卷', '見部', '闚視也。从見，占聲。春秋傳曰「公使覘之，信」。', '見;占', '占', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5497, '覹', 'wéi', '第八卷', '見部', '司也。从見，微聲。', '見;微', '微', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5498, '覢', 'shǎn', '第八卷', '見部', '暫見也。从見，炎聲。春秋公羊傳曰「覢[闖]然公子陽生」。', '見;炎;闖', '炎;闖', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5499, '䚔', 'bìn', '第八卷', '見部', '䚔𧢜，暫見也。从見，賓聲。', '見;賓', '賓', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5500, '𧢜', 'fán', '第八卷', '見部', '䚔𧢜也。从見，樊聲。讀若幡。', '見;樊;幡', '樊;幡', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5501, '𧠠', 'mí', '第八卷', '見部', '病人視也。从見，民聲。讀若迷。', '見;民;迷', '民;迷', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5502, '𧡹', 'yóu', '第八卷', '見部', '下視𥥍也。从見，逌聲。讀若攸。', '見;逌;攸', '逌;攸', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5503, '𧡬', 'chēn', '第八卷', '見部', '私出頭視也。从見，䑣聲。讀若郴。', '見;䑣;郴', '䑣;郴', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5504, '𧠊', 'mào', '第八卷', '見部', '突前也。从見𠔼。', '見;𠔼', '𠔼', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5505, '覬', 'jì', '第八卷', '見部', '𣢆𡴘也。从見，豈聲。', '見;豈', '豈', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5506, '覦', 'yú', '第八卷', '見部', '欲也。从見，俞聲。', '見;俞', '俞', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5507, '䚎', 'chuānɡ', '第八卷', '見部', '視不朙也。从見，舂聲。一曰直視。', '見;舂', '舂', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5508, '𧢢', 'yào', '第八卷', '見部', '視誤也。从見，龠聲。', '見;龠', '龠', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5509, '覺', 'jué', '第八卷', '見部', '悟也。从見，學省聲。一曰發也。', '見;學', '學', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5510, '䚐', 'jí', '第八卷', '見部', '睍也。从見，𥏼省聲。', '見;𥏼', '𥏼', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5511, '靚', 'jìnɡ', '第八卷', '見部', '召也。从見，靑聲。', '見;靑', '靑', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5512, '親', 'qīn', '第八卷', '見部', '至也。从見，𣐽聲。', '見;𣐽', '𣐽', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5513, '覲', 'jìn', '第八卷', '見部', '諸矦秋朝曰覲。勤勞王事也。从見，堇聲。', '見;堇', '堇', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5514, '覜', 'tiào', '第八卷', '見部', '諸矦三年大相聘曰覜。覜，視也。从見，兆聲。', '見;兆', '兆', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5515, '覒', 'máo', '第八卷', '見部', '擇也。从見，毛聲。讀若苗。', '見;毛;苗', '毛;苗', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5516, '覕', 'miè', '第八卷', '見部', '蔽不相見也。从見，必聲。', '見;必', '必', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5517, '䙾', 'shī', '第八卷', '見部', '司人也。从見，它聲。讀若馳。', '見;它;馳', '它;馳', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5518, '𧡸', 'dōu', '第八卷', '見部', '目蔽垢也。从見，𠁁[𨪐]聲。讀若兜。', '見;𨪐;兜', '𨪐;兜', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5519, '覿', 'dí', '第八卷', '見部', '見也。从見，𧷏聲。', '見;𧷏', '𧷏', 'null', NULL, '見', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5520, '覞', 'yào', '第八卷', '覞部', '竝視也。从二見。凡覞之屬皆从覞。', '見', '', 'null', NULL, '見', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5521, '𧢞', 'qiān', '第八卷', '覞部', '很視也。从覞，肩聲。齊景公之勇臣有成𧢞[覵荆]者。', '覞;肩;覵;荆', '肩;覵;荆', 'null', NULL, '覞', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5522, '䨳', 'xì', '第八卷', '覞部', '見雨而比息。从覞雨。讀若欷。', '覞;雨;欷', '欷', 'null', NULL, '覞;雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5523, '欠', 'qiàn', '第八卷', '欠部', '張口气悟也。象气从儿上出之形。凡欠之屬皆从欠。', '儿', '', 'null', NULL, '儿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5524, '欽', 'qīn', '第八卷', '欠部', '欠皃。从欠，金聲。', '欠;金', '金', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5525, '㱍', 'luán', '第八卷', '欠部', '欠皃。从欠，䜌聲。', '欠;䜌', '䜌', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5526, '欯', 'xì', '第八卷', '欠部', '喜也。从欠，吉聲。', '欠;吉', '吉', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5527, '吹', 'chuī', '第八卷', '欠部', '出气也。从欠，从口。', '欠;口', '', 'null', NULL, '欠;口', 'null', 'null', 'null', 'null', '重出应删', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5528, '欨', 'xū', '第八卷', '欠部', '吹也。一曰笑意。从欠，句聲。', '欠;句', '句', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5529, '歑', 'hū', '第八卷', '欠部', '溫吹也。从欠，虖聲。', '欠;虖', '虖', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5530, '㰲', 'yù', '第八卷', '欠部', '吹气也。从欠，或聲。', '欠;或', '或', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5531, '歟', 'yú', '第八卷', '欠部', '安气也。从欠，與聲。', '欠;與', '與', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5532, '𣣲', 'xié', '第八卷', '欠部', '翕气也。从欠，脅聲。', '欠;脅', '脅', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5533, '歕', 'pēn', '第八卷', '欠部', '吹气也。从欠，賁聲。', '欠;賁', '賁', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5534, '歇', 'xiē', '第八卷', '欠部', '息也。一曰气越泄。从欠，曷聲。讀若「香臭盡歇」。', '欠;曷', '曷', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5535, '歡', 'huān', '第八卷', '欠部', '樂也。从欠，雚聲。', '欠;雚', '雚', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5536, '欣;訢', 'xīn', '第八卷', '欠部', '𥬇皃。从欠，斤聲。古文欣从言。', '欠;斤;言', '斤', 'null', NULL, '欠;言', 'null', 'null', 'null', 'null', '政宇并入重文', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5537, '𣢇', 'shěn', '第八卷', '欠部', '𥬇不壞顔曰𣢇。从欠，己聲。', '欠;己', '己', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5538, '款;歀', 'kuǎn', '第八卷', '欠部', '意有所欲也。从欠，㝮省。款或从柰。', '欠;㝮;柰', '㝮;柰', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5539, '𣢆', 'jì', '第八卷', '欠部', '𡴘也。从欠，气聲。一曰口不便言。', '欠;气', '气', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5540, '欲', 'yù', '第八卷', '欠部', '貪也。从欠，谷聲。', '欠;谷', '谷', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5541, '歌;謌', 'ɡē', '第八卷', '欠部', '詠也。从欠，哥聲。歌或从言。', '欠;哥;言', '哥', 'null', NULL, '欠;言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5542, '歂', 'chuǎn', '第八卷', '欠部', '口气引也。从欠，耑聲。讀若輇。', '欠;耑;輇', '耑;輇', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5543, '歍', 'wū', '第八卷', '欠部', '心有所惡若吐也。从欠，烏聲。一曰歍𣤶。口相就也。', '欠;烏', '烏', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5544, '𣤶;噈', 'zú', '第八卷', '欠部', '歍𣤶也。从欠，鼀聲。俗𣤶从口，从就。', '欠;鼀;口;就', '鼀', 'null', NULL, '欠;口;就', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5545, '𣢰', 'zú', '第八卷', '欠部', '惄然也。从欠，尗聲。孟子曰「曾西𣢰[蹵]然」。', '欠;尗;蹵', '尗;蹵', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5546, '欦', 'qiān', '第八卷', '欠部', '含𥬇也。从欠，今聲。', '欠;今', '今', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5547, '歋', 'yí', '第八卷', '欠部', '人相𥬇相歋瘉。从欠，虒聲。', '欠;虒', '虒', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5548, '歊', 'xiāo', '第八卷', '欠部', '歊歊，气上出皃。从欠高。高亦聲。', '欠;高', '高', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5549, '欻', 'xū', '第八卷', '欠部', '有所吹起。从欠，炎[𠦪]聲。讀若忽。', '欠;𠦪;忽', '𠦪;忽', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5550, '𣢑', 'xī', '第八卷', '欠部', '𣢑𣢑，戲𥬇皃。从欠，之聲。', '欠;之', '之', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5551, '𣣳', 'yáo', '第八卷', '欠部', '𣣳𣣳，气出皃。从欠，䍃聲。', '欠;䍃', '䍃', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5552, '歗', 'xiào', '第八卷', '欠部', '吹也。从欠，肅聲。詩曰「其歗也謌」。', '欠;肅', '肅', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5553, '歎', 'tàn', '第八卷', '欠部', '吟也。謂情有所悅。吟歎而歌詠。从欠，𪅀省聲。籒文歎不省。', '欠;𪅀', '𪅀', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5554, '歖', 'xī', '第八卷', '欠部', '卒喜也。从欠，从喜。', '欠;喜', '喜', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5555, '欸', 'xiè', '第八卷', '欠部', '訾也。从欠，矣聲。', '欠;矣', '矣', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5556, '㰣', 'zì', '第八卷', '欠部', '歐也。从欠，此聲。', '欠;此', '此', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5557, '歐', 'ǒu', '第八卷', '欠部', '吐也。从欠，區聲。', '欠;區', '區', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5558, '歔', 'xū', '第八卷', '欠部', '欷也。从欠，虛聲。一曰出气也。', '欠;虛', '虛', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5559, '欷', 'xī', '第八卷', '欠部', '歔也。从欠，希聲。', '欠;希', '希', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5560, '歜', 'chù', '第八卷', '欠部', '盛气怒也。从欠，蜀聲。', '欠;蜀', '蜀', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5561, '𣣸', 'yǒu', '第八卷', '欠部', '言意也。从欠，从逌。逌亦聲。讀若酉。', '欠;逌;酉', '逌;酉', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5562, '㵣', 'kě', '第八卷', '欠部', '欲㱃歠。从欠，渴聲。', '欠;渴', '渴', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5563, '㰾', 'jiǎo', '第八卷', '欠部', '所歌也。从欠，噭省聲。讀若「噭呼」。', '欠;噭', '噭', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5564, '㱇', 'xì', '第八卷', '欠部', '悲意。从欠，嗇聲。', '欠;嗇', '嗇', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5565, '𣤹', 'jiào', '第八卷', '欠部', '盡酒也。从欠，𥼚聲。', '欠;𥼚', '𥼚', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5566, '𣤭', 'jiān', '第八卷', '欠部', '堅持意。口閉也。从欠，緘聲。', '欠;緘', '緘', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5567, '㰮', 'shèn', '第八卷', '欠部', '指而𥬇也。从欠，辰聲。讀若蜃。', '欠;辰;蜃', '辰;蜃', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5568, '𣤿', 'kūn', '第八卷', '欠部', '𣤿干，不可知也。从欠，鰥聲。', '欠;鰥', '鰥', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5569, '歃', 'shà', '第八卷', '欠部', '歠也。从欠，臿聲。春秋傳曰「歃而忘」。', '欠;臿', '臿', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5570, '欶', 'shuò', '第八卷', '欠部', '吮也。从欠，束聲。', '欠;束', '束', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5571, '歁', 'kǎn', '第八卷', '欠部', '食不滿也。从欠，甚聲。讀若坎。', '欠;甚;坎', '甚;坎', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5572, '欿', 'kǎn', '第八卷', '欠部', '欲得也。从欠，臽聲。讀若貪。', '欠;臽;貪', '臽;貪', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5573, '欱', 'hē', '第八卷', '欠部', '歠也。从欠，合聲。', '欠;合', '合', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5574, '歉', 'qiàn', '第八卷', '欠部', '食不飽也。从欠，兼聲。', '欠;兼', '兼', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5575, '䯉', 'wā', '第八卷', '欠部', '咽中息不利也。从欠，骨聲。', '欠;骨', '骨', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5576, '欭', 'yì', '第八卷', '欠部', '嚘也。从欠，因聲。', '欠;因', '因', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5577, '欬', 'kài', '第八卷', '欠部', '屰气也。从欠，亥聲。', '欠;亥', '亥', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5578, '𣤢', 'xì', '第八卷', '欠部', '且唾聲。一曰小𥬇。从欠，毄聲。', '欠;毄', '毄', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5579, '歙', 'xī', '第八卷', '欠部', '縮鼻也。从欠，翕聲。丹陽有歙縣。', '欠;翕', '翕', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5580, '㰶', 'yǒu', '第八卷', '欠部', '蹴鼻也。从欠，咎聲。讀若爾雅曰「麔豭短脰」。', '欠;咎;麔', '咎;麔', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5581, '𣢜', 'yǒu', '第八卷', '欠部', '愁皃。从欠，幼聲。', '欠;幼', '幼', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5582, '欪', 'chù', '第八卷', '欠部', '咄欪，無慙。一曰無腸意。从欠，出聲。讀若屮。', '欠;出;屮', '出;屮', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5583, '欥', 'yù', '第八卷', '欠部', '詮䛐也。从欠曰。曰亦聲。詩曰「欥[遹]求厥甯」。', '欠;曰;遹', '曰;遹', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5584, '次', 'cì', '第八卷', '欠部', '不前不精也。从欠，二聲。古文次。', '欠;二', '二', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5585, '㱂', 'kānɡ', '第八卷', '欠部', '飢虛也。从欠，康聲。', '欠;康', '康', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5586, '欺', 'qī', '第八卷', '欠部', '詐也。从欠，其聲。', '欠;其', '其', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5587, '歆', 'xīn', '第八卷', '欠部', '神食气也。从欠，音聲。', '欠;音', '音', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5588, '歈', 'yú', '第八卷', '欠部', '歌也。从欠，俞聲。', '欠;俞', '俞', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5589, '㱃;飲', 'yǐn', '第八卷', '㱃部', '歠也。从欠，酓聲。凡㱃之屬皆从㱃。古文㱃从今水。古文㱃从今食。', '欠;酓;今;水;今;食', '酓;今;今', 'null', NULL, '欠;水;食', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5590, '歠;吷', 'xuè', '第八卷', '㱃部', '㱃也。从㱃省，叕聲。歠或从口，从夬。', '㱃;叕;口;夬', '叕;夬', 'null', NULL, '㱃;口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5591, '㳄', 'xián', '第八卷', '㳄部', '慕欲口液也。从欠水。凡㳄之屬皆从㳄。㳄或从侃。籒文㳄[沝]。', '欠;水;侃;沝', '侃', 'null', NULL, '欠;水;沝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5592, '羨', 'xiàn', '第八卷', '㳄部', '貪也。从㳄，羑省。羑，讀若「㕗呼」，文王所拘羑里。', '㳄;羑', '', 'null', NULL, '㳄;羑', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5593, '𠩗', 'yí', '第八卷', '㳄部', '歠也。从㳄，𠂆聲。讀若移。', '㳄;𠂆;移', '𠂆;移', 'null', NULL, '㳄', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5594, '盜', 'dào', '第八卷', '㳄部', '厶利物也。从㳄皿。㳄，欲也，欲皿爲盜。', '㳄;皿', '', 'null', NULL, '㳄;皿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5595, '旡', 'jì', '第八卷', '旡部', '㱃食屰气不得息曰旡。从反欠。凡旡之屬皆从旡。古文旡。', '欠', '', 'null', NULL, '欠', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5596, '旤', 'huò', '第八卷', '旡部', '屰惡驚䛐也。从旡，咼聲。讀若楚人名多夥。', '旡;咼;夥', '咼;夥', 'null', NULL, '旡', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5597, '𣄴', 'liànɡ', '第八卷', '旡部', '事有不譱言𣄴也。爾雅「𣄴，薄也」。从旡，京聲。', '旡;京', '京', 'null', NULL, '旡', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5598, '頁;𩑋', 'xié', '第九卷', '頁部', '頭也。从𦣻，从儿。古文䭫首如此。凡𩑋之屬皆从𩑋。', '𦣻;儿', '', 'null', NULL, '𦣻;儿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5599, '頭', 'tóu', '第九卷', '頁部', '𦣻也。从𩑋。豆聲。', '𩑋;豆', '豆', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5600, '顔', 'yán', '第九卷', '頁部', '頟也。从𩑋，彥聲。籒文。', '𩑋;彥', '彥', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5601, '䫤', 'mínɡ', '第九卷', '頁部', '眉目之閒也。从𩑋，冥聲。', '𩑋;冥', '冥', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '政宇补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5602, '頌', 'rónɡ', '第九卷', '頁部', '皃也。从𩑋，公聲。籒文[容]。', '𩑋;公;容', '公;容', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5603, '𩑒', 'duó', '第九卷', '頁部', '𩑒顱，首骨也。从𩑋，乇聲。', '𩑋;乇', '乇', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5604, '顱', 'lú', '第九卷', '頁部', '𩑒顱也。从𩑋，盧聲。', '𩑋;盧', '盧', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5605, '𩕾', 'yuàn', '第九卷', '頁部', '顚頂也。从𩑋，𡚇聲。', '𩑋;𡚇', '𡚇', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5606, '顚;顛', 'diān', '第九卷', '頁部', '頂也。从𩑋，眞聲。', '𩑋;眞', '眞', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5607, '頂', 'dǐnɡ', '第九卷', '頁部', '顚也。从𩑋，丁聲。或从首作。籒文从鼎。', '𩑋;丁;首;鼎', '丁;鼎', 'null', NULL, '𩑋;首', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5608, '顙', 'sǎnɡ', '第九卷', '頁部', '頟也。从𩑋，桑聲。', '𩑋;桑', '桑', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5609, '題', 'tí', '第九卷', '頁部', '頟也。从𩑋，是聲。', '𩑋;是', '是', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5610, '頟', 'é', '第九卷', '頁部', '顙也。从𩑋，各聲。', '𩑋;各', '各', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5611, '頞', 'è', '第九卷', '頁部', '鼻莖也。从𩑋，安聲。或从鼻曷。', '𩑋;安;鼻;曷', '安;曷', 'null', NULL, '𩑋;鼻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5612, '頯', 'kuí', '第九卷', '頁部', '權也。从𩑋，𢌳聲。', '𩑋;𢌳', '𢌳', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5613, '頰', 'jiá', '第九卷', '頁部', '面旁也。从𩑋，夾聲。籒文頰[首]。', '𩑋;夾;首', '夾', 'null', NULL, '𩑋;首', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5614, '䫀', 'ɡěn', '第九卷', '頁部', '頰後也。从𩑋，艮聲。', '𩑋;艮', '艮', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5615, '頜', 'hàn', '第九卷', '頁部', '顄也。从𩑋，合聲。', '𩑋;合', '合', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5616, '𩔞;顄', 'hán', '第九卷', '頁部', '頤也。从𩑋，圅聲。', '𩑋;圅', '圅', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5617, '頸', 'jǐnɡ', '第九卷', '頁部', '頭莖也。从𩑋，巠聲。', '𩑋;巠', '巠', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
