INSERT INTO `shuowen_voice_revel` VALUES (5620, '䪴', 'zhěn', '第九卷', '頁部', '玉枕也。从𩑋，冘聲。', '𩑋;冘', '冘', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5621, '顀', 'chuí', '第九卷', '頁部', '出頟也。从𩑋，隹聲。', '𩑋;隹', '隹', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5622, '𩑢', 'péi', '第九卷', '頁部', '曲頤也。从𩑋，不聲。', '𩑋;不', '不', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5623, '顩', 'yǎn', '第九卷', '頁部', '䫡皃也。从𩑋。僉聲。', '𩑋;僉', '僉', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5624, '䪳', 'yǔn', '第九卷', '頁部', '面目不正皃。从𩑋，尹聲。', '𩑋;尹', '尹', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5625, '頵', 'yūn', '第九卷', '頁部', '頭頵頵大也。从𩑋，君聲。', '𩑋;君', '君', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5626, '䫟', 'hùn', '第九卷', '頁部', '面色䫟䫟皃。从𩑋，員聲。讀若隕。', '𩑋;員;隕', '員;隕', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5627, '䫡', 'yán', '第九卷', '頁部', '頭頰長也。从𩑋，兼聲。', '𩑋;兼', '兼', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5628, '碩', 'shuò', '第九卷', '頁部', '頭大也。从𩑋，石聲。', '𩑋;石', '石', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5629, '頒', 'bān', '第九卷', '頁部', '大頭也。从𩑋，分聲。一曰鬢也。詩曰「有頒其首」。', '𩑋;分', '分', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5630, '顒', 'yónɡ', '第九卷', '頁部', '大頭也。从𩑋，禺聲。詩曰「其大有顒」。', '𩑋;禺', '禺', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5631, '䫞', 'qiāo', '第九卷', '頁部', '大頭也。从𩑋，羔聲。', '𩑋;羔', '羔', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5632, '顝', 'kuī', '第九卷', '頁部', '大頭也。从𩑋，骨聲。讀若魁。', '𩑋;骨;魁', '骨;魁', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5633, '願', 'yuàn', '第九卷', '頁部', '大頭也。从𩑋，原聲。', '𩑋;原', '原', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5634, '顤', 'yáo', '第九卷', '頁部', '高長頭。从𩑋，堯聲。', '𩑋;堯', '堯', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5635, '頎', 'qí', '第九卷', '頁部', '頭佳皃。从𩑋，斤聲。讀又若鬢。', '𩑋;斤;鬢', '斤;鬢', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '大徐无小徐有 | 应补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5636, '𩕀;䫨', 'ào', '第九卷', '頁部', '𩕀。顤高也。从𩑋，敖聲。', '𩑋;敖', '敖', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5637, '𩓥', 'yuè', '第九卷', '頁部', '前面岳岳也。从𩑋，岳聲。', '𩑋;岳', '岳', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5638, '𩒳', 'mèi', '第九卷', '頁部', '昧前也。从𩑋，𡿯聲。讀若昧。', '𩑋;𡿯;昧', '𡿯;昧', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5639, '𩖊', 'línɡ', '第九卷', '頁部', '面𤸃淺𩖊𩖊也。从𩑋，霝聲。', '𩑋;霝', '霝', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5640, '𩔀', 'wài', '第九卷', '頁部', '頭𦳋𩔀也。从𩑋，㒸聲。', '𩑋;㒸', '㒸', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5641, '頑', 'wán', '第九卷', '頁部', '㮯頭也。从𩑋，元聲。', '𩑋;元', '元', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5642, '𩓸;𩓠', 'ɡuī', '第九卷', '頁部', '小頭𩓸𩓸也。从𩑋，枝聲。讀若規。', '𩑋;枝;規', '枝;規', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5643, '顆', 'kě', '第九卷', '頁部', '小頭也。从𩑋，果聲。', '𩑋;果', '果', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5644, '頢', 'huò', '第九卷', '頁部', '短面也。从𩑋，𠯑聲。', '𩑋;𠯑', '𠯑', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5645, '頲', 'tǐnɡ', '第九卷', '頁部', '狹頭頲也。从𩑋，廷聲。', '𩑋;廷', '廷', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5646, '頠', 'wěi', '第九卷', '頁部', '頭閑習也。从𩑋，危聲。', '𩑋;危', '危', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5647, '頷', 'hàn', '第九卷', '頁部', '面黃也。从𩑋，含聲。', '𩑋;含', '含', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5648, '𩔃', 'yuǎn', '第九卷', '頁部', '面不正也。从𩑋，爰聲。', '𩑋;爰', '爰', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5649, '頍', 'kuǐ', '第九卷', '頁部', '舉頭也。从𩑋，支聲。詩曰「有頍者弁」。', '𩑋;支', '支', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5650, '𩑦', 'mò', '第九卷', '頁部', '內頭水中也。从𩑋𠬛。𠬛亦聲。', '𩑋;𠬛', '𠬛', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5651, '顧', 'ɡù', '第九卷', '頁部', '還視也。从𩑋，雇聲。', '𩑋;雇', '雇', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5652, '順', 'shùn', '第九卷', '頁部', '理也。从𩑋川。', '𩑋;川', '川', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5653, '䪾', 'zhěn', '第九卷', '頁部', '顔色䪾䫰，愼事也。从𩑋，㐱聲。', '𩑋;㐱', '㐱', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5654, '䫰;𩕔', 'lǐn', '第九卷', '頁部', '䪾䫰也。从𩑋，粦聲。一曰頭少髮。', '𩑋;粦', '粦', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5655, '顓', 'zhuān', '第九卷', '頁部', '頭顓顓謹皃。从𩑋，耑聲。', '𩑋;耑', '耑', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5656, '頊', 'xū', '第九卷', '頁部', '頭頊頊謹皃。从𩑋，玉聲。', '𩑋;玉', '玉', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5657, '顉', 'ǎn', '第九卷', '頁部', '低頭也。从𩑋，金聲。春秋傳曰「迎于門。顉之而巳」。', '𩑋;金', '金', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5658, '頓', 'dùn', '第九卷', '頁部', '下首也。从𩑋，屯聲。', '𩑋;屯', '屯', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5659, '頫;俛', 'fǔ', '第九卷', '頁部', '低頭也。从𩑋，逃省。大史卜書「頫仰」字如此。楊雄曰「人面頫」。頫或从人免。', '𩑋;逃;人;免', '免', 'null', NULL, '𩑋;逃;人', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5660, '頣', 'shěn', '第九卷', '頁部', '舉目視人皃。从𩑋，臣聲。', '𩑋;臣', '臣', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5661, '𩕊', 'zhǎn', '第九卷', '頁部', '倨視人也。从𩑋，𦎍聲。', '𩑋;𦎍', '𦎍', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5662, '頡', 'xié', '第九卷', '頁部', '直項也。从𩑋，吉聲。', '𩑋;吉', '吉', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5663, '䪼', 'zhuō', '第九卷', '頁部', '頭頡䪼也。从𩑋，出聲。讀又若骨。', '𩑋;出;骨', '出;骨', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5664, '顥', 'hào', '第九卷', '頁部', '白皃。从景𩑋。楚詞曰「天白顥顥，南山四顥」。顥，白首人也。', '景;𩑋', '', 'null', NULL, '景;𩑋', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5665, '䫶', 'fán', '第九卷', '頁部', '大醜皃。从𩑋，樊聲。', '𩑋;樊', '樊', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5666, '𩓞', 'jìnɡ', '第九卷', '頁部', '好皃。从𩑋，爭聲。詩所謂「𩓞首」。', '𩑋;爭', '爭', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5667, '頨', 'yǔ', '第九卷', '頁部', '頨妍也。从𩑋，翩省聲。讀若翩。', '𩑋;翩', '翩', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5668, '顗', 'yǐ', '第九卷', '頁部', '謹莊皃。从𩑋，豈聲。', '𩑋;豈', '豈', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5669, '顅', 'qiān', '第九卷', '頁部', '頭鬢少髮也。从𩑋，肩聲。周禮曰「數目顅脰」。', '𩑋;肩', '肩', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5670, '𩓽', 'kūn', '第九卷', '頁部', '無髮也。一曰耳門也。从𩑋，囷聲。', '𩑋;囷', '囷', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5671, '𩑔', 'kū', '第九卷', '頁部', '禿也。从𩑋，气聲。', '𩑋;气', '气', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5672, '頛', 'lèi', '第九卷', '頁部', '頭不正也。从𩑋耒。耒，頭傾，亦聲。讀又若春秋「陳夏齧」之齧。', '𩑋;耒;齧', '耒;齧', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5673, '䫌', 'pǐ', '第九卷', '頁部', '傾首也。从𩑋，卑聲。', '𩑋;卑', '卑', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5674, '䫔', 'qì', '第九卷', '頁部', '司人也。一曰恐也。从𩑋，契聲。讀若楔。', '𩑋;契;楔', '契;楔', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5675, '䫥', 'kuǐ', '第九卷', '頁部', '頭不正也。从𩑋，鬼聲。', '𩑋;鬼', '鬼', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5676, '頗', 'pō', '第九卷', '頁部', '頭偏也。从𩑋，皮聲。', '𩑋;皮', '皮', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5677, '𩑣;疣', 'yòu', '第九卷', '頁部', '顫也。从𩑋，尤聲。𩑣或从疒。', '𩑋;尤;疒', '尤', 'null', NULL, '𩑋;疒', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5678, '顫', 'chàn', '第九卷', '頁部', '頭不定也。从𩑋，亶聲。', '𩑋;亶', '亶', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5679, '顑', 'kǎn', '第九卷', '頁部', '顑顲，食不飽，面黃起行也。从𩑋，咸聲。讀若戇。', '𩑋;咸;戇', '咸;戇', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5680, '顲', 'lǎn', '第九卷', '頁部', '顑顲也。从𩑋，𤎭聲。', '𩑋;𤎭', '𤎭', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5681, '煩', 'fán', '第九卷', '頁部', '熱頭痛也。从𩑋火。一曰焚省聲。', '𩑋;火;焚', '焚', 'null', NULL, '𩑋;火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5682, '𩕺;顡', 'wài', '第九卷', '頁部', '癡顡，不聰朙也。从𩑋，𧱏聲。', '𩑋;𧱏', '𧱏', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5683, '頪', 'lèi', '第九卷', '頁部', '難曉也。从𩑋米。一曰鮮白皃，从粉省。', '𩑋;米;粉', '', 'null', NULL, '𩑋;米;粉', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5684, '顦', 'qiáo', '第九卷', '頁部', '顦顇也。从頁，焦聲。', '頁;焦', '焦', 'null', NULL, '頁', 'null', 'null', 'null', 'null', '大徐补 | 应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5685, '顇', 'cuì', '第九卷', '頁部', '顦顇也。从𩑋，卒聲。', '𩑋;卒', '卒', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5686, '䫒', 'mén', '第九卷', '頁部', '繫頭殟也。从𩑋，昏聲。', '𩑋;昏', '昏', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5687, '頦', 'hái', '第九卷', '頁部', '醜也。从𩑋，亥聲。', '𩑋;亥', '亥', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5688, '䫏', 'qī', '第九卷', '頁部', '醜也。从𩑋，其聲。今逐疫有「䫏頭」。', '𩑋;其', '其', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5689, '𥸤;籲', 'yù', '第九卷', '頁部', '呼也。从𩑋，籥聲。讀與籥同。商書曰「率籲衆戚」。', '𩑋;籥', '籥', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5690, '顯', 'xiǎn', '第九卷', '頁部', '頭朙飾也。从𩑋，㬎聲。', '𩑋;㬎', '㬎', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5691, '𩔊', 'zhuàn', '第九卷', '頁部', '選具也。从二𩑋。', '𩑋', '', 'null', NULL, '𩑋', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5692, '預', 'yù', '第九卷', '頁部', '安也。从頁。[予聲。]', '頁;予', '予', 'null', NULL, '頁', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5693, '𦣻', 'shǒu', '第九卷', '𦣻部', '頭也。象形。凡𦣻之屬皆从𦣻。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5694, '脜', 'róu', '第九卷', '𦣻部', '面和也。从𦣻肉。讀若柔。', '𦣻;肉;柔', '肉;柔', 'null', NULL, '𦣻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5695, '面', 'miàn', '第九卷', '面部', '顔前也。从𦣻。象人面形。凡面之屬皆从面。', '𦣻', '', 'null', NULL, '𦣻', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5696, '靦', 'tiǎn', '第九卷', '面部', '面見人也。从面見。見亦聲。詩曰「有靦面目」。或从旦。', '面;見;旦', '見;旦', 'null', NULL, '面', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5697, '䩉', 'fǔ', '第九卷', '面部', '頰也。从面，甫聲。', '面;甫', '甫', 'null', NULL, '面', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5698, '䩌', 'jiāo', '第九卷', '面部', '面焦枯小也。从面焦。', '面;焦', '焦', 'null', NULL, '面', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5699, '靨', 'yè', '第九卷', '面部', '姿也。从面，厭聲。', '面;厭', '厭', 'null', NULL, '面', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5700, '丏', 'miǎn', '第九卷', '丏部', '不見也。象雝蔽之形。凡丏之屬皆从丏。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '经', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5701, '首;𩠐', 'shǒu', '第九卷', '首部', '古文𦣻也。川，象髮，髮謂之鬊，鬊卽川也。凡首之屬皆从首。', '𦣻;川', '𦣻', 'null', NULL, '川', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5702, '䭬;䭫;𩠜', 'qǐ', '第九卷', '首部', '䭬首也。从首，旨聲。', '首;旨', '旨', 'null', NULL, '首', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5703, '𩠹;剸', 'tuán', '第九卷', '首部', '𢧵首也。从斷首。或从刀，專聲。', '斷;首;刀;專', '斷;專', 'null', NULL, '首;刀', 'null', 'null', 'null', 'null', '小徐无', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5704, '𥄉', 'jiāo', '第九卷', '𥄉部', '到首也。賈侍中說，此「𣃔首到縣」𥄉字。凡𥄉之屬皆从𥄉。', '首', '', 'null', NULL, '首', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5705, '縣;懸', 'xuán', '第九卷', '𥄉部', '繫也。从系持𥄉。', '系;𥄉', '', 'null', NULL, '系;𥄉', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5706, '須', 'xū', '第九卷', '須部', '頤下毛也。从𩑋彡。凡須之屬皆从須。', '𩑋;彡', '', 'null', NULL, '𩑋;彡', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5707, '頿', 'zī', '第九卷', '須部', '口上須也。从須，此聲。', '須;此', '此', 'null', NULL, '須', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5708, '𩓾', 'rán', '第九卷', '須部', '頰須也。从須冉。冉亦聲。', '須;冉', '冉', 'null', NULL, '須', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5709, '𩔹', 'bēi', '第九卷', '須部', '須髮半白也。从須，卑聲。', '須;卑', '卑', 'null', NULL, '須', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5710, '䫠', 'pī', '第九卷', '須部', '短須髮皃。从須，否聲。', '須;否', '否', 'null', NULL, '須', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5711, '彡', 'shān', '第九卷', '彡部', '毛飾畫文也。象形。凡彡之屬皆从彡。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5712, '形', 'xínɡ', '第九卷', '彡部', '象也。从彡，开聲。', '彡;开', '开', 'null', NULL, '彡', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5713, '㐱;鬒', 'zhěn', '第九卷', '彡部', '稠髮也。从彡，人聲。詩曰「㐱髮如雲」。㐱或从髟，眞聲。', '彡;人;髟;眞', '人;眞', 'null', NULL, '彡;髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5714, '修', 'xiū', '第九卷', '彡部', '飾也。从彡，攸聲。', '彡;攸', '攸', 'null', NULL, '彡', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5715, '彰', 'zhānɡ', '第九卷', '彡部', '彣彰也。从彡章。章亦聲。', '彡;章', '章', 'null', NULL, '彡', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5716, '彫', 'diāo', '第九卷', '彡部', '𤥨文也。从彡，周聲。', '彡;周', '周', 'null', NULL, '彡', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5717, '𩇕', 'jìnɡ', '第九卷', '彡部', '淸飾也。从彡，靑聲。', '彡;靑', '靑', 'null', NULL, '彡', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5718, '㣎', 'mù', '第九卷', '彡部', '細文也。从彡，𡭴省。', '彡;𡭴', '', 'null', NULL, '彡;𡭴', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5719, '弱', 'ruò', '第九卷', '彡部', '橈也。上象橈曲，彡，象毛氂橈弱也。弱物并，故从二𢎥。', '彡;𢎥', '', 'null', NULL, '彡;𢎥', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5720, '彩', 'cǎi', '第九卷', '彡部', '文章也。从彡，采聲。', '彡;采', '采', 'null', NULL, '彡', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5721, '彣', 'wén', '第九卷', '彣部', '𢒰也。从彡文。凡彣之屬皆从彣。', '彡;文', '文', 'null', NULL, '彡', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5722, '彥', 'yàn', '第九卷', '彣部', '美士有彣，人所言也。从彣，厂聲。', '彣;厂', '厂', 'null', NULL, '彣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5723, '文', 'wén', '第九卷', '文部', '錯畫也。象交文。凡文之屬皆从文。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5724, '斐', 'fěi', '第九卷', '文部', '分別文也。从文，非聲。易曰「君子豹變，其文斐也」。', '文;非', '非', 'null', NULL, '文', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5725, '辬', 'bān', '第九卷', '文部', '駁文也。从文，辡聲。', '文;辡', '辡', 'null', NULL, '文', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5726, '𣁟', 'lí', '第九卷', '文部', '微畫文也。从文，𠩺聲。', '文;𠩺', '𠩺', 'null', NULL, '文', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5727, '髟', 'biāo', '第九卷', '髟部', '長髮猋猋也。从長彡。一曰白黑髮襍而髟。凡髟之屬皆从髟。', '長;彡', '', 'null', NULL, '長;彡', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5728, '髮', 'fà', '第九卷', '髟部', '頭上毛也。从髟，犮聲。髮或从首。古文[爻]。', '髟;犮;首;爻', '犮', 'null', NULL, '髟;首;爻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5729, '鬢', 'bìn', '第九卷', '髟部', '頰髮也。从髟，賓聲。', '髟;賓', '賓', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5730, '鬗', 'mán', '第九卷', '髟部', '髮長也。从髟，㒼聲。讀若蔓。', '髟;㒼;蔓', '㒼;蔓', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5731, '䰐', 'lán', '第九卷', '髟部', '髮長也。从髟，監聲。讀若春秋「黑肱㠯濫來奔」。', '髟;監;濫', '監;濫', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5732, '䰈', 'cuǒ', '第九卷', '髟部', '髮好也。从髟，差聲。', '髟;差', '差', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5733, '鬈', 'quán', '第九卷', '髟部', '髮好也。从髟，卷聲。詩曰「其人美且鬈」。', '髟;卷', '卷', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5734, '髦', 'máo', '第九卷', '髟部', '髦髮也。从髟毛。', '髟;毛', '毛', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5735, '䰓', 'mián', '第九卷', '髟部', '髮皃。从髟，𦤝聲。讀若宀。', '髟;𦤝;宀', '𦤝;宀', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5736, '䯾', 'tiáo', '第九卷', '髟部', '髮多也。从髟，周聲。', '髟;周', '周', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5737, '𩯨', 'nǐ', '第九卷', '髟部', '髮皃。从髟，爾聲。讀若江南謂酢母爲𩯨。', '髟;爾', '爾', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5738, '䯽', 'póu', '第九卷', '髟部', '髮皃。从髟，咅聲。', '髟;咅', '咅', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5739, '𩭾;髳', 'máo', '第九卷', '髟部', '髮至眉也。从髟，敄聲。詩曰「紞彼兩𩭾」。𩭾或省[矛]。漢令有「髳長」。', '髟;敄;矛', '敄;矛', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5740, '鬋', 'jiǎn', '第九卷', '髟部', '女鬢垂皃也。从髟，歬聲。', '髟;歬', '歬', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5741, '鬑', 'lián', '第九卷', '髟部', '鬋也。一曰長皃。从髟，兼聲。讀若慊。', '髟;兼;慊', '兼;慊', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5742, '𩯰', 'jié', '第九卷', '髟部', '束髮尐小也。从髟，𢧵聲。', '髟;𢧵', '𢧵', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5743, '鬄', 'xī', '第九卷', '髟部', '髲也。从髟，易聲。鬄或从也聲。', '髟;易;也', '易;也', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5744, '髲', 'bì', '第九卷', '髟部', '益髮也。从髟，皮聲。', '髟;皮', '皮', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5745, '䯸', 'cì', '第九卷', '髟部', '用梳比也。从髟，次聲。', '髟;次', '次', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5746, '髺', 'kuò', '第九卷', '髟部', '絜髮也。从髟，𠯑聲。', '髟;𠯑', '𠯑', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5747, '䰉', 'pán', '第九卷', '髟部', '臥結也。从髟，般聲。讀若槃。', '髟;般;槃', '般;槃', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5748, '𩬙', 'fù', '第九卷', '髟部', '結也。从髟，付聲。', '髟;付', '付', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5749, '鬕', 'mà', '第九卷', '髟部', '鬕帶，結頭飾也。从髟，莫聲。', '髟;莫', '莫', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5750, '䰎', 'kuì', '第九卷', '髟部', '屈髮也。从髟，貴聲。', '髟;貴', '貴', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5751, '䯰', 'jiè', '第九卷', '髟部', '簪結也。从髟，介聲。', '髟;介', '介', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5752, '鬣;鬛', 'liè', '第九卷', '髟部', '髮鬣鬣也。从髟，巤聲。或从毛。或从豕。', '髟;巤;毛;豕', '巤', 'null', NULL, '髟;毛;豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5753, '䰕', 'lú', '第九卷', '髟部', '鬣也。从髟，盧聲。', '髟;盧', '盧', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5754, '髴', 'fú', '第九卷', '髟部', '髴，若佀也。从髟，弗聲。', '髟;弗', '弗', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5755, '髶', 'rónɡ', '第九卷', '髟部', '亂髮也。从髟，耳聲。', '髟;耳', '耳', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5756, '鬌', 'chuí', '第九卷', '髟部', '髮墮也。从髟，隋省聲。', '髟;隋', '隋', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5757, '鬊', 'shùn', '第九卷', '髟部', '鬌髮也。从髟，春聲。', '髟;春', '春', '川', '首下曰：川象髮。髮謂之鬊，鬊卽川也。', '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5758, '鬜;鬝', 'qiān', '第九卷', '髟部', '鬢禿也。从髟，閒聲。', '髟;閒', '閒', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5759, '𩮜', 'tì', '第九卷', '髟部', '鬀髮也。从髟，从刀，易聲。', '刀;易', '易', 'null', NULL, '刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5760, '髡', 'kūn', '第九卷', '髟部', '𩮜髮也。从髟，兀聲。或从元。', '髟;兀;元', '兀;元', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5761, '鬀', 'tì', '第九卷', '髟部', '𩮜髮也。从髟，弟聲。大人曰髡。小兒曰鬀。盡及身毛曰𩮜。', '髟;弟', '弟', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5762, '𩮗', 'bànɡ', '第九卷', '髟部', '䰁也。从髟，竝聲。', '髟;竝', '竝', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5763, '䰁', 'fèi', '第九卷', '髟部', '𩮗也。忽見也。从髟，𢑛聲。𢑛，籒文魅，亦忽見意。', '髟;𢑛', '𢑛', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5764, '髽', 'zhuā', '第九卷', '髟部', '喪結也。禮「女子髽衰，弔則不髽。魯 臧武仲與齊戰於狐鮐，魯人迎䘮者，始髽」。从髟，𡋲聲。', '髟;𡋲', '𡋲', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5765, '鬐', 'qí', '第九卷', '髟部', '馬鬣也。从髟，耆聲。', '髟;耆', '耆', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5766, '髫', 'tiáo', '第九卷', '髟部', '小兒垂結也。从髟，召聲。', '髟;召', '召', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '新 | 𠧪≈髫≈絛≈條', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5767, '髻', 'jì', '第九卷', '髟部', '總髮也。从髟，吉聲。', '髟;吉', '吉', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5768, '鬟', 'huán', '第九卷', '髟部', '總髮也。从髟，瞏聲。', '髟;瞏', '瞏', 'null', NULL, '髟', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5769, '后', 'hòu', '第九卷', '后部', '繼體君也。象人之形。从口。易曰「后㠯施令告四方」。凡后之屬皆从后。', '口', '', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5770, '㖃', 'hǒu', '第九卷', '后部', '厚怒聲。从后口。后亦聲。', '后;口', '口', 'null', NULL, '后', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5771, '司', 'sī', '第九卷', '司部', '臣司事於外者。从反后。凡司之屬皆从司。', '后', '', 'null', NULL, '后', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5772, '䛐;詞', 'cí', '第九卷', '司部', '意內而言外也。从司言。', '司;言', '司', 'null', NULL, '言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5773, '卮;𠨗;巵', 'zhī', '第九卷', '卮部', '圜器也。一名觛。所㠯節㱃食。象人。卪在其下也。易曰「君子節㱃食」。凡卮之屬皆从卮。', '卪', '', 'null', NULL, '卪', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5774, '𡭐', 'shuàn', '第九卷', '卮部', '小卮有耳葢者。从卮，專聲。', '卮;專', '專', 'null', NULL, '卮', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5775, '𦓚;𦓙', 'zhuǎn', '第九卷', '卮部', '小卮也。从卮，耑聲。讀若「捶擊」之捶。', '卮;耑;捶', '耑;捶', 'null', NULL, '卮', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5776, '卪;卩', 'jié', '第九卷', '卪部', '瑞信也。守邦國者用「玉卪」，守都鄙者用「角卪」，使山邦者用「虎卪」，土邦者用「人卪」，澤邦者用「龍卪」。門關者用「符卪」，貨賄用「璽卪」，道路用「旌卪」。象相合之形。凡卪之屬皆从卪。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5777, '令', 'lìnɡ', '第九卷', '卪部', '發號也。从亼卪。', '亼;卪', '', 'null', NULL, '亼;卪', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5778, '𠨒', 'bì', '第九卷', '卪部', '輔信也。相輔之信也。从卪，比聲。虞書曰「𠨒成五服」。', '卪;比', '比', 'null', NULL, '卪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5779, '卶', 'chǐ', '第九卷', '卪部', '有大慶也。从卪，多聲。讀若侈。', '卪;多;侈', '多;侈', 'null', NULL, '卪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5780, '𠨘', 'bì', '第九卷', '卪部', '宰之也。从卪，必聲。', '卪;必', '必', 'null', NULL, '卪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5781, '卲', 'shào', '第九卷', '卪部', '高也。从卪，召聲。', '卪;召', '召', 'null', NULL, '卪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5782, '𠂘;𠂬', 'ě', '第九卷', '卪部', '科𠂘，木卪也。从卪，𠂆聲。賈侍中說㠯爲𠂘，裹也。一曰𠂘，葢也。', '卪;𠂆', '𠂆', 'null', NULL, '卪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5783, '厀', 'xī', '第九卷', '卪部', '脛頭卪也。从卪，桼聲。', '卪;桼', '桼', 'null', NULL, '卪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5784, '卷', 'juǎn', '第九卷', '卪部', '厀曲也。从卪，𢍏聲。', '卪;𢍏', '𢍏', 'null', NULL, '卪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5785, '卻', 'què', '第九卷', '卪部', '卪卻也。从卪，𧮫聲。', '卪;𧮫', '𧮫', 'null', NULL, '卪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5786, '卸', 'xiè', '第九卷', '卪部', '舍車解馬也。从卪止，午聲。讀若汝南人「寫書」之寫。一曰杵之也。', '卪;止;午;寫', '午;寫', 'null', NULL, '卪;止', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5787, '𠨖', 'zhuàn', '第九卷', '卪部', '二卪也。巽从此。闕。', '卪;巽', '巽', 'null', NULL, '卪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5788, '㔿', 'zòu', '第九卷', '卪部', '卪也。闕。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5789, '印', 'yìn', '第九卷', '印部', '執政所持信也。从爪卪。凡印之屬皆从印。', '爪;卪', '', 'null', NULL, '爪;卪', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5790, '𢑏;抑', 'yì', '第九卷', '印部', '按也。从反印。俗从手。', '印;手', '', 'null', NULL, '印;手', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5791, '色', 'sè', '第九卷', '色部', '顔气也。从人卪。凡色之屬皆从色。古文[从彡，从𦣻，疑省声]。', '人;卪;彡;𦣻;疑', '疑', 'null', NULL, '人;卪;彡;𦣻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5792, '艴', 'bó', '第九卷', '色部', '色艴如也。从色，弗聲。論語曰「色艴如也」。', '色;弗', '弗', 'null', NULL, '色', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5793, '艵', 'pīnɡ', '第九卷', '色部', '縹色也。从色，并聲。', '色;并', '并', 'null', NULL, '色', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5794, '𠨍', 'qīnɡ', '第九卷', '𠨍部', '事之制也。从卪㔿。凡𠨍之屬皆从𠨍。闕。', '卪;㔿', '', 'null', NULL, '卪;㔿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5795, '卿', 'qīnɡ', '第九卷', '𠨍部', '章也。「六卿」——天官冢宰、地官司徒、春官宗伯、夏官司馬、秋官司寇、冬官司空。从𠨍，皀聲。', '𠨍;皀', '皀', 'null', NULL, '𠨍', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5796, '辟', 'bì', '第九卷', '辟部', '法也。从卪。辛，節制其辠也。从口，用法者也。凡辟之屬皆从辟。', '卪;辛;口', '', 'null', NULL, '卪;辛;口', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5797, '𨐨', 'bì', '第九卷', '辟部', '法也。从辟井。周書曰「我之不𨐨」。', '辟;井', '辟', 'null', NULL, '井', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5798, '䢃', 'yì', '第九卷', '辟部', '治也。从辟，乂聲。虞書曰「有能俾䢃」。', '辟;乂', '乂', 'null', NULL, '辟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5799, '勹', 'bāo', '第九卷', '勹部', '裹也。象人曲形有所包裹。凡勹之屬皆从勹。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5800, '𠤄', 'jū', '第九卷', '勹部', '曲脊也。从勹，𥷚省聲。', '勹;𥷚', '𥷚', 'null', NULL, '勹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5801, '匍', 'pú', '第九卷', '勹部', '手行也。从勹，甫聲。', '勹;甫', '甫', 'null', NULL, '勹', 'null', 'null', 'null', 'null', '鱼部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5802, '匐', 'fú', '第九卷', '勹部', '伏地也。从勹，畐聲。', '勹;畐', '畐', 'null', NULL, '勹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5803, '匊', 'jū', '第九卷', '勹部', '在手曰匊。从勹米。', '勹;米', '', 'null', NULL, '勹;米', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5804, '匀;勻', 'yún', '第九卷', '勹部', '少也。从勹二。', '勹;二', '', 'null', NULL, '勹;二', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5805, '勼', 'jiū', '第九卷', '勹部', '聚也。从勹，九聲。讀若鳩。', '勹;九;鳩', '九;鳩', 'null', NULL, '勹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5806, '旬', 'xún', '第九卷', '勹部', '徧也。十日爲旬。从勹日。古文[从日匀]。', '勹;日;日;匀', '匀', 'null', NULL, '勹;日;日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5807, '勽', 'bào', '第九卷', '勹部', '覆也。从勹人。', '勹;人', '勹', 'null', NULL, '人', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5808, '匈;胸', 'xiōnɡ', '第九卷', '勹部', '膺也。从勹，凶聲。胸匈或从肉。', '勹;凶;肉', '凶', 'null', NULL, '勹;肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5809, '𠣘', 'zhōu', '第九卷', '勹部', '帀徧也。从勹，舟聲。', '勹;舟', '舟', 'null', NULL, '勹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5810, '匌', 'ɡé', '第九卷', '勹部', '帀也。从勹合。合亦聲。', '勹;合', '合', 'null', NULL, '勹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5811, '𠣿', 'jiù', '第九卷', '勹部', '飽也。从勹，𣪘聲。民祭，祝曰「厭𠣿」。', '勹;𣪘', '𣪘', '句', '𠣿、够古今字。', '勹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5812, '𠣾', 'fù', '第九卷', '勹部', '重也。从勹，復聲。或省彳[复]。', '勹;復;复', '復;复', 'null', NULL, '勹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5813, '冢', 'zhǒnɡ', '第九卷', '勹部', '高墳也。从勹。豖聲。', '勹;豖', '豖', 'null', NULL, '勹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5814, '包', 'bāo', '第九卷', '包部', '妊也。象人褢妊。[从勹巳。]巳在中，象子未成形也。元气起於子。子，人所生也。男左行三十，女右行二十，俱立於巳爲夫婦。褢妊於巳巳爲子。十月而生。男起巳至寅，女起巳至申，故男年始寅，女年始申也。凡包之屬皆从包。', '勹;巳', '勹', 'null', NULL, '巳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5815, '胞', 'bāo', '第九卷', '包部', '兒生裹也。从肉包。', '肉;包', '包', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5816, '匏', 'páo', '第九卷', '包部', '瓠也。从包，从瓠省。包，取其可包藏物也。', '包;瓠', '包', 'null', NULL, '瓠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5817, '茍', 'jì', '第九卷', '茍部', '自急敕也。从羊省，从勹口。勹口，猶愼言也。从羊，與義、善、美同意。凡茍之屬皆从茍。古文不省。', '羊;勹;口', '', 'null', NULL, '羊;勹;口', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5818, '敬', 'jìnɡ', '第九卷', '茍部', '肅也。从攴茍。', '攴;茍', '', 'null', NULL, '攴;茍', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5819, '鬼', 'ɡuǐ', '第九卷', '鬼部', '人所歸爲鬼。从儿，甶象鬼頭。从厶。鬼陰气賊害，故从厶。凡鬼之屬皆从鬼。古文从示。', '儿;甶;厶;示', '', 'null', NULL, '儿;甶;厶;示', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5820, '䰠', 'shén', '第九卷', '鬼部', '神也。从鬼，申聲。', '鬼;申', '申', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5821, '䰟', 'hún', '第九卷', '鬼部', '陽气也。从鬼，云聲。', '鬼;云', '云', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5822, '魄', 'pò', '第九卷', '鬼部', '陰神也。从鬼，白聲。', '鬼;白', '白', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5823, '䰡', 'chì', '第九卷', '鬼部', '厲鬼也。从鬼，失聲。', '鬼;失', '失', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5824, '魖', 'xū', '第九卷', '鬼部', '秏鬼也。从鬼，虚聲。', '鬼;虚', '虚', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5825, '魃', 'bá', '第九卷', '鬼部', '旱鬼也。从鬼，犮聲。周禮有「赤魃氏」，除牆屋之物也。詩曰「旱魃爲虐」。', '鬼;犮', '犮', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5826, '鬽;魅;𢑛;𢑘', 'mèi', '第九卷', '鬼部', '老物精也。从鬼彡。彡，鬼毛。或从未。籒文从彖首，从尾省聲。', '鬼;彡;未;彖;尾', '未;尾', 'null', NULL, '鬼;彡;彖', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5827, '鬾', 'jì', '第九卷', '鬼部', '鬼服也。一曰小兒鬼。从鬼，支聲。韓詩傳曰「鄭交甫逢二女鬾服」。', '鬼;支', '支', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5828, '䰧', 'hū', '第九卷', '鬼部', '鬼皃。从鬼，虎聲。', '鬼;虎', '虎', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5829, '𩴪;𩴆', 'qí', '第九卷', '鬼部', '鬼俗也。从鬼，幾聲。淮南傳曰「吳人鬼，越人𩴆」。', '鬼;幾', '幾', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5830, '䰰', 'rú', '第九卷', '鬼部', '鬼鬽聲䰰䰰不止也。从鬼，需聲。', '鬼;需', '需', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5831, '𩲏', 'huà', '第九卷', '鬼部', '鬼變也。从鬼，化聲。', '鬼;化', '化', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5832, '𩴴', 'nuó', '第九卷', '鬼部', '見鬼驚䛐。从鬼，難省聲。讀若詩「受福不儺」。', '鬼;難;儺', '難;儺', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5833, '𩴱', 'pín', '第九卷', '鬼部', '鬼皃。从鬼，賓聲。', '鬼;賓', '賓', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5834, '醜', 'chǒu', '第九卷', '鬼部', '可惡也。从鬼，酉聲。', '鬼;酉', '酉', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5835, '魋', 'tuí', '第九卷', '鬼部', '神獸也。从鬼，隹聲。', '鬼;隹', '隹', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '大徐补 | 段氏调入隹部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5836, '䰫', 'chāo', '第九卷', '鬼部', '捷健也。从鬼，堯聲。', '鬼;堯', '堯', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '应补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5837, '魑', 'chī', '第九卷', '鬼部', '鬼屬。从鬼，从离。离亦聲。', '鬼;离', '离', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5838, '魔', 'mó', '第九卷', '鬼部', '鬼也。从鬼，麻聲。', '鬼;麻', '麻', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5839, '魘', 'yǎn', '第九卷', '鬼部', '㝱驚也。从鬼，厭聲。', '鬼;厭', '厭', 'null', NULL, '鬼', 'null', 'null', 'null', 'null', '新附应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5840, '甶', 'fú', '第九卷', '甶部', '鬼頭也。象形。凡甶之屬皆从甶。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5841, '畏', 'wèi', '第九卷', '甶部', '惡也。从甶，虎省。鬼頭而虎爪，可畏也。古文省[爲省聲]。', '甶;虎;爲', '虎;爲', 'null', NULL, '甶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5842, '禺', 'yù', '第九卷', '甶部', '母猴屬。頭佀鬼。从甶，从禸。', '甶;禸', '', 'null', NULL, '甶;禸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5843, '厶', 'sī', '第九卷', '厶部', '姦也。韓非曰「倉頡作字，自營爲厶」。凡厶之屬皆从厶。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5844, '篡', 'cuàn', '第九卷', '厶部', '屰而奪取曰篡。从厶，算聲。', '厶;算', '算', 'null', NULL, '厶', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5845, '㕗;誘;羑', 'yòu', '第九卷', '厶部', '相訹呼也。从厶羑。或从言秀。或如此[盾]。古文。', '厶;羑;言;秀;盾', '羑;秀;盾', 'null', NULL, '厶;言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5846, '嵬', 'wéi', '第九卷', '嵬部', '山石崔嵬，高而不平也。从山，鬼聲。[凡嵬之屬皆从嵬。]', '山;鬼', '鬼', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5847, '巍;魏', 'wēi', '第九卷', '嵬部', '高也。从嵬，委聲。', '嵬;委', '委', 'null', NULL, '嵬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5848, '山', 'shān', '第九卷', '山部', '宣也。謂能宣散气，生萬物也。有石而高。象形。凡山之屬皆从山。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5849, '嶽;岳', 'yuè', '第九卷', '山部', '東岱。南靃。西崋。北恆。中，大室。王者之所㠯巡狩所至。从山，獄聲。古文象高形。', '山;獄', '獄', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5850, '岱', 'dài', '第九卷', '山部', '大山也。从山，代聲。', '山;代', '代', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5851, '㠀;島', 'dǎo', '第九卷', '山部', '海中往往有山可依止曰㠀。从山，鳥聲。讀若詩曰「蔦與女蘿」。', '山;鳥;蔦', '鳥;蔦', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5852, '峱', 'náo', '第九卷', '山部', '峱山也。在齊地。从山，狃聲。詩曰「遭我于峱之閒兮」。', '山;狃', '狃', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5853, '嶧', 'yì', '第九卷', '山部', '葛嶧山也。在東海 下邳。从山，睪聲。夏書曰「嶧陽孤桐」。', '山;睪', '睪', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5854, '嵎', 'yú', '第九卷', '山部', '封、嵎之山也。在吳 越之閒，汪芒之國。从山，禺聲。', '山;禺', '禺', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5855, '嶷', 'yí', '第九卷', '山部', '九嶷山也。舜所葬。在零陵 營道。从山，疑聲。', '山;疑', '疑', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5856, '𡼐;崏', 'mín', '第九卷', '山部', '𡼐山也。在蜀 湔氐西徼外。从山，𢾞聲。', '山;𢾞', '𢾞', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5857, '㞦', 'jǐ', '第九卷', '山部', '㞦山也。或曰溺水之所出。从山，几聲。', '山;几', '几', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5858, '巀', 'jié', '第九卷', '山部', '巀𡾦山也。在左馮翊 池陽。从山，𢧵聲。', '山;𢧵', '𢧵', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5859, '𡾦', 'niè', '第九卷', '山部', '巀𡾦山也。从山，辥聲。', '山;辥', '辥', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5860, '嵏', 'zōnɡ', '第九卷', '山部', '九嵏山也。在左馮翊 谷口。从山，㚇聲。', '山;㚇', '㚇', 'null', NULL, '山', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5861, '崋', 'huà', '第九卷', '山部', '崋山也。在弘農 華陰。从山，𠌶聲。', '山;𠌶', '𠌶', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5862, '崞;𡾘', 'ɡuō', '第九卷', '山部', '𡾘山也。在鴈門。从山，𩫖聲。', '山;𩫖', '𩫖', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5863, '崵', 'yánɡ', '第九卷', '山部', '首崵山也。在遼西。从山，昜聲。一曰嵎銕，崵谷也。', '山;昜', '昜', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5864, '岵', 'hù', '第九卷', '山部', '山有艸木也。从山，古聲。詩曰「陟彼岵兮」。', '山;古', '古', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5865, '屺', 'qǐ', '第九卷', '山部', '山無艸木也。从山，己聲。詩曰「陟彼屺兮」。', '山;己', '己', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5866, '嶨', 'xué', '第九卷', '山部', '山多大石也。从山，學省聲。', '山;學', '學', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5867, '嶅', 'áo', '第九卷', '山部', '山多小石也。从山，敖聲。', '山;敖', '敖', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5868, '岨', 'qū', '第九卷', '山部', '石戴土也。从山，且聲。詩曰「陟彼岨矣」。', '山;且', '且', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5869, '岡', 'ɡānɡ', '第九卷', '山部', '山脊也。从山，网聲。', '山;网', '网', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5870, '岑', 'cén', '第九卷', '山部', '山小而高。从山，今聲。', '山;今', '今', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5871, '崟', 'yín', '第九卷', '山部', '山之岑崟也。从山，金聲。', '山;金', '金', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5872, '崒;崪', 'zú', '第九卷', '山部', '崒，危高也。从山，卒聲。', '山;卒', '卒', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5873, '巒', 'luán', '第九卷', '山部', '山小而銳。从山，䜌聲。', '山;䜌', '䜌', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5874, '密', 'mì', '第九卷', '山部', '山如堂者。从山，宓聲。', '山;宓', '宓', 'null', NULL, '山', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5875, '岫', 'xiù', '第九卷', '山部', '山有穴也。从山，由聲。籒文从穴。', '山;由;穴', '由', 'null', NULL, '山;穴', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
