INSERT INTO `shuowen_voice_revel` VALUES (5944, '㡲', 'huán', '第九卷', '广部', '屋牡瓦也。一曰維綱也。从广，𩰎省聲。讀若環。', '广;𩰎;環', '𩰎;環', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5945, '𢊕', 'cōnɡ', '第九卷', '广部', '屋階中會也。从广，悤聲。', '广;悤', '悤', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5946, '㢋', 'chǐ', '第九卷', '广部', '廣也。从广，侈聲。春秋國語曰「俠溝而㢋我」。', '广;侈', '侈', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5947, '廉', 'lián', '第九卷', '广部', '仄也。从广，兼聲。', '广;兼', '兼', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5948, '㢉', 'chá', '第九卷', '广部', '開張屋也。从广，秅聲。', '广;秅', '秅', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5949, '龐', 'pánɡ', '第九卷', '广部', '高屋也。从广，龍聲。', '广;龍', '龍', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5950, '底', 'dǐ', '第九卷', '广部', '山凥也。一曰下也。从广，氐聲。', '广;氐', '氐', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5951, '庢', 'zhì', '第九卷', '广部', '礙止也。从广，至聲。', '广;至', '至', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5952, '廮', 'yǐnɡ', '第九卷', '广部', '安止也。从广，嬰聲。鉅鹿有廮陶縣。', '广;嬰', '嬰', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5953, '𢇷', 'bá', '第九卷', '广部', '舍也。从广，犮聲。詩曰「召伯所𢇷」。', '广;犮', '犮', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5954, '庳', 'bì', '第九卷', '广部', '中伏舍。从广，卑聲。一曰屋卑。或讀若逋。', '广;卑;逋', '卑;逋', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5955, '庇', 'bì', '第九卷', '广部', '蔭也。从广，比聲。', '广;比', '比', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5956, '庶', 'shù', '第九卷', '广部', '屋下眾也。从广炗。炗，古文光字。', '广;炗', '', 'null', NULL, '广;炗', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5957, '庤', 'zhì', '第九卷', '广部', '儲置屋下也。从广，寺聲。', '广;寺', '寺', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5958, '廙', 'yì', '第九卷', '广部', '行屋下聲也。从广，異聲。', '广;異', '異', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5959, '廔', 'lóu', '第九卷', '广部', '屋麗廔也。从广，婁聲。一曰所㠯穜也。', '广;婁', '婁', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5960, '㢈', 'tuí', '第九卷', '广部', '屋从上傾下也。从广，隹聲。', '广;隹', '隹', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5961, '廢', 'fèi', '第九卷', '广部', '屋頓也。从广，發聲。', '广;發', '發', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5962, '庮', 'yǒu', '第九卷', '广部', '久屋朽木。从广，酉聲。周禮曰「牛夜鳴則庮。臭如朽木」。', '广;酉', '酉', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5963, '廑', 'jǐn', '第九卷', '广部', '少劣之凥。从广，堇聲。', '广;堇', '堇', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5964, '廟', 'miào', '第九卷', '广部', '尊先祖皃也。从广，朝聲。古文[苗]。', '广;朝;苗', '朝;苗', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5965, '㡹', 'jū', '第九卷', '广部', '人相依㡹也。从广，且聲。', '广;且', '且', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5966, '𢉥', 'yè', '第九卷', '广部', '屋迫也。从广，曷聲。', '广;曷', '曷', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5967, '㡿;斥', 'chì', '第九卷', '广部', '卻屋也。从广，屰聲。', '广;屰', '屰', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5968, '廞', 'xīn', '第九卷', '广部', '陳輿服於庭也。从广，欽聲。讀若歆。', '广;欽;歆', '欽;歆', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5969, '廫', 'liáo', '第九卷', '广部', '空虛也。从广，膠聲。', '广;膠', '膠', 'null', NULL, '广', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5970, '廈', 'xià', '第九卷', '广部', '屋也。从广，夏聲。', '广;夏', '夏', 'null', NULL, '广', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5971, '廊', 'lánɡ', '第九卷', '广部', '東西序也。从广，郎聲。', '广;郎', '郎', 'null', NULL, '广', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5972, '廂', 'xiānɡ', '第九卷', '广部', '廊也。从广，相聲。', '广;相', '相', 'null', NULL, '广', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5973, '庪', 'ɡuǐ', '第九卷', '广部', '祭山曰「庪縣」。从广，技聲。', '广;技', '技', 'null', NULL, '广', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5974, '庱', 'chěnɡ', '第九卷', '广部', '地名。从广[，从夌]。', '广;夌', '夌', 'null', NULL, '广', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5975, '廖', 'liào', '第九卷', '广部', '人姓。从广[，从翏]。', '广;翏', '翏', 'null', NULL, '广', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5976, '厂;厈', 'hǎn', '第九卷', '厂部', '山石之厓巗。人可凥。象形。凡厂之屬皆从厂。籒文从干。', '干', '干', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5977, '厓', 'yá', '第九卷', '厂部', '山邊也。从厂，圭聲。', '厂;圭', '圭', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5978, '厜', 'zuī', '第九卷', '厂部', '厜㕒，山顚也。从厂，垂聲。', '厂;垂', '垂', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5979, '㕒', 'wēi', '第九卷', '厂部', '厜㕒也。从厂，義聲。', '厂;義', '義', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5980, '𠪚', 'yín', '第九卷', '厂部', '崟也。一曰地名。从厂，敢聲。', '厂;敢', '敢', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5981, '厬', 'ɡuǐ', '第九卷', '厂部', '仄出泉也。从厂，晷聲。讀若軌。', '厂;晷;軌', '晷;軌', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5982, '厎;砥', 'dǐ', '第九卷', '厂部', '柔石也。从厂，氐聲。厎或从石。', '厂;氐;石', '氐', 'null', NULL, '厂;石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5983, '厲;𠪄', 'lì', '第九卷', '厂部', '摩石也。从厂，𧍣省聲。厲或不省。', '厂;𧍣', '𧍣', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5984, '厥;氒', 'jué', '第九卷', '厂部', '發石也。从厂，欮聲。', '厂;欮', '欮', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5985, '厱', 'lán', '第九卷', '厂部', '厱諸，治玉石也。从厂，僉聲。讀若籃。', '厂;僉;籃', '僉;籃', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5986, '厤', 'lì', '第九卷', '厂部', '治也。从厂，秝聲。', '厂;秝', '秝', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5987, '𠪙', 'xǐ', '第九卷', '厂部', '石利也。从厂，異聲。讀若枲。', '厂;異;枲', '異;枲', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5988, '㕆', 'hù', '第九卷', '厂部', '美石也。从厂，古聲。', '厂;古', '古', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5989, '厗', 'tí', '第九卷', '厂部', '唐厗石也。从厂，屖省聲。', '厂;屖', '屖', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5990, '㕇', 'lā', '第九卷', '厂部', '石聲也。从厂，立聲。', '厂;立', '立', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5991, '𠩫', 'yì', '第九卷', '厂部', '石地惡也。从厂，兒聲。', '厂;兒', '兒', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5992, '㕋', 'qín', '第九卷', '厂部', '石地也。从厂，金聲。讀若紟。', '厂;金;紟', '金;紟', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5993, '㕊', 'fū', '第九卷', '厂部', '石閒見也。从厂，甫聲。讀若𢾭。', '厂;甫;𢾭', '甫;𢾭', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5994, '厝', 'cuò', '第九卷', '厂部', '厝石也。从厂，昔聲。詩曰「佗山之石。可㠯爲厝」。', '厂;昔', '昔', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5995, '厖', 'mánɡ', '第九卷', '厂部', '石大也。从厂，尨聲。', '厂;尨', '尨', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5996, '𠨲', 'yuè', '第九卷', '厂部', '岸上見也。从厂，从之省。讀若躍。', '厂;之;躍', '躍', 'null', NULL, '厂;之', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5997, '𠩘', 'xiá', '第九卷', '厂部', '𠪮也。从厂，夾聲。', '厂;夾', '夾', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5998, '仄', 'zè', '第九卷', '厂部', '側傾也。从人在厂下。籒文从夨。夨亦聲。', '人;厂;夨', '夨', 'null', NULL, '人;厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5999, '𠪮', 'pì', '第九卷', '厂部', '仄也。从厂，辟聲。', '厂;辟', '辟', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6000, '厞', 'fèi', '第九卷', '厂部', '隱也。从厂，非聲。', '厂;非', '非', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6001, '厭', 'yā', '第九卷', '厂部', '笮也。从厂，猒聲。一曰合也。', '厂;猒', '猒', 'null', NULL, '厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6002, '厃', 'wěi', '第九卷', '厂部', '仰也。从人在厂上。一曰屋梠也。秦謂之楣。齊謂之厃。', '人;厂', '', 'null', NULL, '人;厂', 'null', 'null', 'null', 'null', '', 0x30, 'null', '应当读若檐，大徐本楣下曰：齊谓之檐。', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6003, '丸', 'wán', '第九卷', '丸部', '圜也。傾側而轉者。从反仄。凡丸之屬皆从丸。', '仄', '', 'null', NULL, '仄', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6004, '𠷏', 'wěi', '第九卷', '丸部', '鷙鳥食巳，吐其皮毛如丸。从丸，咼聲。讀若骫。', '丸;咼;骫', '咼;骫', 'null', NULL, '丸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6005, '䎠', 'nuó', '第九卷', '丸部', '丸之孰也。从丸，而聲。', '丸;而', '而', 'null', NULL, '丸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6006, '奿', 'fàn', '第九卷', '丸部', '闕。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6007, '危', 'wēi', '第九卷', '危部', '在高而懼也。从厃。人在厓上。自卪止之。凡危之屬皆从危。', '厃;卪', '', 'null', NULL, '厃;卪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6008, '㩻', 'qī', '第九卷', '危部', '㩻，䧢也。从危，支聲。', '危;支', '支', 'null', NULL, '危', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6009, '石', 'shí', '第九卷', '石部', '山石也。在厂之下。囗象形。凡石之屬皆从石。', '厂;囗', '', 'null', NULL, '厂;囗', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6010, '磺;礦', 'kuànɡ', '第九卷', '石部', '銅鐵樸石也。从石，黃聲。讀若穬。古文礦。周禮有「卝人」。', '石;黃;穬', '黃;穬', 'null', NULL, '石', 'null', 'null', 'null', 'null', '重文应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6011, '碭', 'dànɡ', '第九卷', '石部', '文石也。从石，昜聲。', '石;昜', '昜', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6012, '碝', 'ruǎn', '第九卷', '石部', '石次玉者。从石，耎聲。', '石;耎', '耎', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6013, '砮', 'nú', '第九卷', '石部', '砮石。可㠯爲矢鏃。从石，奴聲。夏書曰「梁州貢砮、丹」。國語曰「肅愼氏貢楛矢、石砮」。', '石;奴', '奴', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6014, '礜', 'yù', '第九卷', '石部', '毒石也。出漢中。从石，與聲。', '石;與', '與', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6015, '碣', 'jié', '第九卷', '石部', '特立之石也。東海有碣石山。从石，曷聲。古文[𨸏，余未详]。', '石;曷;𨸏', '曷', 'null', NULL, '石;𨸏', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6016, '磏', 'lián', '第九卷', '石部', '厲石也。赤色。从石，兼聲。讀若鎌。', '石;兼;鎌', '兼;鎌', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6017, '碫', 'xiá', '第九卷', '石部', '碫石也。从石段。段亦聲。春秋傳 鄭 公孫段字子石。', '石;段', '段', 'null', NULL, '石', 'null', 'null', 'null', 'null', '篆头应改', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6018, '礫', 'lì', '第九卷', '石部', '小石也。从石，樂聲。', '石;樂', '樂', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6019, '䂬', 'ɡǒnɡ', '第九卷', '石部', '水邊石也。从石，巩聲。', '石;巩', '巩', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6020, '磧', 'qì', '第九卷', '石部', '水陼有石者。从石，責聲。', '石;責', '責', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6021, '碑', 'bēi', '第九卷', '石部', '豎石也。从石，卑聲。', '石;卑', '卑', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6022, '䃍', 'zhuì', '第九卷', '石部', '陊也。从石，㒸聲。', '石;㒸', '㒸', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6023, '磒', 'yǔn', '第九卷', '石部', '落也。从石，員聲。春秋傳曰「磒石于宋五」。', '石;員', '員', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6024, '䂹', 'suǒ', '第九卷', '石部', '碎石磒聲。从石，炙聲。', '石;炙', '炙', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6025, '硞', 'què', '第九卷', '石部', '石聲。从石，告聲。', '石;告', '告', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6026, '硍;硠', 'lánɡ', '第九卷', '石部', '石聲。从石，艮聲。', '石;艮', '艮', 'null', NULL, '石', 'null', 'null', 'null', 'null', '改篆', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6027, '礐', 'què', '第九卷', '石部', '石聲也。从石，學省聲。', '石;學', '學', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6028, '磕', 'kài', '第九卷', '石部', '石聲也。从石，盇聲。', '石;盇', '盇', 'null', NULL, '石', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6029, '硈', 'qià', '第九卷', '石部', '石堅也。从石，吉聲。一曰突也。', '石;吉', '吉', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6030, '硻', 'kēnɡ', '第九卷', '石部', '餘堅也。从石，堅省聲。', '石;堅', '堅', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6031, '磿', 'lì', '第九卷', '石部', '石聲也。从石，厤聲。', '石;厤', '厤', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6032, '磛', 'chán', '第九卷', '石部', '礹石也。从石，斬聲。', '石;斬', '斬', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6033, '礹', 'yán', '第九卷', '石部', '石山也。从石，嚴聲。', '石;嚴', '嚴', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6034, '礊', 'kè', '第九卷', '石部', '堅也。从石，毄聲。', '石;毄', '毄', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6035, '确', 'xué', '第九卷', '石部', '礊也。从石，角聲。确或从㱿。', '石;角;㱿', '角;㱿', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6036, '磽', 'qiāo', '第九卷', '石部', '礊也。从石，堯聲。', '石;堯', '堯', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6037, '硪', 'é', '第九卷', '石部', '石巖也。从石，我聲。', '石;我', '我', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6038, '碞', 'yán', '第九卷', '石部', '磛碞也。从石品。周書曰「畏于民碞」。讀與巖同。', '石;品;巖', '巖', 'null', NULL, '石;品', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6039, '磬;殸', 'qìnɡ', '第九卷', '石部', '石樂也。从石。，象縣虡之形。殳，所㠯擊之也。古者毋句氏作磬。籒文省。古文从巠。', '石;殳;巠', '巠', 'null', NULL, '石;殳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6040, '礙', 'ài', '第九卷', '石部', '止也。从石，疑聲。', '石;疑', '疑', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6041, '𥓊', 'chè', '第九卷', '石部', '上擿山巖空靑、珊瑚陊之。从石，析聲。周禮有「𥓊蔟氏」。', '石;析', '析', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6042, '𥒔', 'chàn', '第九卷', '石部', '㠯石衦繒也。从石，㢟聲。', '石;㢟', '㢟', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6043, '碎', 'suì', '第九卷', '石部', '䊳也。从石，卒聲。', '石;卒', '卒', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6044, '破', 'pò', '第九卷', '石部', '石碎也。从石，皮聲。', '石;皮', '皮', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6045, '礱;礲', 'lónɡ', '第九卷', '石部', '䃺也。从石，龍聲。天子之桷，椓而礱之。', '石;龍', '龍', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6046, '研', 'yán', '第九卷', '石部', '䃺也。从石，开聲。', '石;开', '开', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6047, '䃺', 'mò', '第九卷', '石部', '石磑也。从石，靡聲。', '石;靡', '靡', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6048, '磑', 'wèi', '第九卷', '石部', '䃺也。从石，豈聲。古者公輸班作磑。', '石;豈', '豈', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6049, '碓', 'duì', '第九卷', '石部', '所㠯舂也。从石，隹聲。', '石;隹', '隹', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6050, '䂿', 'tà', '第九卷', '石部', '舂巳複𢶈之曰䂿。从石，沓聲。', '石;沓', '沓', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6051, '磻', 'bō', '第九卷', '石部', '㠯石箸隿繳也。从石，番聲。', '石;番', '番', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6052, '𥗁', 'zhuó', '第九卷', '石部', '斫也。从石，箸聲。', '石;箸', '箸', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6053, '硯', 'yàn', '第九卷', '石部', '石滑也。从石，見聲。', '石;見', '見', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6054, '砭', 'biān', '第九卷', '石部', '㠯石刺病也。从石，乏聲。', '石;乏', '乏', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6055, '䃒', 'hé', '第九卷', '石部', '石地惡也。从石，鬲聲。', '石;鬲', '鬲', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6056, '砢', 'luǒ', '第九卷', '石部', '磊砢也。从石，可聲。', '石;可', '可', 'null', NULL, '石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6057, '磊', 'lěi', '第九卷', '石部', '眾石皃。从三石。', '石', '', 'null', NULL, '石', 'null', 'null', 'null', 'null', '微部', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6058, '礪', 'lì', '第九卷', '石部', '䃺也。从石，厲聲。', '石;厲', '厲', 'null', NULL, '石', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6059, '碏', 'què', '第九卷', '石部', '左氏傳衛大夫「石碏」。从石，昔聲。', '石;昔', '昔', 'null', NULL, '石', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6060, '磯', 'jī', '第九卷', '石部', '大石激水也。从石，幾聲。', '石;幾', '幾', 'null', NULL, '石', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6061, '碌', 'lù', '第九卷', '石部', '石皃。从石，录聲。', '石;录', '录', 'null', NULL, '石', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6062, '砧', 'zhēn', '第九卷', '石部', '石柎也。从石，占聲。', '石;占', '占', 'null', NULL, '石', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6063, '砌', 'qì', '第九卷', '石部', '階甃也。从石，切聲。', '石;切', '切', 'null', NULL, '石', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6064, '礩', 'zhì', '第九卷', '石部', '柱下石也。从石，質聲。', '石;質', '質', 'null', NULL, '石', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6065, '礎', 'chǔ', '第九卷', '石部', '礩也。从石，楚聲。', '石;楚', '楚', 'null', NULL, '石', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6066, '硾', 'zhuì', '第九卷', '石部', '𢶈也。从石，垂聲。', '石;垂', '垂', 'null', NULL, '石', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6067, '長', 'chánɡ', '第九卷', '長部', '久遠也。从兀，从𠤎，亡聲。兀者，高遠意也。久則變𠤎。者，到亡也。凡長之屬皆从長。古文長[未详]。亦古文長[未详]。', '兀;𠤎;亡', '亡', 'null', NULL, '兀;𠤎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6068, '𨽸;肆', 'sì', '第九卷', '長部', '極陳也。从長，隶聲。或从髟。', '長;隶;髟', '隶', 'null', NULL, '長;髟', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6069, '镾', 'mí', '第九卷', '長部', '久長也。从長，爾聲。', '長;爾', '爾', 'null', NULL, '長', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6070, '镻', 'dié', '第九卷', '長部', '蝁也。蛇毒長。从長，失聲。', '長;失', '失', 'null', NULL, '長', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6071, '勿', 'wù', '第九卷', '勿部', '州里所建旗。象其柄。有三游。襍帛。幅半異。所㠯趣民。故遽偁「勿勿」。凡勿之屬皆从勿。勿或从㫃。', '㫃', '', 'null', NULL, '㫃', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6072, '昜', 'yánɡ', '第九卷', '勿部', '開也。从日，一勿。', '日;一;勿', '', 'null', NULL, '日;一;勿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6073, '冉', 'rǎn', '第九卷', '冉部', '毛冉冉也。象形。凡冉之屬皆从冉。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6074, '而', 'ér', '第九卷', '而部', '須也。象形。周禮曰「作其鱗之而」。凡而之屬皆从而。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6075, '耏;耐', 'nài', '第九卷', '而部', '罪不至髡也。从彡而。而亦聲。或从寸，諸法度字从寸。', '彡;而;寸', '而', 'null', NULL, '彡;寸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6076, '豕', 'shǐ', '第九卷', '豕部', '彘也。竭其尾，故謂之豕。象毛足而後有尾。讀與豨同。按，今世字誤㠯豖爲豕、㠯𧰲爲彖，何㠯朙之？爲啄琢从豖、蠡从𧰲皆取其聲，㠯是朙之。凡豕之屬皆从豕。古文[亥]。', '豨;亥', '豨', 'null', NULL, '亥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6077, '豬', 'zhū', '第九卷', '豕部', '豕而三毛叢凥者。从豕，者聲。', '豕;者', '者', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6078, '豰', 'bó', '第九卷', '豕部', '小豚也。从豕，㱿聲。', '豕;㱿', '㱿', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6079, '豯', 'xī', '第九卷', '豕部', '生三月豚。腹奚奚皃也。从豕，奚聲。', '豕;奚', '奚', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6080, '豵', 'zōnɡ', '第九卷', '豕部', '生六月豚。从豕，從聲。一曰一歲曰豵。尙叢聚也。', '豕;從', '從', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6081, '豝', 'bā', '第九卷', '豕部', '牝豕也。从豕，巴聲。一曰二歲豕。能相杷拏者也。詩曰「一發五豝」。', '豕;巴', '巴', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6082, '豜', 'jiān', '第九卷', '豕部', '三歲豕。肩相及者也。从豕，开聲。詩曰「竝驅從兩豜[肩]兮」。', '豕;开', '开;肩', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6083, '豶', 'fén', '第九卷', '豕部', '羠豕也。从豕，賁聲。', '豕;賁', '賁', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6084, '豭', 'jiā', '第九卷', '豕部', '牡豕也。从豕，叚聲。', '豕;叚', '叚', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6085, '豛', 'yì', '第九卷', '豕部', '上谷名豬豛。从豕，役省聲。', '豕;役', '役', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6086, '䝐', 'wéi', '第九卷', '豕部', '豶也。从豕，隋聲。', '豕;隋', '隋', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6087, '豤', 'kěn', '第九卷', '豕部', '豕齧也。从豕，艮聲。', '豕;艮', '艮', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6088, '豷', 'yì', '第九卷', '豕部', '豕息也。从豕，壹聲。春秋傳曰「生敖及豷」。', '豕;壹', '壹', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6089, '豧', 'fū', '第九卷', '豕部', '豕息也。从豕，甫聲。', '豕;甫', '甫', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6090, '豢', 'huàn', '第九卷', '豕部', '㠯穀圈養豕也。从豕，𢍏聲。', '豕;𢍏', '𢍏', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6091, '豠', 'chú', '第九卷', '豕部', '豕屬。从豕，且聲。', '豕;且', '且', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6092, '豲', 'huán', '第九卷', '豕部', '豕屬也。从豕，原聲。讀若桓。逸周書曰「豲有爪而不敢㠯撅」。', '豕;原;桓', '原;桓', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6093, '豨', 'xī', '第九卷', '豕部', '豕走豨豨也。从豕，希聲。古有封豨脩蛇之害。', '豕;希', '希', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6094, '豖', 'chù', '第九卷', '豕部', '豕絆足行豖豖也。从豕繫二足。', '豕', '', 'null', NULL, '豕', 'null', 'null', 'null', 'null', '🐘 | 政宇应删', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6095, '豦', 'qú', '第九卷', '豕部', '鬭相丮不解也。从豕虍。豕虎之鬭不相捨。讀若「蘮蒘艸」之蘮[罽]。司馬相如說，豦，封豕之屬。一曰虎㒳足舉。', '豕;虍;罽', '罽', 'null', NULL, '豕;虍', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6096, '豙;𧱏', 'yì', '第九卷', '豕部', '豕怒毛豎也。一曰殘艾也。从豕，辛省。', '豕;辛', '', 'null', NULL, '豕;辛', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6097, '豩', 'huān', '第九卷', '豕部', '二豕也。豳从此。闕。', '豳', '豳', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6098, '㣇', 'yì', '第九卷', '㣇部', '脩豪獸。一曰河內名豕也。从彑。下象毛足。凡㣇之屬皆从㣇。讀若弟。籒文[未详]。古文[未详]。', '彑;弟', '弟', 'null', NULL, '彑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6099, '𢑢', 'hū', '第九卷', '㣇部', '豕屬。从㣇，曶聲。', '㣇;曶', '曶', 'null', NULL, '㣇', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6100, '𩫚;豪', 'háo', '第九卷', '㣇部', '𩫚豕，鬣如筆管者。出南郡。从㣇，高聲。篆文从豕。', '㣇;高;豕', '高', 'null', NULL, '㣇;豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6101, '彙;蝟', 'wèi', '第九卷', '㣇部', '彙蟲也。似豪豬而小。从㣇，胃省聲。或从虫作。', '㣇;胃;虫', '胃', 'null', NULL, '㣇;虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6102, '㣈', 'sì', '第九卷', '㣇部', '㣇屬。从二㣇。古文㣈。虞書曰「㣈類于上帝」。', '㣇', '', 'null', NULL, '㣇', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6103, '彑', 'jì', '第九卷', '彑部', '豕之頭。象其銳而上見也。凡彑之屬皆从彑。讀若罽。', '罽', '罽', 'null', NULL, '', 'null', 'null', 'null', 'null', '️', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6104, '彘', 'zhì', '第九卷', '彑部', '豕也。後蹏廢謂之彘。从彑，从二匕，矢聲。彘足與鹿足同。', '彑;匕;矢', '矢', 'null', NULL, '彑;匕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6105, '𧰲', 'chǐ', '第九卷', '彑部', '豕也。从彑，从豕。讀若弛。', '彑;豕;弛', '弛', 'null', NULL, '彑;豕', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6106, '𢑓', 'xiá', '第九卷', '彑部', '豕也。从彑，下象其足。讀若瑕。', '彑;瑕', '瑕', 'null', NULL, '彑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6107, '彖', 'tuàn', '第九卷', '彑部', '豕走也。从彑，从豕省。', '彑;豕', '', 'null', NULL, '彑;豕', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6108, '𢄉;豚', 'tún', '第九卷', '豚部', '小豕也。从古文豕。从又持肉。㠯給祠祀也。凡𢄉之屬皆从𢄉。篆文从肉豕。', '豕;又;肉;肉;豕', '', 'null', NULL, '豕;又;肉;肉;豕', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6109, '𧲝', 'wèi', '第九卷', '豚部', '豚屬。从𢄉，衛聲。讀若罽。', '𢄉;衛;罽', '衛;罽', 'null', NULL, '𢄉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6110, '豸', 'zhì', '第九卷', '豸部', '獸長脊行豸豸然。欲有所司殺形。凡豸之屬皆从豸。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6111, '豹', 'bào', '第九卷', '豸部', '佀虎，圜文。从豸，勺聲。', '豸;勺', '勺', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '🤰跑', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6112, '貙', 'chū', '第九卷', '豸部', '貙獌，似貍。从豸，區聲。', '豸;區', '區', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6113, '貚', 'tán', '第九卷', '豸部', '貙屬也。从豸，單聲。', '豸;單', '單', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6114, '貔', 'pí', '第九卷', '豸部', '豹屬。出貉國。从豸，𣬉聲。詩曰「獻其貔皮」。周書曰「如虎如貔」。貔，猛獸。或从比。', '豸;𣬉;比', '𣬉;比', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6115, '豺', 'chái', '第九卷', '豸部', '狼屬，狗聲。从豸，才聲。', '豸;才', '才', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6116, '貐;㺄', 'yǔ', '第九卷', '豸部', '䝟貐。从貙虎爪。食人迅走。从豸，俞聲。', '豸;俞', '俞', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6117, '貘', 'mò', '第九卷', '豸部', '佀熊而黃黑色。出蜀中。从豸，莫聲。', '豸;莫', '莫', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6118, '𧴄', 'yōnɡ', '第九卷', '豸部', '猛獸也。从豸，庸聲。', '豸;庸', '庸', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6119, '貜', 'jué', '第九卷', '豸部', '㺉貜也。从豸，矍聲。', '豸;矍', '矍', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6120, '貀', 'nà', '第九卷', '豸部', '貀獸。無前足。从豸，出聲。漢律「能捕豺貀，購錢百」。', '豸;出', '出', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6121, '貈', 'hé', '第九卷', '豸部', '似狐。善睡獸也。从豸，舟聲。論語曰「狐貈之厚㠯居」。', '豸;舟', '舟', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6122, '貆', 'huán', '第九卷', '豸部', '貈之類。从豸，亘聲。', '豸;亘', '亘', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6123, '豻;犴', 'àn', '第九卷', '豸部', '胡地野狗。从豸，干聲。豻或从犬。詩曰「冝犴冝獄」。', '豸;干;犬', '干', 'null', NULL, '豸;犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6124, '貍', 'lí', '第九卷', '豸部', '伏獸。似貙。从豸，里聲。', '豸;里', '里', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6125, '貒', 'tuān', '第九卷', '豸部', '貒獸也。似豕而肥。从豸，耑聲。讀若湍。', '豸;耑;湍', '耑;湍', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6126, '貛', 'huān', '第九卷', '豸部', '野豕也。从豸，雚聲。', '豸;雚', '雚', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6127, '貁;𮙩', 'yòu', '第九卷', '豸部', '鼠屬。善旋。从豸，宂聲。', '豸;宂', '宂', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6128, '貂', 'diāo', '第九卷', '豸部', '鼠屬。大而黃黑。出胡 丁零國。从豸，召聲。', '豸;召', '召', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6129, '貉', 'mò', '第九卷', '豸部', '北方貉。豸穜也。从豸，各聲。孔子曰「貉之言貉貉惡也」。', '豸;各', '各', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6130, '貓', 'māo', '第九卷', '豸部', '貍屬。从豸，苗聲。', '豸;苗', '苗', 'null', NULL, '豸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6131, '𧰽;兕', 'sì', '第九卷', '𧰽部', '如野牛。靑色。其皮堅厚可制鎧。象形。𧰽頭與禽离頭同。凡𧰽之屬皆从𧰽。古文从儿。', '儿', '', 'null', NULL, '儿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6132, '易', 'yì', '第九卷', '易部', '蜥易，蝘蜓，守宮也。象形。祕書說曰「日月爲易」。象侌昜也。一曰从勿。凡易之屬皆从易。', '勿', '', 'null', NULL, '勿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6133, '象', 'xiànɡ', '第九卷', '象部', '南越大獸。長鼻牙，三年一乳。象耳牙四足尾之形。凡象之屬皆从象。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6134, '豫', 'yù', '第九卷', '象部', '象之大者。賈侍中說，不害於物。从象，予聲。古文。', '象;予', '予', 'null', NULL, '象', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6135, '馬;𢒠', 'mǎ', '第十卷', '馬部', '怒也，武也。象馬頭、髦、尾、四足之形。凡馬之屬皆从馬。古文。籒文馬[彡]。與𢒠同有髦。', '彡', '', 'null', NULL, '彡', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6136, '騭', 'zhì', '第十卷', '馬部', '牡馬也。从馬，陟聲。讀若郅。', '馬;陟', '陟', 'null', NULL, '馬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6137, '𩡧', 'huán', '第十卷', '馬部', '馬一歲也。从馬。一，絆其足。讀若弦。一曰若環。', '馬;一;弦;環', '弦;環', 'null', NULL, '馬;一', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6138, '駒', 'jū', '第十卷', '馬部', '馬二歲曰駒。三歲曰駣。从馬，句聲。', '馬;句', '句', 'null', NULL, '馬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6139, '𩡩', 'bā', '第十卷', '馬部', '馬八歲也。从馬八。八亦聲。', '馬;八', '八', 'null', NULL, '馬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6140, '𩦂', 'xián', '第十卷', '馬部', '馬一目白曰𩦂。二目白曰魚。从馬，閒聲。', '馬;閒', '閒', 'null', NULL, '馬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6141, '騏', 'qí', '第十卷', '馬部', '馬靑驪。文如綦也。从馬，其聲。', '馬;其', '其', 'null', NULL, '馬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
