INSERT INTO `shuowen_voice_revel` VALUES (6276, '麞', 'zhānɡ', '第十卷', '鹿部', '麋屬也。从鹿，章聲。', '鹿;章', '章', 'null', NULL, '鹿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6277, '麠', 'jīnɡ', '第十卷', '鹿部', '大麃也。牛尾，一角。从鹿，畺聲。或从京。', '鹿;畺;京', '畺;京', 'null', NULL, '鹿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6278, '麃', 'páo', '第十卷', '鹿部', '麞屬。从鹿，票省聲。', '鹿;票', '票', 'null', NULL, '鹿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6279, '麈', 'zhǔ', '第十卷', '鹿部', '麋屬。从鹿，主聲。', '鹿;主', '主', 'null', NULL, '鹿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6280, '麑', 'ní', '第十卷', '鹿部', '狻麑，獸也。从鹿，兒聲。', '鹿;兒', '兒', 'null', NULL, '鹿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6281, '麙', 'xián', '第十卷', '鹿部', '山羊而大者細角。从鹿，咸聲。', '鹿;咸', '咸', 'null', NULL, '鹿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6282, '麢', 'línɡ', '第十卷', '鹿部', '大羊而細角。从鹿，霝聲。', '鹿;霝', '霝', 'null', NULL, '鹿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6283, '𪊧', 'ɡuī', '第十卷', '鹿部', '鹿屬。从鹿，圭聲。', '鹿;圭', '圭', 'null', NULL, '鹿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6284, '麝', 'shè', '第十卷', '鹿部', '如小麋。臍有香。从鹿，射聲。', '鹿;射', '射', 'null', NULL, '鹿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6285, '𪋮', 'yù', '第十卷', '鹿部', '佀鹿而大。从鹿，與聲。', '鹿;與', '與', 'null', NULL, '鹿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6286, '麗;丽', 'lì', '第十卷', '鹿部', '旅行也。鹿之性，見食急則必旅行。从鹿丽。禮「麗皮納聘」，葢鹿皮也。古文。篆文麗字。', '鹿', '', 'null', NULL, '鹿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6287, '麤', 'cū', '第十卷', '麤部', '行超遠也。从三鹿。凡麤之屬皆从麤。', '鹿', '', 'null', NULL, '鹿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6288, '𪋻;塵', 'chén', '第十卷', '麤部', '鹿行揚土也。从麤土。籒文。', '麤;土', '', 'null', NULL, '麤;土', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6289, '㲋', 'chuò', '第十卷', '㲋部', '㲋獸也。佀兔靑色而大。象形。頭與兔同，足與鹿同。凡㲋之屬皆从㲋。籒文。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6290, '毚', 'chán', '第十卷', '㲋部', '狡兔也。兔之駿者。从㲋兔。', '㲋;兔', '', 'null', NULL, '㲋;兔', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6291, '𣬕', 'xiě', '第十卷', '㲋部', '獸名。从㲋，吾聲。讀若寫。', '㲋;吾;寫', '吾;寫', 'null', NULL, '㲋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6292, '𣬎', 'jué', '第十卷', '㲋部', '𣬎獸也。佀狌狌。从㲋，夬聲。', '㲋;夬', '夬', 'null', NULL, '㲋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6293, '兔', 'tù', '第十卷', '兔部', '兔獸也。象兔踞。後其尾形。兔頭與㲋頭同。凡兔之屬皆从兔。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6294, '逸', 'yì', '第十卷', '兔部', '失也。从辵兔。兔謾訑善逃也。', '辵;兔', '', 'null', NULL, '辵;兔', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6295, '冤', 'yuān', '第十卷', '兔部', '屈也。从冂兔。兔在冂下不得走，益屈折也。', '冂;兔', '', 'null', NULL, '冂;兔', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6296, '婏', 'fàn', '第十卷', '兔部', '兔子也。一曰婏，疾也。从女兔。', '女;兔', '', 'null', NULL, '女;兔', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6297, '𠓗', 'fù', '第十卷', '兔部', '疾也。从三兔。闕。讀若赴。', '兔;赴', '赴', 'null', NULL, '兔', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6298, '免', 'miǎn', '第十卷', '兔部', '兔逸也。从免不見足。會意。', '免', '', 'null', NULL, '免', 'null', 'null', 'null', 'null', '应补', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6299, '㕙', 'jùn', '第十卷', '兔部', '狡兔也。从兔，夋聲。', '兔;夋', '夋', 'null', NULL, '兔', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6300, '萈', 'huán', '第十卷', '萈部', '山羊細角者。从兔足。从𥄕聲。凡萈之屬皆从萈。讀若丸。寬字从此。', '兔;𥄕;丸;寬', '𥄕;丸;寬', 'null', NULL, '兔', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6301, '犬', 'quǎn', '第十卷', '犬部', '狗之有縣蹏者也。象形。孔子曰「視犬之字，如畫狗也」。凡犬之屬皆从犬。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6302, '狗', 'ɡǒu', '第十卷', '犬部', '孔子曰「狗，叩也。叩气吠㠯守」。从犬，句聲。', '犬;句', '句', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6303, '獀', 'sōu', '第十卷', '犬部', '南越名犬獿獀也。从犬，叜聲。', '犬;叜', '叜', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6304, '尨', 'mánɡ', '第十卷', '犬部', '犬之多毛者。从犬彡。詩曰「無使尨也吠」。', '犬;彡', '', 'null', NULL, '犬;彡', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6305, '狡', 'jiǎo', '第十卷', '犬部', '少犬也。从犬，交聲。匈奴地有狡犬，巨口而黑身。', '犬;交', '交', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6306, '㺜', 'nónɡ', '第十卷', '犬部', '犬惡毛也。从犬，農聲。', '犬;農', '農', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6307, '猲', 'xiē', '第十卷', '犬部', '猲獢，短喙犬也。从犬，曷聲。詩曰「載獫猲獢」。爾雅曰「短喙犬謂之猲獢」。', '犬;曷', '曷', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6308, '獢', 'xiāo', '第十卷', '犬部', '猲獢也。从犬，喬聲。', '犬;喬', '喬', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6309, '獫', 'xiǎn', '第十卷', '犬部', '長喙犬也。一曰黑犬黃頭。从犬，僉聲。', '犬;僉', '僉', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6310, '㹥', 'zhù', '第十卷', '犬部', '黃犬黑頭。从犬，主聲。讀若注。', '犬;主;注', '主;注', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6311, '猈', 'bài', '第十卷', '犬部', '短脛犬。从犬，卑聲。', '犬;卑', '卑', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6312, '猗', 'yī', '第十卷', '犬部', '犗犬也。从犬，奇聲。', '犬;奇', '奇', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6313, '狊', 'jú', '第十卷', '犬部', '犬視皃。从犬目。', '犬;目', '', 'null', NULL, '犬;目', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6314, '𤟟', 'yān', '第十卷', '犬部', '竇中犬聲。从犬音。音亦聲。', '犬;音', '音', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6315, '默', 'mò', '第十卷', '犬部', '犬暫逐人也。从犬，黑聲。讀若墨。', '犬;黑;墨', '黑;墨', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6316, '猝', 'cù', '第十卷', '犬部', '犬从艸𣉱出逐人也。从犬，卒聲。', '犬;卒', '卒', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6317, '猩', 'xīnɡ', '第十卷', '犬部', '猩猩，犬吠聲。从犬，星聲。', '犬;星', '星', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6318, '㺌', 'xiàn', '第十卷', '犬部', '犬吠不止也。从犬，兼聲。讀若檻。一曰兩犬爭也。', '犬;兼;檻', '兼;檻', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6319, '㺖', 'hǎn', '第十卷', '犬部', '小犬吠。从犬，敢聲。南陽 新野有㺖鄉。', '犬;敢', '敢', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6320, '猥', 'wěi', '第十卷', '犬部', '犬吠聲。从犬，畏聲。', '犬;畏', '畏', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6321, '獿', 'nǎo', '第十卷', '犬部', '獿，㺒也。从犬，夒聲。', '犬;夒', '夒', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6322, '㺒', 'xiāo', '第十卷', '犬部', '犬獿獿咳吠也。从犬，翏聲。', '犬;翏', '翏', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6323, '㺑', 'shǎn', '第十卷', '犬部', '犬容頭進也。从犬，參聲。一曰賊疾也。', '犬;參', '參', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '侵部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6324, '𤟌;獎', 'jiǎnɡ', '第十卷', '犬部', '嗾犬厲之也。从犬，將省聲。', '犬;將', '將', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6325, '㹽', 'chǎn', '第十卷', '犬部', '犬齧也。从犬，戔聲。', '犬;戔', '戔', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6326, '狦;㹪', 'shàn', '第十卷', '犬部', '惡健犬也。从犬，刪省聲。', '犬;删', '删', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6327, '狠', 'yán', '第十卷', '犬部', '犬鬥聲。从犬，艮聲。', '犬;艮', '艮', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6328, '㺕', 'fán', '第十卷', '犬部', '犬鬥聲。从犬，番聲。', '犬;番', '番', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6329, '狋', 'yí', '第十卷', '犬部', '犬怒皃。从犬，示聲。一曰犬難附。代郡有狋氏縣。讀又若銀。', '犬;示;銀', '示;銀', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6330, '㹞', 'yín', '第十卷', '犬部', '犬吠聲。从犬，斤聲。', '犬;斤', '斤', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6331, '獡;𤡯', 'shuò', '第十卷', '犬部', '犬獡獡不附人也。从犬，舄聲。南楚謂相驚曰獡。讀若愬。', '犬;舄;愬', '舄;愬', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6332, '獷', 'ɡuǎnɡ', '第十卷', '犬部', '犬獷獷不可附也。从犬，廣聲。漁陽有獷平縣。', '犬;廣', '廣', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6333, '狀', 'zhuànɡ', '第十卷', '犬部', '犬形也。从犬，爿聲。', '犬;爿', '爿', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6334, '𤞛', 'zànɡ', '第十卷', '犬部', '妄彊犬也。从犬壯。壯亦聲。', '犬;壯', '壯', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6335, '獒', 'áo', '第十卷', '犬部', '犬知人心可使者。从犬，敖聲。春秋傳曰「公嗾夫獒」。', '犬;敖', '敖', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6336, '獳', 'nóu', '第十卷', '犬部', '怒犬皃。从犬，需聲。讀若槈。', '犬;需;槈', '需;槈', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6337, '狧', 'tà', '第十卷', '犬部', '犬食也。从犬㐁。讀如「比目魚、鰈」之鰈[魼]。', '犬;㐁;魼', '㐁;魼', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '盍部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6338, '狎', 'xiá', '第十卷', '犬部', '犬可習也。从犬，甲聲。', '犬;甲', '甲', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6339, '狃', 'niǔ', '第十卷', '犬部', '犬性忕也。从犬，丑聲。', '犬;丑', '丑', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6340, '獪', 'kuài', '第十卷', '犬部', '狡獪也。从犬，會聲。', '犬;會', '會', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6341, '犯', 'fàn', '第十卷', '犬部', '侵也。从犬，𢎘聲。', '犬;𢎘', '𢎘', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6342, '猜', 'cāi', '第十卷', '犬部', '恨。賊也。从犬，靑聲。', '犬;靑', '靑', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '之部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6343, '猛', 'měnɡ', '第十卷', '犬部', '健犬也。从犬，孟聲。', '犬;孟', '孟', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6344, '犺', 'kànɡ', '第十卷', '犬部', '健犬也。从犬，亢聲。', '犬;亢', '亢', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6345, '㹤;怯', 'qiè', '第十卷', '犬部', '多畏也。从犬，去聲。杜林說，㹤从心。', '犬;去;心', '去', 'null', NULL, '犬;心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6346, '獜', 'lín', '第十卷', '犬部', '健也。从犬，粦聲。詩曰「盧獜獜」。', '犬;粦', '粦', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6347, '獧', 'juàn', '第十卷', '犬部', '疾跳也。一曰急也。从犬，瞏聲。', '犬;瞏', '瞏', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6348, '倏', 'shū', '第十卷', '犬部', '犬走疾也。从犬，攸聲。讀若叔。', '犬;攸;叔', '攸;叔', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6349, '狟', 'huán', '第十卷', '犬部', '犬行也。从犬，亘聲。周書曰「尙狟狟」。', '犬;亘', '亘', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6350, '犻', 'bó', '第十卷', '犬部', '過弗取也。从犬，𣎵聲。讀若孛。', '犬;𣎵;孛', '𣎵;孛', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6351, '𤟍', 'zhé', '第十卷', '犬部', '犬張耳皃。从犬，易聲。', '犬;易', '易', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6352, '猌', 'yìn', '第十卷', '犬部', '犬張齗怒也。从犬，來聲。讀又若銀。', '犬;來;銀', '銀', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6353, '犮', 'bá', '第十卷', '犬部', '犬走皃。从犬而𠂆之。曳其足，則剌犮也。', '犬;𠂆', '', 'null', NULL, '犬;𠂆', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6354, '戾', 'lì', '第十卷', '犬部', '曲也。从犬出戸下。犬出戸下爲戾者，身曲戾也。', '犬;戸', '', 'null', NULL, '犬;戸', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6355, '獨', 'dú', '第十卷', '犬部', '犬相得而鬥也。从犬，蜀聲。羊爲羣，犬爲獨。一曰北嚻山有獨𤞞獸，如虎，白身，豕鬛，尾如馬。', '犬;蜀', '蜀', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6356, '𤞞', 'yù', '第十卷', '犬部', '獨𤞞獸也。从犬，谷聲。', '犬;谷', '谷', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6357, '𤣔', 'xiǎn', '第十卷', '犬部', '秋田也。从犬，壐聲。𤣔或从示。宗廟之田也，故从豕示。', '犬;壐;豕;示', '壐', 'null', NULL, '犬;豕;示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6358, '獵', 'liè', '第十卷', '犬部', '放獵逐禽也。从犬，巤聲。', '犬;巤', '巤', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6359, '獠', 'liáo', '第十卷', '犬部', '獵也。从犬，尞聲。', '犬;尞', '尞', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6360, '狩', 'shòu', '第十卷', '犬部', '火田也。从犬，守聲。易曰「明夷于南狩」。', '犬;守', '守', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6361, '臭', 'xiù', '第十卷', '犬部', '禽走臭而知其迹者犬也。从犬自。', '犬;自', '', 'null', NULL, '犬;自', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6362, '獲', 'huò', '第十卷', '犬部', '獵所獲也。从犬，蒦聲。', '犬;蒦', '蒦', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6363, '獘;斃', 'bì', '第十卷', '犬部', '頓仆也。从犬，敝聲。春秋傳曰「與犬犬獘」。獘或从死。', '犬;敝;死', '敝', 'null', NULL, '犬;死', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6364, '獻', 'xiàn', '第十卷', '犬部', '宗廟犬名羹獻。犬肥者㠯獻。从犬，鬳聲。', '犬;鬳', '鬳', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6365, '𤜵', 'yàn', '第十卷', '犬部', '獟犬也。从犬，开聲。一曰逐虎犬也。', '犬;开', '开', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6366, '獟', 'yào', '第十卷', '犬部', '𤜵犬也。从犬，堯聲。', '犬;堯', '堯', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6367, '狾', 'zhì', '第十卷', '犬部', '狂犬也。从犬，㪿聲。春秋傳曰「狾犬入華臣氏之門」。', '犬;㪿', '㪿', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6368, '狂', 'kuánɡ', '第十卷', '犬部', '狾犬也。从犬，㞷聲。古文从心。', '犬;㞷;心', '㞷', 'null', NULL, '犬;心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6369, '類', 'lèi', '第十卷', '犬部', '穜類相佀。唯犬爲甚。从犬，頪聲。', '犬;頪', '頪', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6370, '狄', 'dí', '第十卷', '犬部', '北狄也。本犬穜。狄之爲言淫辟也。从犬，亦省聲。', '犬;亦', '亦', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6371, '狻', 'suān', '第十卷', '犬部', '狻麑，如虦苗，食虎、豹。从犬，夋聲。見爾雅。', '犬;夋', '夋', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6372, '玃', 'jué', '第十卷', '犬部', '大母猴也。善攫持人，好顧盼。从犬，矍聲。爾雅曰「玃父善顧」。', '犬;矍', '矍', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6373, '猶;猷', 'yóu', '第十卷', '犬部', '玃屬。从犬，酋聲。一曰隴西謂犬子爲猶。', '犬;酋', '酋', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6374, '狙', 'jū', '第十卷', '犬部', '玃屬。从犬，且聲。一曰犬暫齧人者。一曰犬不齧人者。', '犬;且', '且', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6375, '猴;㺅', 'hóu', '第十卷', '犬部', '夒也。从犬，矦聲。', '犬;矦', '矦', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6376, '㺉', 'hù', '第十卷', '犬部', '犬屬。要巳上黃，要巳下黑，食母猴。从犬，㱿聲。讀若構。', '犬;㱿;構', '㱿;構', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6377, '狼', 'lánɡ', '第十卷', '犬部', '佀犬。銳頭白頰，高歬廣後。从犬，良聲。', '犬;良', '良', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6378, '狛', 'bó', '第十卷', '犬部', '如狼，善驅羊。从犬，白聲。讀若檗。甯嚴讀之若「淺洦」。', '犬;白;檗;洦', '白;檗;洦', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6379, '獌', 'màn', '第十卷', '犬部', '狼屬。从犬，曼聲。爾雅曰「貙獌佀貍」。', '犬;曼', '曼', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6380, '狐', 'hú', '第十卷', '犬部', '䄏獸也。鬼所乘。有「三德」——其色中和，小歬大後，死則丘首。謂之「三德」。从犬，瓜聲。', '犬;瓜', '瓜', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6381, '獺', 'tǎ', '第十卷', '犬部', '水狗也。食魚。从犬，賴聲。', '犬;賴', '賴', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6382, '猵;獱', 'biān', '第十卷', '犬部', '獺屬。从犬，扁聲。或从賓。', '犬;扁;賓', '扁;賓', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6383, '猋', 'biāo', '第十卷', '犬部', '犬走皃。从三犬。', '犬', '', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6384, '狘', 'xuè', '第十卷', '犬部', '獸走皃。从犬，戉聲。', '犬;戉', '戉', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6385, '𤟤', 'huī', '第十卷', '犬部', '獸名。从犬，軍聲。', '犬;軍', '軍', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6386, '狷', 'juàn', '第十卷', '犬部', '褊急也。从犬，肙聲。', '犬;肙', '肙', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '新附应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6387, '猰;䝟', 'yà', '第十卷', '犬部', '猰㺄，獸名。从犬，契聲。', '犬;契', '契', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6388, '㹜', 'yín', '第十卷', '㹜部', '㒳犬相齧也。从二犬。凡㹜之屬皆从㹜。', '犬', '', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6389, '㺇', 'sī', '第十卷', '㹜部', '司空也。从㹜，𦣞聲。復說獄司空。', '㹜;𦣞', '𦣞', 'null', NULL, '㹜', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6390, '獄', 'yù', '第十卷', '㹜部', '确也。从㹜，从言。二犬所㠯守也。', '㹜;言', '', 'null', NULL, '㹜;言', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6391, '鼠', 'shǔ', '第十卷', '鼠部', '穴蟲之總名也。象形。凡鼠之屬皆从鼠。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6392, '𪖇', 'fán', '第十卷', '鼠部', '𪖇鼠也。从鼠，番聲。讀若樊。或曰鼠婦。', '鼠;番;樊', '番;樊', 'null', NULL, '鼠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6393, '䶅', 'hé', '第十卷', '鼠部', '䶅鼠。出胡地。皮可作裘。从鼠，各聲。', '鼠;各', '各', 'null', NULL, '鼠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6394, '鼢;蚡', 'fén', '第十卷', '鼠部', '地中行鼠。伯勞所化也。一曰偃鼠。从鼠，分聲。或从虫分。', '鼠;分;虫', '分', 'null', NULL, '鼠;虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6395, '䶄', 'pínɡ', '第十卷', '鼠部', '䶄令鼠也。从鼠，平聲。', '鼠;平', '平', 'null', NULL, '鼠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6396, '鼶', 'sī', '第十卷', '鼠部', '鼶鼠也。从鼠，虒聲。', '鼠;虒', '虒', 'null', NULL, '鼠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6397, '𪕚', 'liú', '第十卷', '鼠部', '竹鼠也。如犬。从鼠，丣聲。', '鼠;丣', '丣', 'null', NULL, '鼠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6398, '鼫', 'shí', '第十卷', '鼠部', '五技鼠也。能飛不能過屋，能緣不能竆木，能游不能渡谷，能穴不能掩身，能走不能先人，此之謂「五技」。从鼠，石聲。', '鼠;石', '石', 'null', NULL, '鼠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6399, '鼨', 'zhōnɡ', '第十卷', '鼠部', '豹文鼠也。从鼠，冬聲。籒文省。', '鼠;冬', '冬', 'null', NULL, '鼠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6400, '𪕶', 'è', '第十卷', '鼠部', '鼠屬。从鼠，益聲。或从豸作。', '鼠;益;豸', '益', 'null', NULL, '鼠;豸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6401, '鼷', 'xī', '第十卷', '鼠部', '小鼠也。从鼠，奚聲。', '鼠;奚', '奚', 'null', NULL, '鼠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6402, '鼩', 'qú', '第十卷', '鼠部', '精鼩鼠也。从鼠，句聲。', '鼠;句', '句', 'null', NULL, '鼠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6403, '鼸', 'xiàn', '第十卷', '鼠部', '䶃也。从鼠，兼聲。', '鼠;兼', '兼', 'null', NULL, '鼠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6404, '䶃', 'hán', '第十卷', '鼠部', '鼠屬。从鼠，今聲。讀若含。', '鼠;今;含', '今;含', 'null', NULL, '鼠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6405, '鼬', 'yòu', '第十卷', '鼠部', '如鼠。赤黃色，尾大，食鼠者。从鼠，由聲。', '鼠;由', '由', 'null', NULL, '鼠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6406, '䶂', 'zhuó', '第十卷', '鼠部', '胡地風鼠。从鼠，勺聲。', '鼠;勺', '勺', 'null', NULL, '鼠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6407, '𪕎', 'rǒnɡ', '第十卷', '鼠部', '鼠屬。从鼠，宂聲。', '鼠;宂', '宂', 'null', NULL, '鼠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6408, '𪕊', 'zī', '第十卷', '鼠部', '𪕊鼠。佀雞，鼠尾。从鼠，此聲。', '鼠;此', '此', 'null', NULL, '鼠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6409, '鼲', 'hún', '第十卷', '鼠部', '鼲鼠。出丁零 胡，皮可作裘。从鼠，軍聲。', '鼠;軍', '軍', 'null', NULL, '鼠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6410, '𪕮;𪕱', 'hú', '第十卷', '鼠部', '斬𪕮鼠。黑身，白要若帶；手有長白毛，佀握版之狀；類蝯、蜼之屬。从鼠，胡聲。', '鼠;胡', '胡', 'null', NULL, '鼠', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6411, '能', 'nénɡ', '第十卷', '能部', '熊屬。足佀鹿。从肉，㠯聲。能獸堅中，故偁「賢能」。而彊壯，偁「能傑」也。凡能之屬皆从能。', '肉;㠯', '㠯', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6412, '熊', 'xiónɡ', '第十卷', '熊部', '熊獸。佀豕，山凥，冬蟄。从能，炎省聲。凡熊之屬皆从熊。', '能;炎', '炎', 'null', NULL, '能', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6413, '羆', 'pí', '第十卷', '熊部', '如熊。黃白文。从熊，罷省聲。古文从皮[能]。', '熊;罷;皮;能', '罷;皮', 'null', NULL, '熊;能', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6414, '火', 'huǒ', '第十卷', '火部', '𤈦也。南方之行。炎而上。象形。凡火之屬皆从火。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6415, '炟', 'dá', '第十卷', '火部', '上諱。[从火，旦聲。]', '火;旦', '旦', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6416, '𤈦', 'huǐ', '第十卷', '火部', '火也。从火，尾聲。詩曰「王室如𤈦」。', '火;尾', '尾', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6417, '燬', 'huǐ', '第十卷', '火部', '火也。从火，毀聲。春秋傳曰「衛侯燬」。', '火;毀', '毀', 'null', NULL, '火', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6418, '燹', 'xiǎn', '第十卷', '火部', '火也。从火，豩聲。', '火;豩', '豩', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6419, '焌', 'jùn', '第十卷', '火部', '然火也。从火，夋聲。周禮曰「遂䶴其焌」。焌火在歬，㠯焞焯龜。', '火;夋', '夋', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6420, '尞', 'liào', '第十卷', '火部', '祡祭天也。从火昚。昚，古文愼字，祭天所㠯愼也。', '火;昚', '', 'null', NULL, '火;昚', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6421, '然;𤉷', 'rán', '第十卷', '火部', '燒也。从火，肰聲。或从艸難。', '火;肰;艸;難', '肰;難', 'null', NULL, '火;艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6422, '爇;𤑔', 'ruò', '第十卷', '火部', '燒也。从火，蓺聲。春秋傳曰「𤑔僖負羈」。', '火;蓺', '蓺', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6423, '燔', 'fán', '第十卷', '火部', '𤑔也。从火，番聲。', '火;番', '番', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6424, '燒', 'shāo', '第十卷', '火部', '𤑔也。从火，堯聲。', '火;堯', '堯', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6425, '烈', 'liè', '第十卷', '火部', '火猛也。从火，列聲。', '火;列', '列', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6426, '炪', 'zhuō', '第十卷', '火部', '火光也。从火，出聲。商書曰「予亦炪謀」。讀若「巧拙」之拙。', '火;出;拙', '出;拙', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6427, '熚', 'bì', '第十卷', '火部', '熚𤒓，火皃。从火，畢聲。', '火;畢', '畢', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6428, '𤒓', 'fú', '第十卷', '火部', '熚𤒓也。从火，𢨋聲。𢨋，籒文悖字。', '火;𢨋', '𢨋', '沸', '熚𤒓犹畢沸。', '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6429, '烝', 'zhēnɡ', '第十卷', '火部', '火气上行也。从火，㞼聲。', '火;㞼', '㞼', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6430, '烰', 'fú', '第十卷', '火部', '烝也。从火，孚聲。詩曰「烝之烰烰」。', '火;孚', '孚', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6431, '煦', 'xù', '第十卷', '火部', '烝也。一曰赤皃。一曰𥁕潤也。从火，昫聲。', '火;昫', '昫', 'null', NULL, '火', 'null', 'null', 'null', 'null', '旭≌煦≌熹≌熙', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6432, '熯', 'hàn', '第十卷', '火部', '乾皃。从火，漢省聲。詩曰「我孔熯矣」。', '火;漢', '漢', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6433, '炥', 'fú', '第十卷', '火部', '火皃。从火，弗聲。', '火;弗', '弗', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6434, '熮', 'liáo', '第十卷', '火部', '火皃。从火，翏聲。逸周書曰「味辛而不熮」。', '火;翏', '翏', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6435, '焛', 'lìn', '第十卷', '火部', '火皃。从火，𨳌省聲。讀若粦。', '火;𨳌;粦', '𨳌;粦', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6436, '㷳', 'yàn', '第十卷', '火部', '火色也。从火，雁聲。讀若鴈。', '火;雁;鴈', '雁;鴈', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6437, '熲', 'jiǒnɡ', '第十卷', '火部', '火光也。从火，頃聲。', '火;頃', '頃', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6438, '爚', 'yuè', '第十卷', '火部', '火光也。从火，龠聲。一曰𤑔也。', '火;龠', '龠', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6439, '熛', 'biāo', '第十卷', '火部', '火飛也。从火，票聲。讀若摽。', '火;票;摽', '票;摽', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6440, '熇', 'hè', '第十卷', '火部', '火熱也。从火，高聲。詩曰「多將熇熇」。', '火;高', '高', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6441, '烄', 'jiǎo', '第十卷', '火部', '交木然也。从火，交聲。', '火;交', '交', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6442, '𤇇;㶣', 'chán', '第十卷', '火部', '小𤑔也。从火，𢆉聲。詩曰「憂心如𤇇」。', '火;𢆉', '𢆉', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6443, '燋', 'jiāo', '第十卷', '火部', '所㠯然持火也。从火，焦聲。', '火;焦', '焦', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6444, '炭', 'tàn', '第十卷', '火部', '燒木未灰。也。从火，屵聲。', '火;屵', '屵', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6445, '𤈩', 'zhǎ', '第十卷', '火部', '束炭也。从火，差省聲。讀若齹。', '火;差;齹', '差;齹', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6446, '𤉧', 'jiǎo', '第十卷', '火部', '交灼木也。从火，敎省聲。讀若狡。', '火;敎;狡', '敎;狡', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6447, '炦', 'bá', '第十卷', '火部', '火气也。从火，犮聲。', '火;犮', '犮', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6448, '灰', 'huī', '第十卷', '火部', '死火餘㶳也。从火又。又，手也，火旣滅可㠯執持。', '火;又', '又', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6449, '炱', 'tái', '第十卷', '火部', '灰炱煤也。从火，台聲。', '火;台', '台', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6450, '煨', 'wēi', '第十卷', '火部', '盆中火。从火，畏聲。', '火;畏', '畏', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6451, '熄', 'xī', '第十卷', '火部', '畜火也。从火，息聲。亦曰滅火。', '火;息', '息', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6452, '烓', 'wēi jiǒng', '第十卷', '火部', '行竈也。从火，圭聲。讀若冋。', '火;圭;冋', '圭;冋', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6453, '煁', 'chén', '第十卷', '火部', '烓也。从火，甚聲。', '火;甚', '甚', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6454, '燀', 'chǎn', '第十卷', '火部', '炊也。从火，單聲。春秋傳曰「燀之㠯薪」。', '火;單', '單', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6455, '炊', 'chuī', '第十卷', '火部', '爨也。从火，吹省聲。', '火;吹', '吹', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6456, '烘', 'hōnɡ', '第十卷', '火部', '尞也。从火，共聲。詩曰「卬烘于煁」。', '火;共', '共', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6457, '齌', 'jì', '第十卷', '火部', '炊鬴疾也。从火，齊聲。', '火;齊', '齊', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6458, '熹;熺', 'xī', '第十卷', '火部', '炙也。从火，喜聲。', '火;喜', '喜', 'null', NULL, '火', 'null', 'null', 'null', 'null', '旭≌煦≌熹≌熙', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6459, '煎', 'jiān', '第十卷', '火部', '熬也。从火，前聲。', '火;前', '前', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6460, '熬;䵅', 'áo', '第十卷', '火部', '乾煎也。从火，敖聲。熬或从麥作。', '火;敖;麥', '敖', 'null', NULL, '火;麥', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6461, '炮', 'páo', '第十卷', '火部', '毛炙肉也。从火，包聲。', '火;包', '包', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6462, '𤇯', 'ēn', '第十卷', '火部', '炮炙也。㠯微火溫肉。从火，衣聲。', '火;衣', '衣', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6463, '𤎯', 'zēnɡ', '第十卷', '火部', '置魚筩中炙也。从火，曾聲。', '火;曾', '曾', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6464, '𤐸', 'bì', '第十卷', '火部', '㠯火乾肉也。从火，稫[䵗]聲。籒文不省。', '火;䵗', '䵗', '咅', '𤐸、焙古今字。', '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6465, '爆', 'bào', '第十卷', '火部', '灼也。从火，㬥聲。', '火;㬥', '㬥', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6466, '煬', 'yànɡ', '第十卷', '火部', '炙燥也。从火，昜聲。', '火;昜', '昜', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6467, '𤌍', 'hú', '第十卷', '火部', '灼也。从火，隺聲。', '火;隺', '隺', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6468, '爤;爛;燗;𤏐', 'làn', '第十卷', '火部', '火孰也。从火，蘭聲。或从閒。', '火;蘭;閒', '蘭;閒', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6469, '爢', 'mí', '第十卷', '火部', '爤也。从火，靡聲。', '火;靡', '靡', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6470, '尉;熨;𤈫;㷉', 'wèi', '第十卷', '火部', '从上按下也。从𡰥，又持火。所㠯申繒也。', '𡰥;又;火', '', 'null', NULL, '𡰥;又;火', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6471, '𪚰', 'jiāo', '第十卷', '火部', '灼龜不兆也。从龜火。春秋傳曰「卜戰，龜𪚰，不兆」。讀若焦。', '龜;火;焦', '焦', 'null', NULL, '龜;火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6472, '灸', 'jiǔ', '第十卷', '火部', '灼也。从火，久聲。', '火;久', '久', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6473, '灼', 'zhuó', '第十卷', '火部', '灸也。从火，勺聲。', '火;勺', '勺', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6474, '煉', 'liàn', '第十卷', '火部', '鑠治金也。从火，柬聲。', '火;柬', '柬', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6475, '燭', 'zhú', '第十卷', '火部', '庭燎，大燭也。从火，蜀聲。', '火;蜀', '蜀', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6476, '熜', 'zǒnɡ', '第十卷', '火部', '然麻蒸也。从火，悤聲。', '火;悤', '悤', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6477, '灺', 'xiè', '第十卷', '火部', '燭㶳也。从火，也聲。', '火;也', '也', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6478, '㶳', 'jìn', '第十卷', '火部', '火之餘木也。从火，聿聲。一曰薪也。', '火;聿', '聿', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6479, '焠', 'cuì', '第十卷', '火部', '堅刀刃也。从火，卒聲。', '火;卒', '卒', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6480, '煣', 'rǒu', '第十卷', '火部', '屈申木也。从火柔。柔亦聲。', '火;柔', '柔', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6481, '焚', 'fán', '第十卷', '火部', '燒田也。从火林。', '火;林', '', 'null', NULL, '火;林', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6482, '熑', 'lián', '第十卷', '火部', '火煣車网絕也。从火，兼聲。周禮曰「煣牙，外不熑」。', '火;兼', '兼', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6483, '燎', 'liǎo', '第十卷', '火部', '放火也。从火，尞聲。', '火;尞', '尞', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6484, '票', 'biāo', '第十卷', '火部', '火飛也。从火。票與䙴同意。', '火;䙴', '', 'null', NULL, '火;䙴', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6485, '㷮', 'zāo', '第十卷', '火部', '𤓪也。从火，曹聲。', '火;曹', '曹', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6486, '𤓪;焦;𤊙', 'jiāo', '第十卷', '火部', '火所傷也。从火，雥聲。或省。', '火;雥', '雥', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6487, '烖;灾;災', 'zāi', '第十卷', '火部', '天火曰烖。从火，𢦒聲。或从宀火。籒文从𡿧。古文从才。', '火;𢦒;宀;火;𡿧;才', '𢦒;𡿧;才', 'null', NULL, '火;宀;火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6488, '煙;烟', 'yān', '第十卷', '火部', '火气也。从火，垔聲。或从因。籒文从宀。古文。', '火;垔;因;宀', '垔;因', 'null', NULL, '火;宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6489, '焆', 'yè', '第十卷', '火部', '焆焆，煙皃。从火，肙聲。', '火;肙', '肙', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6490, '煴;熅', 'yūn', '第十卷', '火部', '鬱煙也。从火，𥁕聲。', '火;𥁕', '𥁕', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6491, '𤈑', 'dí', '第十卷', '火部', '望見火皃。从火，㫐聲。讀若「馰顙」之馰。', '火;㫐;馰', '㫐;馰', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6492, '燂', 'tán', '第十卷', '火部', '火熱也。从火，覃聲。', '火;覃', '覃', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6493, '焞', 'tūn', '第十卷', '火部', '朙也。从火，𦎫聲。春秋傳曰「焞燿天地」。', '火;𦎫', '𦎫', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6494, '炳', 'bǐnɡ', '第十卷', '火部', '朙也。从火，丙聲。', '火;丙', '丙', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6495, '焯', 'zhuó', '第十卷', '火部', '朙也。从火，卓聲。周書曰「焯[灼]見三有俊心」。', '火;卓;灼', '卓;灼', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6496, '照', 'zhào', '第十卷', '火部', '朙也。从火，昭聲。', '火;昭', '昭', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6497, '煒', 'wěi', '第十卷', '火部', '盛朙皃也。从火，韋聲。詩曰「彤管有煒」。', '火;韋', '韋', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6498, '㶴', 'chǐ', '第十卷', '火部', '盛火也。从火，多聲。', '火;多', '多', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6499, '熠', 'yì', '第十卷', '火部', '盛光也。从火，習聲。詩曰「熠熠宵行」。', '火;習', '習', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6500, '煜', 'yù', '第十卷', '火部', '燿也。从火，昱聲。', '火;昱', '昱', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6501, '燿', 'yào', '第十卷', '火部', '照也。从火，翟聲。', '火;翟', '翟', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6502, '煇', 'huī', '第十卷', '火部', '光也。从火，軍聲。', '火;軍', '軍', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6503, '炯', 'jiǒnɡ', '第十卷', '火部', '光也。从火，冋聲。', '火;冋', '冋', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6504, '𤑼;爗;燁', 'yè', '第十卷', '火部', '盛也。从火，𣋓聲。詩曰「𤑼𤑼震電」。', '火;𣋓', '𣋓', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6505, '爓', 'yàn', '第十卷', '火部', '火爓也。从火，閻聲。', '火;閻', '閻', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6506, '炫', 'xuàn', '第十卷', '火部', '爓燿也。从火，玄聲。', '火;玄', '玄', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6507, '煌', 'huánɡ', '第十卷', '火部', '煌煌，煇也。从火，皇聲。', '火;皇', '皇', 'null', NULL, '火', 'null', 'null', 'null', 'null', '依小徐调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6508, '焜', 'kūn', '第十卷', '火部', '煌也。从火，昆聲。', '火;昆', '昆', 'null', NULL, '火', 'null', 'null', 'null', 'null', '依小徐调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6509, '光;炗', 'ɡuānɡ', '第十卷', '火部', '朙也。从火在儿上。光朙意也。古文[炎]。古文。', '火;儿;炎', '', 'null', NULL, '火;儿;炎', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6510, '熱', 'rè', '第十卷', '火部', '𥁕也。从火，埶聲。', '火;埶', '埶', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6511, '熾', 'chì', '第十卷', '火部', '盛也。从火，戠聲。古文。', '火;戠', '戠', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6512, '燠', 'ào', '第十卷', '火部', '熱在中也。从火，奥聲。', '火;奥', '奥', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6513, '煖', 'xuān', '第十卷', '火部', '𥁕也。从火，爰聲。', '火;爰', '爰', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6514, '煗', 'nuǎn', '第十卷', '火部', '𥁕也。从火，耎聲。', '火;耎', '耎', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6515, '炅', 'jiǒnɡ', '第十卷', '火部', '見也。从火日。', '火;日', '', 'null', NULL, '火;日', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6516, '炕', 'kànɡ', '第十卷', '火部', '乾也。从火，亢聲。', '火;亢', '亢', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6517, '燥', 'zào', '第十卷', '火部', '乾也。从火，喿聲。', '火;喿', '喿', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6518, '烕', 'miè', '第十卷', '火部', '滅也。从火戌。火死於戌，陽氣至戌而盡。詩曰「赫赫宗周，褒姒烕之」。', '火;戌', '', 'null', NULL, '火;戌', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6519, '焅', 'kù', '第十卷', '火部', '旱气也。从火，告聲。', '火;告', '告', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6520, '燾', 'dào', '第十卷', '火部', '溥覆照也。从火，壽聲。', '火;壽', '壽', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6521, '爟;烜', 'ɡuàn', '第十卷', '火部', '取火於日。官名。从火，雚聲。周禮曰「司爟掌行火之政令」。舉火曰爟。或从亘。', '火;雚;亘', '雚;亘', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6522, '㷭', 'fēnɡ', '第十卷', '火部', '㷭𤎩，候表也。邊有警則舉火。从火，逢聲。', '火;逢', '逢', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6523, '爝', 'jiào', '第十卷', '火部', '苣火祓也。从火，爵聲。呂不韋曰「湯得伊尹，爝㠯爟火，釁㠯犧豭」。', '火;爵', '爵', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6524, '熭', 'wèi', '第十卷', '火部', '㬥乾也。从火，彗聲。', '火;彗', '彗', 'null', NULL, '火', 'null', 'null', 'null', 'null', '月部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6525, '熙', 'xī', '第十卷', '火部', '燥也。一曰怡也。从火，巸聲。', '火;巸', '巸', 'null', NULL, '火', 'null', 'null', 'null', 'null', '旭≌煦≌熹≌熙', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6526, '爞', 'chónɡ', '第十卷', '火部', '旱气也。从火，蟲聲。', '火;蟲', '蟲', 'null', NULL, '火', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6527, '煽', 'shàn', '第十卷', '火部', '熾盛也。从火，扇聲。', '火;扇', '扇', 'null', NULL, '火', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6528, '烙', 'luò', '第十卷', '火部', '灼也。从火，各聲。', '火;各', '各', 'null', NULL, '火', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6529, '爍', 'shuò', '第十卷', '火部', '灼爍，光也。从火，樂聲。', '火;樂', '樂', 'null', NULL, '火', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6530, '燦', 'càn', '第十卷', '火部', '燦爤，明瀞皃。从火，粲聲。', '火;粲', '粲', 'null', NULL, '火', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6531, '煥', 'huàn', '第十卷', '火部', '火光也。从火，奐聲。', '火;奐', '奐', 'null', NULL, '火', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6532, '炎', 'yán', '第十卷', '炎部', '火光上也。从重火。凡炎之屬皆从炎。', '火', '', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6533, '燄', 'yàn', '第十卷', '炎部', '火行微燄燄也。从炎，臽聲。', '炎;臽', '臽', '庸', '雒誥：無若火始燄燄。漢書引作「庸庸」。', '炎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6534, '𤌜', 'yǎn', '第十卷', '炎部', '炎光也。从炎，㐁聲。', '炎;㐁', '㐁', 'null', NULL, '炎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6535, '𤎭', 'lǐn', '第十卷', '炎部', '侵火也。从炎，㐭聲。讀若「桑葚」之葚。', '炎;㐭;葚', '㐭;葚', 'null', NULL, '炎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6536, '煔', 'shǎn', '第十卷', '炎部', '火行也。从炎，占聲。', '炎;占', '占', '彡', '柀，煔也。煔、杉古今字。', '炎', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6537, '燅', 'xián', '第十卷', '炎部', '於湯中爚肉也。从炎，从熱省。或从炙作。', '炎;熱;炙', '', 'null', NULL, '炎;熱;炙', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6538, '𤏻', 'xiè', '第十卷', '炎部', '大孰也。从又，持炎辛。辛者，物孰味也。', '又;炎;辛', '', 'null', NULL, '又;炎;辛', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6539, '粦', 'lín', '第十卷', '炎部', '兵死及牛馬之血爲粦。粦，鬼火也。从炎舛。', '炎;舛', '', 'null', NULL, '炎;舛', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6540, '黑', 'hēi', '第十卷', '黑部', '北方色也。火所熏之色也。从炎上出𡆧[囪]。凡黑之屬皆从黑。', '炎;囪', '', 'null', NULL, '炎;囪', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6541, '黸', 'lú', '第十卷', '黑部', '齊謂黑爲黸。从黑，盧聲。', '黑;盧', '盧', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6542, '䵳', 'wèi', '第十卷', '黑部', '𣵽黑色也。从黑，會聲。', '黑;會', '會', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6543, '黯', 'àn', '第十卷', '黑部', '深黑也。从黑，音聲。', '黑;音', '音', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6544, '黶', 'yǎn', '第十卷', '黑部', '面中黑子也。从黑，厭聲。', '黑;厭', '厭', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6545, '黳', 'yī', '第十卷', '黑部', '小黑子。从黑，殹聲。', '黑;殹', '殹', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6546, '䵣', 'dá', '第十卷', '黑部', '白而有黑也。从黑，旦聲。五原有莫䵣縣。', '黑;旦', '旦', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6547, '𪒹', 'jiān', '第十卷', '黑部', '雖皙而黑也。从黑，箴聲。古人名𪒹字皙。', '黑;箴', '箴', '點', '今本論語曾皙名點。', '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6548, '䵮', 'yànɡ', '第十卷', '黑部', '赤黑也。从黑，昜聲。讀若煬。', '黑;昜;煬', '昜;煬', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6549, '黲', 'cǎn', '第十卷', '黑部', '淺靑黑色也。从黑，參聲。', '黑;參', '參', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6550, '黤', 'yǎn', '第十卷', '黑部', '靑黑色也。从黑，奄聲。', '黑;奄', '奄', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6551, '黝', 'yǒu', '第十卷', '黑部', '微靑黑色也。从黑，幼聲。爾雅曰「地謂之黝」。', '黑;幼', '幼', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6552, '黗', 'tūn', '第十卷', '黑部', '黃濁黑也。从黑，屯聲。', '黑;屯', '屯', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6553, '點', 'diǎn', '第十卷', '黑部', '小黑也。从黑，占聲。', '黑;占', '占', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6554, '黚', 'qián', '第十卷', '黑部', '淺黃黑也。从黑，甘聲。讀若「染繒中束緅黚[紺]」。', '黑;甘;紺', '甘;紺', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6555, '𪑙', 'jiān', '第十卷', '黑部', '黃黑也。从黑，金聲。', '黑;金', '金', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6556, '𪑲', 'yuè', '第十卷', '黑部', '黑有文也。从黑，冤聲。讀若「飴𧯡」字。', '黑;冤;𧯡', '冤;𧯡', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6557, '䵵', 'chuā', '第十卷', '黑部', '黃黑而白也。从黑，算聲。一曰短黑。讀若「㠯芥爲齏，名曰『芥荃』」也。', '黑;算;荃', '算;荃', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6558, '䵤', 'jiǎn', '第十卷', '黑部', '黑皴也。从黑，开聲。', '黑;开', '开', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6559, '黠', 'xiá', '第十卷', '黑部', '堅黑也。从黑，吉聲。', '黑;吉', '吉', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6560, '黔', 'qián', '第十卷', '黑部', '黎也。从黑，今聲。秦謂民爲「黔首」，謂黑色。周謂之「黎民」。易曰「爲黔喙」。', '黑;今', '今', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6561, '黕', 'dǎn', '第十卷', '黑部', '滓垢也。从黑，冘聲。', '黑;冘', '冘', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6562, '黨', 'dǎnɡ', '第十卷', '黑部', '不鮮也。从黑，尙聲。', '黑;尙', '尙', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6563, '黷', 'dú', '第十卷', '黑部', '握持垢也。从黑，𧷏聲。易曰「再三黷」。', '黑;𧷏', '𧷏', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6564, '黵', 'dǎn', '第十卷', '黑部', '大污也。从黑，詹聲。', '黑;詹', '詹', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6565, '黴', 'méi', '第十卷', '黑部', '中久雨靑黑也。从黑，微省聲。', '黑;微', '微', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6566, '黜', 'chù', '第十卷', '黑部', '貶下也。从黑，出聲。', '黑;出', '出', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6567, '𪒀', 'pán', '第十卷', '黑部', '𪒀姍，下色也。从黑，般聲。', '黑;般', '般', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6568, '黱', 'dài', '第十卷', '黑部', '畫眉墨也。从黑，朕聲。', '黑;朕', '朕', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6569, '儵', 'shū', '第十卷', '黑部', '靑黑繒發白色也。从黑，攸聲。', '黑;攸', '攸', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6570, '𪑝', 'yù', '第十卷', '黑部', '羔裘之縫也。从黑，或聲。', '黑;或', '或', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6571, '𪑩', 'diàn', '第十卷', '黑部', '𪑩謂之垽。垽，滓也。从黑，殿省聲。', '黑;殿', '殿', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6572, '黮', 'dǎn', '第十卷', '黑部', '桑葚之黑也。从黑，甚聲。', '黑;甚', '甚', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6573, '黭', 'yǎn', '第十卷', '黑部', '果實黭黯黑也。从黑，弇聲。', '黑;弇', '弇', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6574, '黥', 'qínɡ', '第十卷', '黑部', '墨㓝在面也。从黑，京聲。或从刀作。', '黑;京;刀', '京', 'null', NULL, '黑;刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6575, '𪒠', 'yǎn', '第十卷', '黑部', '𪒠者忘而息也。从黑，敢聲。', '黑;敢', '敢', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6576, '黟', 'yī', '第十卷', '黑部', '黑木也。从黑，多聲。丹楊有黟縣。', '黑;多', '多', 'null', NULL, '黑', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6577, '囪;窗;𡆧', 'chuānɡ', '第十卷', '囪部', '在牆曰牖。在屋曰囪。象形。凡囪之屬皆从囪。古文。窗或从穴。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6578, '悤', 'cōnɡ', '第十卷', '囪部', '多遽悤悤也。从囪，从心。囪亦聲。', '囪;心', '囪', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6579, '焱', 'yàn', '第十卷', '焱部', '火華也。从三火。凡焱之屬皆从焱。', '火', '', 'null', NULL, '火', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6580, '熒', 'yínɡ', '第十卷', '焱部', '屋下鐙燭之光也。从焱冂。', '焱;冂', '', 'null', NULL, '焱;冂', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6581, '燊', 'shēn', '第十卷', '焱部', '盛皃。从焱在木上。讀若詩曰「莘莘征夫」。莘，古文侁，役皃。', '焱;木;莘;侁', '莘;侁', 'null', NULL, '焱;木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6582, '炙', 'zhì', '第十卷', '炙部', '炙肉也。从肉在火上。凡炙之屬皆从炙。籒文[𣐺]。', '肉;火;𣐺', '', 'null', NULL, '肉;火;𣐺', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6583, '㸋', 'fán', '第十卷', '炙部', '宗廟火孰肉。天子所㠯饋同姓。从炙，番聲。春秋傳曰「天子有事㸋焉」。', '炙;番', '番', 'null', NULL, '炙', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6584, '爒', 'liǎo', '第十卷', '炙部', '炙也。从炙，尞聲。讀若「𪚰燎」。', '炙;尞;燎', '尞;燎', 'null', NULL, '炙', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6585, '赤', 'chì', '第十卷', '赤部', '南方色也。从大火。凡赤之屬皆从赤。古文从炎土。', '大;火;炎;土', '', 'null', NULL, '大;火;炎;土', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6586, '赨', 'tónɡ', '第十卷', '赤部', '赤色也。从赤，蟲省聲。', '赤;蟲', '蟲', 'null', NULL, '赤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6587, '𧹲', 'hù', '第十卷', '赤部', '日出之赤也。从赤，㱿聲。', '赤;㱿', '㱿', 'null', NULL, '赤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6588, '赧', 'nǎn', '第十卷', '赤部', '面慙而赤也。从赤，㞋聲。周失天下於赧王。', '赤;㞋', '㞋', 'null', NULL, '赤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6589, '䞓;赬', 'chēnɡ', '第十卷', '赤部', '赤色也。从赤，巠聲。詩曰「魴魚䞓尾」。䞓或从貞。或从丁。', '赤;巠;貞;丁', '巠;貞;丁', 'null', NULL, '赤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6590, '浾', 'chēnɡ', '第十卷', '赤部', '棠棗之汁也。从赤水。浾或从正。', '赤;水;正', '正', 'null', NULL, '赤;水', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6591, '赭', 'zhě', '第十卷', '赤部', '赤土也。从赤，者聲。', '赤;者', '者', 'null', NULL, '赤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6592, '𧹳', 'ɡàn', '第十卷', '赤部', '赤色也。从赤，倝聲。讀若浣。', '赤;倝;浣', '倝;浣', 'null', NULL, '赤', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6593, '赫', 'hè', '第十卷', '赤部', '大赤皃。从二赤。', '赤', '赤', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6594, '赩', 'xì', '第十卷', '赤部', '大赤也。从赤色。色亦聲。', '赤;色', '色', 'null', NULL, '赤', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6595, '赮', 'xiá', '第十卷', '赤部', '赤色也。从赤，叚聲。', '赤;叚', '叚', 'null', NULL, '赤', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6596, '大;太', 'dà', '第十卷', '大部', '天大。地大。人亦大焉。象人形。古文亣也。凡大之屬皆从大。', '亣', '亣', 'null', NULL, '', 'null', 'null', 'null', 'null', '月部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6597, '奎', 'kuí', '第十卷', '大部', '㒳髀之閒。从大，圭聲。', '大;圭', '圭', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6598, '夾', 'jiā', '第十卷', '大部', '持也。从大，㚒二人。', '大;人', '', 'null', NULL, '大;人', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6599, '奄', 'yǎn', '第十卷', '大部', '覆也。大有餘也。又欠也。从大申。申，展也。', '大;申', '', 'null', NULL, '大;申', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6600, '夸', 'kuā', '第十卷', '大部', '奢也。从大，亏聲。', '大;亏', '亏', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6601, '𡘍', 'huán', '第十卷', '大部', '奢𡘍也。从大，亘聲。', '大;亘', '亘', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6602, '𡗷', 'ɡū', '第十卷', '大部', '𡗷大也。从大，瓜聲。', '大;瓜', '瓜', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6603, '奯', 'huò', '第十卷', '大部', '空大也。从大，歲聲。讀若詩「施罟濊濊」。', '大;歲;濊', '歲;濊', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6604, '𢧤', 'zhì', '第十卷', '大部', '大也。从大，𢧄聲。讀若詩「𢧤𢧤[秩]大猷」。', '大;𢧄;秩', '𢧄;秩', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6605, '奅', 'pào', '第十卷', '大部', '大也。从大，丣聲。', '大;丣', '丣', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6606, '夽', 'yǔn', '第十卷', '大部', '大也。从大，云聲。讀若齳。', '大;云;齳', '云;齳', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6607, '奃', 'dī', '第十卷', '大部', '大也。从大，氐聲。讀若氐。', '大;氐', '氐', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6608, '𡗦', 'jiè', '第十卷', '大部', '大也。从大，介聲。讀若葢。', '大;介;葢', '介;葢', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6609, '𡗼', 'xiè', '第十卷', '大部', '瞋大聲也。从大，此聲。', '大;此', '此', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6610, '㚕;𡘉', 'bì', '第十卷', '大部', '大也。从大，弗聲。讀若「予違汝弻」。', '大;弗;弻', '弗;弻', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6611, '𡗥', 'chún', '第十卷', '大部', '大也。从大，屯聲。讀若鶉[𨿡]。', '大;屯;𨿡', '屯;𨿡', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6612, '契', 'qì', '第十卷', '大部', '大約也。从大，㓞聲。易曰「後世聖人，易之㠯書契」。', '大;㓞', '㓞', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6613, '夷', 'yí', '第十卷', '大部', '東方之人也。从大，从弓。', '大;弓', '', 'null', NULL, '大;弓', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6614, '亦', 'yì', '第十卷', '亦部', '人之臂亦也。从大。象㒳亦之形。凡亦之屬皆从亦。', '大', '', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6615, '㚒', 'shǎn', '第十卷', '亦部', '盜竊褱物也。从亦，有所持。俗謂「蔽人俾㚒」是也。弘農「陝」字从此。', '亦;陝', '陝', 'null', NULL, '亦', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6616, '夨', 'zè', '第十卷', '夨部', '傾頭也。从大。象形。凡夨之屬皆从夨。', '大', '', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6617, '𡔢', 'jié', '第十卷', '夨部', '頭傾也。从夨，吉聲。讀若孑。', '夨;吉;孑', '吉;孑', 'null', NULL, '夨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6618, '奊', 'xié', '第十卷', '夨部', '頭衺、骫奊態也。从夨，圭聲。', '夨;圭', '圭', 'null', NULL, '夨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6619, '吳', 'wú', '第十卷', '夨部', '大言也。从夨口。古文如此[大口]。', '夨;口;大;口', '', 'null', NULL, '夨;口;大;口', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6620, '𣅳', 'zè', '第十卷', '夨部', '日西也。从夨，日聲。', '夨;日', '日', 'null', NULL, '夨', 'null', 'null', 'null', 'null', '小徐重出应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6621, '夭', 'yāo', '第十卷', '夭部', '屈也。从大。象形。凡夭之屬皆从夭。', '大', '', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6622, '喬', 'qiáo', '第十卷', '夭部', '高而曲也。从夭，从高省。', '夭;高', '高', 'null', NULL, '夭', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6623, '幸;𡴘', 'xìnɡ', '第十卷', '夭部', '吉而免凶也。从屰，从夭。夭，死之事。死謂之不幸。', '屰;夭', '', 'null', NULL, '屰;夭', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6624, '奔', 'bēn', '第十卷', '夭部', '走也。从夭，卉聲。與走同意，俱从夭。', '夭;卉', '卉', 'null', NULL, '夭', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6625, '交', 'jiāo', '第十卷', '交部', '交脛也。从大。象交形。凡交之屬皆从交。', '大', '', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6626, '𩎮', 'wéi', '第十卷', '交部', '衺也。从交，韋聲。', '交;韋', '韋', 'null', NULL, '交', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6627, '絞', 'jiǎo', '第十卷', '交部', '縊也。从交糸。', '交;糸', '交', 'null', NULL, '糸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6628, '尢', 'wānɡ', '第十卷', '尢部', '㝿也。曲脛人也。从大。象偏曲之形。凡尢之屬皆从尢。篆文从㞷。', '大;㞷', '㞷', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6629, '尳', 'hú', '第十卷', '尢部', '厀病也。从尢骨。骨亦聲。', '尢;骨', '骨', 'null', NULL, '尢', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6630, '㝿', 'bǒ', '第十卷', '尢部', '蹇也。从尢，皮聲。', '尢;皮', '皮', 'null', NULL, '尢', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6631, '㝾', 'zuǒ', '第十卷', '尢部', '㝾㝾，行不正也。从尢，左聲。', '尢;左', '左', 'null', NULL, '尢', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6632, '㞁;𡯣', 'yào', '第十卷', '尢部', '行不正也。从尢，㫐聲。讀若燿。', '尢;㫐;燿', '㫐;燿', 'null', NULL, '尢', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6633, '尲', 'ɡān', '第十卷', '尢部', '尲尬，行不正也。从尢，兼聲。', '尢;兼', '兼', 'null', NULL, '尢', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6634, '尬', 'jiè', '第十卷', '尢部', '尲尬也。从尢，介聲。', '尢;介', '介', 'null', NULL, '尢', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6635, '尥', 'liào', '第十卷', '尢部', '行脛相交也。从尢，勺聲。', '尢;勺', '勺', 'null', NULL, '尢', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6636, '𡰖', 'dī', '第十卷', '尢部', '㝿不能行。爲人所引曰「𡰖𡰡」。从尢，从爪，是聲。', '尢;爪;是', '是', 'null', NULL, '尢;爪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6637, '𡰢;𡰡', 'xié', '第十卷', '尢部', '𡰖𡰢也。从尢，从爪，巂聲。', '尢;爪;巂', '巂', 'null', NULL, '尢;爪', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6638, '㝼', 'yū', '第十卷', '尢部', '股㝼也。从尢，于聲。', '尢;于', '于', 'null', NULL, '尢', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6639, '𡰠', 'léi', '第十卷', '尢部', '厀中病也。从尢，羸聲。', '尢;羸', '羸', 'null', NULL, '尢', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6640, '壺', 'hú', '第十卷', '壺部', '昆吾圜器也。象形。从大，象其葢也。凡壺之屬皆从壺。', '大', '', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6641, '㚃', 'yūn', '第十卷', '壺部', '壹㚃也。从凶，从壺。壺不得渫也。易曰「天地壹㚃」。', '凶;壺', '', 'null', NULL, '凶;壺', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6642, '壹', 'yī', '第十卷', '壹部', '嫥也。从壺吉。吉亦聲。凡壹之屬皆从壹。', '壺;吉', '吉', 'null', NULL, '壺', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6643, '懿', 'yì', '第十卷', '壹部', '嫥久而美也。从壹，从恣省聲。', '壹;恣', '恣', 'null', NULL, '壹', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6644, '𫯟', 'niè', '第十卷', '𫯟部', '所㠯驚人也。从大，从。一曰大聲也。凡𫯟之屬皆从𫯟。一曰讀若「瓠讘」。一曰俗語㠯盜不止爲𫯟。讀若籋。', '大;大;讘;籋', '大;讘;籋', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6645, '睪', 'yì', '第十卷', '𫯟部', '司視也。从目，从𫯟。今吏將目捕辠人也。', '目;𫯟', '', 'null', NULL, '目;𫯟', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6646, '執;𡘺', 'zhí', '第十卷', '𫯟部', '捕辠人也。从丮𫯟。𫯟亦聲。', '丮;𫯟', '𫯟', 'null', NULL, '丮', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6647, '圉', 'yǔ', '第十卷', '𫯟部', '囹圉，所㠯拘辠人。从囗𫯟。一曰圉，垂也。一曰圉人，掌馬者。', '囗;𫯟', '', 'null', NULL, '囗;𫯟', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6648, '盩', 'zhōu', '第十卷', '𫯟部', '引擊也。从𫯟，攴見血也。扶風有盩庢縣。', '𫯟;攴;血', '', 'null', NULL, '𫯟;攴;血', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6649, '報', 'bào', '第十卷', '𫯟部', '當辠人也。从𫯟，从𠬝。𠬝，服辠也。', '𫯟;𠬝', '', 'null', NULL, '𫯟;𠬝', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6650, '𥷚;𥷤;𥱩', 'jū', '第十卷', '𫯟部', '竆治辠人也。从𫯟人言，竹聲。或省言。', '𫯟;人;言;竹', '竹', 'null', NULL, '𫯟;人;言', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6651, '奢', 'shē', '第十卷', '奢部', '張也。从大，者聲。凡奢之屬皆从奢。籒文[多]。', '大;者;多', '者', 'null', NULL, '大;多', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6652, '奲', 'duǒ', '第十卷', '奢部', '富奲奲皃。从奢，單聲。', '奢;單', '單', 'null', NULL, '奢', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6653, '亢;頏', 'ɡānɡ', '第十卷', '亢部', '人頸也。从大省。象頸脈形。凡亢之屬皆从亢。亢或从頁。', '大;頁', '', 'null', NULL, '大;頁', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6654, '𡕬', 'hànɡ', '第十卷', '亢部', '直項莽𡕬皃。从亢，从夋。夋，倨也。亢亦聲。', '亢;夋', '亢', 'null', NULL, '夋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6655, '夲', 'tāo', '第十卷', '夲部', '進趣也。从大十。大十者，猶兼十人也。凡夲之屬皆从夲。讀若滔。', '大;十;滔', '滔', 'null', NULL, '大;十', 'null', 'null', 'null', 'null', '️', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6656, '𠦪', 'hū', '第十卷', '夲部', '疾也。从夲，卉聲。', '夲;卉', '卉', 'null', NULL, '夲', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6657, '𣉱;暴', 'bào', '第十卷', '夲部', '疾有所趣也。从日出夲廾之。', '日;出;夲;廾', '', 'null', NULL, '日;出;夲;廾', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6658, '𡴞', 'yǔn', '第十卷', '夲部', '進也。从夲，从屮，允聲。易曰「𡴞升大吉」。', '夲;屮;允', '允', 'null', NULL, '夲;屮', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6659, '奏', 'zòu', '第十卷', '夲部', '奏，進也。从夲，从廾，从屮。屮，上進之義。古文[尸釆廾]。亦古文[釆攴]。', '夲;廾;屮;尸;釆;廾;釆;攴', '', 'null', NULL, '夲;廾;屮;尸;釆;廾;釆;攴', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6660, '皋;臯', 'ɡāo', '第十卷', '夲部', '气皋白之進也。从白夲。禮「祝曰皋，登謌曰奏」，故皋、奏皆从夲。周禮曰「詔來𡔷皋舞」。', '白;夲', '', 'null', NULL, '白;夲', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6661, '夰', 'ɡǎo', '第十卷', '夰部', '放也。从大八。八，分也。凡夰之屬皆从夰。', '大;八', '', 'null', NULL, '大;八', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6662, '𥉁', 'jù', '第十卷', '夰部', '舉目驚𥉁然也。从夰，从䀠。䀠亦聲。', '夰;䀠', '䀠', 'null', NULL, '夰', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6663, '奡', 'ào', '第十卷', '夰部', '嫚也。从𦣻，从夰。夰亦聲。虞書曰「若丹朱奡」。讀若傲。論語「奡湯舟」。', '𦣻;夰;傲', '夰;傲', 'null', NULL, '𦣻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6664, '昦;昊', 'hào', '第十卷', '夰部', '春爲昦天。元气昦昦也。从日夰。夰亦聲。', '日;夰', '夰', 'null', NULL, '日', 'null', 'null', 'null', 'null', '经', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6665, '臩', 'ɡuǎnɡ', '第十卷', '夰部', '驚走也。一曰往來皃。从夰，臦聲。周書曰「伯臩」。古文臦古文囧字。', '夰;臦;囧', '臦;囧', 'null', NULL, '夰', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6666, '亣', 'dà', '第十卷', '亣部', '籒文大。改古文。亦象人形。凡亣之屬皆从亣。', '大', '大', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6667, '奕', 'yì', '第十卷', '亣部', '大也。从亣，亦聲。詩曰「奕奕梁山」。', '亣;亦', '亦', 'null', NULL, '亣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6668, '奘', 'zànɡ', '第十卷', '亣部', '駔大也。从亣壯。壯亦聲。', '亣;壯', '壯', 'null', NULL, '亣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6669, '㚖', 'ɡǎo', '第十卷', '亣部', '大白也。从亣白。古文㠯爲澤字。', '亣;白;皋', '皋', 'null', NULL, '亣;白', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6670, '奚', 'xī', '第十卷', '亣部', '大腹也。从亣，𦃟省聲。𦃟，籒文系。', '亣;𦃟', '𦃟', 'null', NULL, '亣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6671, '耎', 'ruǎn', '第十卷', '亣部', '稍歬大也。从亣，而聲。讀若「畏偄」。', '亣;而;偄', '而;偄', 'null', NULL, '亣', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6672, '𡚇', 'yàn', '第十卷', '亣部', '大皃。从大，𥆞聲。或曰「拳[捲]勇」字。讀若傿。', '大;𥆞;捲;傿', '𥆞;捲;傿', 'null', NULL, '大', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6673, '𡚤;奰', 'bì', '第十卷', '亣部', '壯大也。从三大三目。三目爲𡚤，益大也。一曰迫也。讀若易「虙羲氏」。詩曰「不醉而怒謂之奰」。', '大;目;虙', '虙', 'null', NULL, '大;目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6674, '夫', 'fū', '第十卷', '夫部', '丈夫也。从大一。一㠯象兂。周制：八寸爲尺，十尺爲丈，人長八尺，故曰「丈夫」。凡夫之屬皆从夫。', '大;一', '', 'null', NULL, '大;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6675, '規', 'ɡuī', '第十卷', '夫部', '規巨，有法度也。从夫見。', '夫;見', '', 'null', NULL, '夫;見', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6676, '㚘', 'bàn', '第十卷', '夫部', '竝行也。从二夫。輦字从此。讀若「伴侣」之伴。', '夫;伴', '伴', 'null', NULL, '夫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6677, '立', 'lì', '第十卷', '立部', '侸也。从大。在一之上。凡立之屬皆从立。', '大;一', '', 'null', NULL, '大;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6678, '䇐', 'lì', '第十卷', '立部', '臨也。从立，隶聲。', '立;隶', '隶', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6679, '䇏', 'duì', '第十卷', '立部', '磊䇏，重聚也。从立，𦎫聲。', '立;𦎫', '𦎫', 'null', NULL, '立', 'null', 'null', 'null', 'null', '微部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6680, '端', 'duān', '第十卷', '立部', '直也。从立，耑聲。', '立;耑', '耑', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6681, '竱', 'zhuǎn', '第十卷', '立部', '等也。从立，專聲。春秋傳曰「竱本肈末」。', '立;專', '專', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6682, '竦', 'sǒnɡ', '第十卷', '立部', '敬也。从立，从束。束，自申束也。', '立;束', '束', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6683, '竫', 'jìnɡ', '第十卷', '立部', '亭安也。从立，爭聲。', '立;爭', '爭', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6684, '靖', 'jìnɡ', '第十卷', '立部', '立竫也。从立，靑聲。一曰細皃。', '立;靑', '靑', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6685, '竢', 'sì', '第十卷', '立部', '待也。从立，矣聲。或从巳。', '立;矣;巳', '矣;巳', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6686, '竘', 'qǔ', '第十卷', '立部', '健也。一曰匠也。从立，句聲。讀若齲。周書有「竘匠」。', '立;句;齲', '句;齲', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6687, '竵', 'huā', '第十卷', '立部', '不正也。从立，𩰫聲。', '立;𩰫', '𩰫', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6688, '竭', 'jié', '第十卷', '立部', '負舉也。从立，曷聲。', '立;曷', '曷', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6689, '𩓣;䇓', 'xū', '第十卷', '立部', '立而待也。从立，須聲。或从芻。', '立;須;芻', '須;芻', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6690, '䇔', 'luò', '第十卷', '立部', '痿也。从立，𦟀聲。', '立;𦟀', '𦟀', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6691, '竣', 'jùn', '第十卷', '立部', '居也。从立，夋聲。國語曰「有司巳事而竣」。', '立;夋', '夋', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6692, '𥪋', 'fú', '第十卷', '立部', '見鬼鬽皃。从立，从𢑘。𢑘，籒文鬽。讀若「虙羲氏」之虙。', '立;𢑘;虙', '𢑘;虙', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6693, '䇎', 'què', '第十卷', '立部', '驚皃。从立，昔聲。', '立;昔', '昔', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6694, '䇑', 'bà', '第十卷', '立部', '短人立䇑䇑皃。从立，卑聲。', '立;卑', '卑', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6695, '竲', 'cénɡ', '第十卷', '立部', '北地高樓無屋者。从立，曾聲。', '立;曾', '曾', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6696, '竝;並', 'bìnɡ', '第十卷', '竝部', '倂也。从二立。凡竝之屬皆从竝。', '立', '', 'null', NULL, '立', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6697, '替', 'tì', '第十卷', '竝部', '廢也。一偏下也。从竝，𪞶聲。或从曰。或从兟，从曰。', '竝;𪞶;曰;兟;曰', '𪞶', 'null', NULL, '竝;曰;兟;曰', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6698, '𦥓;囟', 'xìn', '第十卷', '𦥓部', '頭會匘葢也。象形。凡𦥓之屬皆从𦥓。或从肉宰。古文𦥓字。', '肉;宰', '', 'null', NULL, '肉;宰', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6699, '巤', 'liè', '第十卷', '𦥓部', '毛巤也。象髮在𦥓上。及毛髮巤巤之形也。此與籒文子字同意。', '𦥓', '', 'null', NULL, '𦥓', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6700, '𣬈;毗;𣬉;毘', 'pí', '第十卷', '𦥓部', '毗臍，人臍也。从𦥓。𦥓取通气也。从比聲。', '𦥓;比', '比', 'null', NULL, '𦥓', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6701, '思;恖', 'sī', '第十卷', '思部', '䜭也。从心，从𦥓。凡思之屬皆从思。', '心;𦥓', '', 'null', NULL, '心;𦥓', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6702, '慮', 'lǜ', '第十卷', '思部', '謀思也。从思，虍聲。', '思;虍', '虍', 'null', NULL, '思', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6703, '心', 'xīn', '第十卷', '心部', '人心。土臧也。在身之中象形。博士說㠯爲火臧。凡心之屬皆从心。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6704, '息', 'xī', '第十卷', '心部', '喘也。从心自。', '心;自', '', 'null', NULL, '心;自', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6705, '情', 'qínɡ', '第十卷', '心部', '人之侌气有欲者。从心，靑聲。', '心;靑', '靑', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6706, '性', 'xìnɡ', '第十卷', '心部', '人之昜气性。善者也。从心，生聲。', '心;生', '生', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6707, '志', 'zhì', '第十卷', '心部', '意也。从心之。之亦聲。', '心;之', '之', 'null', NULL, '心', 'null', 'null', 'null', 'null', '大徐补', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6708, '意', 'yì', '第十卷', '心部', '志也。从心音。察言而知意也。一曰十萬曰意。籒文意。', '心;音', '', 'null', NULL, '心;音', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6709, '恉', 'zhǐ', '第十卷', '心部', '意也。从心，旨聲。', '心;旨', '旨', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6710, '悳;𢛳', 'dé', '第十卷', '心部', '外得於人，內得於己也。从直心。古文。', '直;心', '直', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6711, '應', 'yīnɡ', '第十卷', '心部', '當也。从心，䧹聲。', '心;䧹', '䧹', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6712, '愼;慎;昚', 'shèn', '第十卷', '心部', '謹也。从心，眞聲。古文。', '心;眞', '眞', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6713, '忠', 'zhōnɡ', '第十卷', '心部', '敬也。盡心曰忠。从心，中聲。', '心;中', '中', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6714, '愨;慤', 'què', '第十卷', '心部', '謹也。从心，㱿聲。', '心;㱿', '㱿', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6715, '𢤧', 'miǎo', '第十卷', '心部', '美也。从心，䫉聲。', '心;䫉', '䫉', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6716, '快', 'kuài', '第十卷', '心部', '喜也。从心，夬聲。一曰疾也。', '心;夬', '夬', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6717, '愷', 'kǎi', '第十卷', '心部', '樂也。从心，豈聲。', '心;豈', '豈', 'null', NULL, '心', 'null', 'null', 'null', 'null', '重出应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6718, '㥦', 'qiè', '第十卷', '心部', '快也。从心，匧聲。', '心;匧', '匧', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6719, '念', 'niàn', '第十卷', '心部', '常思也。从心，今聲。', '心;今', '今', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6720, '怤', 'fū', '第十卷', '心部', '思也。从心，付聲。', '心;付', '付', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6721, '憲', 'xiàn', '第十卷', '心部', '敏也。从心目。害省聲。', '心;目;害', '害', 'null', NULL, '心;目', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6722, '憕', 'chénɡ', '第十卷', '心部', '平也。从心，登聲。', '心;登', '登', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6723, '戁', 'nǎn', '第十卷', '心部', '敬也。从心，難聲。', '心;難', '難', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6724, '忻', 'xīn', '第十卷', '心部', '闓也。从心，斤聲。司馬法曰「善者，忻民之善，閉民之惡」。', '心;斤', '斤', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6725, '𢝆', 'zhònɡ', '第十卷', '心部', '遲也。从心，重聲。', '心;重', '重', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6726, '惲', 'yùn', '第十卷', '心部', '重厚也。从心，軍聲。', '心;軍', '軍', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6727, '惇', 'dūn', '第十卷', '心部', '厚也。从心，𦎫聲。', '心;𦎫', '𦎫', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6728, '忼', 'kànɡ', '第十卷', '心部', '忼慨也。忼慨壯士，不得志於心也。从心，亢聲。易曰「忼龍有悔」。', '心;亢', '亢', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6729, '慨', 'kǎi', '第十卷', '心部', '忼慨也。从心，漑聲。', '心;漑', '漑', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6730, '𢛕;悃', 'kǔn', '第十卷', '心部', '𢛕愊，至誠也。从心，囷聲。', '心;囷', '囷', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6731, '愊', 'bì', '第十卷', '心部', '𢛕愊也。从心，畐聲。', '心;畐', '畐', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6732, '愿', 'yuàn', '第十卷', '心部', '謹也。从心，原聲。', '心;原', '原', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6733, '慧', 'huì', '第十卷', '心部', '儇也。从心，彗聲。', '心;彗', '彗', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6734, '憭', 'liǎo', '第十卷', '心部', '慧也。从心，尞聲。', '心;尞', '尞', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6735, '恔', 'xiáo', '第十卷', '心部', '憭也。从心，交聲。', '心;交', '交', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6736, '瘱', 'yì', '第十卷', '心部', '靜也。从心，㾜聲。', '心;㾜', '㾜', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6737, '悊', 'zhé', '第十卷', '心部', '敬也。从心，㪿聲。', '心;㪿', '㪿', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6738, '悰', 'cónɡ', '第十卷', '心部', '樂也。从心，宗聲。', '心;宗', '宗', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6739, '𢙖;恬', 'tián', '第十卷', '心部', '安也。从心，㐁聲。', '心;㐁', '㐁', 'null', NULL, '心', 'null', 'null', 'null', 'null', '谈部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6740, '恢', 'huī', '第十卷', '心部', '大也。从心，灰聲。', '心;灰', '灰', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6741, '恭', 'ɡōnɡ', '第十卷', '心部', '肅也。从心，共聲。', '心;共', '共', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6742, '憼', 'jǐnɡ', '第十卷', '心部', '敬也。从心敬。敬亦聲。', '心;敬', '敬', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6743, '恕', 'shù', '第十卷', '心部', '仁也。从心，如聲。古文省。', '心;如', '如', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6744, '怡', 'yí', '第十卷', '心部', '龢也。从心，台聲。', '心;台', '台', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6745, '慈', 'cí', '第十卷', '心部', '㤅也。从心，茲聲。', '心;茲', '茲', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6746, '忯', 'qí', '第十卷', '心部', '㤅也。从心，氏聲。', '心;氏', '氏', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6747, '㥴', 'yǐ', '第十卷', '心部', '忯㥴，不𢝊事也。从心，虒聲。讀若移。', '心;虒;移', '虒;移', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6748, '恮', 'quān', '第十卷', '心部', '謹也。从心，全聲。', '心;全', '全', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6749, '恩', 'ēn', '第十卷', '心部', '惠也。从心因。因亦聲。', '心;因', '因', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6750, '慸', 'dì', '第十卷', '心部', '高也。一曰極也。一曰困劣也。从心，帶聲。', '心;帶', '帶', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6751, '憖', 'yìn', '第十卷', '心部', '肎也。謹敬也。从心，猌聲。一曰說也。一曰且也。春秋傳曰「昊天不憖」。又曰「兩軍之士皆未憖」。', '心;猌', '猌', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6752, '懬', 'kuànɡ', '第十卷', '心部', '闊也，廣大也。从心廣。廣亦聲。一曰寬也。詩曰「懬彼淮夷」。', '心;廣', '廣', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6753, '悈', 'jiè', '第十卷', '心部', '飭也。从心，戒聲。司馬法曰「有虞氏悈於中國」。', '心;戒', '戒', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6754, '㥯', 'yǐn', '第十卷', '心部', '謹也。从心，𤔌聲。', '心;𤔌', '𤔌', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6755, '慶', 'qìnɡ', '第十卷', '心部', '行賀人也。从心夊，从鹿省。吉禮㠯鹿皮爲摯，故从鹿省。', '心;夊;鹿', '', 'null', NULL, '心;夊;鹿', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6756, '愃', 'xuǎn', '第十卷', '心部', '寛閒心腹皃。从心，宣聲。詩曰「赫兮愃兮」。', '心;宣', '宣', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6757, '愻', 'xùn', '第十卷', '心部', '順也。从心，孫聲。唐書曰「五品不愻」。', '心;孫', '孫', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6758, '𢥛;㥶', 'sè', '第十卷', '心部', '實也。从心，𡫳聲。虞書曰「剛而𢥛」。', '心;𡫳', '𡫳', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6759, '恂', 'xún', '第十卷', '心部', '信心也。从心，旬聲。', '心;旬', '旬', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6760, '忱', 'chén', '第十卷', '心部', '誠也。从心，冘聲。詩曰「天命匪忱」。', '心;冘', '冘', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6761, '惟', 'wéi', '第十卷', '心部', '凡思也。从心，隹聲。', '心;隹', '隹', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6762, '懷', 'huái', '第十卷', '心部', '念思也。从心，褱聲。', '心;褱', '褱', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6763, '惀', 'lún', '第十卷', '心部', '欲知之皃。从心，侖聲。', '心;侖', '侖', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6764, '想', 'xiǎnɡ', '第十卷', '心部', '覬思也。从心，相聲。', '心;相', '相', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6765, '㥞', 'suì', '第十卷', '心部', '深也。从心，㒸聲。', '心;㒸', '㒸', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6766, '慉', 'xù', '第十卷', '心部', '起也。从心，畜聲。詩曰「能不我慉」。', '心;畜', '畜', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6767, '𢡃', 'yì', '第十卷', '心部', '滿也。从心，𠶷聲。一曰十萬曰𢡃。籒文省。', '心;𠶷', '𠶷', 'null', NULL, '心', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6768, '悹', 'ɡuàn', '第十卷', '心部', '𢝊也。从心，官聲。', '心;官', '官', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6769, '憀', 'liáo', '第十卷', '心部', '憀然也。从心，翏聲。', '心;翏', '翏', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6770, '愙', 'kè', '第十卷', '心部', '敬也。从心，客聲。春秋傳曰「㠯陳僃三愙」。', '心;客', '客', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6771, '愯', 'sǒnɡ', '第十卷', '心部', '懼也。从心，雙省聲。春秋傳曰「駟氏愯」。', '心;雙', '雙', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6772, '懼', 'jù', '第十卷', '心部', '恐也。从心，瞿聲。古文[䀠]。', '心;瞿;䀠', '瞿;䀠', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6773, '怙', 'hù', '第十卷', '心部', '恃也。从心，古聲。', '心;古', '古', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6774, '恃', 'shì', '第十卷', '心部', '賴也。从心，寺聲。', '心;寺', '寺', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6775, '慒', 'cónɡ', '第十卷', '心部', '慮也。从心，曹聲。', '心;曹', '曹', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6776, '悟', 'wù', '第十卷', '心部', '覺也。从心，吾聲。古文悟[疊五]。', '心;吾;五', '吾;五', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6777, '憬', 'jǐnɡ', '第十卷', '心部', '悟也。从心，景聲。詩曰「憬彼淮夷」。', '心;景', '景', 'null', NULL, '心', 'null', 'null', 'null', 'null', '政宇应删 | 调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6778, '憮', 'wǔ', '第十卷', '心部', '㤅也。韓、鄭曰憮。一曰不動。从心，無聲。', '心;無', '無', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6779, '㤅', 'ài', '第十卷', '心部', '惠也。从心，旡聲。古文[旣]。', '心;旡;旣', '旡;旣', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6780, '㥠', 'xǔ', '第十卷', '心部', '知也。从心，胥聲。', '心;胥', '胥', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6781, '慰', 'wèi', '第十卷', '心部', '安也。从心，𤈫聲。一曰恚怒也。', '心;𤈫', '𤈫', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6782, '𢡈', 'cuì', '第十卷', '心部', '謹也。从心，𠭥聲。讀若毳。', '心;𠭥;毳', '𠭥;毳', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6783, '𥲅', 'chóu', '第十卷', '心部', '𥲅箸也。从心，聲[籌省]。', '心;籌', '籌', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6784, '怞', 'chóu', '第十卷', '心部', '朖也。从心，由聲。詩曰「憂心且怞」。', '心;由', '由', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6785, '𢜮', 'wǔ', '第十卷', '心部', '𢜮，憮也。从心，某聲。讀若侮。', '心;某;侮', '某;侮', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6786, '忞', 'mín', '第十卷', '心部', '自勉彊也。从心，文聲。周書曰「在受德忞」。讀若旻。', '心;文;旻', '文;旻', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6787, '慔', 'mù', '第十卷', '心部', '勉也。从心，莫聲。', '心;莫', '莫', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6788, '愐', 'miǎn', '第十卷', '心部', '勉也。从心，面聲。', '心;面', '面', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6789, '𢘽', 'yì', '第十卷', '心部', '習也。一曰朙也。从心，曳聲。', '心;曳', '曳', 'null', NULL, '心', 'null', 'null', 'null', 'null', '篆头应复正', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6790, '懋', 'mào', '第十卷', '心部', '勉也。从心，楙聲。虞書曰「時惟懋哉」。或省[矛]。', '心;楙;矛', '楙;矛', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6791, '𢟽;慕', 'mù', '第十卷', '心部', '習也。从心，莫聲。', '心;莫', '莫', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6792, '悛', 'quān', '第十卷', '心部', '止也。从心，夋聲。', '心;夋', '夋', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6793, '㥆', 'tū tuì', '第十卷', '心部', '肆也。从心，隶聲。', '心;隶', '隶', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6794, '㦛', 'yǔ', '第十卷', '心部', '趣步㦛㦛也。从心，與聲。', '心;與', '與', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6795, '慆', 'tāo', '第十卷', '心部', '說也。从心，舀聲。', '心;舀', '舀', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6796, '懕', 'yān', '第十卷', '心部', '安也。从心，厭聲。詩曰「懕懕夜㱃」。', '心;厭', '厭', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6797, '憺', 'dàn', '第十卷', '心部', '安也。从心，詹聲。', '心;詹', '詹', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6798, '怕', 'bó', '第十卷', '心部', '無僞也。从心，白聲。', '心;白', '白', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6799, '恤', 'xù', '第十卷', '心部', '𢝊也，收也。从心，血聲。', '心;血', '血', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6800, '忓', 'ɡān', '第十卷', '心部', '極也。从心，干聲。', '心;干', '干', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6801, '懽', 'ɡuàn', '第十卷', '心部', '喜款也。从心，雚聲。爾雅曰「懽懽、愮愮，憂無告也」。', '心;雚', '雚', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6802, '㥥', 'yú', '第十卷', '心部', '懽也。从心，禺聲。琅邪 朱虛 有㥥亭。', '心;禺', '禺', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6803, '惄', 'nì', '第十卷', '心部', '飢餓也。从心，叔聲。一曰𢝊也。詩曰「惄如輖飢」。', '心;叔', '叔', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6804, '𢜭', 'jǐ', '第十卷', '心部', '勞也。从心，卻聲。', '心;卻', '卻', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6805, '憸', 'xiān', '第十卷', '心部', '憸詖也。憸利於上，佞人也。从心，僉聲。', '心;僉', '僉', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6806, '愒', 'qì', '第十卷', '心部', '息也。从心，曷聲。', '心;曷', '曷', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6807, '㦌', 'hū', '第十卷', '心部', '精戇也。从心，毳聲。', '心;毳', '毳', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6808, '𢘁', 'xiān', '第十卷', '心部', '疾利口也。从心，从冊。詩曰「相時𢘁民」。', '心;冊', '', 'null', NULL, '心;冊', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6809, '急', 'jí', '第十卷', '心部', '褊也。从心，及聲。', '心;及', '及', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
