INSERT INTO `shuowen_voice_revel` VALUES (6815, '𢛆', 'xián', '第十卷', '心部', '急也。从心弦。弦亦聲。河南 密縣有「𢛆亭」。', '心;弦', '弦', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
