INSERT INTO `shuowen_voice_revel` VALUES (6818, '恁', 'rèn', '第十卷', '心部', '下齎也。从心，任聲。', '心;任', '任', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6819, '𢘋', 'tè', '第十卷', '心部', '失常也。从心，代聲。', '心;代', '代', 'null', NULL, '心', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6820, '怚', 'jù', '第十卷', '心部', '驕也。从心，且聲。', '心;且', '且', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6821, '悒', 'yì', '第十卷', '心部', '不安也。从心，邑聲。', '心;邑', '邑', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6822, '悆', 'yù', '第十卷', '心部', '忘也，嘾也。从心，余聲。周書曰「有疾不悆」。悆，喜也。', '心;余', '余', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6823, '忒', 'tè', '第十卷', '心部', '更也。从心，弋聲。', '心;弋', '弋', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6824, '憪;𢢀', 'xián', '第十卷', '心部', '愉也。从心，閒聲。', '心;閒', '閒', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6825, '愉', 'yú', '第十卷', '心部', '薄也。从心，俞聲。論語曰「私覿愉愉如也」。', '心;俞', '俞', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6826, '懱', 'miè', '第十卷', '心部', '輕易也。从心，蔑聲。商書曰「㠯相陵懱」。', '心;蔑', '蔑', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6827, '愚', 'yú', '第十卷', '心部', '戇也。从心禺。禺，母猴屬，獸之愚者。', '心;禺', '禺', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6828, '戇', 'zhuànɡ', '第十卷', '心部', '愚也。从心，贛聲。', '心;贛', '贛', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6829, '憃', 'chōnɡ', '第十卷', '心部', '愚也。从心，舂聲。', '心;舂', '舂', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6830, '懝', 'ài', '第十卷', '心部', '騃也。从心疑。疑亦聲。一曰惶也。', '心;疑', '疑', 'null', NULL, '心', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
